INSERT INTO `poem` VALUES (1, '行宫', '唐代', '元稹', '寥落古行宫，宫花寂寞红。 白头宫女在，闲坐说玄宗。', '唐诗三百首');
INSERT INTO `poem` VALUES (2, '登鹳雀楼', '唐代', '王之涣', '白日依山尽，黄河入海流。 欲穷千里目，更上一层楼。', '唐诗三百首');
INSERT INTO `poem` VALUES (3, '新嫁娘词', '唐代', '王建', '三日入厨下，洗手作羹汤。 未谙姑食性，先遣小姑尝。', '唐诗三百首');
INSERT INTO `poem` VALUES (4, '相思', '唐代', '王维', '红豆生南国，春来发几枝。 愿君多采撷，此物最相思。', '唐诗三百首');
INSERT INTO `poem` VALUES (5, '杂诗三首·其二', '唐代', '王维', '君自故乡来，应知故乡事。 来日绮窗前，寒梅著花未？', '唐诗三百首');
INSERT INTO `poem` VALUES (6, '鹿柴', '唐代', '王维', '空山不见人，但闻人语响。 返景入深林，复照青苔上。', '唐诗三百首');
INSERT INTO `poem` VALUES (7, '竹里馆', '唐代', '王维', '独坐幽篁里，弹琴复长啸。 深林人不知，明月来相照。', '唐诗三百首');
INSERT INTO `poem` VALUES (8, '送别 / 山中送别', '唐代', '王维', '山中相送罢，日暮掩柴扉。 春草明年绿，王孙归不归？(明年 一作：年年)', '唐诗三百首');
INSERT INTO `poem` VALUES (9, '问刘十九', '唐代', '白居易', '绿蚁新醅酒，红泥小火炉。 晚来天欲雪，能饮一杯无？', '唐诗三百首');
INSERT INTO `poem` VALUES (10, '哥舒歌', '唐代', '西鄙人', '北斗七星高，哥舒夜带刀。 至今窥牧马，不敢过临洮。', '唐诗三百首');
INSERT INTO `poem` VALUES (11, '静夜思', '唐代', '李白', '床前明月光，疑是地上霜。 举头望明月，低头思故乡。', '唐诗三百首');
INSERT INTO `poem` VALUES (12, '怨情', '唐代', '李白', '美人卷珠帘，深坐颦蛾眉。(颦 一作：蹙) 但见泪痕湿，不知心恨谁。', '唐诗三百首');
INSERT INTO `poem` VALUES (13, '乐游原 / 登乐游原', '唐代', '李商隐', '向晚意不适，驱车登古原。 夕阳无限好，只是近黄昏。', '唐诗三百首');
INSERT INTO `poem` VALUES (14, '听筝', '唐代', '李端', '鸣筝金粟柱，素手玉房前。 欲得周郎顾，时时误拂弦。', '唐诗三百首');
INSERT INTO `poem` VALUES (15, '渡汉江', '唐代', '宋之问', '岭外音书断，经冬复历春。 近乡情更怯，不敢问来人。', '唐诗三百首');
INSERT INTO `poem` VALUES (16, '八阵图', '唐代', '杜甫', '功盖三分国，名成八阵图。（名成 一作：名高） 江流石不转，遗恨失吞吴。', '唐诗三百首');
INSERT INTO `poem` VALUES (17, '宿建德江', '唐代', '孟浩然', '移舟泊烟渚，日暮客愁新。 野旷天低树，江清月近人。', '唐诗三百首');
INSERT INTO `poem` VALUES (18, '春晓', '唐代', '孟浩然', '春眠不觉晓，处处闻啼鸟。 夜来风雨声，花落知多少。', '唐诗三百首');
INSERT INTO `poem` VALUES (19, '春怨 / 伊州歌', '唐代', '金昌绪', '打起黄莺儿，莫教枝上啼。 啼时惊妾梦，不得到辽西。', '唐诗三百首');
INSERT INTO `poem` VALUES (20, '江雪', '唐代', '柳宗元', '千山鸟飞绝，万径人踪灭。 孤舟蓑笠翁，独钓寒江雪。', '唐诗三百首');
INSERT INTO `poem` VALUES (21, '秋夜寄邱员外 / 秋夜寄丘二十二员外', '唐代', '韦应物', '怀君属秋夜，散步咏凉天。 空山松子落，幽人应未眠。', '唐诗三百首');
INSERT INTO `poem` VALUES (22, '终南望余雪', '唐代', '祖咏', '终南阴岭秀，积雪浮云端。 林表明霁色，城中增暮寒。', '唐诗三百首');
INSERT INTO `poem` VALUES (23, '宫词', '唐代', '张祜', '故国三千里，深宫二十年。 一声何满子，双泪落君前。', '唐诗三百首');
INSERT INTO `poem` VALUES (24, '寻隐者不遇 / 孙革访羊尊师诗', '唐代', '贾岛', '松下问童子，言师采药去。 只在此山中，云深不知处。', '唐诗三百首');
INSERT INTO `poem` VALUES (25, '崔九欲往南山马上口号与别 / 留别王维 / 送崔九', '唐代', '裴迪', '归山深浅去，须尽丘壑美。 莫学武陵人，暂游桃源里。', '唐诗三百首');
INSERT INTO `poem` VALUES (26, '送灵澈上人', '唐代', '刘长卿', '苍苍竹林寺，杳杳钟声晚。 荷笠带斜阳，青山独归远。(斜阳 一作：夕阳)', '唐诗三百首');
INSERT INTO `poem` VALUES (27, '听弹琴', '唐代', '刘长卿', '泠泠七弦上，静听松风寒。(七弦 一作：七丝) 古调虽自爱，今人多不弹。', '唐诗三百首');
INSERT INTO `poem` VALUES (28, '送方外上人 / 送上人', '唐代', '刘长卿', '孤云将野鹤，岂向人间住。 莫买沃洲山，时人已知处。', '唐诗三百首');
INSERT INTO `poem` VALUES (29, '玉台体', '唐代', '权德舆', '昨夜裙带解，今朝蟢子飞。 铅华不可弃，莫是藁砧归。', '唐诗三百首');
INSERT INTO `poem` VALUES (30, '芙蓉楼送辛渐', '唐代', '王昌龄', '寒雨连江夜入吴，平明送客楚山孤。 洛阳亲友如相问，一片冰心在玉壶。', '唐诗三百首');
INSERT INTO `poem` VALUES (31, '闺怨', '唐代', '王昌龄', '闺中少妇不知愁，春日凝妆上翠楼。 忽见陌头杨柳色，悔教夫婿觅封侯。', '唐诗三百首');
INSERT INTO `poem` VALUES (32, '春宫曲', '唐代', '王昌龄', '昨夜风开露井桃，未央前殿月轮高。 平阳歌舞新承宠，帘外春寒赐锦袍。', '唐诗三百首');
INSERT INTO `poem` VALUES (33, '九月九日忆山东兄弟', '唐代', '王维', '独在异乡为异客，每逢佳节倍思亲。 遥知兄弟登高处，遍插茱萸少一人。', '唐诗三百首');
INSERT INTO `poem` VALUES (34, '凉州词二首·其一', '唐代', '王翰', '葡萄美酒夜光杯，欲饮琵琶马上催。 醉卧沙场君莫笑，古来征战几人回？', '唐诗三百首');
INSERT INTO `poem` VALUES (35, '后宫词', '唐代', '白居易', '泪湿罗巾梦不成，夜深前殿按歌声。 红颜未老恩先断，斜倚薰笼坐到明。', '唐诗三百首');
INSERT INTO `poem` VALUES (36, '宫词 / 宫中词', '唐代', '朱庆馀', '寂寂花时闭院门，美人相并立琼轩。 含情欲说宫中事，鹦鹉前头不敢言。', '唐诗三百首');
INSERT INTO `poem` VALUES (37, '近试上张籍水部 / 近试上张水部 / 闺意献张水部', '唐代', '朱庆馀', '洞房昨夜停红烛，待晓堂前拜舅姑。 妆罢低声问夫婿，画眉深浅入时无。', '唐诗三百首');
INSERT INTO `poem` VALUES (38, '逢入京使', '唐代', '岑参', '故园东望路漫漫，双袖龙钟泪不干。 马上相逢无纸笔，凭君传语报平安。', '唐诗三百首');
INSERT INTO `poem` VALUES (39, '黄鹤楼送孟浩然之广陵', '唐代', '李白', '故人西辞黄鹤楼，烟花三月下扬州。 孤帆远影碧空尽，唯见长江天际流。 (唯 通：惟)', '唐诗三百首');
INSERT INTO `poem` VALUES (40, '早发白帝城 / 白帝下江陵', '唐代', '李白', '朝辞白帝彩云间，千里江陵一日还。 两岸猿声啼不住，轻舟已过万重山。', '唐诗三百首');
INSERT INTO `poem` VALUES (41, '夜上受降城闻笛', '唐代', '李益', '回乐峰前沙似雪，受降城外月如霜。(回乐峰 一作：回乐烽） 不知何处吹芦管，一夜征人尽望乡。', '唐诗三百首');
INSERT INTO `poem` VALUES (42, '贾生', '唐代', '李商隐', '宣室求贤访逐臣，贾生才调更无伦。 可怜夜半虚前席，不问苍生问鬼神。', '唐诗三百首');
INSERT INTO `poem` VALUES (43, '隋宫', '唐代', '李商隐', '乘兴南游不戒严，九重谁省谏书函。 春风举国裁宫锦，半作障泥半作帆。', '唐诗三百首');
INSERT INTO `poem` VALUES (44, '瑶池', '唐代', '李商隐', '瑶池阿母绮窗开，黄竹歌声动地哀。 八骏日行三万里，穆王何事不重来。', '唐诗三百首');
INSERT INTO `poem` VALUES (45, '嫦娥', '唐代', '李商隐', '云母屏风烛影深，长河渐落晓星沉。 嫦娥应悔偷灵药，碧海青天夜夜心。', '唐诗三百首');
INSERT INTO `poem` VALUES (46, '夜雨寄北', '唐代', '李商隐', '君问归期未有期，巴山夜雨涨秋池。 何当共剪西窗烛，却话巴山夜雨时。', '唐诗三百首');
INSERT INTO `poem` VALUES (47, '寄令狐郎中', '唐代', '李商隐', '嵩云秦树久离居，双鲤迢迢一纸书。 休问梁园旧宾客，茂陵秋雨病相如。', '唐诗三百首');
INSERT INTO `poem` VALUES (48, '为有', '唐代', '李商隐', '为有云屏无限娇，凤城寒尽怕春宵。 无端嫁得金龟婿，辜负香衾事早朝。', '唐诗三百首');
INSERT INTO `poem` VALUES (49, '江南逢李龟年', '唐代', '杜甫', '岐王宅里寻常见，崔九堂前几度闻。 正是江南好风景，落花时节又逢君。', '唐诗三百首');
INSERT INTO `poem` VALUES (50, '赠别', '唐代', '杜牧', '多情却似总无情，唯觉樽前笑不成。 蜡烛有心还惜别，替人垂泪到天明。', '唐诗三百首');
INSERT INTO `poem` VALUES (51, '赠别二首', '唐代', '杜牧', '娉娉袅袅十三余，豆蔻梢头二月初。 春风十里扬州路，卷上珠帘总不如。 多情却似总无情，唯觉樽前笑不成。 蜡烛有心还惜别，替人垂泪到天明。', '唐诗三百首');
INSERT INTO `poem` VALUES (52, '金谷园', '唐代', '杜牧', '繁华事散逐香尘，流水无情草自春。 日暮东风怨啼鸟，落花犹似坠楼人。', '唐诗三百首');
INSERT INTO `poem` VALUES (53, '寄扬州韩绰判官', '唐代', '杜牧', '青山隐隐水迢迢，秋尽江南草未凋。 二十四桥明月夜，玉人何处教吹箫？', '唐诗三百首');
INSERT INTO `poem` VALUES (54, '遣怀', '唐代', '杜牧', '落魄江南载酒行，楚腰纤细掌中轻。(江南 一作：江湖；纤细 一作：肠断) 十年一觉扬州梦，赢得青楼薄幸名。', '唐诗三百首');
INSERT INTO `poem` VALUES (55, '秋夕', '唐代', '杜牧', '银烛秋光冷画屏，轻罗小扇扑流萤。 天阶夜色凉如水，卧看牵牛织女星。 (天阶 一作：天街；卧看 一作：坐看)', '唐诗三百首');
INSERT INTO `poem` VALUES (56, '将赴吴兴登乐游原一绝', '唐代', '杜牧', '清时有味是无能，闲爱孤云静爱僧。 欲把一麾江海去，乐游原上望昭陵。', '唐诗三百首');
INSERT INTO `poem` VALUES (57, '赤壁', '唐代', '杜牧', '折戟沉沙铁未销，自将磨洗认前朝。 东风不与周郎便，铜雀春深锁二乔。', '唐诗三百首');
INSERT INTO `poem` VALUES (58, '泊秦淮', '唐代', '杜牧', '烟笼寒水月笼沙，夜泊秦淮近酒家。 商女不知亡国恨，隔江犹唱《后庭花》。', '唐诗三百首');
INSERT INTO `poem` VALUES (59, '征人怨 / 征怨', '唐代', '柳中庸', '岁岁金河复玉关，朝朝马策与刀环。 三春白雪归青冢，万里黄河绕黑山。', '唐诗三百首');
INSERT INTO `poem` VALUES (60, '金陵图', '唐代', '韦庄', '谁谓伤心画不成，画人心逐世人情。 君看六幅南朝事，老木寒云满故城。', '唐诗三百首');
INSERT INTO `poem` VALUES (61, '滁州西涧', '唐代', '韦应物', '独怜幽草涧边生，上有黄鹂深树鸣。 春潮带雨晚来急，野渡无人舟自横。', '唐诗三百首');
INSERT INTO `poem` VALUES (62, '桃花溪', '唐代', '张旭', '隐隐飞桥隔野烟，石矶西畔问渔船。 桃花尽日随流水，洞在清溪何处边。', '唐诗三百首');
INSERT INTO `poem` VALUES (63, '寄人', '唐代', '张泌', '别梦依依到谢家，小廊回合曲阑斜。 多情只有春庭月，犹为离人照落花。', '唐诗三百首');
INSERT INTO `poem` VALUES (64, '题金陵渡', '唐代', '张祜', '金陵津渡小山楼，一宿行人自可愁。 潮落夜江斜月里，两三星火是瓜州。', '唐诗三百首');
INSERT INTO `poem` VALUES (65, '赠内人', '唐代', '张祜', '禁门宫树月痕过，媚眼惟看宿鹭窠。 斜拔玉钗灯影畔，剔开红焰救飞蛾。', '唐诗三百首');
INSERT INTO `poem` VALUES (66, '集灵台·其一', '唐代', '张祜', '日光斜照集灵台，红树花迎晓露开。 昨夜上皇新授箓，太真含笑入帘来。', '唐诗三百首');
INSERT INTO `poem` VALUES (67, '集灵台·其二', '唐代', '张祜', '虢国夫人承主恩，平明骑马入宫门。 却嫌脂粉污颜色，淡扫蛾眉朝至尊。', '唐诗三百首');
INSERT INTO `poem` VALUES (68, '枫桥夜泊', '唐代', '张继', '月落乌啼霜满天，江枫渔火对愁眠。 姑苏城外寒山寺，夜半钟声到客船。', '唐诗三百首');
INSERT INTO `poem` VALUES (69, '陇西行四首·其二', '唐代', '陈陶', '誓扫匈奴不顾身，五千貂锦丧胡尘。 可怜无定河边骨，犹是春闺梦里人！(春闺 一作：深闺)', '唐诗三百首');
INSERT INTO `poem` VALUES (70, '杂诗', '唐代', '佚名', '近寒食雨草萋萋，著麦苗风柳映堤。 等是有家归未得，杜鹃休向耳边啼。', '唐诗三百首');
INSERT INTO `poem` VALUES (71, '回乡偶书二首·其一', '唐代', '贺知章', '少小离家老大回，乡音无改鬓毛衰。 儿童相见不相识，笑问客从何处来。', '唐诗三百首');
INSERT INTO `poem` VALUES (72, '瑶瑟怨', '唐代', '温庭筠', '冰簟银床梦不成，碧天如水夜云轻。 雁声远过潇湘去，十二楼中月自明。', '唐诗三百首');
INSERT INTO `poem` VALUES (73, '月夜 / 夜月', '唐代', '刘方平', '更深月色半人家，北斗阑干南斗斜。 今夜偏知春气暖，虫声新透绿窗纱。', '唐诗三百首');
INSERT INTO `poem` VALUES (74, '春怨', '唐代', '刘方平', '纱窗日落渐黄昏，金屋无人见泪痕。 寂寞空庭春欲晚，梨花满地不开门。', '唐诗三百首');
INSERT INTO `poem` VALUES (75, '乌衣巷', '唐代', '刘禹锡', '朱雀桥边野草花，乌衣巷口夕阳斜。 旧时王谢堂前燕，飞入寻常百姓家。', '唐诗三百首');
INSERT INTO `poem` VALUES (76, '和乐天春词', '唐代', '刘禹锡', '新妆宜面下朱楼，深锁春光一院愁。 行到中庭数花朵，蜻蜓飞上玉搔头。', '唐诗三百首');
INSERT INTO `poem` VALUES (77, '马嵬坡', '唐代', '郑畋', '玄宗回马杨妃死，云雨难忘日月新。 终是圣明天子事，景阳宫井又何人。', '唐诗三百首');
INSERT INTO `poem` VALUES (78, '寒食', '唐代', '韩翃', '春城无处不飞花，寒食东风御柳斜。 日暮汉宫传蜡烛，轻烟散入五侯家。', '唐诗三百首');
INSERT INTO `poem` VALUES (79, '已凉', '唐代', '韩偓', '碧阑干外绣帘垂，猩血屏风画折枝。 八尺龙须方锦褥，已凉天气未寒时。', '唐诗三百首');
INSERT INTO `poem` VALUES (80, '宫词', '唐代', '顾况', '长乐宫连上苑春，玉楼金殿艳歌新。 君门一入无由出，唯有宫莺得见人。', '唐诗三百首');
INSERT INTO `poem` VALUES (81, '送杜少府之任蜀州 / 送杜少府之任蜀川', '唐代', '王勃', '城阙辅三秦，风烟望五津。 与君离别意，同是宦游人。 海内存知己，天涯若比邻。 无为在歧路，儿女共沾巾。', '唐诗三百首');
INSERT INTO `poem` VALUES (82, '送梓州李使君', '唐代', '王维', '万壑树参天，千山响杜鹃。 山中一夜雨，树杪百重泉。 汉女输橦布，巴人讼芋田。 文翁翻教授，不敢倚先贤。', '唐诗三百首');
INSERT INTO `poem` VALUES (83, '汉江临泛 / 汉江临眺', '唐代', '王维', '楚塞三湘接，荆门九派通。 江流天地外，山色有无中。 郡邑浮前浦，波澜动远空。 襄阳好风日，留醉与山翁。', '唐诗三百首');
INSERT INTO `poem` VALUES (84, '终南别业', '唐代', '王维', '中岁颇好道，晚家南山陲。 兴来每独往，胜事空自知。 行到水穷处，坐看云起时。 偶然值林叟，谈笑无还期。', '唐诗三百首');
INSERT INTO `poem` VALUES (85, '终南山', '唐代', '王维', '太乙近天都，连山接海隅。 白云回望合，青霭入看无。 分野中峰变，阴晴众壑殊。 欲投人处宿，隔水问樵夫。', '唐诗三百首');
INSERT INTO `poem` VALUES (86, '酬张少府', '唐代', '王维', '晚年唯好静，万事不关心。自顾无长策，空知返旧林。 松风吹解带，山月照弹琴。君问穷通理，渔歌入浦深。', '唐诗三百首');
INSERT INTO `poem` VALUES (87, '过香积寺', '唐代', '王维', '不知香积寺，数里入云峰。 古木无人径，深山何处钟。 泉声咽危石，日色冷青松。 薄暮空潭曲，安禅制毒龙。', '唐诗三百首');
INSERT INTO `poem` VALUES (88, '辋川闲居赠裴秀才迪', '唐代', '王维', '寒山转苍翠，秋水日潺湲。 倚杖柴门外，临风听暮蝉。 渡头馀落日，墟里上孤烟。(馀 一作 余) 复值接舆醉，狂歌五柳前。', '唐诗三百首');
INSERT INTO `poem` VALUES (89, '山居秋暝', '唐代', '王维', '空山新雨后，天气晚来秋。 明月松间照，清泉石上流。 竹喧归浣女，莲动下渔舟。 随意春芳歇，王孙自可留。', '唐诗三百首');
INSERT INTO `poem` VALUES (90, '归嵩山作', '唐代', '王维', '清川带长薄，车马去闲闲。 流水如有意，暮禽相与还。 荒城临古渡，落日满秋山。 迢递嵩高下，归来且闭关。', '唐诗三百首');
INSERT INTO `poem` VALUES (91, '次北固山下', '唐代', '王湾', '客路青山外，行舟绿水前。(青山外 一作：青山下) 潮平两岸阔，风正一帆悬。 海日生残夜，江春入旧年。 乡书何处达？归雁洛阳边。', '唐诗三百首');
INSERT INTO `poem` VALUES (92, '云阳馆与韩绅宿别', '唐代', '司空曙', '故人江海别，几度隔山川。 乍见翻疑梦，相悲各问年。 孤灯寒照雨，深竹暗浮烟。 更有明朝恨，离杯惜共传。', '唐诗三百首');
INSERT INTO `poem` VALUES (93, '喜外弟卢纶见宿', '唐代', '司空曙', '静夜四无邻，荒居旧业贫。 雨中黄叶树，灯下白头人。 以我独沉久，愧君相见频。 平生自有分，况是蔡家亲。', '唐诗三百首');
INSERT INTO `poem` VALUES (94, '贼平后送人北归', '唐代', '司空曙', '世乱同南去，时清独北还。 他乡生白发，旧国见青山。 晓月过残垒，繁星宿故关。 寒禽与衰草，处处伴愁颜。', '唐诗三百首');
INSERT INTO `poem` VALUES (95, '草 / 赋得古原草送别', '唐代', '白居易', '离离原上草，一岁一枯荣。 野火烧不尽，春风吹又生。 远芳侵古道，晴翠接荒城。 又送王孙去，萋萋满别情。', '唐诗三百首');
INSERT INTO `poem` VALUES (96, '题大庾岭北驿', '唐代', '宋之问', '阳月南飞雁，传闻至此回。 我行殊未已，何日复归来。 江静潮初落，林昏瘴不开。 明朝望乡处，应见陇头梅。', '唐诗三百首');
INSERT INTO `poem` VALUES (97, '寄左省杜拾遗', '唐代', '岑参', '联步趋丹陛，分曹限紫微。 晓随天仗入，暮惹御香归。 白发悲花落，青云羡鸟飞。 圣朝无阙事，自觉谏书稀。', '唐诗三百首');
INSERT INTO `poem` VALUES (98, '听蜀僧濬弹琴 / 听蜀僧浚弹琴', '唐代', '李白', '蜀僧抱绿绮，西下峨眉峰。 为我一挥手，如听万壑松。 客心洗流水，余响入霜钟。 不觉碧山暮，秋云暗几重。', '唐诗三百首');
INSERT INTO `poem` VALUES (99, '夜泊牛渚怀古', '唐代', '李白', '牛渚西江夜，青天无片云。 登舟望秋月，空忆谢将军。 余亦能高咏，斯人不可闻。 明朝挂帆席，枫叶落纷纷。(挂帆席 一作：去)', '唐诗三百首');
INSERT INTO `poem` VALUES (100, '赠孟浩然', '唐代', '李白', '吾爱孟夫子，风流天下闻。 红颜弃轩冕，白首卧松云。 醉月频中圣，迷花不事君。 高山安可仰，徒此揖清芬。', '唐诗三百首');
INSERT INTO `poem` VALUES (101, '渡荆门送别', '唐代', '李白', '渡远荆门外，来从楚国游。 山随平野尽，江入大荒流。 月下飞天镜，云生结海楼。 仍怜故乡水，万里送行舟。', '唐诗三百首');
INSERT INTO `poem` VALUES (102, '送友人', '唐代', '李白', '青山横北郭，白水绕东城。 此地一为别，孤蓬万里征。 浮云游子意，落日故人情。 挥手自兹去，萧萧班马鸣。', '唐诗三百首');
INSERT INTO `poem` VALUES (103, '喜见外弟又言别', '唐代', '李益', '十年离乱后，长大一相逢。 问姓惊初见，称名忆旧容。 别来沧海事，语罢暮天钟。 明日巴陵道，秋山又几重。', '唐诗三百首');
INSERT INTO `poem` VALUES (104, '凉思', '唐代', '李商隐', '客去波平槛，蝉休露满枝。永怀当此节，倚立自移时。 北斗兼春远，南陵寓使迟。天涯占梦数，疑误有新知。', '唐诗三百首');
INSERT INTO `poem` VALUES (105, '北青萝', '唐代', '李商隐', '残阳西入崦，茅屋访孤僧。落叶人何在，寒云路几层。 独敲初夜磬，闲倚一枝藤。世界微尘里，吾宁爱与憎。', '唐诗三百首');
INSERT INTO `poem` VALUES (106, '蝉', '唐代', '李商隐', '本以高难饱，徒劳恨费声。 五更疏欲断，一树碧无情。 薄宦梗犹泛，故园芜已平。 烦君最相警，我亦举家清。', '唐诗三百首');
INSERT INTO `poem` VALUES (107, '风雨', '唐代', '李商隐', '凄凉宝剑篇，羁泊欲穷年。 黄叶仍风雨，青楼自管弦。 新知遭薄俗，旧好隔良缘。 心断新丰酒，销愁斗几千。', '唐诗三百首');
INSERT INTO `poem` VALUES (108, '落花', '唐代', '李商隐', '高阁客竟去，小园花乱飞。参差连曲陌，迢递送斜晖。 肠断未忍扫，眼穿仍欲归。芳心向春尽，所得是沾衣。', '唐诗三百首');
INSERT INTO `poem` VALUES (109, '登岳阳楼', '唐代', '杜甫', '昔闻洞庭水，今上岳阳楼。 吴楚东南坼，乾坤日夜浮。 亲朋无一字，老病有孤舟。 戎马关山北，凭轩涕泗流。', '唐诗三百首');
INSERT INTO `poem` VALUES (110, '奉济驿重送严公四韵', '唐代', '杜甫', '远送从此别，青山空复情。几时杯重把，昨夜月同行。 列郡讴歌惜，三朝出入荣。江村独归处，寂寞养残生。', '唐诗三百首');
INSERT INTO `poem` VALUES (111, '别房太尉墓', '唐代', '杜甫', '他乡复行役，驻马别孤坟。 近泪无干土，低空有断云。 对棋陪谢傅，把剑觅徐君。 唯见林花落，莺啼送客闻。', '唐诗三百首');
INSERT INTO `poem` VALUES (112, '旅夜书怀', '唐代', '杜甫', '细草微风岸，危樯独夜舟。 星垂平野阔，月涌大江流。 名岂文章著，官应老病休。 飘飘何所似，天地一沙鸥。', '唐诗三百首');
INSERT INTO `poem` VALUES (113, '至德二载甫自京金光门出间道归凤翔乾元初…有悲往事', '唐代', '杜甫', '此道昔归顺，西郊胡正繁。 至今残破胆，应有未招魂。 近得归京邑，移官岂至尊。 无才日衰老，驻马望千门。', '唐诗三百首');
INSERT INTO `poem` VALUES (114, '月夜忆舍弟', '唐代', '杜甫', '戍鼓断人行，边秋一雁声。(边秋 一作:秋边) 露从今夜白，月是故乡明。 有弟皆分散，无家问死生。 寄书长不达，况乃未休兵。', '唐诗三百首');
INSERT INTO `poem` VALUES (115, '天末怀李白', '唐代', '杜甫', '凉风起天末，君子意如何。 鸿雁几时到，江湖秋水多。 文章憎命达，魑魅喜人过。 应共冤魂语，投诗赠汨罗。', '唐诗三百首');
INSERT INTO `poem` VALUES (116, '月夜', '唐代', '杜甫', '今夜鄜州月，闺中只独看。遥怜小儿女，未解忆长安。 香雾云鬟湿，清辉玉臂寒。何时倚虚幌，双照泪痕干。', '唐诗三百首');
INSERT INTO `poem` VALUES (117, '春望', '唐代', '杜甫', '国破山河在，城春草木深。 感时花溅泪，恨别鸟惊心。 烽火连三月，家书抵万金。 白头搔更短，浑欲不胜簪。', '唐诗三百首');
INSERT INTO `poem` VALUES (118, '春宿左省', '唐代', '杜甫', '花隐掖垣暮，啾啾栖鸟过。 星临万户动，月傍九霄多。 不寝听金钥，因风想玉珂。 明朝有封事，数问夜如何。', '唐诗三百首');
INSERT INTO `poem` VALUES (119, '旅宿', '唐代', '杜牧', '旅馆无良伴，凝情自悄然。 寒灯思旧事，断雁警愁眠。 远梦归侵晓，家书到隔年。 沧江好烟月，门系钓鱼船。', '唐诗三百首');
INSERT INTO `poem` VALUES (120, '春宫怨', '唐代', '杜荀鹤', '早被婵娟误，欲妆临镜慵。承恩不在貌，教妾若为容。 风暖鸟声碎，日高花影重。年年越溪女，相忆采芙蓉。', '唐诗三百首');
INSERT INTO `poem` VALUES (121, '和晋陵陆丞早春游望', '唐代', '杜审言', '独有宦游人，偏惊物候新。 云霞出海曙，梅柳渡江春。 淑气催黄鸟，晴光转绿蘋。 忽闻歌古调，归思欲沾巾。', '唐诗三百首');
INSERT INTO `poem` VALUES (122, '杂诗三首·其三', '唐代', '沈佺期', '闻道黄龙戍，频年不解兵。 可怜闺里月，长在汉家营。 少妇今春意，良人昨夜情。 谁能将旗鼓，一为取龙城。', '唐诗三百首');
INSERT INTO `poem` VALUES (123, '宿桐庐江寄广陵旧游', '唐代', '孟浩然', '山暝闻猿愁，沧江急夜流。(闻 一作：听) 风鸣两岸叶，月照一孤舟。 建德非吾土，维扬忆旧游。 还将两行泪，遥寄海西头。', '唐诗三百首');
INSERT INTO `poem` VALUES (124, '留别王侍御维 / 留别王维', '唐代', '孟浩然', '寂寂竟何待，朝朝空自归。欲寻芳草去，惜与故人违。 当路谁相假，知音世所稀。只应守寂寞，还掩故园扉。', '唐诗三百首');
INSERT INTO `poem` VALUES (125, '早寒江上有怀 / 早寒有怀', '唐代', '孟浩然', '木落雁南度，北风江上寒。 我家襄水曲，遥隔楚云端。 乡泪客中尽，孤帆天际看。(孤帆 一作：归帆) 迷津欲有问，平海夕漫漫。', '唐诗三百首');
INSERT INTO `poem` VALUES (126, '岁暮归南山 / 归故园作 / 归终南山', '唐代', '孟浩然', '北阙休上书，南山归敝庐。不才明主弃，多病故人疏。 白发催年老，青阳逼岁除。永怀愁不寐，松月夜窗虚。', '唐诗三百首');
INSERT INTO `poem` VALUES (127, '过故人庄', '唐代', '孟浩然', '故人具鸡黍，邀我至田家。 绿树村边合，青山郭外斜。 开轩面场圃，把酒话桑麻。 待到重阳日，还来就菊花。', '唐诗三百首');
INSERT INTO `poem` VALUES (128, '秦中感秋寄远上人 / 秦中寄远上人', '唐代', '孟浩然', '一丘常欲卧，三径苦无资。 北土非吾愿，东林怀我师。 黄金燃桂尽，壮志逐年衰。 日夕凉风至，闻蝉但益悲。', '唐诗三百首');
INSERT INTO `poem` VALUES (129, '望洞庭湖赠张丞相 / 临洞庭湖赠张丞相', '唐代', '孟浩然', '八月湖水平，涵虚混太清。 气蒸云梦泽，波撼岳阳城。 欲济无舟楫，端居耻圣明。 坐观垂钓者，徒有羡鱼情。', '唐诗三百首');
INSERT INTO `poem` VALUES (130, '与诸子登岘山', '唐代', '孟浩然', '人事有代谢，往来成古今。江山留胜迹，我辈复登临。 水落鱼梁浅，天寒梦泽深。羊公碑尚在，读罢泪沾襟。(尚在 一作：字在)', '唐诗三百首');
INSERT INTO `poem` VALUES (131, '清明日宴梅道士房', '唐代', '孟浩然', '林卧愁春尽，开轩览物华。 忽逢青鸟使，邀入赤松家。 金灶初开火，仙桃正发花。 童颜若可驻，何惜醉流霞。', '唐诗三百首');
INSERT INTO `poem` VALUES (132, '章台夜思', '唐代', '韦庄', '清瑟怨遥夜，绕弦风雨哀。孤灯闻楚角，残月下章台。 芳草已云暮，故人殊未来。乡书不可寄，秋雁又南回。', '唐诗三百首');
INSERT INTO `poem` VALUES (133, '淮上喜会梁川故人 / 淮上喜会梁州故人', '唐代', '韦应物', '江汉曾为客，相逢每醉还。 浮云一别后，流水十年间。 欢笑情如旧，萧疏鬓已斑。 何因不归去？淮上有秋山。', '唐诗三百首');
INSERT INTO `poem` VALUES (134, '赋得暮雨送李胄 / 赋得暮雨送李曹', '唐代', '韦应物', '楚江微雨里，建业暮钟时。 漠漠帆来重，冥冥鸟去迟。 海门深不见，浦树远含滋。 相送情无限，沾襟比散丝。', '唐诗三百首');
INSERT INTO `poem` VALUES (135, '经邹鲁祭孔子而叹之', '唐代', '李隆基', '夫子何为者，栖栖一代中。 地犹鄹氏邑，宅即鲁王宫。 叹凤嗟身否，伤麟怨道穷。 今看两楹奠，当与梦时同。', '唐诗三百首');
INSERT INTO `poem` VALUES (136, '灞上秋居', '唐代', '马戴', '灞原风雨定，晚见雁行频。 落叶他乡树，寒灯独夜人。 空园白露滴，孤壁野僧邻。 寄卧郊扉久，何年致此身。', '唐诗三百首');
INSERT INTO `poem` VALUES (137, '楚江怀古三首·其一', '唐代', '马戴', '露气寒光集，微阳下楚丘。猿啼洞庭树，人在木兰舟。 广泽生明月，苍山夹乱流。云中君不见，竟夕自悲秋。', '唐诗三百首');
INSERT INTO `poem` VALUES (138, '除夜 / 巴山道中除夜书怀', '唐代', '崔涂', '迢递三巴路，羁危万里身。 乱山残雪夜，孤烛异乡人。 渐与骨肉远，转于僮仆亲。 那堪正飘泊，明日岁华新。', '唐诗三百首');
INSERT INTO `poem` VALUES (139, '孤雁二首·其二', '唐代', '崔涂', '几行归塞尽，念尔独何之。 暮雨相呼失，寒塘欲下迟。 渚云低暗度，关月冷相随。 未必逢矰缴，孤飞自可疑。', '唐诗三百首');
INSERT INTO `poem` VALUES (140, '题破山寺后禅院', '唐代', '常建', '清晨入古寺，初日照高林。 竹径通幽处，禅房花木深。 山光悦鸟性，潭影空人心。 万籁此都寂，但余钟磬音。(版本一) 清晨入古寺，初日照高林。 曲径通幽处，禅房花木深。 山光悦鸟性，潭影空人心。 万籁此俱寂，惟余钟磬音。(版本二) 清晨入古寺，初日照高林。 竹径通幽处，禅房花木深。 山光悦鸟性，潭影空人心。 万籁此俱寂，但余钟磬音。(版本三) 清晨入古寺，初日照高林。 曲径通幽处，禅房花木深。 山光悦鸟性，潭影空人心。 万籁此俱寂，但余钟磬音。(版本四)', '唐诗三百首');
INSERT INTO `poem` VALUES (141, '望月怀远 / 望月怀古', '唐代', '张九龄', '海上生明月，天涯共此时。 情人怨遥夜，竟夕起相思。 灭烛怜光满，披衣觉露滋。 不堪盈手赠，还寝梦佳期。', '唐诗三百首');
INSERT INTO `poem` VALUES (142, '书边事', '唐代', '张乔', '调角断清秋，征人倚戍楼。春风对青冢，白日落梁州。 大汉无兵阻，穷边有客游。蕃情似此水，长愿向南流。', '唐诗三百首');
INSERT INTO `poem` VALUES (143, '没蕃故人', '唐代', '张籍', '前年伐月支，城下没全师。 蕃汉断消息，死生长别离。 无人收废帐，归马识残旗。 欲祭疑君在，天涯哭此时。', '唐诗三百首');
INSERT INTO `poem` VALUES (144, '秋日赴阙题潼关驿楼 / 行次潼关逢魏扶东归', '唐代', '许浑', '红叶晚萧萧，长亭酒一瓢。 残云归太华，疏雨过中条。 树色随山迥，河声入海遥。 帝乡明日到，犹自梦渔樵。', '唐诗三百首');
INSERT INTO `poem` VALUES (145, '早秋三首·其一', '唐代', '许浑', '遥夜泛清瑟，西风生翠萝。 残萤委玉露，早雁拂金河。 高树晓还密，远山晴更多。 淮南一叶下，自觉洞庭波。', '唐诗三百首');
INSERT INTO `poem` VALUES (146, '送人东游', '唐代', '温庭筠', '荒戍落黄叶，浩然离故关。 高风汉阳渡，初日郢门山。 江上几人在，天涯孤棹还。 何当重相见，樽酒慰离颜。', '唐诗三百首');
INSERT INTO `poem` VALUES (147, '寻陆鸿渐不遇', '唐代', '皎然', '移家虽带郭，野径入桑麻。近种篱边菊，秋来未著花。 扣门无犬吠，欲去问西家。报道山中去，归时每日斜。', '唐诗三百首');
INSERT INTO `poem` VALUES (148, '寻南溪常山道人隐居 / 寻南溪常道士', '唐代', '刘长卿', '一路经行处，莓苔见履痕。 白云依静渚，春草闭闲门。 过雨看松色，随山到水源。 溪花与禅意，相对亦忘言。', '唐诗三百首');
INSERT INTO `poem` VALUES (149, '新年作', '唐代', '刘长卿', '乡心新岁切，天畔独潸然。 老至居人下，春归在客先。 岭猿同旦暮，江柳共风烟。 已似长沙傅，从今又几年。', '唐诗三百首');
INSERT INTO `poem` VALUES (150, '秋日登吴公台上寺远眺', '唐代', '刘长卿', '古台摇落后，秋日望乡心。 野寺来人少，云峰隔水深。 夕阳依旧垒，寒磬满空林。 惆怅南朝事，长江独至今。', '唐诗三百首');
INSERT INTO `poem` VALUES (151, '送李中丞之襄州 / 送李中丞归汉阳别业', '唐代', '刘长卿', '流落征南将，曾驱十万师。 罢归无旧业，老去恋明时。 独立三边静，轻生一剑知。 茫茫江汉上，日暮欲何之。', '唐诗三百首');
INSERT INTO `poem` VALUES (152, '饯别王十一南游', '唐代', '刘长卿', '望君烟水阔，挥手泪沾巾。 飞鸟没何处，青山空向人。 长江一帆远，落日五湖春。 谁见汀洲上，相思愁白蘋。', '唐诗三百首');
INSERT INTO `poem` VALUES (153, '蜀先主庙', '唐代', '刘禹锡', '天地英雄气，千秋尚凛然。势分三足鼎，业复五铢钱。 得相能开国，生儿不象贤。凄凉蜀故妓，来舞魏宫前。', '唐诗三百首');
INSERT INTO `poem` VALUES (154, '阙题', '唐代', '刘昚虚', '道由白云尽，春与青溪长。 时有落花至，远随流水香。 闲门向山路，深柳读书堂。 幽映每白日，清辉照衣裳。', '唐诗三百首');
INSERT INTO `poem` VALUES (155, '李端公 / 送李端', '唐代', '卢纶', '故关衰草遍，离别自堪悲。(自堪悲 一作：正堪悲) 路出寒云外，人归暮雪时。 少孤为客早，多难识君迟。 掩泪空相向，风尘何处期。', '唐诗三百首');
INSERT INTO `poem` VALUES (156, '送僧归日本', '唐代', '钱起', '上国随缘住，来途若梦行。 浮天沧海远，去世法舟轻。 水月通禅寂，鱼龙听梵声。 惟怜一灯影，万里眼中明。', '唐诗三百首');
INSERT INTO `poem` VALUES (157, '谷口书斋寄杨补阙', '唐代', '钱起', '泉壑带茅茨，云霞生薜帷。 竹怜新雨后，山爱夕阳时。 闲鹭栖常早，秋花落更迟。 家僮扫萝径，昨与故人期。', '唐诗三百首');
INSERT INTO `poem` VALUES (158, '咏蝉 / 在狱咏蝉', '唐代', '骆宾王', '西陆蝉声唱，南冠客思深。(客思深 一作：客思侵) 不堪玄鬓影，来对白头吟。(不堪 一作：那堪) 露重飞难进，风多响易沉。 无人信高洁，谁为表予心？', '唐诗三百首');
INSERT INTO `poem` VALUES (159, '客夜与故人偶集 / 江乡故人偶集客舍', '唐代', '戴叔伦', '天秋月又满，城阙夜千重。 还作江南会，翻疑梦里逢。 风枝惊暗鹊，露草覆寒蛩。 羁旅长堪醉，相留畏晓钟。', '唐诗三百首');
INSERT INTO `poem` VALUES (160, '酬程延秋夜即事见赠', '唐代', '韩翃', '长簟迎风早，空城澹月华。 星河秋一雁，砧杵夜千家。 节候看应晚，心期卧亦赊。 向来吟秀句，不觉已鸣鸦。', '唐诗三百首');
INSERT INTO `poem` VALUES (161, '遣悲怀三首·其一', '唐代', '元稹', '谢公最小偏怜女，自嫁黔娄百事乖。 顾我无衣搜荩箧，泥他沽酒拔金钗。(荩箧 一作：画箧) 野蔬充膳甘长藿，落叶添薪仰古槐。 今日俸钱过十万，与君营奠复营斋。', '唐诗三百首');
INSERT INTO `poem` VALUES (162, '遣悲怀三首·其二', '唐代', '元稹', '昔日戏言身后意，今朝都到眼前来。(身后意 一作：身后事) 衣裳已施行看尽，针线犹存未忍开。 尚想旧情怜婢仆，也曾因梦送钱财。 诚知此恨人人有，贫贱夫妻百事哀。', '唐诗三百首');
INSERT INTO `poem` VALUES (163, '遣悲怀三首·其三', '唐代', '元稹', '闲坐悲君亦自悲，百年都是几多时。 邓攸无子寻知命，潘岳悼亡犹费词。 同穴窅冥何所望，他生缘会更难期。 惟将终夜常开眼，报答平生未展眉。', '唐诗三百首');
INSERT INTO `poem` VALUES (164, '酬郭给事', '唐代', '王维', '洞门高阁霭馀辉，桃李阴阴柳絮飞。 禁里疏钟官舍晚，省中啼鸟吏人稀。 晨摇玉佩趋金殿，夕奉天书拜琐闱。 强欲从君无那老，将因卧病解朝衣。', '唐诗三百首');
INSERT INTO `poem` VALUES (165, '和贾舍人早朝大明宫之作', '唐代', '王维', '绛帻鸡人送晓筹，尚衣方进翠云裘。 九天阊阖开宫殿，万国衣冠拜冕旒。 日色才临仙掌动，香烟欲傍衮龙浮。 朝罢须裁五色诏，佩声归向凤池头。', '唐诗三百首');
INSERT INTO `poem` VALUES (166, '奉和圣制从蓬莱向兴庆阁道中留春雨中春望之作应制', '唐代', '王维', '渭水自萦秦塞曲，黄山旧绕汉宫斜。 銮舆迥出千门柳，阁道回看上苑花。 云里帝城双凤阙，雨中春树万人家。 为乘阳气行时令，不是宸游玩物华。', '唐诗三百首');
INSERT INTO `poem` VALUES (167, '积雨辋川庄作 / 秋归辋川庄作', '唐代', '王维', '积雨空林烟火迟，蒸藜炊黍饷东菑。 漠漠水田飞白鹭，阴阴夏木啭黄鹂。 山中习静观朝槿，松下清斋折露葵。 野老与人争席罢，海鸥何事更相疑。', '唐诗三百首');
INSERT INTO `poem` VALUES (168, '望月有感', '唐代', '白居易', '自河南经乱，关内阻饥，兄弟离散，各在一处。因望月有感，聊书所怀，寄上浮梁大兄、於潜七兄、乌江十五兄，兼示符离及下邽弟妹。 时难年荒世业空，弟兄羁旅各西东。 田园寥落干戈后，骨肉流离道路中。 吊影分为千里雁，辞根散作九秋蓬。 共看明月应垂泪，一夜乡心五处同。', '唐诗三百首');
INSERT INTO `poem` VALUES (169, '奉和中书舍人贾至早朝大明宫', '唐代', '岑参', '鸡鸣紫陌曙光寒，莺啭皇州春色阑。 金阙晓钟开万户，玉阶仙仗拥千官。 花迎剑珮星初落，柳拂旌旗露未干。 独有凤凰池上客，阳春一曲和皆难。', '唐诗三百首');
INSERT INTO `poem` VALUES (170, '登金陵凤凰台', '唐代', '李白', '凤凰台上凤凰游，凤去台空江自流。 吴宫花草埋幽径，晋代衣冠成古丘。 三山半落青天外，二水中分白鹭洲。(二水 一作：一水) 总为浮云能蔽日，长安不见使人愁。', '唐诗三百首');
INSERT INTO `poem` VALUES (171, '无题·重帏深下莫愁堂', '唐代', '李商隐', '重帏深下莫愁堂，卧后清宵细细长。 神女生涯原是梦，小姑居处本无郎。 风波不信菱枝弱，月露谁教桂叶香。 直道相思了无益，未妨惆怅是清狂。', '唐诗三百首');
INSERT INTO `poem` VALUES (172, '无题·凤尾香罗薄几重', '唐代', '李商隐', '凤尾香罗薄几重，碧文圆顶夜深缝。 扇裁月魄羞难掩，车走雷声语未通。 曾是寂寥金烬暗，断无消息石榴红。 斑骓只系垂杨岸，何处西南任好风。(任 一作：待)', '唐诗三百首');
INSERT INTO `poem` VALUES (173, '无题·相见时难别亦难', '唐代', '李商隐', '相见时难别亦难，东风无力百花残。 春蚕到死丝方尽，蜡炬成灰泪始干。 晓镜但愁云鬓改，夜吟应觉月光寒。 蓬山此去无多路，青鸟殷勤为探看。(蓬山 一作：蓬莱)', '唐诗三百首');
INSERT INTO `poem` VALUES (174, '无题·来是空言去绝踪', '唐代', '李商隐', '来是空言去绝踪，月斜楼上五更钟。 梦为远别啼难唤，书被催成墨未浓。 蜡照半笼金翡翠，麝熏微度绣芙蓉。 刘郎已恨蓬山远，更隔蓬山一万重！', '唐诗三百首');
INSERT INTO `poem` VALUES (175, '无题·飒飒东风细雨来', '唐代', '李商隐', '飒飒东风细雨来，芙蓉塘外有轻雷。 金蟾啮锁烧香入，玉虎牵丝汲井回。 贾氏窥帘韩掾少，宓妃留枕魏王才。 春心莫共花争发，一寸相思一寸灰！', '唐诗三百首');
INSERT INTO `poem` VALUES (176, '无题·昨夜星辰昨夜风', '唐代', '李商隐', '昨夜星辰昨夜风，画楼西畔桂堂东。 身无彩凤双飞翼，心有灵犀一点通。 隔座送钩春酒暖，分曹射覆蜡灯红。 嗟余听鼓应官去，走马兰台类转蓬。', '唐诗三百首');
INSERT INTO `poem` VALUES (177, '春雨', '唐代', '李商隐', '怅卧新春白袷衣，白门寥落意多违。 红楼隔雨相望冷，珠箔飘灯独自归。 远路应悲春晼晚，残霄犹得梦依稀。 玉珰缄札何由达，万里云罗一雁飞。', '唐诗三百首');
INSERT INTO `poem` VALUES (178, '筹笔驿', '唐代', '李商隐', '猿鸟犹疑畏简书，风云常为护储胥。 徒令上将挥神笔，终见降王走传车。 管乐有才原不忝，关张无命欲何如？ 他年锦里经祠庙，梁父吟成恨有余。', '唐诗三百首');
INSERT INTO `poem` VALUES (179, '锦瑟', '唐代', '李商隐', '锦瑟无端五十弦，一弦一柱思华年。 庄生晓梦迷蝴蝶，望帝春心托杜鹃。 沧海月明珠有泪，蓝田日暖玉生烟。 此情可待成追忆，只是当时已惘然。', '唐诗三百首');
INSERT INTO `poem` VALUES (180, '隋宫', '唐代', '李商隐', '紫泉宫殿锁烟霞，欲取芜城作帝家。 玉玺不缘归日角，锦帆应是到天涯。 于今腐草无萤火，终古垂杨有暮鸦。 地下若逢陈后主，岂宜重问后庭花。', '唐诗三百首');
INSERT INTO `poem` VALUES (181, '送魏万之京', '唐代', '李颀', '朝闻游子唱离歌，昨夜微霜初渡河。 鸿雁不堪愁里听，云山况是客中过。 关城树色催寒近，御苑砧声向晚多。(树色 一作：曙) 莫见长安行乐处，空令岁月易蹉跎。', '唐诗三百首');
INSERT INTO `poem` VALUES (182, '咏怀古迹五首·其一', '唐代', '杜甫', '支离东北风尘际，漂泊西南天地间。 三峡楼台淹日月，五溪衣服共云山。 羯胡事主终无赖，词客哀时且未还。 庾信平生最萧瑟，暮年诗赋动江关。', '唐诗三百首');
INSERT INTO `poem` VALUES (183, '咏怀古迹五首·其二', '唐代', '杜甫', '摇落深知宋玉悲，风流儒雅亦吾师。 怅望千秋一洒泪，萧条异代不同时。 江山故宅空文藻，云雨荒台岂梦思。 最是楚宫俱泯灭，舟人指点到今疑。', '唐诗三百首');
INSERT INTO `poem` VALUES (184, '咏怀古迹五首·其三', '唐代', '杜甫', '群山万壑赴荆门，生长明妃尚有村。 一去紫台连朔漠，独留青冢向黄昏。 画图省识春风面，环佩空归夜月魂。(夜月 一作：月夜 环佩 一作：环珮) 千载琵琶作胡语，分明怨恨曲中论。', '唐诗三百首');
INSERT INTO `poem` VALUES (185, '咏怀古迹五首·其四', '唐代', '杜甫', '蜀主窥吴幸三峡，崩年亦在永安宫。 翠华想像空山里，玉殿虚无野寺中。 古庙杉松巢水鹤，岁时伏腊走村翁。 武侯祠堂常邻近，一体君臣祭祀同。', '唐诗三百首');
INSERT INTO `poem` VALUES (186, '咏怀古迹五首·其五', '唐代', '杜甫', '诸葛大名垂宇宙，宗臣遗像肃清高。 三分割据纡筹策，万古云霄一羽毛。 伯仲之间见伊吕，指挥若定失萧曹。 运移汉祚终难复，志决身歼军务劳。', '唐诗三百首');
INSERT INTO `poem` VALUES (187, '宿府', '唐代', '杜甫', '清秋幕府井梧寒，独宿江城蜡炬残。 永夜角声悲自语，中天月色好谁看。 风尘荏苒音书绝，关塞萧条行路难。 已忍伶俜十年事，强移栖息一枝安。', '唐诗三百首');
INSERT INTO `poem` VALUES (188, '阁夜', '唐代', '杜甫', '岁暮阴阳催短景，天涯霜雪霁寒宵。 五更鼓角声悲壮，三峡星河影动摇。 野哭几家闻战伐，夷歌数处起渔樵。 卧龙跃马终黄土，人事依依漫寂寥。(版本一) 岁暮阴阳催短景，天涯霜雪霁寒宵。 五更鼓角声悲壮，三峡星河影动摇。 野哭千家闻战伐，夷歌数处起渔樵。 卧龙跃马终黄土，人事音书漫寂寥。(版本二)', '唐诗三百首');
INSERT INTO `poem` VALUES (189, '闻官军收河南河北', '唐代', '杜甫', '剑外忽传收蓟北，初闻涕泪满衣裳。 却看妻子愁何在，漫卷诗书喜欲狂。 白日放歌须纵酒，青春作伴好还乡。 即从巴峡穿巫峡，便下襄阳向洛阳。', '唐诗三百首');
INSERT INTO `poem` VALUES (190, '登高', '唐代', '杜甫', '风急天高猿啸哀，渚清沙白鸟飞回。 无边落木萧萧下，不尽长江滚滚来。 万里悲秋常作客，百年多病独登台。 艰难苦恨繁霜鬓，潦倒新停浊酒杯。', '唐诗三百首');
INSERT INTO `poem` VALUES (191, '登楼', '唐代', '杜甫', '花近高楼伤客心，万方多难此登临。 锦江春色来天地，玉垒浮云变古今。 北极朝廷终不改，西山寇盗莫相侵。 可怜后主还祠庙，日暮聊为《梁甫吟》。(梁甫 一作：梁父)', '唐诗三百首');
INSERT INTO `poem` VALUES (192, '蜀相', '唐代', '杜甫', '丞相祠堂何处寻，锦官城外柏森森。 映阶碧草自春色，隔叶黄鹂空好音。 三顾频烦天下计，两朝开济老臣心。(频烦 一作：频繁) 出师未捷身先死，长使英雄泪满襟。', '唐诗三百首');
INSERT INTO `poem` VALUES (193, '客至', '唐代', '杜甫', '舍南舍北皆春水，但见群鸥日日来。 花径不曾缘客扫，蓬门今始为君开。 盘飧市远无兼味，樽酒家贫只旧醅。 肯与邻翁相对饮，隔篱呼取尽馀杯。(馀 通：余)', '唐诗三百首');
INSERT INTO `poem` VALUES (194, '野望', '唐代', '杜甫', '西山白雪三城戍，南浦清江万里桥。 海内风尘诸弟隔，天涯涕泪一身遥。 惟将迟暮供多病，未有涓埃答圣朝。(惟 通：唯) 跨马出郊时极目，不堪人事日萧条。', '唐诗三百首');
INSERT INTO `poem` VALUES (195, '登柳州城楼寄漳汀封连四州', '唐代', '柳宗元', '城上高楼接大荒，海天愁思正茫茫。 惊风乱飐芙蓉水，密雨斜侵薜荔墙。 岭树重遮千里目，江流曲似九回肠。 共来百越文身地，犹自音书滞一乡。', '唐诗三百首');
INSERT INTO `poem` VALUES (196, '春思', '唐代', '皇甫冉', '莺啼燕语报新年，马邑龙堆路几千。 家住层城临汉苑，心随明月到胡天。(层城 一作：秦城) 机中锦字论长恨，楼上花枝笑独眠。 为问元戎窦车骑，何时返旆勒燕然。', '唐诗三百首');
INSERT INTO `poem` VALUES (197, '寄李儋元锡', '唐代', '韦应物', '去年花里逢君别，今日花开又一年。 世事茫茫难自料，春愁黯黯独成眠。 身多疾病思田里，邑有流亡愧俸钱。 闻道欲来相问讯，西楼望月几回圆。', '唐诗三百首');
INSERT INTO `poem` VALUES (198, '望蓟门', '唐代', '祖咏', '燕台一望客心惊，笳鼓喧喧汉将营。(笳鼓 一作：箫鼓) 万里寒光生积雪，三边曙色动危旌。 沙场烽火侵胡月，海畔云山拥蓟城。 少小虽非投笔吏，论功还欲请长缨。', '唐诗三百首');
INSERT INTO `poem` VALUES (199, '贫女', '唐代', '秦韬玉', '蓬门未识绮罗香，拟托良媒益自伤。 谁爱风流高格调，共怜时世俭梳妆。 敢将十指夸针巧，不把双眉斗画长。 苦恨年年压金线，为他人作嫁衣裳。', '唐诗三百首');
INSERT INTO `poem` VALUES (200, '送李少府贬峡中王少府贬长沙', '唐代', '高适', '嗟君此别意何如，驻马衔杯问谪居。 巫峡啼猿数行泪，衡阳归雁几封书。 青枫江上秋帆远，白帝城边古木疏。 圣代即今多雨露，暂时分手莫踌躇。', '唐诗三百首');
INSERT INTO `poem` VALUES (201, '九日登望仙台呈刘明府容', '唐代', '崔曙', '汉文皇帝有高台，此日登临曙色开。 三晋云山皆北向，二陵风雨自东来。 关门令尹谁能识，河上仙翁去不回。 且欲近寻彭泽宰，陶然共醉菊花杯。', '唐诗三百首');
INSERT INTO `poem` VALUES (202, '黄鹤楼 / 登黄鹤楼', '唐代', '崔颢', '昔人已乘黄鹤去，此地空余黄鹤楼。 黄鹤一去不复返，白云千载空悠悠。 晴川历历汉阳树，芳草萋萋鹦鹉洲。 日暮乡关何处是？烟波江上使人愁。', '唐诗三百首');
INSERT INTO `poem` VALUES (203, '行经华阴', '唐代', '崔颢', '岧峣太华俯咸京，天外三峰削不成。 武帝祠前云欲散，仙人掌上雨初晴。 河山北枕秦关险，驿路西连汉畤平。 借问路傍名利客，何如此处学长生。(何 一作：无)', '唐诗三百首');
INSERT INTO `poem` VALUES (204, '利州南渡', '唐代', '温庭筠', '澹然空水对斜晖，曲岛苍茫接翠微。 波上马嘶看棹去，柳边人歇待船归。 数丛沙草群鸥散，万顷江田一鹭飞。 谁解乘舟寻范蠡，五湖烟水独忘机。', '唐诗三百首');
INSERT INTO `poem` VALUES (205, '苏武庙', '唐代', '温庭筠', '苏武魂销汉使前，古祠高树两茫然。 云边雁断胡天月，陇上羊归塞草烟。 回日楼台非甲帐，去时冠剑是丁年。 茂陵不见封侯印，空向秋波哭逝川。', '唐诗三百首');
INSERT INTO `poem` VALUES (206, '江州重别薛六柳八二员外', '唐代', '刘长卿', '生涯岂料承优诏，世事空知学醉歌。 江上月明胡雁过，淮南木落楚山多。 寄身且喜沧洲近，顾影无如白发何。 今日龙钟人共弃，愧君犹遣慎风波。', '唐诗三百首');
INSERT INTO `poem` VALUES (207, '长沙过贾谊宅', '唐代', '刘长卿', '三年谪宦此栖迟，万古惟留楚客悲。 秋草独寻人去后，寒林空见日斜时。 汉文有道恩犹薄，湘水无情吊岂知。 寂寂江山摇落处，怜君何事到天涯。', '唐诗三百首');
INSERT INTO `poem` VALUES (208, '自夏口至鹦鹉洲夕望岳阳寄源中丞', '唐代', '刘长卿', '汀洲无浪复无烟，楚客相思益渺然。 汉口夕阳斜渡鸟，洞庭秋水远连天。 孤城背岭寒吹角，独树临江夜泊船。(独树 一作：独戍) 贾谊上书忧汉室，长沙谪去古今怜。', '唐诗三百首');
INSERT INTO `poem` VALUES (209, '西塞山怀古', '唐代', '刘禹锡', '王濬楼船下益州，金陵王气黯然收。 千寻铁锁沉江底，一片降幡出石头。 人世几回伤往事，山形依旧枕寒流。 今逢四海为家日，故垒萧萧芦荻秋。', '唐诗三百首');
INSERT INTO `poem` VALUES (210, '晚次鄂州', '唐代', '卢纶', '云开远见汉阳城，犹是孤帆一日程。 估客昼眠知浪静，舟人夜语觉潮生。 三湘愁鬓逢秋色，万里归心对月明。 旧业已随征战尽，更堪江上鼓鼙声。', '唐诗三百首');
INSERT INTO `poem` VALUES (211, '赠阙下裴舍人', '唐代', '钱起', '二月黄莺飞上林，春城紫禁晓阴阴。 长乐钟声花外尽，龙池柳色雨中深。 阳和不散穷途恨，霄汉长怀捧日心。 献赋十年犹未遇，羞将白发对华簪。', '唐诗三百首');
INSERT INTO `poem` VALUES (212, '宫词', '唐代', '薛逢', '十二楼中尽晓妆，望仙楼上望君王。 锁衔金兽连环冷，水滴铜龙昼漏长。 云髻罢梳还对镜，罗衣欲换更添香。 遥窥正殿帘开处，袍袴宫人扫御床。', '唐诗三百首');
INSERT INTO `poem` VALUES (213, '同题仙游观', '唐代', '韩翃', '仙台初见五城楼，风物凄凄宿雨收。 山色遥连秦树晚，砧声近报汉宫秋。 疏松影落空坛静，细草香闲小洞幽。 何用别寻方外去，人间亦自有丹丘。', '唐诗三百首');
INSERT INTO `poem` VALUES (214, '贼退示官吏', '唐代', '元结', '癸卯岁，西原贼入道州，焚烧杀掠，几尽而去。明年，贼又攻永破邵，不犯此州边鄙而退。岂力能制敌与？盖蒙其伤怜而已。诸使何为忍苦征敛，故作诗一篇以示官吏。 昔岁逢太平，山林二十年。 泉源在庭户，洞壑当门前。 井税有常期，日晏犹得眠。 忽然遭世变，数岁亲戎旃。 今来典斯郡，山夷又纷然。 城小贼不屠，人贫伤可怜。 是以陷邻境，此州独见全。 使臣将王命，岂不如贼焉？ 今彼征敛者，迫之如火煎。 谁能绝人命，以作时世贤！ 思欲委符节，引竿自刺船。 将家就鱼麦，归老江湖边。', '唐诗三百首');
INSERT INTO `poem` VALUES (215, '同从弟南斋玩月忆山阴崔少府', '唐代', '王昌龄', '高卧南斋时，开帷月初吐。 清辉澹水木，演漾在窗户。 冉冉几盈虚，澄澄变今古。 美人清江畔，是夜越吟苦。 千里共如何，微风吹兰杜。', '唐诗三百首');
INSERT INTO `poem` VALUES (216, '渭川田家', '唐代', '王维', '斜阳照墟落，穷巷牛羊归。(斜阳 一作：斜光) 野老念牧童，倚杖候荆扉。 雉雊麦苗秀，蚕眠桑叶稀。 田夫荷锄至，相见语依依。 即此羡闲逸，怅然吟式微。', '唐诗三百首');
INSERT INTO `poem` VALUES (217, '西施咏', '唐代', '王维', '艳色天下重，西施宁久微。 朝为越溪女，暮作吴宫妃。 贱日岂殊众，贵来方悟稀。 邀人傅香粉，不自著罗衣。 君宠益娇态，君怜无是非。 当时浣纱伴，莫得同车归。 持谢邻家子，效颦安可希。', '唐诗三百首');
INSERT INTO `poem` VALUES (218, '送别', '唐代', '王维', '下马饮君酒，问君何所之？ 君言不得意，归卧南山陲。 但去莫复问，白云无尽时。', '唐诗三百首');
INSERT INTO `poem` VALUES (219, '送綦毋潜落第还乡', '唐代', '王维', '圣代无隐者，英灵尽来归。 遂令东山客，不得顾采薇。 既至金门远，孰云吾道非。 江淮度寒食，京洛缝春衣。 置酒长安道，同心与我违。 行当浮桂棹，未几拂荆扉。 远树带行客，孤城当落晖。 吾谋适不用，勿谓知音稀。', '唐诗三百首');
INSERT INTO `poem` VALUES (220, '青溪 / 过青溪水作', '唐代', '王维', '言入黄花川，每逐清溪水。 随山将万转，趣途无百里。 声喧乱石中，色静深松里。 漾漾泛菱荇，澄澄映葭苇。 我心素已闲，清川澹如此。 请留磐石上，垂钓将已矣。(磐石 一作：盘石)', '唐诗三百首');
INSERT INTO `poem` VALUES (221, '与高适薛据同登慈恩寺浮图', '唐代', '岑参', '塔势如涌出，孤高耸天宫。 登临出世界，磴道盘虚空。 突兀压神州，峥嵘如鬼工。 四角碍白日，七层摩苍穹。 下窥指高鸟，俯听闻惊风。 连山若波涛，奔凑似朝东。(凑 一作：走；似 一作：如) 青槐夹驰道，宫馆何玲珑。(馆 一作：观) 秋色从西来，苍然满关中。 五陵北原上，万古青濛濛。 净理了可悟，胜因夙所宗。 誓将挂冠去，觉道资无穷。', '唐诗三百首');
INSERT INTO `poem` VALUES (222, '下终南山过斛斯山人宿置酒', '唐代', '李白', '暮从碧山下，山月随人归。 却顾所来径，苍苍横翠微。 相携及田家，童稚开荆扉。 绿竹入幽径，青萝拂行衣。 欢言得所憩，美酒聊共挥。 长歌吟松风，曲尽河星稀。 我醉君复乐，陶然共忘机。', '唐诗三百首');
INSERT INTO `poem` VALUES (223, '月下独酌四首·其一', '唐代', '李白', '花间一壶酒，独酌无相亲。 举杯邀明月，对影成三人。 月既不解饮，影徒随我身。 暂伴月将影，行乐须及春。 我歌月徘徊，我舞影零乱。 醒时同交欢，醉后各分散。(同交欢 一作：相交欢) 永结无情游，相期邈云汉。', '唐诗三百首');
INSERT INTO `poem` VALUES (224, '春思', '唐代', '李白', '燕草如碧丝，秦桑低绿枝。 当君怀归日，是妾断肠时。 春风不相识，何事入罗帏。', '唐诗三百首');
INSERT INTO `poem` VALUES (225, '梦李白二首·其一', '唐代', '杜甫', '死别已吞声，生别常恻恻。江南瘴疠地，逐客无消息。 故人入我梦，明我长相忆。恐非平生魂，路远不可测。 魂来枫林青，魂返关塞黑。君今在罗网，何以有羽翼。 落月满屋梁，犹疑照颜色。水深波浪阔，无使蛟龙得。(版本一) 死别已吞声，生别常恻恻。江南瘴疠地，逐客无消息。 故人入我梦，明我长相忆。君今在罗网，何以有羽翼。 恐非平生魂，路远不可测。魂来枫林青，魂返关塞黑。 落月满屋梁，犹疑照颜色。水深波浪阔，无使蛟龙得。(版本二)', '唐诗三百首');
INSERT INTO `poem` VALUES (226, '梦李白二首·其二', '唐代', '杜甫', '浮云终日行，游子久不至。 三夜频梦君，情亲见君意。 告归常局促，苦道来不易。 江湖多风波，舟楫恐失坠。 出门搔白首，若负平生志。 冠盖满京华，斯人独憔悴。 孰云网恢恢，将老身反累。 千秋万岁名，寂寞身后事。', '唐诗三百首');
INSERT INTO `poem` VALUES (227, '望岳', '唐代', '杜甫', '岱宗夫如何？齐鲁青未了。 造化钟神秀，阴阳割昏晓。 荡胸生曾云，决眦入归鸟。( 曾 通：层) 会当凌绝顶，一览众山小。', '唐诗三百首');
INSERT INTO `poem` VALUES (228, '赠卫八处士', '唐代', '杜甫', '人生不相见，动如参与商。 今夕复何夕，共此灯烛光。 少壮能几时，鬓发各已苍。 访旧半为鬼，惊呼热中肠。 焉知二十载，重上君子堂。 昔别君未婚，儿女忽成行。 怡然敬父执，问我来何方。 问答未及已，儿女罗酒浆。 夜雨剪春韭，新炊间黄粱。 主称会面难，一举累十觞。 十觞亦不醉，感子故意长。 明日隔山岳，世事两茫茫。', '唐诗三百首');
INSERT INTO `poem` VALUES (229, '佳人', '唐代', '杜甫', '绝代有佳人，幽居在空谷。 自云良家子，零落依草木。 关中昔丧乱，兄弟遭杀戮。 官高何足论，不得收骨肉。 世情恶衰歇，万事随转烛。 夫婿轻薄儿，新人美如玉。 合昏尚知时，鸳鸯不独宿。 但见新人笑，那闻旧人哭。 在山泉水清，出山泉水浊。 侍婢卖珠回，牵萝补茅屋。 摘花不插发，采柏动盈掬。 天寒翠袖薄，日暮倚修竹。', '唐诗三百首');
INSERT INTO `poem` VALUES (230, '秋登兰山寄张五 / 九月九日岘山寄张子容', '唐代', '孟浩然', '北山白云里，隐者自怡悦。 相望试登高，心随雁飞灭。(试 一作 始) 愁因薄暮起，兴是清秋发。 时见归村人，沙行渡头歇。 天边树若荠，江畔洲如月。 何当载酒来，共醉重阳节。', '唐诗三百首');
INSERT INTO `poem` VALUES (231, '夏日南亭怀辛大', '唐代', '孟浩然', '山光忽西落，池月渐东上。 散发乘夕凉，开轩卧闲敞。 荷风送香气，竹露滴清响。 欲取鸣琴弹，恨无知音赏。 感此怀故人，中宵劳梦想。(中宵 一作：终)', '唐诗三百首');
INSERT INTO `poem` VALUES (232, '宿业师山房期丁大不至 / 宿业师山房待丁大不至', '唐代', '孟浩然', '夕阳度西岭，群壑倏已暝。 松月生夜凉，风泉满清听。 樵人归欲尽，烟鸟栖初定。 之子期宿来，孤琴候萝径。', '唐诗三百首');
INSERT INTO `poem` VALUES (233, '寻西山隐者不遇', '唐代', '丘为', '绝顶一茅茨，直上三十里。 扣关无僮仆，窥室唯案几。 若非巾柴车，应是钓秋水。 差池不相见，黾勉空仰止。 草色新雨中，松声晚窗里。 及兹契幽绝，自足荡心耳。 虽无宾主意，颇得清净理。 兴尽方下山，何必待之子。', '唐诗三百首');
INSERT INTO `poem` VALUES (234, '晨诣超师院读禅经', '唐代', '柳宗元', '汲井漱寒齿，清心拂尘服。 闲持贝叶书，步出东斋读。 真源了无取，妄迹世所逐。 遗言冀可冥，缮性何由熟。 道人庭宇静，苔色连深竹。 日出雾露馀，青松如膏沐。 澹然离言说，悟悦心自足。', '唐诗三百首');
INSERT INTO `poem` VALUES (235, '溪居', '唐代', '柳宗元', '久为簪组累，幸此南夷谪。 闲依农圃邻，偶似山林客。 晓耕翻露草，夜榜响溪石。 来往不逢人，长歌楚天碧。', '唐诗三百首');
INSERT INTO `poem` VALUES (236, '送杨氏女', '唐代', '韦应物', '永日方戚戚，出行复悠悠。 女子今有行，大江溯轻舟。 尔辈苦无恃，抚念益慈柔。 幼为长所育，两别泣不休。 对此结中肠，义往难复留。 自小阙内训，事姑贻我忧。 赖兹托令门，任恤庶无尤。 贫俭诚所尚，资从岂待周。 孝恭遵妇道，容止顺其猷。 别离在今晨，见尔当何秋。 居闲始自遣，临感忽难收。 归来视幼女，零泪缘缨流。', '唐诗三百首');
INSERT INTO `poem` VALUES (237, '长安遇冯著', '唐代', '韦应物', '客从东方来，衣上灞陵雨。问客何为来，采山因买斧。 冥冥花正开，飏飏燕新乳。昨别今已春，鬓丝生几缕。', '唐诗三百首');
INSERT INTO `poem` VALUES (238, '夕次盱眙县', '唐代', '韦应物', '落帆逗淮镇，停舫临孤驿。浩浩风起波，冥冥日沉夕。 人归山郭暗，雁下芦洲白。独夜忆秦关，听钟未眠客。', '唐诗三百首');
INSERT INTO `poem` VALUES (239, '东郊', '唐代', '韦应物', '吏舍跼终年，出郊旷清曙。 杨柳散和风，青山澹吾虑。 依丛适自憩，缘涧还复去。 微雨霭芳原，春鸠鸣何处。 乐幽心屡止，遵事迹犹遽。 终罢斯结庐，慕陶直可庶。', '唐诗三百首');
INSERT INTO `poem` VALUES (240, '郡斋雨中与诸文士燕集', '唐代', '韦应物', '兵卫森画戟，宴寝凝清香。 海上风雨至，逍遥池阁凉。 烦疴近消散，嘉宾复满堂。 自惭居处崇，未睹斯民康。 理会是非遣，性达形迹忘。 鲜肥属时禁，蔬果幸见尝。 俯饮一杯酒，仰聆金玉章。 神欢体自轻，意欲凌风翔。 吴中盛文史，群彦今汪洋。 方知大藩地，岂曰财赋强。', '唐诗三百首');
INSERT INTO `poem` VALUES (241, '初发扬子寄元大校书', '唐代', '韦应物', '凄凄去亲爱，泛泛入烟雾。 归棹洛阳人，残钟广陵树。 今朝此为别，何处还相遇。 世事波上舟，沿洄安得住。', '唐诗三百首');
INSERT INTO `poem` VALUES (242, '寄全椒山中道士', '唐代', '韦应物', '今朝郡斋冷，忽念山中客。 涧底束荆薪，归来煮白石。 欲持一瓢酒，远慰风雨夕。 落叶满空山，何处寻行迹。', '唐诗三百首');
INSERT INTO `poem` VALUES (243, '宿王昌龄隐居', '唐代', '常建', '清溪深不测，隐处唯孤云。 松际露微月，清光犹为君。 茅亭宿花影，药院滋苔纹。 余亦谢时去，西山鸾鹤群。', '唐诗三百首');
INSERT INTO `poem` VALUES (244, '感遇十二首·其四', '唐代', '张九龄', '孤鸿海上来，池潢不敢顾。 侧见双翠鸟，巢在三珠树。 矫矫珍木巅，得无金丸惧？ 美服患人指，高明逼神恶。 今我游冥冥，弋者何所慕！', '唐诗三百首');
INSERT INTO `poem` VALUES (245, '感遇十二首·其一', '唐代', '张九龄', '兰叶春葳蕤，桂华秋皎洁。 欣欣此生意，自尔为佳节。 谁知林栖者，闻风坐相悦。 草木有本心，何求美人折！', '唐诗三百首');
INSERT INTO `poem` VALUES (246, '感遇十二首·其二', '唐代', '张九龄', '幽人归独卧，滞虑洗孤清。 持此谢高鸟，因之传远情。 日夕怀空意，人谁感至精？ 飞沈理自隔，何所慰吾诚?', '唐诗三百首');
INSERT INTO `poem` VALUES (247, '感遇·江南有丹橘', '唐代', '张九龄', '江南有丹橘，经冬犹绿林。 岂伊地气暖？自有岁寒心。 可以荐嘉客，奈何阻重深。 运命惟所遇，循环不可寻。 徒言树桃李，此木岂无阴？', '唐诗三百首');
INSERT INTO `poem` VALUES (248, '春泛若耶溪', '唐代', '綦毋潜', '幽意无断绝，此去随所偶。 晚风吹行舟，花路入溪口。 际夜转西壑，隔山望南斗。 潭烟飞溶溶，林月低向后。 生事且弥漫，愿为持竿叟。', '唐诗三百首');
INSERT INTO `poem` VALUES (249, '石鱼湖上醉歌', '唐代', '元结', '漫叟以公田酿酒，因休暇，载酒于湖上，时取一醉。欢醉中，据湖岸，引臂向鱼取酒，使舫载之，遍饮坐者。意疑倚巴丘酌于君山之上，诸子环洞庭而坐，酒舫泛泛然触波涛。而往来者，乃作歌以长之。 石鱼湖，似洞庭，夏水欲满君山青。 山为樽，水为沼，酒徒历历坐洲岛。 长风连日作大浪，不能废人运酒舫。 我持长瓢坐巴丘，酌饮四坐以散愁。', '唐诗三百首');
INSERT INTO `poem` VALUES (250, '长恨歌', '唐代', '白居易', '汉皇重色思倾国，御宇多年求不得。 杨家有女初长成，养在深闺人未识。 天生丽质难自弃，一朝选在君王侧。 回眸一笑百媚生，六宫粉黛无颜色。 春寒赐浴华清池，温泉水滑洗凝脂。 侍儿扶起娇无力，始是新承恩泽时。 云鬓花颜金步摇，芙蓉帐暖度春宵。 春宵苦短日高起，从此君王不早朝。 承欢侍宴无闲暇，春从春游夜专夜。 后宫佳丽三千人，三千宠爱在一身。 金屋妆成娇侍夜，玉楼宴罢醉和春。 姊妹弟兄皆列土，可怜光彩生门户。 遂令天下父母心，不重生男重生女。 骊宫高处入青云，仙乐风飘处处闻。 缓歌慢舞凝丝竹，尽日君王看不足。 渔阳鼙鼓动地来，惊破霓裳羽衣曲。 九重城阙烟尘生，千乘万骑西南行。 翠华摇摇行复止，西出都门百余里。 六军不发无奈何，宛转蛾眉马前死。 花钿委地无人收，翠翘金雀玉搔头。 君王掩面救不得，回看血泪相和流。 黄埃散漫风萧索，云栈萦纡登剑阁。 峨嵋山下少人行，旌旗无光日色薄。 蜀江水碧蜀山青，圣主朝朝暮暮情。 行宫见月伤心色，夜雨闻铃肠断声。 天旋地转回龙驭，到此踌躇不能去。 马嵬坡下泥土中，不见玉颜空死处。 君臣相顾尽沾衣，东望都门信马归。 归来池苑皆依旧，太液芙蓉未央柳。 芙蓉如面柳如眉，对此如何不泪垂。 春风桃李花开日，秋雨梧桐叶落时。 西宫南内多秋草，落叶满阶红不扫。(花开日 一作：花开夜；南内 一作：南苑) 梨园弟子白发新，椒房阿监青娥老。 夕殿萤飞思悄然，孤灯挑尽未成眠。 迟迟钟鼓初长夜，耿耿星河欲曙天。 鸳鸯瓦冷霜华重，翡翠衾寒谁与共。 悠悠生死别经年，魂魄不曾来入梦。 临邛道士鸿都客，能以精诚致魂魄。 为感君王辗转思，遂教方士殷勤觅。 排空驭气奔如电，升天入地求之遍。 上穷碧落下黄泉，两处茫茫皆不见。 忽闻海上有仙山，山在虚无缥渺间。 楼阁玲珑五云起，其中绰约多仙子。 中有一人字太真，雪肤花貌参差是。 金阙西厢叩玉扃，转教小玉报双成。 闻道汉家天子使，九华帐里梦魂惊。 揽衣推枕起徘徊，珠箔银屏迤逦开。 云鬓半偏新睡觉，花冠不整下堂来。 风吹仙袂飘飖举，犹似霓裳羽衣舞。 玉容寂寞泪阑干，梨花一枝春带雨。(阑 通：栏；飘飘 一作：飘飖) 含情凝睇谢君王，一别音容两渺茫。 昭阳殿里恩爱绝，蓬莱宫中日月长。 回头下望人寰处，不见长安见尘雾。 惟将旧物表深情，钿合金钗寄将去。 钗留一股合一扇，钗擘黄金合分钿。 但教心似金钿坚，天上人间会相见。 临别殷勤重寄词，词中有誓两心知。 七月七日长生殿，夜半无人私语时。 在天愿作比翼鸟，在地愿为连理枝。 天长地久有时尽，此恨绵绵无绝期。', '唐诗三百首');
INSERT INTO `poem` VALUES (251, '琵琶行 / 琵琶引', '唐代', '白居易', '元和十年，予左迁九江郡司马。明年秋，送客湓浦口，闻舟中夜弹琵琶者，听其音，铮铮然有京都声。问其人，本长安倡女，尝学琵琶于穆、曹二善才，年长色衰，委身为贾人妇。遂命酒，使快弹数曲。曲罢悯然，自叙少小时欢乐事，今漂沦憔悴，转徙于江湖间。予出官二年，恬然自安，感斯人言，是夕始觉有迁谪意。因为长句，歌以赠之，凡六百一十六言，命曰《琵琶行》。 浔阳江头夜送客，枫叶荻花秋瑟瑟。 主人下马客在船，举酒欲饮无管弦。 醉不成欢惨将别，别时茫茫江浸月。 忽闻水上琵琶声，主人忘归客不发。 寻声暗问弹者谁？琵琶声停欲语迟。 移船相近邀相见，添酒回灯重开宴。 千呼万唤始出来，犹抱琵琶半遮面。 转轴拨弦三两声，未成曲调先有情。 弦弦掩抑声声思，似诉平生不得志。（不得志 一作：不得意） 低眉信手续续弹，说尽心中无限事。 轻拢慢捻抹复挑，初为《霓裳》后《六幺》（六幺 一作：绿腰）。 大弦嘈嘈如急雨，小弦切切如私语。 嘈嘈切切错杂弹，大珠小珠落玉盘。 间关莺语花底滑，幽咽泉流冰下难。 冰泉冷涩弦凝绝，凝绝不通声暂歇。(暂歇 一作：渐歇) 别有幽愁暗恨生，此时无声胜有声。 银瓶乍破水浆迸，铁骑突出刀枪鸣。 曲终收拨当心画，四弦一声如裂帛。 东船西舫悄无言，唯见江心秋月白。 沉吟放拨插弦中，整顿衣裳起敛容。 自言本是京城女，家在虾蟆陵下住。 十三学得琵琶成，名属教坊第一部。 曲罢曾教善才服，妆成每被秋娘妒。 五陵年少争缠头，一曲红绡不知数。 钿头银篦击节碎，血色罗裙翻酒污。(银篦 一作：云篦) 今年欢笑复明年，秋月春风等闲度。 弟走从军阿姨死，暮去朝来颜色故。 门前冷落鞍马稀，老大嫁作商人妇。 商人重利轻别离，前月浮梁买茶去。 去来江口守空船，绕船月明江水寒。 夜深忽梦少年事，梦啼妆泪红阑干。 我闻琵琶已叹息，又闻此语重唧唧。 同是天涯沦落人，相逢何必曾相识！ 我从去年辞帝京，谪居卧病浔阳城。 浔阳地僻无音乐，终岁不闻丝竹声。 住近湓江地低湿，黄芦苦竹绕宅生。 其间旦暮闻何物？杜鹃啼血猿哀鸣。 春江花朝秋月夜，往往取酒还独倾。 岂无山歌与村笛？呕哑嘲哳难为听。 今夜闻君琵琶语，如听仙乐耳暂明。 莫辞更坐弹一曲，为君翻作《琵琶行》。 感我此言良久立，却坐促弦弦转急。 凄凄不似向前声，满座重闻皆掩泣。 座中泣下谁最多？江州司马青衫湿。', '唐诗三百首');
INSERT INTO `poem` VALUES (252, '走马川行奉送出师西征 / 走马川行奉送封大夫出师西征', '唐代', '岑参', '君不见走马川行雪海边，平沙莽莽黄入天。 轮台九月风夜吼，一川碎石大如斗，随风满地石乱走。 匈奴草黄马正肥，金山西见烟尘飞，汉家大将西出师。 将军金甲夜不脱，半夜军行戈相拨，风头如刀面如割。 马毛带雪汗气蒸，五花连钱旋作冰，幕中草檄砚水凝。 虏骑闻之应胆慑，料知短兵不敢接，车师西门伫献捷。', '唐诗三百首');
INSERT INTO `poem` VALUES (253, '轮台歌奉送封大夫出师西征', '唐代', '岑参', '轮台城头夜吹角，轮台城北旄头落。 羽书昨夜过渠黎，单于已在金山西。 戍楼西望烟尘黑，汉军屯在轮台北。 上将拥旄西出征，平明吹笛大军行。 四边伐鼓雪海涌，三军大呼阴山动。 虏塞兵气连云屯，战场白骨缠草根。 剑河风急雪片阔，沙口石冻马蹄脱。(雪片阔 一作：云片阔) 亚相勤王甘苦辛，誓将报主静边尘。 古来青史谁不见，今见功名胜古人。', '唐诗三百首');
INSERT INTO `poem` VALUES (254, '白雪歌送武判官归京', '唐代', '岑参', '北风卷地白草折，胡天八月即飞雪。 忽如一夜春风来，千树万树梨花开。 散入珠帘湿罗幕，狐裘不暖锦衾薄。 将军角弓不得控，都护铁衣冷难着。(难着 一作：犹著) 瀚海阑干百丈冰，愁云惨淡万里凝。 中军置酒饮归客，胡琴琵琶与羌笛。 纷纷暮雪下辕门，风掣红旗冻不翻。 轮台东门送君去，去时雪满天山路。 山回路转不见君，雪上空留马行处。', '唐诗三百首');
INSERT INTO `poem` VALUES (255, '宣州谢脁楼饯别校书叔云', '唐代', '李白', '弃我去者，昨日之日不可留； 乱我心者，今日之日多烦忧。 长风万里送秋雁，对此可以酣高楼。 蓬莱文章建安骨，中间小谢又清发。 俱怀逸兴壮思飞，欲上青天览明月。(览 通：揽；明月 一作：日月) 抽刀断水水更流，举杯销愁愁更愁。(销愁 一作 消愁) 人生在世不称意，明朝散发弄扁舟。', '唐诗三百首');
INSERT INTO `poem` VALUES (256, '庐山谣寄卢侍御虚舟', '唐代', '李白', '我本楚狂人，凤歌笑孔丘。 手持绿玉杖，朝别黄鹤楼。 五岳寻仙不辞远，一生好入名山游。 庐山秀出南斗傍，屏风九叠云锦张。 影落明湖青黛光，金阙前开二峰长，银河倒挂三石梁。 香炉瀑布遥相望，回崖沓嶂凌苍苍。 翠影红霞映朝日，鸟飞不到吴天长。 登高壮观天地间，大江茫茫去不还。 黄云万里动风色，白波九道流雪山。 好为庐山谣，兴因庐山发。 闲窥石镜清我心，谢公行处苍苔没。 早服还丹无世情，琴心三叠道初成。 遥见仙人彩云里，手把芙蓉朝玉京。 先期汗漫九垓上，愿接卢敖游太清。', '唐诗三百首');
INSERT INTO `poem` VALUES (257, '梦游天姥吟留别 / 梦游天姥山别东鲁诸公', '唐代', '李白', '海客谈瀛洲，烟涛微茫信难求； 越人语天姥，云霞明灭或可睹。 天姥连天向天横，势拔五岳掩赤城。 天台四万八千丈，对此欲倒东南倾。(四万 一作：一万) 我欲因之梦吴越，一夜飞度镜湖月。(度 通：渡) 湖月照我影，送我至剡溪。 谢公宿处今尚在，渌水荡漾清猿啼。 脚著谢公屐，身登青云梯。 半壁见海日，空中闻天鸡。 千岩万转路不定，迷花倚石忽已暝。 熊咆龙吟殷岩泉，栗深林兮惊层巅。 云青青兮欲雨，水澹澹兮生烟。 列缺霹雳，丘峦崩摧。 洞天石扉，訇然中开。 青冥浩荡不见底，日月照耀金银台。 霓为衣兮风为马，云之君兮纷纷而来下。 虎鼓瑟兮鸾回车，仙之人兮列如麻。 忽魂悸以魄动，恍惊起而长嗟。 惟觉时之枕席，失向来之烟霞。 世间行乐亦如此，古来万事东流水。 别君去兮何时还？且放白鹿青崖间，须行即骑访名山。 安能摧眉折腰事权贵，使我不得开心颜!', '唐诗三百首');
INSERT INTO `poem` VALUES (258, '金陵酒肆留别', '唐代', '李白', '风吹柳花满店香，吴姬压酒唤客尝。(劝客 一作：唤客) 金陵子弟来相送，欲行不行各尽觞。 请君试问东流水，别意与之谁短长。', '唐诗三百首');
INSERT INTO `poem` VALUES (259, '韩碑', '唐代', '李商隐', '元和天子神武姿，彼何人哉轩与羲。 誓将上雪列圣耻，坐法宫中朝四夷。 淮西有贼五十载，封狼生貙貙生罴。 不据山河据平地，长戈利矛日可麾。 帝得圣相相曰度，贼斫不死神扶持。 腰悬相印作都统，阴风惨澹天王旗。 愬武古通作牙爪，仪曹外郎载笔随。 行军司马智且勇，十四万众犹虎貔。 入蔡缚贼献太庙，功无与让恩不訾。 帝曰汝度功第一，汝从事愈宜为辞。 愈拜稽首蹈且舞，金石刻画臣能为。 古者世称大手笔，此事不系于职司。 当仁自古有不让，言讫屡颔天子颐。 公退斋戒坐小阁，濡染大笔何淋漓。 点窜《尧典》《舜典》字，涂改《清庙》《生民》诗。 文成破体书在纸，清晨再拜铺丹墀。 表曰臣愈昧死上，咏神圣功书之碑。 碑高三丈字如斗，负以灵鳌蟠以螭。 句奇语重喻者少，谗之天子言其私。 长绳百尺拽碑倒，粗砂大石相磨治。 公之斯文若元气，先时已入人肝脾。 汤盘孔鼎有述作，今无其器存其辞。 呜呼圣王及圣相，相与烜赫流淳熙。 公之斯文不示后，曷与三五相攀追。 愿书万本诵万遍，口角流沫右手胝。 传之七十有二代，以为封禅玉检明堂基。', '唐诗三百首');
INSERT INTO `poem` VALUES (260, '听董大弹胡笳声兼寄语弄房给事', '唐代', '李颀', '蔡女昔造胡笳声，一弹一十有八拍。 胡人落泪沾边草，汉使断肠对归客。 古戍苍苍烽火寒，大荒沉沉飞雪白。 先拂商弦后角羽，四郊秋叶惊摵摵。 董夫子，通神明，深山窃听来妖精。 言迟更速皆应手，将往复旋如有情。 空山百鸟散还合，万里浮云阴且晴。 嘶酸雏雁失群夜，断绝胡儿恋母声。 川为静其波，鸟亦罢其鸣。 乌孙部落家乡远，逻娑沙尘哀怨生。 幽音变调忽飘洒，长风吹林雨堕瓦。 迸泉飒飒飞木末，野鹿呦呦走堂下。 长安城连东掖垣，凤凰池对青琐门。 高才脱略名与利，日夕望君抱琴至。', '唐诗三百首');
INSERT INTO `poem` VALUES (261, '听安万善吹觱篥歌', '唐代', '李颀', '南山截竹为觱篥，此乐本自龟兹出。 流传汉地曲转奇，凉州胡人为我吹。 傍邻闻者多叹息，远客思乡皆泪垂。 世人解听不解赏，长飙风中自来往。 枯桑老柏寒飕飗，九雏鸣凤乱啾啾。 龙吟虎啸一时发，万籁百泉相与秋。 忽然更作渔阳掺，黄云萧条白日暗。 变调如闻杨柳春，上林繁花照眼新。 岁夜高堂列明烛，美酒一杯声一曲。', '唐诗三百首');
INSERT INTO `poem` VALUES (262, '古意', '唐代', '李颀', '男儿事长征，少小幽燕客。 赌胜马蹄下，由来轻七尺。 杀人莫敢前，须如猬毛磔。 黄云陇底白云飞，未得报恩不能归。 辽东小妇年十五，惯弹琵琶解歌舞。 今为羌笛出塞声，使我三军泪如雨。', '唐诗三百首');
INSERT INTO `poem` VALUES (263, '送陈章甫', '唐代', '李颀', '四月南风大麦黄，枣花未落桐阴长。 青山朝别暮还见，嘶马出门思旧乡。 陈侯立身何坦荡，虬须虎眉仍大颡。 腹中贮书一万卷，不肯低头在草莽。 东门酤酒饮我曹，心轻万事如鸿毛。 醉卧不知白日暮，有时空望孤云高。 长河浪头连天黑，津口停舟渡不得。 郑国游人未及家，洛阳行子空叹息。 闻道故林相识多，罢官昨日今如何。', '唐诗三百首');
INSERT INTO `poem` VALUES (264, '琴歌', '唐代', '李颀', '主人有酒欢今夕，请奏鸣琴广陵客。 月照城头乌半飞，霜凄万树风入衣。(万树 一作：万木) 铜炉华烛烛增辉，初弹渌水后楚妃。 一声已动物皆静，四座无言星欲稀。 清淮奉使千余里，敢告云山从此始。', '唐诗三百首');
INSERT INTO `poem` VALUES (265, '古柏行', '唐代', '杜甫', '孔明庙前有老柏，柯如青铜根如石。 霜皮溜雨四十围，黛色参天二千尺。 君臣已与时际会，树木犹为人爱惜。 云来气接巫峡长，月出寒通雪山白。 忆昨路绕锦亭东，先主武侯同閟宫。 崔嵬枝干郊原古，窈窕丹青户牖空。 落落盘踞虽得地，冥冥孤高多烈风。 扶持自是神明力，正直原因造化工。 大厦如倾要梁栋，万牛回首丘山重。 不露文章世已惊，未辞翦伐谁能送？ 苦心岂免容蝼蚁，香叶终经宿鸾凤。 志士幽人莫怨嗟：古来材大难为用。', '唐诗三百首');
INSERT INTO `poem` VALUES (266, '观公孙大娘弟子舞剑器行', '唐代', '杜甫', '大历二年十月十九日，夔府别驾元持宅，见临颍李十二娘 舞剑器，壮其蔚跂，问其所师，曰：“余公孙大娘弟子也。” 开元五载，余尚童稚，记于郾城观公孙氏，舞剑器浑脱， 浏漓顿挫，独出冠时，自高头宜春梨园二伎坊内人洎外供奉， 晓是舞者，圣文神武皇帝初，公孙一人而已。 玉貌锦衣，况余白首，今兹弟子，亦非盛颜。 既辨其由来，知波澜莫二，抚事慷慨，聊为《剑器行》。 昔者吴人张旭，善草书帖，数常于邺县见公孙大娘舞西河剑器，自此草书长进，豪荡感激，即公孙可知矣。 昔有佳人公孙氏，一舞剑器动四方。 观者如山色沮丧，天地为之久低昂。 霍如羿射九日落，矫如群帝骖龙翔。 来如雷霆收震怒，罢如江海凝清光。 绛唇珠袖两寂寞，晚有弟子传芬芳。 临颍美人在白帝，妙舞此曲神扬扬。 与余问答既有以，感时抚事增惋伤。 先帝侍女八千人，公孙剑器初第一。 五十年间似反掌，风尘倾动昏王室。 梨园弟子散如烟，女乐余姿映寒日。 金粟堆前木已拱，瞿唐石城草萧瑟。 玳筵急管曲复终，乐极哀来月东出。 老夫不知其所往，足茧荒山转愁疾。', '唐诗三百首');
INSERT INTO `poem` VALUES (267, '韦讽录事宅观曹将军画马图', '唐代', '杜甫', '国初已来画鞍马，神妙独数江都王。 将军得名三十载，人间又见真乘黄。 曾貌先帝照夜白，龙池十日飞霹雳。 内府殷红玛瑙盘，婕妤传诏才人索。 盘赐将军拜舞归，轻纨细绮相追飞。 贵戚权门得笔迹，始觉屏障生光辉。 昔日太宗拳毛騧，近时郭家狮子花。 今之新图有二马，复令识者久叹嗟。 此皆骑战一敌万，缟素漠漠开风沙。 其余七匹亦殊绝，迥若寒空动烟雪。 霜蹄蹴踏长楸间，马官厮养森成列。 可怜九马争神骏，顾视清高气深稳。 借问苦心爱者谁，后有韦讽前支遁。 忆昔巡幸新丰宫，翠华拂天来向东。 腾骧磊落三万匹，皆与此图筋骨同。 自从献宝朝河宗，无复射蛟江水中。 君不见金粟堆前松柏里，龙媒去尽鸟呼风。', '唐诗三百首');
INSERT INTO `poem` VALUES (268, '丹青引赠曹将军霸', '唐代', '杜甫', '将军魏武之子孙，于今为庶为清门。 英雄割据虽已矣，文采风流今尚存。 学书初学卫夫人，但恨无过王右军。 丹青不知老将至，富贵于我如浮云。 开元之中常引见，承恩数上南薰殿。 凌烟功臣少颜色，将军下笔开生面。 良相头上进贤冠，猛将腰间大羽箭。 褒公鄂公毛发动，英姿飒爽来酣战。 先帝御马五花骢，画工如山貌不同。 是日牵来赤墀下，迥立阊阖生长风。 诏谓将军拂绢素，意匠惨澹经营中。 斯须九重真龙出，一洗万古凡马空。 玉花却在御榻上，榻上庭前屹相向。 至尊含笑催赐金，圉人太仆皆惆怅。 弟子韩干早入室，亦能画马穷殊相。 干惟画肉不画骨，忍使骅骝气凋丧。 将军画善盖有神，必逢佳士亦写真。 即今漂泊干戈际，屡貌寻常行路人。 途穷反遭俗眼白，世上未有如公贫。 但看古来盛名下，终日坎壈缠其身。', '唐诗三百首');
INSERT INTO `poem` VALUES (269, '寄韩谏议注', '唐代', '杜甫', '今我不乐思岳阳，身欲奋飞病在床。 美人娟娟隔秋水，濯足洞庭望八荒。 鸿飞冥冥日月白，青枫叶赤天雨霜。 玉京群帝集北斗，或骑麒麟翳凤凰。 芙蓉旌旗烟雾落，影动倒景摇潇湘。 星宫之君醉琼浆，羽人稀少不在旁。 似闻昨者赤松子，恐是汉代韩张良。 昔随刘氏定长安，帷幄未改神惨伤。 国家成败吾岂敢，色难腥腐餐枫香。 周南留滞古所惜，南极老人应寿昌。 美人胡为隔秋水，焉得置之贡玉堂。', '唐诗三百首');
INSERT INTO `poem` VALUES (270, '夜归鹿门山歌 / 夜归鹿门歌', '唐代', '孟浩然', '山寺钟鸣昼已昏，渔梁渡头争渡喧。 人随沙岸向江村，余亦乘舟归鹿门。 鹿门月照开烟树，忽到庞公栖隐处。 岩扉松径长寂寥，惟有幽人自来去。', '唐诗三百首');
INSERT INTO `poem` VALUES (271, '渔翁', '唐代', '柳宗元', '渔翁夜傍西岩宿，晓汲清湘燃楚竹。 烟销日出不见人，欸乃一声山水绿。 回看天际下中流，岩上无心云相逐。', '唐诗三百首');
INSERT INTO `poem` VALUES (272, '登幽州台歌', '唐代', '陈子昂', '前不见古人，后不见来者。 念天地之悠悠，独怆然而涕下。', '唐诗三百首');
INSERT INTO `poem` VALUES (273, '石鼓歌', '唐代', '韩愈', '张生手持石鼓文，劝我试作石鼓歌。 少陵无人谪仙死，才薄将奈石鼓何。 周纲凌迟四海沸，宣王愤起挥天戈。 大开明堂受朝贺，诸侯剑佩鸣相磨。 蒐于岐阳骋雄俊，万里禽兽皆遮罗。 镌功勒成告万世，凿石作鼓隳嵯峨。 从臣才艺咸第一，拣选撰刻留山阿。 雨淋日灸野火燎，鬼物守护烦撝呵。 公从何处得纸本，毫发尽备无差讹。 辞严义密读难晓，字体不类隶与蝌。 年深岂免有缺画，快剑斫断生蛟鼍。 鸾翔凤翥众仙下，珊瑚碧树交枝柯。 金绳铁索锁钮壮，古鼎跃水龙腾梭。 陋儒编诗不收入，二雅褊迫无委蛇。 孔子西行不到秦，掎摭星宿遗羲娥。 嗟余好古生苦晚，对此涕泪双滂沱。 忆昔初蒙博士征，其年始改称元和。 故人从军在右辅，为我度量掘臼科。 濯冠沐浴告祭酒，如此至宝存岂多。 毡包席裹可立致，十鼓只载数骆驼。 荐诸太庙比郜鼎，光价岂止百倍过。 圣恩若许留太学，诸生讲解得切磋。 观经鸿都尚填咽，坐见举国来奔波。 剜苔剔藓露节角，安置妥帖平不颇。 大厦深檐与盖覆，经历久远期无佗。 中朝大官老于事，讵肯感激徒媕婀。 牧童敲火牛砺角，谁复著手为摩挲。 日销月铄就埋没，六年西顾空吟哦。 羲之俗书趁姿媚，数纸尚可博白鹅。 继周八代争战罢，无人收拾理则那。 方今太平日无事，柄任儒术崇丘轲。 安能以此尚论列，愿借辩口如悬河。 石鼓之歌止于此，呜呼吾意其蹉跎。', '唐诗三百首');
INSERT INTO `poem` VALUES (274, '山石', '唐代', '韩愈', '山石荦确行径微，黄昏到寺蝙蝠飞。 升堂坐阶新雨足，芭蕉叶大栀子肥。 僧言古壁佛画好，以火来照所见稀。 铺床拂席置羹饭，疏粝亦足饱我饥。 夜深静卧百虫绝，清月出岭光入扉。 天明独去无道路，出入高下穷烟霏。 山红涧碧纷烂漫，时见松枥皆十围。 当流赤足踏涧石，水声激激风吹衣。 人生如此自可乐，岂必局束为人鞿？(鞿 一作：靰) 嗟哉吾党二三子，安得至老不更归。', '唐诗三百首');
INSERT INTO `poem` VALUES (275, '八月十五夜赠张功曹', '唐代', '韩愈', '纤云四卷天无河，清风吹空月舒波。 沙平水息声影绝，一杯相属君当歌。 君歌声酸辞且苦，不能听终泪如雨。 洞庭连天九疑高，蛟龙出没猩鼯号。 十生九死到官所，幽居默默如藏逃。 下床畏蛇食畏药，海气湿蛰熏腥臊。 昨者州前捶大鼓，嗣皇继圣登夔皋。 赦书一日行万里，罪从大辟皆除死。 迁者追回流者还，涤瑕荡垢清朝班。 州家申名使家抑，坎轲只得移荆蛮。 判司卑官不堪说，未免捶楚尘埃间。 同时辈流多上道，天路幽险难追攀。 君歌且休听我歌，我歌今与君殊科。 一年明月今宵多，人生由命非由他。 有酒不饮奈明何。', '唐诗三百首');
INSERT INTO `poem` VALUES (276, '谒衡岳庙遂宿岳寺题门楼', '唐代', '韩愈', '五岳祭秩皆三公，四方环镇嵩当中。 火维地荒足妖怪，天假神柄专其雄。 喷云泄雾藏半腹，虽有绝顶谁能穷？ 我来正逢秋雨节，阴气晦昧无清风。 潜心默祷若有应，岂非正直能感通！ 须臾静扫众峰出，仰见突兀撑青空。 紫盖连延接天柱，石廪腾掷堆祝融。 森然魄动下马拜，松柏一径趋灵宫。 粉墙丹柱动光彩，鬼物图画填青红。 升阶伛偻荐脯酒，欲以菲薄明其衷。 庙令老人识神意，睢盱侦伺能鞠躬。 手持杯珓导我掷，云此最吉余难同。 窜逐蛮荒幸不死，衣食才足甘长终。 侯王将相望久绝，神纵欲福难为功。 夜投佛寺上高阁，星月掩映云曈昽。 猿鸣钟动不知曙，杲杲寒日生于东。', '唐诗三百首');
INSERT INTO `poem` VALUES (277, '凉州词二首·其一', '唐代', '王之涣', '黄河远上白云间，一片孤城万仞山。 羌笛何须怨杨柳，春风不度玉门关。', '唐诗三百首');
INSERT INTO `poem` VALUES (278, '出塞二首·其一', '唐代', '王昌龄', '秦时明月汉时关，万里长征人未还。 但使龙城飞将在，不教胡马度阴山。', '唐诗三百首');
INSERT INTO `poem` VALUES (279, '塞上曲·其一', '唐代', '王昌龄', '蝉鸣空桑林，八月萧关道。 出塞入塞寒，处处黄芦草。 从来幽并客，皆共尘沙老。 莫学游侠儿，矜夸紫骝好。', '唐诗三百首');
INSERT INTO `poem` VALUES (280, '塞下曲', '唐代', '王昌龄', '饮马渡秋水，水寒风似刀。 平沙日未没，黯黯见临洮。 昔日长城战，咸言意气高。 黄尘足今古，白骨乱蓬蒿。', '唐诗三百首');
INSERT INTO `poem` VALUES (281, '长信怨', '唐代', '王昌龄', '金井梧桐秋叶黄，珠帘不卷夜来霜。 熏笼玉枕无颜色，卧听南宫清漏长。 高殿秋砧响夜阑，霜深犹忆御衣寒。 银灯青琐裁缝歇，还向金城明主看。 奉帚平明金殿开，暂将团扇共徘徊。 玉颜不及寒鸦色，犹带昭阳日影来。 真成薄命久寻思，梦见君王觉后疑。 火照西宫知夜饮，分明复道奉恩时。 长信宫中秋月明，昭阳殿下捣衣声。 白露堂中细草迹，红罗帐里不胜情。', '唐诗三百首');
INSERT INTO `poem` VALUES (282, '送元二使安西 / 渭城曲', '唐代', '王维', '渭城朝雨浥轻尘，客舍青青柳色新。(一作：客舍依依杨柳春) 劝君更尽一杯酒，西出阳关无故人。', '唐诗三百首');
INSERT INTO `poem` VALUES (283, '秋夜曲', '唐代', '王维', '桂魄初生秋露微，轻罗已薄未更衣。 银筝夜久殷勤弄，心怯空房不忍归。', '唐诗三百首');
INSERT INTO `poem` VALUES (284, '洛阳女儿行', '唐代', '王维', '洛阳女儿对门居，才可颜容十五余。 良人玉勒乘骢马，侍女金盘脍鲤鱼。 画阁朱楼尽相望，红桃绿柳垂檐向。 罗帷送上七香车，宝扇迎归九华帐。 狂夫富贵在青春，意气骄奢剧季伦。 自怜碧玉亲教舞，不惜珊瑚持与人。 春窗曙灭九微火，九微片片飞花琐。 戏罢曾无理曲时，妆成祗是熏香坐。 城中相识尽繁华，日夜经过赵李家。 谁怜越女颜如玉，贫贱江头自浣纱。', '唐诗三百首');
INSERT INTO `poem` VALUES (285, '老将行', '唐代', '王维', '少年十五二十时，步行夺得胡马骑。 射杀山中白额虎，肯数邺下黄须儿！ 一身转战三千里，一剑曾当百万师。 汉兵奋迅如霹雳，虏骑崩腾畏蒺藜。 卫青不败由天幸，李广无功缘数奇。 自从弃置便衰朽，世事蹉跎成白首。 昔时飞箭无全目，今日垂杨生左肘。 路旁时卖故侯瓜，门前学种先生柳。 苍茫古木连穷巷，寥落寒山对虚牖。 誓令疏勒出飞泉，不似颍川空使酒。 贺兰山下阵如云，羽檄交驰日夕闻。 节使三河募年少，诏书五道出将军。 试拂铁衣如雪色，聊持宝剑动星文。 愿得燕弓射大将，耻令越甲鸣吾君。 莫嫌旧日云中守，犹堪一战取功勋。', '唐诗三百首');
INSERT INTO `poem` VALUES (286, '桃源行', '唐代', '王维', '渔舟逐水爱山春，两岸桃花夹古津。 坐看红树不知远，行尽青溪不见人。 山口潜行始隈隩，山开旷望旋平陆。 遥看一处攒云树，近入千家散花竹。 樵客初传汉姓名，居人未改秦衣服。 居人共住武陵源，还从物外起田园。 月明松下房栊静，日出云中鸡犬喧。 惊闻俗客争来集，竞引还家问都邑。 平明闾巷扫花开，薄暮渔樵乘水入。 初因避地去人间，及至成仙遂不还。 峡里谁知有人事，世中遥望空云山。 不疑灵境难闻见，尘心未尽思乡县。 出洞无论隔山水，辞家终拟长游衍。 自谓经过旧不迷，安知峰壑今来变。 当时只记入山深，青溪几度到云林。 春来遍是桃花水，不辨仙源何处寻。', '唐诗三百首');
INSERT INTO `poem` VALUES (287, '清平调·其一', '唐代', '李白', '云想衣裳花想容，春风拂槛露华浓。 若非群玉山头见，会向瑶台月下逢。', '唐诗三百首');
INSERT INTO `poem` VALUES (288, '清平调·其二', '唐代', '李白', '一枝红艳露凝香，云雨巫山枉断肠。 借问汉宫谁得似，可怜飞燕倚新妆。', '唐诗三百首');
INSERT INTO `poem` VALUES (289, '清平调·名花倾国两相欢', '唐代', '李白', '名花倾国两相欢，常得君王带笑看。 解释春风无限恨，沉香亭北倚栏杆。', '唐诗三百首');
INSERT INTO `poem` VALUES (290, '行路难·其一', '唐代', '李白', '金樽清酒斗十千，玉盘珍羞直万钱。(羞 通：馐；直 通：值) 停杯投箸不能食，拔剑四顾心茫然。 欲渡黄河冰塞川，将登太行雪满山。(雪满山 一作：雪暗天) 闲来垂钓碧溪上，忽复乘舟梦日边。(碧 一作：坐) 行路难！行路难！多歧路，今安在？ 长风破浪会有时，直挂云帆济沧海。', '唐诗三百首');
INSERT INTO `poem` VALUES (291, '行路难·其二', '唐代', '李白', '大道如青天，我独不得出。 羞逐长安社中儿，赤鸡白雉赌梨栗。 弹剑作歌奏苦声，曳裾王门不称情。 淮阴市井笑韩信，汉朝公卿忌贾生。 君不见昔时燕家重郭隗，拥篲折节无嫌猜。 剧辛乐毅感恩分，输肝剖胆效英才。 昭王白骨萦蔓草，谁人更扫黄金台？ 行路难，归去来！', '唐诗三百首');
INSERT INTO `poem` VALUES (292, '行路难·其三', '唐代', '李白', '有耳莫洗颍川水，有口莫食首阳蕨。 含光混世贵无名，何用孤高比云月？ 吾观自古贤达人，功成不退皆殒身。 子胥既弃吴江上，屈原终投湘水滨。 陆机雄才岂自保？李斯税驾苦不早。 华亭鹤唳讵可闻？上蔡苍鹰何足道？ 君不见吴中张翰称达生，秋风忽忆江东行。 且乐生前一杯酒，何须身后千载名？', '唐诗三百首');
INSERT INTO `poem` VALUES (293, '将进酒', '唐代', '李白', '君不见，黄河之水天上来，奔流到海不复回。 君不见，高堂明镜悲白发，朝如青丝暮成雪。 人生得意须尽欢，莫使金樽空对月。 天生我材必有用，千金散尽还复来。 烹羊宰牛且为乐，会须一饮三百杯。 岑夫子，丹丘生，将进酒，杯莫停。 与君歌一曲，请君为我倾耳听。(倾耳听 一作：侧耳听) 钟鼓馔玉不足贵，但愿长醉不复醒。(不足贵 一作：何足贵；不复醒 一作：不愿醒/不用醒) 古来圣贤皆寂寞，惟有饮者留其名。(古来 一作：自古；惟 通：唯) 陈王昔时宴平乐，斗酒十千恣欢谑。 主人何为言少钱，径须沽取对君酌。 五花马，千金裘，呼儿将出换美酒，与尔同销万古愁。', '唐诗三百首');
INSERT INTO `poem` VALUES (294, '玉阶怨', '唐代', '李白', '玉阶生白露，夜久侵罗袜。 却下水晶帘，玲珑望秋月。(水晶 一作 水精)', '唐诗三百首');
INSERT INTO `poem` VALUES (295, '长相思·其一', '唐代', '李白', '长相思，在长安。 络纬秋啼金井阑，微霜凄凄簟色寒。 孤灯不明思欲绝，卷帷望月空长叹。 美人如花隔云端！ 上有青冥之长天，下有渌水之波澜。 天长路远魂飞苦，梦魂不到关山难。 长相思，摧心肝！', '唐诗三百首');
INSERT INTO `poem` VALUES (296, '长相思·其二', '唐代', '李白', '日色欲尽花含烟，月明欲素愁不眠。(欲素 一作：如素) 赵瑟初停凤凰柱，蜀琴欲奏鸳鸯弦。 此曲有意无人传，愿随春风寄燕然。 忆君迢迢隔青天，昔日横波目，今作流泪泉。 不信妾断肠，归来看取明镜前。(断肠 一作：肠断)', '唐诗三百首');
INSERT INTO `poem` VALUES (297, '长干行·其一', '唐代', '李白', '妾发初覆额，折花门前剧。 郎骑竹马来，绕床弄青梅。 同居长干里，两小无嫌猜， 十四为君妇，羞颜未尝开。 低头向暗壁，千唤不一回。 十五始展眉，愿同尘与灰。 常存抱柱信，岂上望夫台。 十六君远行，瞿塘滟滪堆。 五月不可触，猿声天上哀。(猿声 一作：鸣) 门前迟行迹，一一生绿苔。 苔深不能扫，落叶秋风早。 八月蝴蝶来，双飞西园草。 感此伤妾心，坐愁红颜老。 早晚下三巴，预将书报家。 相迎不道远，直至长风沙。', '唐诗三百首');
INSERT INTO `poem` VALUES (298, '蜀道难', '唐代', '李白', '噫吁嚱，危乎高哉！蜀道之难，难于上青天！ 蚕丛及鱼凫，开国何茫然！ 尔来四万八千岁，不与秦塞通人烟。 西当太白有鸟道，可以横绝峨眉巅。 地崩山摧壮士死，然后天梯石栈相钩连。 上有六龙回日之高标，下有冲波逆折之回川。 黄鹤之飞尚不得过，猿猱欲度愁攀援。(攀援 一作：攀缘) 青泥何盘盘，百步九折萦岩峦。 扪参历井仰胁息，以手抚膺坐长叹。 问君西游何时还？畏途巉岩不可攀。 但见悲鸟号古木，雄飞雌从绕林间。 又闻子规啼夜月，愁空山。 蜀道之难,难于上青天，使人听此凋朱颜！ 连峰去天不盈尺，枯松倒挂倚绝壁。 飞湍瀑流争喧豗，砯崖转石万壑雷。 其险也如此，嗟尔远道之人胡为乎来哉！(也如此 一作：也若此) 剑阁峥嵘而崔嵬，一夫当关，万夫莫开。 所守或匪亲，化为狼与豺。 朝避猛虎，夕避长蛇；磨牙吮血，杀人如麻。 锦城虽云乐，不如早还家。 蜀道之难,难于上青天，侧身西望长咨嗟！', '唐诗三百首');
INSERT INTO `poem` VALUES (299, '子夜吴歌·春歌', '唐代', '李白', '秦地罗敷女，采桑绿水边。 素手青条上，红妆白日鲜。 蚕饥妾欲去，五马莫留连。', '唐诗三百首');
INSERT INTO `poem` VALUES (300, '子夜吴歌·夏歌', '唐代', '李白', '镜湖三百里，菡萏发荷花。 五月西施采，人看隘若耶。 回舟不待月，归去越王家。', '唐诗三百首');
INSERT INTO `poem` VALUES (301, '子夜吴歌·秋歌', '唐代', '李白', '长安一片月，万户捣衣声。 秋风吹不尽，总是玉关情。 何日平胡虏，良人罢远征。', '唐诗三百首');
INSERT INTO `poem` VALUES (302, '子夜吴歌·冬歌', '唐代', '李白', '明朝驿使发，一夜絮征袍。 素手抽针冷，那堪把剪刀。 裁缝寄远道，几日到临洮。', '唐诗三百首');
INSERT INTO `poem` VALUES (303, '关山月', '唐代', '李白', '明月出天山，苍茫云海间。 长风几万里，吹度玉门关。 汉下白登道，胡窥青海湾。 由来征战地，不见有人还。 戍客望边邑，思归多苦颜。(望边邑 一作：望边色) 高楼当此夜，叹息未应闲。', '唐诗三百首');
INSERT INTO `poem` VALUES (304, '江南曲', '唐代', '李益', '嫁得瞿塘贾，朝朝误妾期。 早知潮有信，嫁与弄潮儿。', '唐诗三百首');
INSERT INTO `poem` VALUES (305, '古从军行', '唐代', '李颀', '白日登山望烽火，黄昏饮马傍交河。 行人刁斗风沙暗，公主琵琶幽怨多。 野云万里无城郭，雨雪纷纷连大漠。 胡雁哀鸣夜夜飞，胡儿眼泪双双落。 闻道玉门犹被遮，应将性命逐轻车。 年年战骨埋荒外，空见蒲桃入汉家。', '唐诗三百首');
INSERT INTO `poem` VALUES (306, '哀王孙', '唐代', '杜甫', '长安城头头白乌，夜飞延秋门上呼。 又向人家啄大屋，屋底达官走避胡。 金鞭断折九马死，骨肉不得同驰驱。 腰下宝玦青珊瑚，可怜王孙泣路隅。 问之不肯道姓名，但道困苦乞为奴。 已经百日窜荆棘，身上无有完肌肤。 高帝子孙尽隆准，龙种自与常人殊。 豺狼在邑龙在野，王孙善保千金躯。 不敢长语临交衢，且为王孙立斯须。 昨夜东风吹血腥，东来橐驼满旧都。 朔方健儿好身手，昔何勇锐今何愚。 窃闻天子已传位，圣德北服南单于。 花门剺面请雪耻，慎勿出口他人狙。 哀哉王孙慎勿疏，五陵佳气无时无。', '唐诗三百首');
INSERT INTO `poem` VALUES (307, '兵车行', '唐代', '杜甫', '车辚辚，马萧萧，行人弓箭各在腰。 耶娘妻子走相送，尘埃不见咸阳桥。(耶娘 一作：“爷”) 牵衣顿足拦道哭，哭声直上干云霄。 道旁过者问行人，行人但云点行频。 或从十五北防河，便至四十西营田。 去时里正与裹头，归来头白还戍边。 边庭流血成海水，武皇开边意未已。 君不闻汉家山东二百州，千村万落生荆杞。 纵有健妇把锄犁，禾生陇亩无东西。 况复秦兵耐苦战，被驱不异犬与鸡。 长者虽有问，役夫敢申恨？ 且如今年冬，未休关西卒。 县官急索租，租税从何出？ 信知生男恶，反是生女好。 生女犹得嫁比邻，生男埋没随百草。 君不见，青海头，古来白骨无人收。 新鬼烦冤旧鬼哭，天阴雨湿声啾啾！', '唐诗三百首');
INSERT INTO `poem` VALUES (308, '丽人行', '唐代', '杜甫', '三月三日天气新，长安水边多丽人。 态浓意远淑且真，肌理细腻骨肉匀。 绣罗衣裳照暮春，蹙金孔雀银麒麟。 头上何所有？翠微盍叶垂鬓唇。 背后何所见？珠压腰衱稳称身。 就中云幕椒房亲，赐名大国虢与秦。 紫驼之峰出翠釜，水精之盘行素鳞。 犀箸厌饫久未下，鸾刀缕切空纷纶。 黄门飞鞚不动尘，御厨络绎送八珍。 箫鼓哀吟感鬼神，宾从杂遝实要津。 后来鞍马何逡巡，当轩下马入锦茵。 杨花雪落覆白苹，青鸟飞去衔红巾。 炙手可热势绝伦，慎莫近前丞相嗔！', '唐诗三百首');
INSERT INTO `poem` VALUES (309, '哀江头', '唐代', '杜甫', '少陵野老吞声哭，春日潜行曲江曲。 江头宫殿锁千门，细柳新蒲为谁绿？ 忆昔霓旌下南苑，苑中万物生颜色。 昭阳殿里第一人，同辇随君侍君侧。 辇前才人带弓箭，白马嚼啮黄金勒。 翻身向天仰射云，一笑正坠双飞翼。 明眸皓齿今何在？血污游魂归不得。 清渭东流剑阁深，去住彼此无消息。 人生有情泪沾臆，江水江花岂终极！ 黄昏胡骑尘满城，欲往城南望城北。', '唐诗三百首');
INSERT INTO `poem` VALUES (310, '金缕衣', '唐代', '佚名', '劝君莫惜金缕衣，劝君惜取少年时。(惜取 一作：须取) 花开堪折直须折，莫待无花空折枝。(花开 一作：有花)', '唐诗三百首');
INSERT INTO `poem` VALUES (311, '古意呈补阙乔知之 / 独不见', '唐代', '沈佺期', '卢家少妇郁金堂，海燕双栖玳瑁梁。 九月寒砧催木叶，十年征戍忆辽阳。 白狼河北音书断，丹凤城南秋夜长。 谁谓含愁独不见，更教明月照流黄。', '唐诗三百首');
INSERT INTO `poem` VALUES (312, '烈女操', '唐代', '孟郊', '梧桐相待老，鸳鸯会双死。 贞女贵殉夫，舍生亦如此。 波澜誓不起，妾心古井水。(古井水 一作：井中水)', '唐诗三百首');
INSERT INTO `poem` VALUES (313, '游子吟', '唐代', '孟郊', '慈母手中线，游子身上衣。 临行密密缝，意恐迟迟归。 谁言寸草心，报得三春晖。', '唐诗三百首');
INSERT INTO `poem` VALUES (314, '燕歌行', '唐代', '高适', '开元二十六年，客有从御史大夫张公出塞而还者；作《燕歌行》以示适，感征戍之事，因而和焉。 汉家烟尘在东北，汉将辞家破残贼。 男儿本自重横行，天子非常赐颜色。 摐金伐鼓下榆关，旌旆逶迤碣石间。 校尉羽书飞瀚海，单于猎火照狼山。 山川萧条极边土，胡骑凭陵杂风雨。 战士军前半死生，美人帐下犹歌舞。 大漠穷秋塞草腓，孤城落日斗兵稀。 身当恩遇常轻敌，力尽关山未解围。(常轻敌 一作：恒轻敌) 铁衣远戍辛勤久，玉箸应啼别离后。 少妇城南欲断肠，征人蓟北空回首。 边庭飘飖那可度，绝域苍茫更何有。(飘飖 一作：飘飘) 杀气三时作阵云，寒声一夜传刁斗。 相看白刃血纷纷，死节从来岂顾勋。 君不见沙场征战苦，至今犹忆李将军。', '唐诗三百首');
INSERT INTO `poem` VALUES (315, '长干行·君家何处住', '唐代', '崔颢', '君家何处住，妾住在横塘。 停船暂借问，或恐是同乡。', '唐诗三百首');
INSERT INTO `poem` VALUES (316, '长干行·家临九江水', '唐代', '崔颢', '家临九江水，来去九江侧。 同是长干人，生小不相识。', '唐诗三百首');
INSERT INTO `poem` VALUES (317, '和张仆射塞下曲·其一', '唐代', '卢纶', '鹫翎金仆姑，燕尾绣蝥弧。 独立扬新令，千营共一呼。', '唐诗三百首');
INSERT INTO `poem` VALUES (318, '和张仆射塞下曲·其二', '唐代', '卢纶', '林暗草惊风，将军夜引弓。 平明寻白羽，没在石棱中。', '唐诗三百首');
INSERT INTO `poem` VALUES (319, '和张仆射塞下曲·其三', '唐代', '卢纶', '月黑雁飞高，单于夜遁逃。 欲将轻骑逐，大雪满弓刀。', '唐诗三百首');
INSERT INTO `poem` VALUES (320, '和张仆射塞下曲·其四', '唐代', '卢纶', '野幕敞琼筵，羌戎贺劳旋。 醉和金甲舞，雷鼓动山川。', '唐诗三百首');
INSERT INTO `poem` VALUES (321, '击壤歌', '先秦', '佚名', '日出而作，日入而息。 凿井而饮，耕田而食。 帝力于我何有哉！', '古诗三百首');
INSERT INTO `poem` VALUES (322, '关雎', '先秦', '佚名', '关关雎鸠，在河之洲。窈窕淑女，君子好逑。 参差荇菜，左右流之。窈窕淑女，寤寐求之。 求之不得，寤寐思服。悠哉悠哉，辗转反侧。 参差荇菜，左右采之。窈窕淑女，琴瑟友之。 参差荇菜，左右芼之。窈窕淑女，钟鼓乐之。', '古诗三百首');
INSERT INTO `poem` VALUES (323, '国风·卫风·木瓜', '先秦', '佚名', '投我以木瓜，报之以琼琚。匪报也，永以为好也！ 投我以木桃，报之以琼瑶。匪报也，永以为好也！ 投我以木李，报之以琼玖。匪报也，永以为好也！', '古诗三百首');
INSERT INTO `poem` VALUES (324, '桃夭', '先秦', '佚名', '桃之夭夭，灼灼其华。之子于归，宜其室家。 桃之夭夭，有蕡其实。之子于归，宜其家室。 桃之夭夭，其叶蓁蓁。之子于归，宜其家人。', '古诗三百首');
INSERT INTO `poem` VALUES (325, '蒹葭', '先秦', '佚名', '蒹葭苍苍，白露为霜。所谓伊人，在水一方。 溯洄从之，道阻且长。溯游从之，宛在水中央。 蒹葭萋萋，白露未晞。所谓伊人，在水之湄。 溯洄从之，道阻且跻。溯游从之，宛在水中坻。 蒹葭采采，白露未已。所谓伊人，在水之涘。 溯洄从之，道阻且右。溯游从之，宛在水中沚。', '古诗三百首');
INSERT INTO `poem` VALUES (326, '无衣', '先秦', '佚名', '岂曰无衣？七兮。不如子之衣，安且吉兮。 岂曰无衣？六兮。不如子之衣，安且燠兮。', '古诗三百首');
INSERT INTO `poem` VALUES (327, '国风·卫风·河广', '先秦', '佚名', '谁谓河广？一苇杭之。谁谓宋远？跂予望之。 谁谓河广？曾不容刀。谁谓宋远？曾不崇朝。', '古诗三百首');
INSERT INTO `poem` VALUES (328, '采薇', '先秦', '佚名', '采薇采薇，薇亦作止。曰归曰归，岁亦莫止。 靡室靡家，猃狁之故。不遑启居，猃狁之故。 采薇采薇，薇亦柔止。曰归曰归，心亦忧止。 忧心烈烈，载饥载渴。我戍未定，靡使归聘。 采薇采薇，薇亦刚止。曰归曰归，岁亦阳止。 王事靡盬，不遑启处。忧心孔疚，我行不来！ 彼尔维何？维常之华。彼路斯何？君子之车。 戎车既驾，四牡业业。岂敢定居？一月三捷。 驾彼四牡，四牡骙骙。君子所依，小人所腓。 四牡翼翼，象弭鱼服。岂不日戒？猃狁孔棘！ 昔我往矣，杨柳依依。今我来思，雨雪霏霏。 行道迟迟，载渴载饥。我心伤悲，莫知我哀！', '古诗三百首');
INSERT INTO `poem` VALUES (329, '国风·魏风·硕鼠', '先秦', '佚名', '硕鼠硕鼠，无食我黍！三岁贯女，莫我肯顾。逝将去女，适彼乐土。乐土乐土，爰得我所。 硕鼠硕鼠，无食我麦！三岁贯女，莫我肯德。逝将去女，适彼乐国。乐国乐国，爰得我直。 硕鼠硕鼠，无食我苗！三岁贯女，莫我肯劳。逝将去女，适彼乐郊。乐郊乐郊，谁之永号？(女 通 汝)', '古诗三百首');
INSERT INTO `poem` VALUES (330, '伐檀', '先秦', '佚名', '坎坎伐檀兮，置之河之干兮。河水清且涟猗。不稼不穑，胡取禾三百廛兮？不狩不猎，胡瞻尔庭有县貆兮？彼君子兮，不素餐兮！ 坎坎伐辐兮，置之河之侧兮。河水清且直猗。不稼不穑，胡取禾三百亿兮？不狩不猎，胡瞻尔庭有县特兮？彼君子兮，不素食兮！ 坎坎伐轮兮，置之河之漘兮。河水清且沦猗。不稼不穑，胡取禾三百囷兮？不狩不猎，胡瞻尔庭有县鹑兮？彼君子兮，不素飧兮！', '古诗三百首');
INSERT INTO `poem` VALUES (331, '国风·鄘风·相鼠', '先秦', '佚名', '相鼠有皮，人而无仪！人而无仪，不死何为？ 相鼠有齿，人而无止！人而无止，不死何俟？ 相鼠有体，人而无礼，人而无礼！胡不遄死？', '古诗三百首');
INSERT INTO `poem` VALUES (332, '荆轲歌 / 渡易水歌', '先秦', '佚名', '风萧萧兮易水寒，壮士一去兮不复还。(复还 一作：复反) 探虎穴兮入蛟宫，仰天呼气兮成白虹。(此句疑为后人补作)', '古诗三百首');
INSERT INTO `poem` VALUES (333, '垓下歌', '两汉', '项羽', '力拔山兮气盖世。时不利兮骓不逝。 骓不逝兮可奈何！虞兮虞兮奈若何！', '古诗三百首');
INSERT INTO `poem` VALUES (334, '大风歌', '两汉', '刘邦', '大风起兮云飞扬， 威加海内兮归故乡， 安得猛士兮守四方！', '古诗三百首');
INSERT INTO `poem` VALUES (335, '秋风辞', '两汉', '刘彻', '秋风起兮白云飞，草木黄落兮雁南归。 兰有秀兮菊有芳，怀佳人兮不能忘。 泛楼船兮济汾河，横中流兮扬素波。 箫鼓鸣兮发棹歌，欢乐极兮哀情多。 少壮几时兮奈老何！', '古诗三百首');
INSERT INTO `poem` VALUES (336, '赋得北方有佳人', '唐代', '徐惠', '由来称独立，本自号倾城。 柳叶眉间发，桃花脸上生。 腕摇金钏响，步转玉环鸣。 纤腰宜宝袜，红衫艳织成。 悬知一顾重，别觉舞腰轻。', '古诗三百首');
INSERT INTO `poem` VALUES (337, '上邪', '两汉', '佚名', '上邪！我欲与君相知，长命无绝衰。 山无陵，江水为竭，冬雷震震，夏雨雪，天地合，乃敢与君绝。', '古诗三百首');
INSERT INTO `poem` VALUES (338, '江南', '两汉', '佚名', '江南可采莲，莲叶何田田，鱼戏莲叶间。 鱼戏莲叶东，鱼戏莲叶西，鱼戏莲叶南，鱼戏莲叶北。', '古诗三百首');
INSERT INTO `poem` VALUES (339, '长歌行', '两汉', '佚名', '青青园中葵，朝露待日晞。 阳春布德泽，万物生光辉。 常恐秋节至，焜黄华叶衰。 百川东到海，何时复西归？ 少壮不努力，老大徒伤悲。', '古诗三百首');
INSERT INTO `poem` VALUES (340, '古歌', '两汉', '佚名', '秋风萧萧愁杀人，出亦愁，入亦愁。 座中何人，谁不怀忧。 令我白头。 胡地多飚风，树木何修修。 离家日趋远，衣带日趋缓。 心思不能言，肠中车轮转。', '古诗三百首');
INSERT INTO `poem` VALUES (341, '十五从军征', '两汉', '佚名', '十五从军征，八十始得归。 道逢乡里人，家中有阿谁？ 遥看是君家，松柏冢累累。(遥看 一作：遥望) 兔从狗窦入，雉从梁上飞。 中庭生旅谷，井上生旅葵。 舂谷持作饭，采葵持作羹。 羹饭一时熟，不知贻阿谁？ 出门东向看，泪落沾我衣。', '古诗三百首');
INSERT INTO `poem` VALUES (342, '明月何皎皎', '两汉', '佚名', '明月何皎皎，照我罗床帏。 忧愁不能寐，揽衣起徘徊。 客行虽云乐，不如早旋归。 出户独彷徨，愁思当告谁！ 引领还入房，泪下沾裳衣。', '古诗三百首');
INSERT INTO `poem` VALUES (343, '东城高且长', '两汉', '佚名', '东城高且长，逶迤自相属。 回风动地起，秋草萋已绿。 四时更变化，岁暮一何速！ 晨风怀苦心，蟋蟀伤局促。 荡涤放情志，何为自结束？ 燕赵多佳人，美者颜如玉。 被服罗裳衣，当户理清曲。 音响一何悲！弦急知柱促。 驰情整巾带，沉吟聊踯躅。 思为双飞燕，衔泥巢君屋。', '古诗三百首');
INSERT INTO `poem` VALUES (344, '行行重行行', '两汉', '佚名', '行行重行行，与君生别离。 相去万余里，各在天一涯。 道路阻且长，会面安可知？ 胡马依北风，越鸟巢南枝。 相去日已远，衣带日已缓。 浮云蔽白日，游子不顾返。 思君令人老，岁月忽已晚。 弃捐勿复道，努力加餐饭。', '古诗三百首');
INSERT INTO `poem` VALUES (345, '涉江采芙蓉', '两汉', '佚名', '涉江采芙蓉，兰泽多芳草。 采之欲遗谁，所思在远道。 还顾望旧乡，长路漫浩浩。 同心而离居，忧伤以终老。', '古诗三百首');
INSERT INTO `poem` VALUES (346, '迢迢牵牛星', '两汉', '佚名', '迢迢牵牛星，皎皎河汉女。 纤纤擢素手，札札弄机杼。 终日不成章，泣涕零如雨。 河汉清且浅，相去复几许！ 盈盈一水间，脉脉不得语。', '古诗三百首');
INSERT INTO `poem` VALUES (347, '回车驾言迈', '两汉', '佚名', '回车驾言迈，悠悠涉长道。 四顾何茫茫，东风摇百草。 所遇无故物，焉得不速老。 盛衰各有时，立身苦不早。 人生非金石，岂能长寿考？ 奄忽随物化，荣名以为宝。', '古诗三百首');
INSERT INTO `poem` VALUES (348, '客从远方来', '两汉', '佚名', '客从远方来，遗我一端绮。 相去万余里，故人心尚尔。 文采双鸳鸯，裁为合欢被。 著以长相思，缘以结不解。 以胶投漆中，谁能别离此？', '古诗三百首');
INSERT INTO `poem` VALUES (349, '凛凛岁云暮', '两汉', '佚名', '凛凛岁云暮，蝼蛄夕鸣悲。 凉风率已厉，游子寒无衣。 锦衾遗洛浦，同袍与我违。 独宿累长夜，梦想见容辉。 良人惟古欢，枉驾惠前绥。 愿得常巧笑，携手同车归。 既来不须臾，又不处重闱。 亮无晨风翼，焉能凌风飞？ 眄睐以适意，引领遥相睎。 徙倚怀感伤，垂涕沾双扉。', '古诗三百首');
INSERT INTO `poem` VALUES (350, '桓灵时童谣', '两汉', '佚名', '举秀才，不知书。 举孝廉，父别居。 寒素清白浊如泥， 高第良将怯如鸡。', '古诗三百首');
INSERT INTO `poem` VALUES (351, '龟虽寿', '两汉', '曹操', '神龟虽寿，犹有竟时。 螣蛇乘雾，终为土灰。 老骥伏枥，志在千里。 烈士暮年，壮心不已。 盈缩之期，不但在天； 养怡之福，可得永年。 幸甚至哉，歌以咏志。', '古诗三百首');
INSERT INTO `poem` VALUES (352, '蒿里行', '两汉', '曹操', '关东有义士，兴兵讨群凶。 初期会盟津，乃心在咸阳。 军合力不齐，踌躇而雁行。 势利使人争，嗣还自相戕。 淮南弟称号，刻玺于北方。 铠甲生虮虱，万姓以死亡。 白骨露於野，千里无鸡鸣。 生民百遗一，念之断人肠。', '古诗三百首');
INSERT INTO `poem` VALUES (353, '观沧海', '两汉', '曹操', '东临碣石，以观沧海。 水何澹澹，山岛竦峙。 树木丛生，百草丰茂。 秋风萧瑟，洪波涌起。 日月之行，若出其中； 星汉灿烂，若出其里。 幸甚至哉，歌以咏志。', '古诗三百首');
INSERT INTO `poem` VALUES (354, '短歌行', '两汉', '曹操', '对酒当歌，人生几何！譬如朝露，去日苦多。 慨当以慷，忧思难忘。何以解忧？唯有杜康。 青青子衿，悠悠我心。但为君故，沉吟至今。 呦呦鹿鸣，食野之苹。我有嘉宾，鼓瑟吹笙。 明明如月，何时可掇？忧从中来，不可断绝。 越陌度阡，枉用相存。契阔谈讌，心念旧恩。(谈讌 一作：谈宴) 月明星稀，乌鹊南飞。绕树三匝，何枝可依？ 山不厌高，海不厌深。周公吐哺，天下归心。(海 一作：水)', '古诗三百首');
INSERT INTO `poem` VALUES (355, '赠从弟', '魏晋', '刘桢', '亭亭山上松，瑟瑟谷中风。 风声一何盛，松枝一何劲。 冰霜正惨凄，终岁常端正。 岂不罹凝寒，松柏有本性。', '古诗三百首');
INSERT INTO `poem` VALUES (356, '室思', '魏晋', '徐干', '沉阴结愁忧，愁忧为谁兴？ 念与君相别，各在天一方。 良会未有期，中心摧且伤。 不聊忧餐食，慊慊常饥空。 端坐而无为，仿佛君容光。 峨峨高山首，悠悠万里道。 君去日已远，郁结令人老。 人生一世间，忽若暮春草。 时不可再得，何为自愁恼？ 每诵昔鸿恩，贱躯焉足保。 浮云何洋洋，愿因通我词。 飘摇不可寄，徙倚徒相思。 人离皆复会，君独无返期。 自君之出矣，明镜暗不治。 思君如流水，何有穷已时。 惨惨时节尽，兰叶复凋零。 喟然长叹息，君期慰我情。 辗转不能寐，长夜何绵绵。 蹑履起出户，仰观三星连。 自恨志不遂，泣涕如涌泉。 思君见巾栉，以益我劳勤。 安得鸿鸾羽，觏此心中人。 诚心亮不遂，搔首立悁悁。 何言一不见，复会无因缘。 故如比目鱼，今隔如参辰。 人靡不有初，想君能终之。 别来历年岁，旧恩何可期。 重新而忘故，君子所尤讥。 寄身虽在远，岂忘君须臾。 既厚不为薄，想君时见思。', '古诗三百首');
INSERT INTO `poem` VALUES (357, '燕歌行二首·其一', '魏晋', '曹丕', '秋风萧瑟天气凉，草木摇落露为霜，群燕辞归鹄南翔。(鹄南翔 一作 雁南翔） 念君客游思断肠，慊慊思归恋故乡，君何淹留寄他方？ 贱妾茕茕守空房，忧来思君不敢忘，不觉泪下沾衣裳。 援琴鸣弦发清商，短歌微吟不能长。 明月皎皎照我床，星汉西流夜未央。 牵牛织女遥相望，尔独何辜限河梁。', '古诗三百首');
INSERT INTO `poem` VALUES (358, '白马篇', '两汉', '曹植', '白马饰金羁，连翩西北驰。借问谁家子，幽并游侠儿。 少小去乡邑，扬声沙漠垂。宿昔秉良弓，楛矢何参差。 控弦破左的，右发摧月支。仰手接飞猱，俯身散马蹄。 狡捷过猴猿，勇剽若豹螭。边城多警急，虏骑数迁移。 羽檄从北来，厉马登高堤。长驱蹈匈奴，左顾凌鲜卑。 弃身锋刃端，性命安可怀？父母且不顾，何言子与妻！ 名编壮士籍，不得中顾私。捐躯赴国难，视死忽如归！(名编 一作：名在)', '古诗三百首');
INSERT INTO `poem` VALUES (359, '七步诗', '两汉', '曹植', '煮豆燃豆萁，豆在釜中泣。 本是同根生，相煎何太急？（版本一） 煮豆持作羹，漉菽以为汁。(菽 一作：豉) 萁在釜下燃，豆在釜中泣。 本自同根生，相煎何太急？（版本二）', '古诗三百首');
INSERT INTO `poem` VALUES (360, '杂诗七首·其四', '两汉', '曹植', '南国有佳人，容华若桃李。 朝游江北岸，夕宿潇湘沚。 时俗薄朱颜，谁为发皓齿？ 俯仰岁将暮，荣耀难久恃。', '古诗三百首');
INSERT INTO `poem` VALUES (361, '七哀诗三首·其一', '两汉', '王粲', '西京乱无象，豺虎方遘患。 复弃中国去，委身适荆蛮。 亲戚对我悲，朋友相追攀。 出门无所见，白骨蔽平原。 路有饥妇人，抱子弃草间。 顾闻号泣声，挥涕独不还。 “未知身死处，何能两相完？” 驱马弃之去，不忍听此言。 南登霸陵岸，回首望长安， 悟彼下泉人，喟然伤心肝。', '古诗三百首');
INSERT INTO `poem` VALUES (362, '赠秀才入军·其十四', '魏晋', '嵇康', '息徒兰圃，秣马华山。 流磻平皋，垂纶长川。 目送归鸿，手挥五弦。 俯仰自得，游心太玄。 嘉彼钓叟，得鱼忘筌。 郢人逝矣，谁与尽言？', '古诗三百首');
INSERT INTO `poem` VALUES (363, '咏怀八十二首·其一', '魏晋', '阮籍', '夜中不能寐，起坐弹鸣琴。 薄帷鉴明月，清风吹我襟。 孤鸿号外野，翔鸟鸣北林。 徘徊将何见？忧思独伤心。', '古诗三百首');
INSERT INTO `poem` VALUES (364, '吴孙皓初童谣', '魏晋', '佚名', '宁饮建业水，不食武昌鱼。 宁还建业死，不止武昌居。', '古诗三百首');
INSERT INTO `poem` VALUES (365, '咏史·郁郁涧底松', '魏晋', '左思', '郁郁涧底松，离离山上苗。 以彼径寸茎，荫此百尺条。 世胄蹑高位，英俊沉下僚。 地势使之然，由来非一朝。 金张藉旧业，七叶珥汉貂。 冯公岂不伟，白首不见招。', '古诗三百首');
INSERT INTO `poem` VALUES (366, '思吴江歌', '魏晋', '张翰', '秋风起兮木叶飞，吴江水兮鲈正肥。 三千里兮家未归，恨难禁兮仰天悲。', '古诗三百首');
INSERT INTO `poem` VALUES (367, '归园田居·其一', '魏晋', '陶渊明', '少无适俗韵，性本爱丘山。 误落尘网中，一去三十年。(误落 一作：误入) 羁鸟恋旧林，池鱼思故渊。 开荒南野际，守拙归园田。 方宅十余亩，草屋八九间。 榆柳荫后檐，桃李罗堂前。 暧暧远人村，依依墟里烟。 狗吠深巷中，鸡鸣桑树颠。 户庭无尘杂，虚室有余闲。 久在樊笼里，复得返自然。', '古诗三百首');
INSERT INTO `poem` VALUES (368, '归园田居·其三', '魏晋', '陶渊明', '种豆南山下，草盛豆苗稀。 晨兴理荒秽，带月荷锄归。 道狭草木长，夕露沾我衣。 衣沾不足惜，但使愿无违。', '古诗三百首');
INSERT INTO `poem` VALUES (369, '饮酒·其五', '魏晋', '陶渊明', '结庐在人境，而无车马喧。 问君何能尔？心远地自偏。 采菊东篱下，悠然见南山。 山气日夕佳，飞鸟相与还。 此中有真意，欲辨已忘言。', '古诗三百首');
INSERT INTO `poem` VALUES (370, '咏荆轲', '魏晋', '陶渊明', '燕丹善养士，志在报强嬴。 招集百夫良，岁暮得荆卿。 君子死知己，提剑出燕京； 素骥鸣广陌，慷慨送我行。 雄发指危冠，猛气冲长缨。 饮饯易水上，四座列群英。 渐离击悲筑，宋意唱高声。 萧萧哀风逝，淡淡寒波生。 商音更流涕，羽奏壮士惊。 心知去不归，且有后世名。 登车何时顾，飞盖入秦庭。 凌厉越万里，逶迤过千城。 图穷事自至，豪主正怔营。 惜哉剑术疏，奇功遂不成。 其人虽已没，千载有馀情。', '古诗三百首');
INSERT INTO `poem` VALUES (371, '读山海经·其一', '魏晋', '陶渊明', '孟夏草木长，绕屋树扶疏。 众鸟欣有托，吾亦爱吾庐。 既耕亦已种，时还读我书。 穷巷隔深辙，颇回故人车。 欢言酌春酒，摘我园中蔬。 微雨从东来，好风与之俱。 泛览《周王传》，流观《山海》图。 俯仰终宇宙，不乐复何如？', '古诗三百首');
INSERT INTO `poem` VALUES (372, '读山海经·其十', '魏晋', '陶渊明', '精卫衔微木，将以填沧海。 刑天舞干戚，猛志固常在。 同物既无虑，化去不复悔。 徒设在昔心，良辰讵可待。(辰 一作：晨)', '古诗三百首');
INSERT INTO `poem` VALUES (373, '杂诗', '魏晋', '陶渊明', '人生无根蒂，飘如陌上尘。 分散逐风转，此已非常身。 落地为兄弟，何必骨肉亲！ 得欢当作乐，斗酒聚比邻。 盛年不重来，一日难再晨。 及时当勉励，岁月不待人。', '古诗三百首');
INSERT INTO `poem` VALUES (374, '酌贪泉', '魏晋', '吴隐之', '古人云此水，一歃怀千金。 试使夷齐饮，终当不易心。', '古诗三百首');
INSERT INTO `poem` VALUES (375, '登江中孤屿', '南北朝', '谢灵运', '江南倦历览，江北旷周旋。 怀新道转迥，寻异景不延。 乱流趋正绝，孤屿媚中川。 云日相晖映，空水共澄鲜。 表灵物莫赏，蕴真谁为传。 想象昆山姿，缅邈区中缘。 始信安期术，得尽养生年。', '古诗三百首');
INSERT INTO `poem` VALUES (376, '拟行路难·其四', '南北朝', '鲍照', '泻水置平地，各自东西南北流。 人生亦有命，安能行叹复坐愁？ 酌酒以自宽，举杯断绝歌路难。 心非木石岂无感？吞声踯躅不敢言。', '古诗三百首');
INSERT INTO `poem` VALUES (377, '拟行路难·其六', '南北朝', '鲍照', '对案不能食，拔剑击柱长叹息。 丈夫生世会几时？安能蹀躞垂羽翼！ 弃置罢官去，还家自休息。 朝出与亲辞，暮还在亲侧。 弄儿床前戏，看妇机中织。 自古圣贤尽贫贱，何况我辈孤且直！', '古诗三百首');
INSERT INTO `poem` VALUES (378, '梅花落', '南北朝', '鲍照', '中庭多杂树，偏为梅咨嗟。问君何独然？念其霜中能作花，露中能作实。摇荡春风媚春日，念尔零落逐寒风，徒有霜华无霜质。(寒风 一作：风飚)', '古诗三百首');
INSERT INTO `poem` VALUES (379, '赠范晔诗', '南北朝', '陆凯', '折花逢驿使，寄与陇头人。(折花 一作：折梅) 江南无所有，聊赠一枝春。', '古诗三百首');
INSERT INTO `poem` VALUES (380, '晚登三山还望京邑', '南北朝', '谢朓', '灞涘望长安，河阳视京县。 白日丽飞甍，参差皆可见。 余霞散成绮，澄江静如练。 喧鸟覆春洲，杂英满芳甸。 去矣方滞淫，怀哉罢欢宴。 佳期怅何许，泪下如流霰。 有情知望乡，谁能鬒不变？', '古诗三百首');
INSERT INTO `poem` VALUES (381, '王孙游', '南北朝', '谢朓', '绿草蔓如丝，杂树红英发。 无论君不归，君归芳已歇。', '古诗三百首');
INSERT INTO `poem` VALUES (382, '之零陵郡次新亭', '南北朝', '范云', '江干远树浮，天末孤烟起。 江天自如合，烟树还相似。 沧流未可源，高帆去何已。', '古诗三百首');
INSERT INTO `poem` VALUES (383, '估客乐四首', '南北朝', '释宝月', '郎作十里行，侬作九里送。 拔侬头上钗，与郎资路用。 有信数寄书，无信心相忆。 莫作瓶落井，一去无消息。 大艑珂峨头，何处发扬州。 借问艑上郎，见侬所欢不？ 初发扬州时，船出平津泊。 五两如竹林，何处相寻博。', '古诗三百首');
INSERT INTO `poem` VALUES (384, '夜夜曲', '南北朝', '沈约', '河汉纵且横，北斗横复直。 星汉空如此，宁知心有忆？ 孤灯暧不明，寒机晓犹织。 零泪向谁道，鸡鸣徒叹息。', '古诗三百首');
INSERT INTO `poem` VALUES (385, '咏早梅 / 扬州法曹梅花盛开', '南北朝', '何逊', '兔园标物序，惊时最是梅。 衔霜当路发，映雪拟寒开。 枝横却月观，花绕凌风台。 朝洒长门泣，夕驻临邛杯。 应知早飘落，故逐上春来。', '古诗三百首');
INSERT INTO `poem` VALUES (386, '边城思', '南北朝', '何逊', '柳黄未吐叶，水绿半含苔。 春色边城动，客思故乡来。', '古诗三百首');
INSERT INTO `poem` VALUES (387, '山中杂诗', '南北朝', '吴均', '山际见来烟，竹中窥落日。 鸟向檐上飞，云从窗里出。', '古诗三百首');
INSERT INTO `poem` VALUES (388, '诏问山中何所有赋诗以答', '南北朝', '陶弘景', '山中何所有，岭上多白云。 只可自怡悦，不堪持赠君。', '古诗三百首');
INSERT INTO `poem` VALUES (389, '入若耶溪', '南北朝', '王籍', '艅艎何泛泛，空水共悠悠。 阴霞生远岫，阳景逐回流。 蝉噪林逾静，鸟鸣山更幽。 此地动归念，长年悲倦游。', '古诗三百首');
INSERT INTO `poem` VALUES (390, '蜀道难·其一', '南北朝', '萧纲', '建平督邮道，鱼复永安宫。 若奏巴渝曲，时当君思中。', '古诗三百首');
INSERT INTO `poem` VALUES (391, '蜀道难·其二', '南北朝', '萧纲', '巫山七百里，巴水三回曲。 笛声下复高，猿啼断还续。', '古诗三百首');
INSERT INTO `poem` VALUES (392, '晚出新亭', '南北朝', '阴铿', '大江一浩荡，离悲足几重。 潮落犹如盖，云昏不作峰。 远戍唯闻鼓，寒山但见松。 九十方称半，归途讵有踪。', '古诗三百首');
INSERT INTO `poem` VALUES (393, '关山月', '南北朝', '徐陵', '关山三五月，客子忆秦川。 思妇高楼上，当窗应未眠。 星旗映疏勒，云阵上祁连。 战气今如此，从军复几年。', '古诗三百首');
INSERT INTO `poem` VALUES (394, '断句', '南北朝', '刘昶', '白云满鄣来，黄尘暗天起。 关山四面绝，故乡几千里。', '古诗三百首');
INSERT INTO `poem` VALUES (395, '寄王琳', '南北朝', '庾信', '玉关道路远，金陵信使疏。 独下千行泪，开君万里书。', '古诗三百首');
INSERT INTO `poem` VALUES (396, '作蚕丝', '南北朝', '佚名', '春蚕不应老，昼夜常怀丝。 何惜微躯尽，缠绵自有时。', '古诗三百首');
INSERT INTO `poem` VALUES (397, '陇头歌辞三首', '南北朝', '佚名', '陇头流水，流离山下。 念吾一身，飘然旷野。 朝发欣城，暮宿陇头。 寒不能语，舌卷入喉。 陇头流水，鸣声呜咽。 遥望秦川，心肝断绝。', '古诗三百首');
INSERT INTO `poem` VALUES (398, '折杨柳歌辞五首', '南北朝', '佚名', '上马不捉鞭，反折杨柳枝。 蹀座吹长笛，愁杀行客儿。 腹中愁不乐，愿作郎马鞭。 出入擐郎臂，蹀座郎膝边。 放马两泉泽，忘不著连羁。 担鞍逐马走，何见得马骑。 遥看孟津河，杨柳郁婆娑。 我是虏家儿，不解汉儿歌。 健儿须快马，快马须健儿。 跸跋黄尘下，然后别雄雌。', '古诗三百首');
INSERT INTO `poem` VALUES (399, '敕勒歌', '南北朝', '乐府诗集', '敕勒川，阴山下。天似穹庐，笼盖四野。 天苍苍，野茫茫。风吹草低见牛羊。', '古诗三百首');
INSERT INTO `poem` VALUES (400, '于长安归还扬州九月九日行薇山亭赋韵 / 长安九日诗', '南北朝', '江总', '心逐南云逝，形随北雁来。 故乡篱下菊，今日几花开。', '古诗三百首');
INSERT INTO `poem` VALUES (401, '人日思归', '南北朝', '薛道衡', '入春才七日，离家已二年。 人归落雁后，思发在花前。', '古诗三百首');
INSERT INTO `poem` VALUES (402, '侍宴咏石榴', '唐代', '孔绍安', '可惜庭中树，移根逐汉臣。 只为来时晚，花开不及春。', '古诗三百首');
INSERT INTO `poem` VALUES (403, '塞上', '宋代', '柳开', '鸣骹直上一千尺，天静无风声更干。 碧眼胡儿三百骑，尽提金勒向云看。', '古诗三百首');
INSERT INTO `poem` VALUES (404, '柳枝词', '宋代', '郑文宝', '亭亭画舸系春潭，直到行人酒半酣。 不管烟波与风雨，载将离恨过江南。', '古诗三百首');
INSERT INTO `poem` VALUES (405, '村行', '宋代', '王禹偁', '马穿山径菊初黄，信马悠悠野兴长。 万壑有声含晚籁，数峰无语立斜阳。 棠梨叶落胭脂色，荞麦花开白雪香。 何事吟余忽惆怅，村桥原树似吾乡。', '古诗三百首');
INSERT INTO `poem` VALUES (406, '春居杂兴·两株桃杏映篱斜', '宋代', '王禹偁', '两株桃杏映篱斜，妆点商州副使家。 何事春风容不得？和莺吹折数枝花。', '古诗三百首');
INSERT INTO `poem` VALUES (407, '柳', '宋代', '寇准', '晓带轻烟间杏花，晚凝深翠拂平沙。 长条别有风流处，密映钱塘苏小家。', '古诗三百首');
INSERT INTO `poem` VALUES (408, '书河上亭壁', '宋代', '寇准', '岸阔樯稀波渺茫，独凭危槛思何长。 萧萧远树疏林外，一半秋山带夕阳。', '古诗三百首');
INSERT INTO `poem` VALUES (409, '咏傀儡', '宋代', '杨亿', '鲍老当筵笑郭郎，笑他舞袖太郎当。 若教鲍老当筵舞，转更郎当舞袖长。', '古诗三百首');
INSERT INTO `poem` VALUES (410, '山园小梅二首', '宋代', '林逋', '众芳摇落独暄妍，占尽风情向小园。 疏影横斜水清浅，暗香浮动月黄昏。 霜禽欲下先偷眼，粉蝶如知合断魂。 幸有微吟可相狎，不须檀板共金樽。 剪绡零碎点酥乾，向背稀稠画亦难。 日薄从甘春至晚，霜深应怯夜来寒。 澄鲜只共邻僧惜，冷落犹嫌俗客看。 忆着江南旧行路，酒旗斜拂堕吟鞍。', '古诗三百首');
INSERT INTO `poem` VALUES (411, '落花', '宋代', '宋祁', '坠素翻红各自伤，青楼烟雨忍相忘。 将飞更作回风舞，已落犹成半面妆。 沧海客归珠有泪，章台人去骨遗香。 可能无意传双蝶，尽付芳心与蜜房。', '古诗三百首');
INSERT INTO `poem` VALUES (412, '宿甘露寺僧舍', '宋代', '曾公亮', '枕中云气千峰近，床底松声万壑哀。 要看银山拍天浪，开窗放入大江来。', '古诗三百首');
INSERT INTO `poem` VALUES (413, '东溪', '宋代', '梅尧臣', '行到东溪看水时，坐临孤屿发船迟。 野凫眠岸有闲意，老树着花无丑枝。 短短蒲茸齐似剪，平平沙石净于筛。 情虽不厌住不得，薄暮归来车马疲。', '古诗三百首');
INSERT INTO `poem` VALUES (414, '鲁山山行', '宋代', '梅尧臣', '适与野情惬，千山高复低。 好峰随处改，幽径独行迷。 霜落熊升树，林空鹿饮溪。 人家在何许？云外一声鸡。', '古诗三百首');
INSERT INTO `poem` VALUES (415, '陶者', '宋代', '梅尧臣', '陶尽门前土，屋上无片瓦。 十指不沾泥，鳞鳞居大厦。', '古诗三百首');
INSERT INTO `poem` VALUES (416, '考试毕登铨楼', '宋代', '梅尧臣', '春云浓淡日微光，双阙重门耸建章。 不上楼来知几日，满城无算柳梢黄。', '古诗三百首');
INSERT INTO `poem` VALUES (417, '初晴游沧浪亭', '宋代', '苏舜钦', '夜雨连明春水生，娇云浓暖弄阴晴。 帘虚日薄花竹静，时有乳鸠相对鸣。', '古诗三百首');
INSERT INTO `poem` VALUES (418, '夏意', '宋代', '苏舜钦', '别院深深夏席清，石榴开遍透帘明。(夏席清 一作：夏簟清) 树阴满地日当午，梦觉流莺时一声。', '古诗三百首');
INSERT INTO `poem` VALUES (419, '蚕妇', '宋代', '张俞', '昨日入城市，归来泪满巾。 遍身罗绮者，不是养蚕人。', '古诗三百首');
INSERT INTO `poem` VALUES (420, '晚泊岳阳', '宋代', '欧阳修', '卧闻岳阳城里钟，系舟岳阳城下树。 正见空江明月来，云水苍茫失江路。 夜深江月弄清辉，水上人歌月下归； 一阕声长听不尽，轻舟短楫去如飞。', '古诗三百首');
INSERT INTO `poem` VALUES (421, '画眉鸟', '宋代', '欧阳修', '百啭千声随意移，山花红紫树高低。 始知锁向金笼听，不及林间自在啼。', '古诗三百首');
INSERT INTO `poem` VALUES (422, '戏答元珍', '宋代', '欧阳修', '春风疑不到天涯，二月山城未见花。 残雪压枝犹有橘，冻雷惊笋欲抽芽。 夜闻归雁生乡思，病入新年感物华。 曾是洛阳花下客，野芳虽晚不须嗟。', '古诗三百首');
INSERT INTO `poem` VALUES (423, '丰乐亭游春·其三', '宋代', '欧阳修', '红树青山日欲斜，长郊草色绿无涯。 游人不管春将老，来往亭前踏落花。', '古诗三百首');
INSERT INTO `poem` VALUES (424, '别滁', '宋代', '欧阳修', '花光浓烂柳轻明，酌酒花前送我行。 我亦且如常日醉，莫教弦管作离声。', '古诗三百首');
INSERT INTO `poem` VALUES (425, '乡思', '宋代', '李觏', '人言落日是天涯，望极天涯不见家。 已恨碧山相阻隔，碧山还被暮云遮。', '古诗三百首');
INSERT INTO `poem` VALUES (426, '梅花', '宋代', '王安石', '墙角数枝梅，凌寒独自开。 遥知不是雪，为有暗香来。', '古诗三百首');
INSERT INTO `poem` VALUES (427, '叠题乌江亭', '宋代', '王安石', '百战疲劳壮士哀，中原一败势难回。 江东子弟今虽在，肯与君王卷土来？', '古诗三百首');
INSERT INTO `poem` VALUES (428, '元日', '宋代', '王安石', '爆竹声中一岁除，春风送暖入屠苏。 千门万户曈曈日，总把新桃换旧符。', '古诗三百首');
INSERT INTO `poem` VALUES (429, '泊船瓜洲', '宋代', '王安石', '京口瓜洲一水间，钟山只隔数重山。 春风又绿江南岸，明月何时照我还？', '古诗三百首');
INSERT INTO `poem` VALUES (430, '登飞来峰', '宋代', '王安石', '飞来山上千寻塔，闻说鸡鸣见日升。(飞来山 一作：飞来峰) 不畏浮云遮望眼，只缘身在最高层。(只缘 一作：自缘)', '古诗三百首');
INSERT INTO `poem` VALUES (431, '春夜', '宋代', '王安石', '金炉香烬漏声残，翦翦轻风阵阵寒。 春色恼人眠不得，月移花影上栏杆。', '古诗三百首');
INSERT INTO `poem` VALUES (432, '春日偶成', '宋代', '程颢', '云淡风轻近午天，傍花随柳过前川。 时人不识余心乐，将谓偷闲学少年。', '古诗三百首');
INSERT INTO `poem` VALUES (433, '新晴', '宋代', '刘攽', '青苔满地初晴后，绿树无人昼梦余。 唯有南风旧相识，偷开门户又翻书。', '古诗三百首');
INSERT INTO `poem` VALUES (434, '雨后池上', '宋代', '刘攽', '一雨池塘水面平，淡磨明镜照檐楹。 东风忽起垂杨舞，更作荷心万点声。', '古诗三百首');
INSERT INTO `poem` VALUES (435, '暑旱苦热', '宋代', '王令', '清风无力屠得热，落日着翅飞上山。 人固已惧江海竭，天岂不惜河汉干？ 昆仑之高有积雪，蓬莱之远常遗寒。 不能手提天下往，何忍身去游其间？', '古诗三百首');
INSERT INTO `poem` VALUES (436, '送春 / 春晚', '宋代', '王令', '三月残花落更开，小檐日日燕飞来。 子规夜半犹啼血，不信东风唤不回。', '古诗三百首');
INSERT INTO `poem` VALUES (437, '村居', '宋代', '张舜民', '水绕陂田竹绕篱，榆钱落尽槿花稀。 夕阳牛背无人卧，带得寒鸦两两归。', '古诗三百首');
INSERT INTO `poem` VALUES (438, '咏柳', '宋代', '曾巩', '乱条犹未变初黄，倚得东风势便狂。 解把飞花蒙日月，不知天地有清霜。', '古诗三百首');
INSERT INTO `poem` VALUES (439, '城南', '宋代', '曾巩', '雨过横塘水满堤，乱山高下路东西。 一番桃李花开尽，惟有青青草色齐。', '古诗三百首');
INSERT INTO `poem` VALUES (440, '题西林壁', '宋代', '苏轼', '横看成岭侧成峰，远近高低各不同。 不识庐山真面目，只缘身在此山中。', '古诗三百首');
INSERT INTO `poem` VALUES (441, '惠崇春江晚景二首 / 惠崇春江晓景二首', '宋代', '苏轼', '竹外桃花三两枝，春江水暖鸭先知。 蒌蒿满地芦芽短，正是河豚欲上时。 两两归鸿欲破群，依依还似北归人。 遥知朔漠多风雪，更待江南半月春。', '古诗三百首');
INSERT INTO `poem` VALUES (442, '和子由渑池怀旧', '宋代', '苏轼', '人生到处知何似，应似飞鸿踏雪泥。 泥上偶然留指爪，鸿飞那复计东西。 老僧已死成新塔，坏壁无由见旧题。 往日崎岖还记否，路长人困蹇驴嘶。', '古诗三百首');
INSERT INTO `poem` VALUES (443, '春宵', '宋代', '苏轼', '春宵一刻值千金，花有清香月有阴。 歌管楼台声细细，秋千院落夜沉沉。', '古诗三百首');
INSERT INTO `poem` VALUES (444, '饮湖上初晴后雨二首·其二', '宋代', '苏轼', '水光潋滟晴方好，山色空蒙雨亦奇。(蒙 通：濛) 欲把西湖比西子，淡妆浓抹总相宜。', '古诗三百首');
INSERT INTO `poem` VALUES (445, '赠刘景文', '宋代', '苏轼', '荷尽已无擎雨盖，菊残犹有傲霜枝。 一年好景君须记，最是橙黄橘绿时。(最是 一作：正是)', '古诗三百首');
INSERT INTO `poem` VALUES (446, '海棠', '宋代', '苏轼', '东风袅袅泛崇光，香雾空蒙月转廊。 只恐夜深花睡去，故烧高烛照红妆。', '古诗三百首');
INSERT INTO `poem` VALUES (447, '禾熟', '宋代', '孔平仲', '百里西风禾黍香，鸣泉落窦谷登场。 老牛粗了耕耘债，啮草坡头卧夕阳。', '古诗三百首');
INSERT INTO `poem` VALUES (448, '寄内', '宋代', '孔平仲', '试说途中景，方知别后心。 行人日暮少，风雪乱山深。', '古诗三百首');
INSERT INTO `poem` VALUES (449, '牧童诗', '宋代', '黄庭坚', '骑牛远远过前村，短笛横吹隔陇闻。 多少长安名利客，机关用尽不如君。', '古诗三百首');
INSERT INTO `poem` VALUES (450, '登快阁', '宋代', '黄庭坚', '痴儿了却公家事，快阁东西倚晚晴。 落木千山天远大，澄江一道月分明。 朱弦已为佳人绝，青眼聊因美酒横。 万里归船弄长笛，此心吾与白鸥盟。', '古诗三百首');
INSERT INTO `poem` VALUES (451, '鄂州南楼书事', '宋代', '黄庭坚', '四顾山光接水光，凭栏十里芰荷香。 清风明月无人管，并作南楼一味凉。', '古诗三百首');
INSERT INTO `poem` VALUES (452, '雨中登岳阳楼望君山', '宋代', '黄庭坚', '投荒万死鬓毛斑，生出瞿塘滟滪关。 未到江南先一笑，岳阳楼上对君山。 满川风雨独凭栏，绾结湘娥十二鬟。 可惜不当湖水面，银山堆里看青山。', '古诗三百首');
INSERT INTO `poem` VALUES (453, '临平道中', '宋代', '道潜', '风蒲猎猎弄清柔，欲立蜻蜓不自由。 五月临平山下路，藕花无数满汀州。', '古诗三百首');
INSERT INTO `poem` VALUES (454, '春日', '宋代', '晁冲之', '阴阴溪曲绿交加，小雨翻萍上浅沙。 鹅鸭不知春去尽，争随流水趁桃花。', '古诗三百首');
INSERT INTO `poem` VALUES (455, '春日', '宋代', '张耒', '如丝苣甲饤春盘，韭叶金黄雪未干。 旅饭二年无此味，故园千里几时还。 异方时节三巵酒，残岁风烟一惨颜。 曾奉龙旗典邦礼，岁穷祠祀少休闲。', '古诗三百首');
INSERT INTO `poem` VALUES (456, '夜坐', '宋代', '张耒', '庭户无人秋月明，夜霜欲落气先清。 梧桐真不甘衰谢，数叶迎风尚有声。', '古诗三百首');
INSERT INTO `poem` VALUES (457, '纳凉', '宋代', '秦观', '携扙来追柳外凉，画桥南畔倚胡床。 月明船笛参差起，风定池莲自在香。', '古诗三百首');
INSERT INTO `poem` VALUES (458, '春日', '宋代', '秦观', '一夕轻雷落万丝，霁光浮瓦碧参差。 有情芍药含春泪，无力蔷薇卧晓枝。', '古诗三百首');
INSERT INTO `poem` VALUES (459, '还自广陵', '宋代', '秦观', '天寒水鸟自相依，十百为群戏落晖。 过尽行人都不起，忽闻水响一齐飞。', '古诗三百首');
INSERT INTO `poem` VALUES (460, '春怀示邻里', '宋代', '陈师道', '断墙着雨蜗成字，老屋无僧燕作家， 剩欲出门追语笑，却嫌归鬓著尘沙。 风翻蛛网开三面，雷动蜂窠趁两衙。 屡失南邻春事约，只今容有未开花。', '古诗三百首');
INSERT INTO `poem` VALUES (461, '示三子', '宋代', '陈师道', '时三子已归自外家 去远即相忘，归近不可忍。 儿女已在眼，眉目略不省。 喜极不得语，泪尽方一哂。 了知不是梦，忽忽心未稳。', '古诗三百首');
INSERT INTO `poem` VALUES (462, '春游湖', '宋代', '徐俯', '双飞燕子几时回？夹岸桃花蘸水开。 春雨断桥人不渡，小舟撑出柳阴来。(渡 一作：度)', '古诗三百首');
INSERT INTO `poem` VALUES (463, '绝句', '宋代', '吴涛', '游子春衫已试单，桃花飞尽野梅酸。 怪来一夜蛙声歇，又作东风十日寒。', '古诗三百首');
INSERT INTO `poem` VALUES (464, '连州阳山归路', '宋代', '吕本中', '稍离烟瘴近湘潭，疾病衰颓已不堪。 儿女不知来避地，强言风物胜江南。', '古诗三百首');
INSERT INTO `poem` VALUES (465, '自责二首', '宋代', '朱淑真', '女子弄文诚可罪，那堪咏月更吟风。 磨穿铁砚非吾事，绣折金针却有功。 闷无消遣只看诗，不见诗中话别离。 添得情怀转萧索，始知伶俐不如痴。', '古诗三百首');
INSERT INTO `poem` VALUES (466, '落花', '宋代', '朱淑真', '连理枝头花正开，妒花风雨便相催。 愿教青帝常为主，莫遣纷纷点翠苔。', '古诗三百首');
INSERT INTO `poem` VALUES (467, '春日', '宋代', '汪藻', '一春略无十日晴，处处浮云将雨行。 野田春水碧于镜，人影渡傍鸥不惊。 桃花嫣然出篱笑，似开未开最有情。 茅茨烟暝客衣湿，破梦午鸡啼一声。', '古诗三百首');
INSERT INTO `poem` VALUES (468, '汴京纪事', '宋代', '刘子翚', '辇毂繁华事可伤，师师垂老过湖湘。 缕衣檀板无颜色，一曲当时动帝王。', '古诗三百首');
INSERT INTO `poem` VALUES (469, '夏日绝句', '宋代', '李清照', '生当作人杰，死亦为鬼雄。 至今思项羽，不肯过江东。', '古诗三百首');
INSERT INTO `poem` VALUES (470, '苏秀道中', '宋代', '曾几', '苏秀道中，自七月二十五日夜大雨三日，秋苗以苏，喜而有作。 一夕骄阳转作霖，梦回凉冷润衣襟。 不愁屋漏床床湿，且喜溪流岸岸深。 千里稻花应秀色，五更桐叶最佳音。 无田似我犹欣舞，何况田间望岁心。', '古诗三百首');
INSERT INTO `poem` VALUES (471, '三衢道中', '宋代', '曾几', '梅子黄时日日晴，小溪泛尽却山行。 绿阴不减来时路，添得黄鹂四五声。', '古诗三百首');
INSERT INTO `poem` VALUES (472, '病牛', '宋代', '李纲', '耕犁千亩实千箱，力尽筋疲谁复伤？ 但得众生皆得饱，不辞羸病卧残阳。', '古诗三百首');
INSERT INTO `poem` VALUES (473, '襄邑道中', '宋代', '陈与义', '飞花两岸照船红，百里榆堤半日风。 卧看满天云不动，不知云与我俱东。', '古诗三百首');
INSERT INTO `poem` VALUES (474, '雨晴', '宋代', '陈与义', '天缺西南江面清，纤云不动小滩横。 墙头语鹊衣犹湿，楼外残雷气未平。 尽取微凉供稳睡，急搜奇句报新晴。 今宵绝胜无人共，卧看星河尽意明。', '古诗三百首');
INSERT INTO `poem` VALUES (475, '咏牡丹', '宋代', '陈与义', '一自胡尘入汉关，十年伊洛路漫漫。 青墩溪畔龙钟客，独立东风看牡丹。', '古诗三百首');
INSERT INTO `poem` VALUES (476, '池州翠微亭', '宋代', '岳飞', '经年尘土满征衣，特特寻芳上翠微。 好水好山看不足，马蹄催趁月明归。', '古诗三百首');
INSERT INTO `poem` VALUES (477, '樵夫', '宋代', '萧德藻', '一担乾柴古渡头，盘缠一日颇优游。 归来涧底磨刀斧，又作全家明日谋。', '古诗三百首');
INSERT INTO `poem` VALUES (478, '临安春雨初霁', '宋代', '陆游', '世味年来薄似纱，谁令骑马客京华。 小楼一夜听春雨，深巷明朝卖杏花。 矮纸斜行闲作草，晴窗细乳戏分茶。 素衣莫起风尘叹，犹及清明可到家。', '古诗三百首');
INSERT INTO `poem` VALUES (479, '金错刀行', '宋代', '陆游', '黄金错刀白玉装，夜穿窗扉出光芒。 丈夫五十功未立，提刀独立顾八荒。 京华结交尽奇士，意气相期共生死。 千年史册耻无名，一片丹心报天子。 尔来从军天汉滨，南山晓雪玉嶙峋。 呜呼！楚虽三户能亡秦，岂有堂堂中国空无人！', '古诗三百首');
INSERT INTO `poem` VALUES (480, '剑门道中遇微雨', '宋代', '陆游', '衣上征尘杂酒痕，远游无处不销魂。 此身合是诗人未？细雨骑驴入剑门。', '古诗三百首');
INSERT INTO `poem` VALUES (481, '梅花绝句·其一', '宋代', '陆游', '闻道梅花坼晓风，雪堆遍满四山中。 何方可化身千亿，一树梅花一放翁。', '古诗三百首');
INSERT INTO `poem` VALUES (482, '示儿', '宋代', '陆游', '死去元知万事空，但悲不见九州同。 王师北定中原日，家祭无忘告乃翁。', '古诗三百首');
INSERT INTO `poem` VALUES (483, '十一月四日风雨大作二首', '宋代', '陆游', '风卷江湖雨暗村，四山声作海涛翻。 溪柴火软蛮毡暖，我与狸奴不出门。 僵卧孤村不自哀，尚思为国戍轮台。 夜阑卧听风吹雨，铁马冰河入梦来。', '古诗三百首');
INSERT INTO `poem` VALUES (484, '书愤五首·其一', '宋代', '陆游', '早岁那知世事艰，中原北望气如山。 楼船夜雪瓜洲渡，铁马秋风大散关。 塞上长城空自许，镜中衰鬓已先斑。 出师一表真名世，千载谁堪伯仲间！', '古诗三百首');
INSERT INTO `poem` VALUES (485, '病起书怀', '宋代', '陆游', '病骨支离纱帽宽，孤臣万里客江干。 位卑未敢忘忧国，事定犹须待阖棺。 天地神灵扶庙社，京华父老望和銮。 出师一表通今古，夜半挑灯更细看。', '古诗三百首');
INSERT INTO `poem` VALUES (486, '秋夜将晓出篱门迎凉有感二首', '宋代', '陆游', '迢迢天汉西南落，喔喔邻鸡一再鸣。 壮志病来消欲尽，出门搔首怆平生。 三万里河东入海，五千仞岳上摩天。 遗民泪尽胡尘里，南望王师又一年。', '古诗三百首');
INSERT INTO `poem` VALUES (487, '四时田园杂兴·其二', '宋代', '范成大', '梅子金黄杏子肥，麦花雪白菜花稀。 日长篱落无人过，惟有蜻蜓蛱蝶飞。(惟 通 唯)', '古诗三百首');
INSERT INTO `poem` VALUES (488, '横塘', '宋代', '范成大', '南浦春来绿一川，石桥朱塔两依然。 年年送客横塘路，细雨垂杨系画船。', '古诗三百首');
INSERT INTO `poem` VALUES (489, '州桥', '宋代', '范成大', '南望朱雀门，北望宣德楼，皆旧御路也。 州桥南北是天街，父老年年等驾回。 忍泪失声询使者，\"几时真有六军来？\"', '古诗三百首');
INSERT INTO `poem` VALUES (490, '三江小渡', '宋代', '杨万里', '溪水将桥不复回，小舟犹倚短篙开。 交情得似山溪渡，不管风波去又来。', '古诗三百首');
INSERT INTO `poem` VALUES (491, '舟过安仁', '宋代', '杨万里', '一叶渔船两小童，收篙停棹坐船中。 怪生无雨都张伞，不是遮头是使风。', '古诗三百首');
INSERT INTO `poem` VALUES (492, '初入淮河四绝句·其三', '宋代', '杨万里', '两岸舟船各背驰，波痕交涉亦难为。 只余鸥鹭无拘管，北去南来自在飞。', '古诗三百首');
INSERT INTO `poem` VALUES (493, '小池', '宋代', '杨万里', '泉眼无声惜细流，树阴照水爱晴柔。 小荷才露尖尖角，早有蜻蜓立上头。', '古诗三百首');
INSERT INTO `poem` VALUES (494, '晓出净慈寺送林子方', '宋代', '杨万里', '毕竟西湖六月中，风光不与四时同。 接天莲叶无穷碧，映日荷花别样红。', '古诗三百首');
INSERT INTO `poem` VALUES (495, '宿新市徐公店', '宋代', '杨万里', '篱落疏疏一径深，树头花落未成阴。(花落 一作：新绿) 儿童急走追黄蝶，飞入菜花无处寻。', '古诗三百首');
INSERT INTO `poem` VALUES (496, '题临安邸', '宋代', '林升', '山外青山楼外楼，西湖歌舞几时休？ 暖风熏得游人醉，直把杭州作汴州。', '古诗三百首');
INSERT INTO `poem` VALUES (497, '活水亭观书有感二首·其二', '宋代', '朱熹', '昨夜江边春水生，艨艟巨舰一毛轻。 向来枉费推移力，此日中流自在行。', '古诗三百首');
INSERT INTO `poem` VALUES (498, '春日', '宋代', '朱熹', '胜日寻芳泗水滨，无边光景一时新。 等闲识得东风面，万紫千红总是春。', '古诗三百首');
INSERT INTO `poem` VALUES (499, '立春偶成', '宋代', '张栻', '律回岁晚冰霜少，春到人间草木知。 便觉眼前生意满，东风吹水绿参差。', '古诗三百首');
INSERT INTO `poem` VALUES (500, '除放自石湖归苕溪', '宋代', '姜夔', '美人台上昔欢娱，今日空台望五湖。残雪未融青草死，苦无麋鹿过姑苏。 黄帽传呼睡不成，投稿细细激流冰。分明旧泊江南岸，舟尾春风飐客灯。 千门列炬散林鸦，儿女相思未到家。应是不眠非守岁，小客春意入灯花。 三生定自陆天随，又向顺松作客归。已拚新年舟上过，倩人和雪洗片衣。 沙尾风回一棹寒，椒花今夕不登盘。百年草草都寒夜，自琢春词剪烛看。 笠泽茫茫雁影微，玉峰重叠护云衣。长桥寂寞春寒放，只有诗人一舸归。 桑间篝火却定蚕，风土相传我未谙。但得明年少行役，只裁白伫作春衫。 少小知名翰墨场，十年心事只凄凉。旧时曾作梅花赋，研墨于今亦自香。 环玦随波冷未销，古苔留雪卧墙腰。谁家玉笛吹春怨，看见鹅黄上柳条。', '古诗三百首');
INSERT INTO `poem` VALUES (501, '过垂虹', '宋代', '姜夔', '自作新词韵最娇，小红低唱我吹箫。 曲终过尽松陵路，回首烟波十四桥。', '古诗三百首');
INSERT INTO `poem` VALUES (502, '新凉', '宋代', '徐玑', '水满田畴稻叶齐，日光穿树晓烟低。 黄莺也爱新凉好，飞过青山影里啼。', '古诗三百首');
INSERT INTO `poem` VALUES (503, '约客', '宋代', '赵师秀', '黄梅时节家家雨，青草池塘处处蛙。 有约不来过夜半，闲敲棋子落灯花。', '古诗三百首');
INSERT INTO `poem` VALUES (504, '乡村四月', '宋代', '翁卷', '绿遍山原白满川，子规声里雨如烟。 乡村四月闲人少，才了蚕桑又插田。', '古诗三百首');
INSERT INTO `poem` VALUES (505, '江村晚眺', '宋代', '戴复古', '江头落日照平沙，潮退渔船阁岸斜。 白鸟一双临水立，见人惊起入芦花。', '古诗三百首');
INSERT INTO `poem` VALUES (506, '淮村兵后', '宋代', '戴复古', '小桃无主自开花，烟草茫茫带晚鸦。 几处败垣围故井，向来一一是人家。', '古诗三百首');
INSERT INTO `poem` VALUES (507, '游园不值', '宋代', '叶绍翁', '应怜屐齿印苍苔，小扣柴扉久不开。 春色满园关不住，一枝红杏出墙来。', '古诗三百首');
INSERT INTO `poem` VALUES (508, '落梅', '宋代', '刘克庄', '一片能教一断肠，可堪平砌更堆墙。 飘如迁客来过岭，坠似骚人去赴湘。 乱点莓苔多莫数，偶粘衣袖久犹香。 东风谬掌花权柄，却忌孤高不主张。', '古诗三百首');
INSERT INTO `poem` VALUES (509, '莺梭', '宋代', '刘克庄', '掷柳迁乔太有情，交交时作弄机声。 洛阳三月花如锦，多少工夫织得成。', '古诗三百首');
INSERT INTO `poem` VALUES (510, '初夏游张园', '宋代', '戴复古', '乳鸭池塘水浅深，熟梅天气半阴晴。 东园载酒西园醉，摘尽枇杷一树金。', '古诗三百首');
INSERT INTO `poem` VALUES (511, '春暮', '宋代', '曹豳', '门外无人问落花，绿阴冉冉遍天涯。 林莺啼到无声处，青草池塘独听蛙。', '古诗三百首');
INSERT INTO `poem` VALUES (512, '绝句·古木阴中系短篷', '宋代', '志南', '古木阴中系短篷，杖藜扶我过桥东。 沾衣欲湿杏花雨，吹面不寒杨柳风。', '古诗三百首');
INSERT INTO `poem` VALUES (513, '过零丁洋', '宋代', '文天祥', '辛苦遭逢起一经，干戈寥落四周星。 山河破碎风飘絮，身世浮沉雨打萍。 惶恐滩头说惶恐，零丁洋里叹零丁。 人生自古谁无死？留取丹心照汗青。', '古诗三百首');
INSERT INTO `poem` VALUES (514, '二砺', '宋代', '郑思肖', '愁里高歌梁父吟，犹如金玉戛商音。 十年勾践亡吴计，七日包胥哭楚心。 秋送新鸿哀破国，昼行饥虎齧空林。 胸中有誓深于海，肯使神州竟陆沉？', '古诗三百首');
INSERT INTO `poem` VALUES (515, '寒菊 / 画菊', '宋代', '郑思肖', '花开不并百花丛，独立疏篱趣未穷。 宁可枝头抱香死，何曾吹落北风中。', '古诗三百首');
INSERT INTO `poem` VALUES (516, '寒夜', '宋代', '杜耒', '寒夜客来茶当酒，竹炉汤沸火初红。 寻常一样窗前月，才有梅花便不同。', '古诗三百首');
INSERT INTO `poem` VALUES (517, '雪梅·其一', '宋代', '卢梅坡', '梅雪争春未肯降，骚人搁笔费评章。 梅须逊雪三分白，雪却输梅一段香。', '古诗三百首');
INSERT INTO `poem` VALUES (518, '武夷山中', '宋代', '谢枋得', '十年无梦得还家，独立青峰野水涯。 天地寂寥山雨歇，几生修得到梅花？', '古诗三百首');
INSERT INTO `poem` VALUES (519, '月儿弯弯照九州', '宋代', '佚名', '月儿弯弯照九州，几家欢乐几家愁。 几家夫妇同罗帐，几个飘零在外头？', '古诗三百首');
INSERT INTO `poem` VALUES (520, '绝句', '元代', '王庭筠', '竹影和诗瘦，梅花入梦香。 可怜今夜月，不肯下西厢。', '古诗三百首');
INSERT INTO `poem` VALUES (521, '台山杂咏', '金朝', '元好问', '西北天低五顶高，茫茫松海露灵鳌。 太行直上犹千里，井底残山枉呼号。 万壑千岩位置雄，偶从天巧见神功。 湍溪已作风雷恶，更在云山气象中。 山云吞吐翠微中，淡绿深青一万重。 此景只应天上有，岂知身在妙高峰？', '古诗三百首');
INSERT INTO `poem` VALUES (522, '壬辰十二月车驾东狩后即事', '金朝', '元好问', '惨澹龙蛇日斗争，干戈直欲尽生灵。 高原出水山河改，战地风来草木腥。 精卫有冤填瀚海，包胥无泪哭秦庭。 并州豪杰知谁在，莫拟分军下井陉。', '古诗三百首');
INSERT INTO `poem` VALUES (523, '同儿辈赋未开海棠', '金朝', '元好问', '枝间新绿一重重， 小蕾深藏数点红。 爱惜芳心莫轻吐， 且教桃李闹春风。', '古诗三百首');
INSERT INTO `poem` VALUES (524, '博浪沙', '元代', '陈孚', '一击车中胆气豪，祖龙社稷已惊摇； 如何十二金人外，犹有人间铁未销？', '古诗三百首');
INSERT INTO `poem` VALUES (525, '山家', '元代', '刘因', '马蹄踏水乱明霞，醉袖迎风受落花。 怪见溪童出门望，雀声先我到山家。', '古诗三百首');
INSERT INTO `poem` VALUES (526, '东城', '元代', '赵孟頫', '野店桃花红粉姿，陌头杨柳绿烟丝。 不因送客东城去，过却春光总不知。', '古诗三百首');
INSERT INTO `poem` VALUES (527, '临平泊舟', '元代', '黄庚', '客舟系缆柳阴旁，湖影侵篷夜气凉。 万顷波光摇月碎，一天风露藕花香。', '古诗三百首');
INSERT INTO `poem` VALUES (528, '到京师', '元代', '杨载', '城雪初消荠菜生，角门深巷少人行。 柳梢听得黄鹂语，此是春来第一声。', '古诗三百首');
INSERT INTO `poem` VALUES (529, '院中独坐', '宋代', '虞集', '何处它年寄此生，山中江上总关情。 无端绕屋长松树，尽把风声作雨声。', '古诗三百首');
INSERT INTO `poem` VALUES (530, '画鸭', '元代', '揭傒斯', '春草细还生，春雏养渐成。 茸茸毛色起，应解自呼名。', '古诗三百首');
INSERT INTO `poem` VALUES (531, '墨梅', '元代', '王冕', '吾家洗砚池头树，个个花开淡墨痕。 不要人夸好颜色，只流清气满乾坤。(版本一) 我家洗砚池边树，朵朵花开淡墨痕。 不要人夸颜色好，只留清气满乾坤。(版本二)', '古诗三百首');
INSERT INTO `poem` VALUES (532, '湖上', '宋代', '徐元杰', '花开红树乱莺啼，草长平湖白鹭飞。 风日晴和人意好，夕阳箫鼓几船归。', '古诗三百首');
INSERT INTO `poem` VALUES (533, '上京即事', '元代', '萨都剌', '牛羊散漫落日下，野草生香乳酪甜。 卷地朔风沙似雪，家家行帐下毡帘。', '古诗三百首');
INSERT INTO `poem` VALUES (534, '京师得家书', '明代', '袁凯', '江水三千里，家书十五行。 行行无别语，只道早还乡。', '古诗三百首');
INSERT INTO `poem` VALUES (535, '天平山中', '明代', '杨基', '细雨茸茸湿楝花，南风树树熟枇杷； 徐行不记山深浅，一路莺啼送到家。', '古诗三百首');
INSERT INTO `poem` VALUES (536, '春暮西园', '明代', '高启', '绿池芳草满晴波，春色都从雨里过。 知是人家花落尽，菜畦今日蝶来多。', '古诗三百首');
INSERT INTO `poem` VALUES (537, '寻胡隐君', '明代', '高启', '渡水复渡水，看花还看花。 春风江上路，不觉到君家。', '古诗三百首');
INSERT INTO `poem` VALUES (538, '春雁', '明代', '王恭', '春风一夜到衡阳，楚水燕山万里长。 莫道春来便归去，江南虽好是他乡。', '古诗三百首');
INSERT INTO `poem` VALUES (539, '发淮安', '明代', '杨士奇', '岸蓼疏红水荇青，茨菰花白小如萍。 双鬟短袖惭人见，背立船头自采菱。', '古诗三百首');
INSERT INTO `poem` VALUES (540, '幼女词', '明代', '毛铉', '下床着新衣，初学小姑拜。 低头羞见人，双手结裙带。', '古诗三百首');
INSERT INTO `poem` VALUES (541, '乡人至夜话', '明代', '李昌祺', '形容不识识乡音，挑尽寒灯到夜深。 故旧凭君休更说，老怀容易便沾襟。', '古诗三百首');
INSERT INTO `poem` VALUES (542, '石灰吟', '明代', '于谦', '千锤万凿出深山，烈火焚烧若等闲。 粉骨碎身全不怕，要留清白在人间。 (一作：千锤万击出深山 / 千鎚万击出深山) (一作：粉骨碎身全不惜 / 粉身碎骨浑不怕 / 粉身碎骨全不怕)', '古诗三百首');
INSERT INTO `poem` VALUES (543, '题画', '明代', '沈周', '碧水丹山映杖藜，夕阳犹在小桥西。 微吟不道惊溪鸟，飞入乱云深处啼。', '古诗三百首');
INSERT INTO `poem` VALUES (544, '柯敬仲墨竹', '明代', '李东阳', '莫将画竹论难易，刚道繁难简更难。 君看萧萧只数叶，满堂风雨不胜寒。', '古诗三百首');
INSERT INTO `poem` VALUES (545, '言志', '明代', '唐寅', '不炼金丹不坐禅，不为商贾不耕田。 闲来写就青山卖，不使人间造孽钱。', '古诗三百首');
INSERT INTO `poem` VALUES (546, '船板床', '明代', '李梦阳', '船板胡在兹，而我寝其上。 情知非江湖，梦寐亦风浪。', '古诗三百首');
INSERT INTO `poem` VALUES (547, '在武昌作', '明代', '徐祯卿', '洞庭叶未下，潇湘秋欲生。 高斋今夜雨，独卧武昌城。 重以桑梓念，凄其江汉情。 不知天外雁，何事乐长征？', '古诗三百首');
INSERT INTO `poem` VALUES (548, '重赠吴国宾', '明代', '边贡', '汉江明月照归人，万里秋风一叶身。 休把客衣轻浣濯，此中犹有帝京尘。', '古诗三百首');
INSERT INTO `poem` VALUES (549, '竹枝词', '明代', '何景明', '十二峰头秋草荒，冷烟寒月过瞿塘。 青枫江上孤舟客，不听猿啼亦断肠。', '古诗三百首');
INSERT INTO `poem` VALUES (550, '出郊', '明代', '杨慎', '高田如楼梯，平田如棋局。 白鹭忽飞来，点破秧针绿。', '古诗三百首');
INSERT INTO `poem` VALUES (551, '塞下曲', '明代', '谢榛', '暝色满西山，将军猎骑还。 隔河见烽火，骄虏夜临关。', '古诗三百首');
INSERT INTO `poem` VALUES (552, '塞上曲送元美', '明代', '李攀龙', '白羽如霜出塞寒，胡烽不断接长安。 城头一片西山月，多少征人马上看。', '古诗三百首');
INSERT INTO `poem` VALUES (553, '就义诗', '明代', '杨继盛', '浩气还太虚，丹心照千古。 生平未报国，留作忠魂补。', '古诗三百首');
INSERT INTO `poem` VALUES (554, '题《墨葡萄图》 / 题葡萄图', '明代', '徐渭', '半生落魄已成翁,独立书斋啸晚风。 笔底明珠无处卖,闲抛闲掷野藤中。', '古诗三百首');
INSERT INTO `poem` VALUES (555, '马上作', '明代', '戚继光', '南北驱驰报主情，江花边草笑平生。(边草 一作：边月) 一年三百六十日，多是横戈马上行。 (多是 一作：都是)', '古诗三百首');
INSERT INTO `poem` VALUES (556, '今日歌', '明代', '文嘉', '今日复今日，今日何其少！ 今日又不为，此事何时了？ 人生百年几今日，今日不为真可惜！ 若言姑待明朝至，明朝又有明朝事。 为君聊赋今日诗，努力请从今日始。', '古诗三百首');
INSERT INTO `poem` VALUES (557, '枕石', '明代', '高攀龙', '心同流水净，身与白云轻。 寂寂深山暮，微闻钟磬声。', '古诗三百首');
INSERT INTO `poem` VALUES (558, '感事', '明代', '袁宏道', '湘山晴色远微微，尽日江头独醉归。 不见两关传露布，尚闻三殿未垂衣。 边筹自古无中下，朝论于今有是非。 日暮平沙秋草乱，一双白鸟避人飞。', '古诗三百首');
INSERT INTO `poem` VALUES (559, '江宿', '明代', '汤显祖', '寂历秋江渔火稀，起看残月映林微。 波光水鸟惊犹宿，露冷流萤湿不飞。', '古诗三百首');
INSERT INTO `poem` VALUES (560, '渡易水', '明代', '陈子龙', '并刀昨夜匣中鸣，燕赵悲歌最不平； 易水潺湲云草碧，可怜无处送荆卿！', '古诗三百首');
INSERT INTO `poem` VALUES (561, '即事三首', '明代', '夏完淳', '复楚情何极，亡秦气未平。 雄风清角劲，落日大旗明。 缟素酬家国，戈船决死生！ 胡笳千古恨，一片月临城。 战苦难酬国，仇深敢忆家？ 一身存汉腊，满目渺胡沙。 落月翻旗影，清霜冷剑花。 六军浑散尽，半夜起悲笳。 一旅同仇谊，三秋故主怀。 将星沉左辅，卿月隐中台。 东阁尘宾幕，西征愧赋才。 月明笳鼓切，今夜为谁哀。', '古诗三百首');
INSERT INTO `poem` VALUES (562, '别云间', '明代', '夏完淳', '三年羁旅客，今日又南冠。 无限山河泪，谁言天地宽！(山河 一作：河山) 已知泉路近，欲别故乡难。 毅魄归来日，灵旗空际看。', '古诗三百首');
INSERT INTO `poem` VALUES (563, '闯王', '明代', '佚名', '朝求升，暮求合，近来贫汉难存活。 早早开门拜闯王，管教大小都欢悦。 杀牛羊，备酒浆，开了城门迎闯王，闯王来了不纳粮。 吃他娘，着她娘，吃着不够有闯王。 不当差，不纳粮，大家快活过一场。', '古诗三百首');
INSERT INTO `poem` VALUES (564, '阻雪', '清代', '吴伟业', '关山虽胜路难堪，才上征鞍又解骖。 十丈黄尘千尺雪，可知俱不似江南。', '古诗三百首');
INSERT INTO `poem` VALUES (565, '闻鹧鸪', '清代', '尤侗', '鹧鸪声里夕阳西，陌上征人首尽低。 遍地关山行不得，为谁辛苦尽情啼？', '古诗三百首');
INSERT INTO `poem` VALUES (566, '出居庸关', '清代', '朱彝尊', '居庸关上子规啼，饮马流泉落日低。 雨雪自飞千嶂外，榆林只隔数峰西。', '古诗三百首');
INSERT INTO `poem` VALUES (567, '真州绝句', '清代', '王士祯', '江干多是钓人居，柳陌菱塘一带疏。 好是日斜风定后，半江红树卖鲈鱼。', '古诗三百首');
INSERT INTO `poem` VALUES (568, '过湖北山家', '清代', '施闰章', '路回临石岸，树老出墙根。 野水合诸涧，桃花成一村。 呼鸡过篱栅，行酒尽儿孙。 老矣吾将隐，前峰恰对门。', '古诗三百首');
INSERT INTO `poem` VALUES (569, '自湘东驿遵陆至芦溪', '清代', '查慎行', '黄花古渡接芦溪，行过萍乡路渐低。 吠犬鸣鸡村远近，乳鹅新鸭岸东西。 丝缫细雨沾衣润，刀剪良苗出水齐。 犹与湖南风土近，春深无处不耕犁。', '古诗三百首');
INSERT INTO `poem` VALUES (570, '舟夜书所见', '清代', '查慎行', '月黑见渔灯，孤光一点萤。 微微风簇浪，散作满河星。', '古诗三百首');
INSERT INTO `poem` VALUES (571, '过许州', '清代', '沈德潜', '到处陂塘决决流，垂杨百里罨平畴。 行人便觉须眉绿，一路蝉声过许州。', '古诗三百首');
INSERT INTO `poem` VALUES (572, '秣陵怀古', '清代', '纳兰性德', '山色江声共寂寥，十三陵树晚萧萧。 中原事业如江左，芳草何须怨六朝。', '古诗三百首');
INSERT INTO `poem` VALUES (573, '萤火', '清代', '赵执信', '和雨还穿户，经风忽过墙。 虽缘草成质，不借月为光。 解识幽人意，请今聊处囊。 君看落空阔，何异大星芒。', '古诗三百首');
INSERT INTO `poem` VALUES (574, '灵隐寺月夜', '清代', '厉鹗', '夜寒香界白，涧曲寺门通。 月在众峰顶，泉流乱叶中。 一灯群动息，孤磬四天空。 归路畏逢虎，况闻岩下风。', '古诗三百首');
INSERT INTO `poem` VALUES (575, '竹石', '清代', '郑燮', '咬定青山不放松，立根原在破岩中。 千磨万击还坚劲，任尔东西南北风。(坚劲 一作：韧)', '古诗三百首');
INSERT INTO `poem` VALUES (576, '山中雪后', '清代', '郑燮', '晨起开门雪满山，雪晴云淡日光寒。 檐流未滴梅花冻，一种清孤不等闲。', '古诗三百首');
INSERT INTO `poem` VALUES (577, '渔家·卖得鲜鱼二百钱', '清代', '郑燮', '卖得鲜鱼二百钱，籴粮炊饭放归船。 拔来湿苇烧难着，晒在垂杨古岸边。', '古诗三百首');
INSERT INTO `poem` VALUES (578, '偶然作', '清代', '屈复', '百金买骏马，千金买美人； 万金买高爵，何处买青春？', '古诗三百首');
INSERT INTO `poem` VALUES (579, '岁暮到家 / 岁末到家', '清代', '蒋士铨', '爱子心无尽，归家喜及辰。 寒衣针线密，家信墨痕新。 见面怜清瘦，呼儿问苦辛。 低徊愧人子，不敢叹风尘。', '古诗三百首');
INSERT INTO `poem` VALUES (580, '马嵬', '清代', '袁枚', '莫唱当年长恨歌，人间亦自有银河。 石壕村里夫妻别，泪比长生殿上多。', '古诗三百首');
INSERT INTO `poem` VALUES (581, '夜过借园见主人坐月下吹笛', '清代', '袁枚', '秋夜访秋士，先闻水上音。 半天凉月色，一笛酒人心。 响遏碧云近，香传红藕深。 相逢清露下，流影湿衣襟。', '古诗三百首');
INSERT INTO `poem` VALUES (582, '山行杂咏', '清代', '袁枚', '十里崎岖半里平，一峰才送一峰迎。 青山似茧将人裹，不信前头有路行。', '古诗三百首');
INSERT INTO `poem` VALUES (583, '所见', '清代', '袁枚', '牧童骑黄牛，歌声振林樾。 意欲捕鸣蝉，忽然闭口立。', '古诗三百首');
INSERT INTO `poem` VALUES (584, '遣兴', '清代', '袁枚', '爱好由来下笔难，一诗千改始心安。 阿婆还似初笄女，头未梳成不许看。', '古诗三百首');
INSERT INTO `poem` VALUES (585, '富春至严陵山水甚佳', '清代', '纪昀', '浓似春云淡似烟，参差绿到大江边。 斜阳流水推篷坐，翠色随人欲上船。', '古诗三百首');
INSERT INTO `poem` VALUES (586, '论诗五首·其二', '清代', '赵翼', '李杜诗篇万口传，至今已觉不新鲜。 江山代有才人出，各领风骚数百年。', '古诗三百首');
INSERT INTO `poem` VALUES (587, '山行·布谷飞飞劝早耕', '清代', '姚鼐', '布谷飞飞劝早耕，舂锄扑扑趁春睛。 千层石树遥行路，一带山田放水声。', '古诗三百首');
INSERT INTO `poem` VALUES (588, '感旧四首', '清代', '黄景仁', '大道青楼望不遮，年时系马醉流霞。 风前带是同心结，杯底人如解语花。 下杜城边南北路，上阑门外去来车。 匆匆觉得扬州梦，检点闲愁在鬓华。 唤起窗前尚宿酲，啼鹃催去又声声。 丹青旧誓相如札，禅榻经时杜牧情。 别后相思空一水，重来回首已三生。 云阶月地依然在，细逐空香百遍行。 遮莫临行念我频，竹枝留涴泪痕新。 多缘刺史无坚约，岂视萧郎作路人。 望里彩云疑冉冉，愁边春水故粼粼。 珊瑚百尺珠千斛，难换罗敷未嫁身。 从此音尘各悄然，春山如黛草如烟。 泪添吴苑三更雨，恨惹邮亭一夜眠。 讵有青马缄别句，聊将锦瑟记流年。 他时脱便微之过，百转千回只自怜。', '古诗三百首');
INSERT INTO `poem` VALUES (589, '别老母', '清代', '黄景仁', '搴帷拜母河梁去，白发愁看泪眼枯。 惨惨柴门风雪夜，此时有子不如无。', '古诗三百首');
INSERT INTO `poem` VALUES (590, '咏史', '清代', '龚自珍', '金粉东南十五州，万重恩怨属名流。 牢盆狎客操全算，团扇才人踞上游。 避席畏闻文字狱，著书都为稻粱谋。 田横五百人安在，难道归来尽列侯？', '古诗三百首');
INSERT INTO `poem` VALUES (591, '己亥杂诗·其五', '清代', '龚自珍', '浩荡离愁白日斜，吟鞭东指即天涯。 落红不是无情物，化作春泥更护花。', '古诗三百首');
INSERT INTO `poem` VALUES (592, '己亥杂诗·其二百二十', '清代', '龚自珍', '九州生气恃风雷，万马齐喑究可哀。 我劝天公重抖擞，不拘一格降人才。', '古诗三百首');
INSERT INTO `poem` VALUES (593, '村居', '清代', '高鼎', '草长莺飞二月天，拂堤杨柳醉春烟。 儿童散学归来早，忙趁东风放纸鸢。', '古诗三百首');
INSERT INTO `poem` VALUES (594, '画', '唐代', '王维', '远看山有色，近听水无声。 春去花还在，人来鸟不惊。', '古诗三百首');
INSERT INTO `poem` VALUES (595, '春愁', '清代', '丘逢甲', '春愁难遣强看山，往事惊心泪欲潸。 四百万人同一哭，去年今日割台湾。', '古诗三百首');
INSERT INTO `poem` VALUES (596, '狱中题壁', '近现代', '谭嗣同', '望门投止思张俭，忍死须臾待杜根。 我自横刀向天笑，去留肝胆两昆仑。(版本一) 望门投趾怜张俭，直谏陈书愧杜根。 手掷欧刀仰天笑，留将公罪后人论。(版本二)', '古诗三百首');
INSERT INTO `poem` VALUES (597, '读陆放翁集', '近现代', '梁启超', '诗界千年靡靡风，兵魂销尽国魂空。 集中什九从军乐，亘古男儿一放翁。', '古诗三百首');
INSERT INTO `poem` VALUES (598, '对酒', '近现代', '秋瑾', '不惜千金买宝刀，貂裘换酒也堪豪。 一腔热血勤珍重，洒去犹能化碧涛。', '古诗三百首');
INSERT INTO `poem` VALUES (599, '狱中赠邹容', '近现代', '章炳麟', '邹容吾小弟，被发下瀛州。 快剪刀除辫，干牛肉作餱。 英雄一入狱，天地亦悲秋。 临命须掺手，乾坤只两头！', '古诗三百首');
INSERT INTO `poem` VALUES (600, '菩萨蛮·平林漠漠烟如织', '唐代', '李白', '平林漠漠烟如织，寒山一带伤心碧。暝色入高楼，有人楼上愁。 玉阶空伫立，宿鸟归飞急。何处是归程？长亭连短亭。(连短亭 一作：更短亭)', '宋词三百首');
INSERT INTO `poem` VALUES (601, '忆秦娥·箫声咽', '唐代', '李白', '箫声咽，秦娥梦断秦楼月。秦楼月，年年柳色，灞陵伤别。 乐游原上清秋节，咸阳古道音尘绝。音尘绝，西风残照，汉家陵阙。', '宋词三百首');
INSERT INTO `poem` VALUES (602, '三五七言 / 秋风词', '唐代', '李白', '秋风清，秋月明， 落叶聚还散，寒鸦栖复惊。 相思相见知何日？此时此夜难为情！ ', '宋词三百首');
INSERT INTO `poem` VALUES (603, '渔歌子·西塞山前白鹭飞', '唐代', '张志和', '西塞山前白鹭飞，桃花流水鳜鱼肥。 青箬笠，绿蓑衣，斜风细雨不须归。', '宋词三百首');
INSERT INTO `poem` VALUES (604, '调笑令·边草', '唐代', '戴叔伦', '边草，边草，边草尽来兵老。山南山北雪晴， 千里万里月明。明月，明月，胡笳一声愁绝。', '宋词三百首');
INSERT INTO `poem` VALUES (605, '宫中调笑·团扇', '唐代', '王建', '团扇，团扇，美人病来遮面。 玉颜憔悴三年，谁复商量管弦。 弦管，弦管，春草昭阳路断。', '宋词三百首');
INSERT INTO `poem` VALUES (606, '竹枝词·山桃红花满上头', '唐代', '刘禹锡', '山桃红花满上头，蜀江春水拍山流。 花红易衰似郎意，水流无限似侬愁。', '宋词三百首');
INSERT INTO `poem` VALUES (607, '潇湘神·斑竹枝', '唐代', '刘禹锡', '斑竹枝，斑竹枝，泪痕点点寄相思。楚客欲听瑶瑟怨，潇湘深夜月明时。', '宋词三百首');
INSERT INTO `poem` VALUES (608, '忆江南·江南好', '唐代', '白居易', '江南好，风景旧曾谙。日出江花红胜火，春来江水绿如蓝。能不忆江南？', '宋词三百首');
INSERT INTO `poem` VALUES (609, '忆江南词三首', '唐代', '白居易', '江南好，风景旧曾谙。日出江花红胜火，春来江水绿如蓝。能不忆江南？ 江南忆，最忆是杭州。山寺月中寻桂子，郡亭枕上看潮头。何日更重游！ 江南忆，其次忆吴宫。吴酒一杯春竹叶，吴娃双舞醉芙蓉。早晚复相逢！', '宋词三百首');
INSERT INTO `poem` VALUES (610, '长相思·汴水流', '唐代', '白居易', '汴水流，泗水流，流到瓜州古渡头。吴山点点愁。 思悠悠，恨悠悠，恨到归时方始休。月明人倚楼。', '宋词三百首');
INSERT INTO `poem` VALUES (611, '花非花', '唐代', '白居易', '花非花，雾非雾。 夜半来，天明去。 来如春梦几多时？ 去似朝云无觅处。', '宋词三百首');
INSERT INTO `poem` VALUES (612, '浪淘沙·借问江潮与海水', '唐代', '白居易', '借问江潮与海水，何似君情与妾心？ 相恨不如潮有信，相思始觉海非深。', '宋词三百首');
INSERT INTO `poem` VALUES (613, '采莲子·菡萏香莲十顷陂', '唐代', '皇甫松', '菡萏香连十顷陂，小姑贪戏采莲迟。 晚来弄水船头湿，更脱红裙裹鸭儿。', '宋词三百首');
INSERT INTO `poem` VALUES (614, '梦江南·兰烬落', '唐代', '皇甫松', '兰烬落，屏上暗红蕉。闲梦江南梅熟日，夜船吹笛雨萧萧。人语驿边桥。', '宋词三百首');
INSERT INTO `poem` VALUES (615, '望江南·梳洗罢', '唐代', '温庭筠', '梳洗罢，独倚望江楼。过尽千帆皆不是，斜晖脉脉水悠悠。肠断白蘋洲。', '宋词三百首');
INSERT INTO `poem` VALUES (616, '菩萨蛮·小山重叠金明灭', '唐代', '温庭筠', '小山重叠金明灭，鬓云欲度香腮雪。懒起画蛾眉，弄妆梳洗迟。 照花前后镜，花面交相映。新帖绣罗襦，双双金鹧鸪。', '宋词三百首');
INSERT INTO `poem` VALUES (617, '更漏子·柳丝长', '唐代', '温庭筠', '柳丝长，春雨细，花外漏声迢递。惊塞雁，起城乌，画屏金鹧鸪。 香雾薄，透帘幕，惆怅谢家池阁。红烛背，绣帘垂，梦长君不知。', '宋词三百首');
INSERT INTO `poem` VALUES (618, '菩萨蛮·红楼别夜堪惆怅', '唐代', '韦庄', '红楼别夜堪惆怅，香灯半卷流苏帐。 残月出门时，美人和泪辞。 琵琶金翠羽，弦上黄莺语。 劝我早还家，绿窗人似花。', '宋词三百首');
INSERT INTO `poem` VALUES (619, '菩萨蛮·人人尽说江南好', '唐代', '韦庄', '人人尽说江南好，游人只合江南老。春水碧于天，画船听雨眠。 垆边人似月，皓腕凝霜雪。未老莫还乡，还乡须断肠。', '宋词三百首');
INSERT INTO `poem` VALUES (620, '菩萨蛮·劝君今夜须沉醉', '唐代', '韦庄', '劝君今夜须沉醉，尊前莫话明朝事。珍重主人心，酒深情亦深。 须愁春漏短，莫诉金杯满。遇酒且呵呵，人生能几何。', '宋词三百首');
INSERT INTO `poem` VALUES (621, '女冠子·四月十七', '唐代', '韦庄', '四月十七，正是去年今日，别君时。忍泪佯低面，含羞半敛眉。 不知魂已断，空有梦相随。除却天边月，没人知。', '宋词三百首');
INSERT INTO `poem` VALUES (622, '女冠子·昨夜夜半', '唐代', '韦庄', '昨夜夜半，枕上分明梦见。语多时。依旧桃花面，频低柳叶眉。 半羞还半喜，欲去又依依。觉来知是梦，不胜悲。', '宋词三百首');
INSERT INTO `poem` VALUES (623, '思帝乡·春日游', '唐代', '韦庄', '春日游，杏花吹满头。陌上谁家年少足风流？ 妾拟将身嫁与一生休。纵被无情弃，不能羞。', '宋词三百首');
INSERT INTO `poem` VALUES (624, '浣溪沙·红蓼渡头秋正雨', '唐代', '薛昭蕴', '红蓼渡头秋正雨，印沙鸥迹自成行，整鬟飘袖野风香。 不语含嚬深浦里，几回愁煞棹船郎，燕归帆尽水茫茫。', '宋词三百首');
INSERT INTO `poem` VALUES (625, '忆江南·衔泥燕', '唐代', '牛峤', '衔泥燕，飞到画堂前。占得杏梁安稳处，体轻唯有主人怜，堪羡好因缘。', '宋词三百首');
INSERT INTO `poem` VALUES (626, '生查子·新月曲如眉', '五代', '牛希济', '新月曲如眉，未有团圞意。红豆不堪看，满眼相思泪。 终日劈桃穰，人在心儿里。两朵隔墙花，早晚成连理。', '宋词三百首');
INSERT INTO `poem` VALUES (627, '巫山一段云·古庙依青嶂', '五代', '李珣', '古庙依青嶂，行宫枕碧流。水声山色锁妆楼。往事思悠悠。 云雨朝还暮，烟花春复秋。啼猿何必近孤舟。行客自多愁。', '宋词三百首');
INSERT INTO `poem` VALUES (628, '南乡子·乘彩舫', '五代', '李珣', '乘彩舫，过莲塘，棹歌惊起睡鸳鸯。游女带香偎伴笑，争窈窕，竞折团荷遮晚照。', '宋词三百首');
INSERT INTO `poem` VALUES (629, '诉衷情·永夜抛人何处去', '五代', '顾夐', '永夜抛人何处去？绝来音。香阁掩，眉敛，月将沉。 争忍不相寻？怨孤衾。换我心，为你心，始知相忆深。', '宋词三百首');
INSERT INTO `poem` VALUES (630, '浣溪沙·蓼岸风多橘柚香', '五代', '孙光宪', '蓼岸风多橘柚香。江边一望楚天长。片帆烟际闪孤光。 目送征鸿飞杳杳，思随流水去茫茫。兰红波碧忆潇湘。', '宋词三百首');
INSERT INTO `poem` VALUES (631, '谒金门·风乍起', '五代', '冯延巳', '风乍起，吹皱一池春水。闲引鸳鸯香径里，手挼红杏蕊。 斗鸭阑干独倚，碧玉搔头斜坠。终日望君君不至，举头闻鹊喜。', '宋词三百首');
INSERT INTO `poem` VALUES (632, '鹊踏枝·谁道闲情抛掷久', '五代', '冯延巳', '谁道闲情抛掷久？每到春来，惆怅还依旧。日日花前常病酒，敢辞镜里朱颜瘦。(敢辞 一作：不辞) 河畔青芜堤上柳，为问新愁，何事年年有？独立小桥风满袖，平林新月人归后。', '宋词三百首');
INSERT INTO `poem` VALUES (633, '鹊踏枝·几日行云何处去', '五代', '冯延巳', '几日行云何处去？忘却归来，不道春将暮。百草千花寒食路，香车系在谁家树？ 泪眼倚楼频独语。双燕来时，陌上相逢否？撩乱春愁如柳絮，依依梦里无寻处。(依依 一作：悠悠)', '宋词三百首');
INSERT INTO `poem` VALUES (634, '清平乐·雨晴烟晚', '五代', '冯延巳', '雨晴烟晚。绿水新池满。双燕飞来垂柳院，小阁画帘高卷。 黄昏独倚朱阑。西南新月眉弯。砌下落花风起，罗衣特地春寒。', '宋词三百首');
INSERT INTO `poem` VALUES (635, '长命女·春日宴', '五代', '冯延巳', '春日宴，绿酒一杯歌一遍。再拜陈三愿：一愿郎君千岁，二愿妾身常健，三愿如同梁上燕，岁岁长相见。', '宋词三百首');
INSERT INTO `poem` VALUES (636, '摊破浣溪沙·菡萏香销翠叶残', '五代', '李璟', '菡萏香销翠叶残，西风愁起绿波间。还与韶光共憔悴，不堪看。 细雨梦回鸡塞远，小楼吹彻玉笙寒。多少泪珠何限恨，倚栏干。(栏 通：阑) (何限恨 一作：无限恨)', '宋词三百首');
INSERT INTO `poem` VALUES (637, '摊破浣溪沙·手卷真珠上玉钩', '五代', '李璟', '手卷真珠上玉钩，依前春恨锁重楼。风里落花谁是主？思悠悠。 青鸟不传云外信，丁香空结雨中愁。回首绿波三楚暮，接天流。', '宋词三百首');
INSERT INTO `poem` VALUES (638, '乌夜啼·昨夜风兼雨', '五代', '李煜', '昨夜风兼雨，帘帏飒飒秋声。烛残漏断频欹枕，起坐不能平。　 世事漫随流水，算来一梦浮生。醉乡路稳宜频到，此外不堪行。(一梦 一作：梦里)', '宋词三百首');
INSERT INTO `poem` VALUES (639, '虞美人·春花秋月何时了', '五代', '李煜', '春花秋月何时了？往事知多少。小楼昨夜又东风，故国不堪回首月明中。 雕栏玉砌应犹在，只是朱颜改。问君能有几多愁？恰似一江春水向东流。(雕栏 通：阑)', '宋词三百首');
INSERT INTO `poem` VALUES (640, '虞美人·风回小院庭芜绿', '五代', '李煜', '风回小院庭芜绿，柳眼春相续。凭阑半日独无言，依旧竹声新月似当年。 笙歌未散尊罍在，池面冰初解。烛明香暗画堂深，满鬓青霜残雪思难任。', '宋词三百首');
INSERT INTO `poem` VALUES (641, '相见欢·无言独上西楼', '五代', '李煜', '无言独上西楼，月如钩。寂寞梧桐深院锁清秋。 剪不断，理还乱，是离愁。别是一般滋味在心头。(一般 一作：一番)', '宋词三百首');
INSERT INTO `poem` VALUES (642, '相见欢·林花谢了春红', '五代', '李煜', '林花谢了春红，太匆匆。无奈朝来寒雨晚来风。 胭脂泪，相留醉，几时重。自是人生长恨水长东。(相留 一作：留人)', '宋词三百首');
INSERT INTO `poem` VALUES (643, '长相思·一重山', '五代', '李煜', '一重山，两重山。山远天高烟水寒，相思枫叶丹。 菊花开，菊花残。塞雁高飞人未还，一帘风月闲。', '宋词三百首');
INSERT INTO `poem` VALUES (644, '浪淘沙令·帘外雨潺潺', '五代', '李煜', '帘外雨潺潺，春意阑珊。罗衾不耐五更寒。梦里不知身是客，一晌贪欢。 独自莫凭栏，无限江山，别时容易见时难。流水落花春去也，天上人间。', '宋词三百首');
INSERT INTO `poem` VALUES (645, '清平乐·别来春半', '五代', '李煜', '别来春半，触目柔肠断。砌下落梅如雪乱，拂了一身还满。(柔肠断 一作：愁肠断) 雁来音信无凭，路遥归梦难成。离恨恰如春草，更行更远还生。', '宋词三百首');
INSERT INTO `poem` VALUES (646, '捣练子令·深院静', '五代', '李煜', '深院静，小庭空，断续寒砧断续风。 无奈夜长人不寐，数声和月到帘栊。', '宋词三百首');
INSERT INTO `poem` VALUES (647, '破阵子·四十年来家国', '五代', '李煜', '四十年来家国，三千里地山河。凤阁龙楼连霄汉，玉树琼枝作烟萝，几曾识干戈？ 一旦归为臣虏，沈腰潘鬓消磨。最是仓皇辞庙日，教坊犹奏别离歌，垂泪对宫娥。', '宋词三百首');
INSERT INTO `poem` VALUES (648, '临江仙·饮散离亭西去', '五代', '徐昌图', '饮散离亭西去，浮生长恨飘蓬。回头烟柳渐重重。淡云孤雁远，寒日暮天红。 今夜画船何处？潮平淮月朦胧。酒醒人静奈愁浓。残灯孤枕梦，轻浪五更风。', '宋词三百首');
INSERT INTO `poem` VALUES (649, '菩萨蛮·枕前发尽千般愿', '五代', '佚名', '枕前发尽千般愿，要休且待青山烂。水面上秤锤浮，直待黄河彻底枯。 白日参辰现，北斗回南面。休即未能休，且待三更见日头。', '宋词三百首');
INSERT INTO `poem` VALUES (650, '鹊踏枝·叵耐灵鹊多谩语', '五代', '佚名', '叵耐灵鹊多谩语，送喜何曾有凭据？几度飞来活捉取，锁上金笼休共语。 比拟好心来送喜，谁知锁我在金笼里。欲他征夫早归来，腾身却放我向青云里。', '宋词三百首');
INSERT INTO `poem` VALUES (651, '浣溪沙·五两竿头风欲平', '五代', '佚名', '五两竿头风欲平，长风举棹觉船轻。柔橹不施停却棹，是船行。 满眼风波多闪灼，看山恰似走来迎。仔细看山山不动，是船行。', '宋词三百首');
INSERT INTO `poem` VALUES (652, '望江南·天上月', '五代', '佚名', '天上月，遥望似一团银。夜久更阑风渐紧。与奴吹散月边云。照见负心人。', '宋词三百首');
INSERT INTO `poem` VALUES (653, '点绛唇·感兴', '宋代', '王禹偁', '雨恨云愁，江南依旧称佳丽。水村渔市。一缕孤烟细。 天际征鸿，遥认行如缀。平生事。此时凝睇。谁会凭栏意。(栏 通：阑)', '宋词三百首');
INSERT INTO `poem` VALUES (654, '酒泉子·长忆观潮', '宋代', '潘阆', '长忆观潮，满郭人争江上望。来疑沧海尽成空。万面鼓声中。 弄潮儿向涛头立。手把红旗旗不湿。别来几向梦中看。梦觉尚心寒。', '宋词三百首');
INSERT INTO `poem` VALUES (655, '相思令·吴山青', '宋代', '林逋', '吴山青，越山青，两岸青山相送迎，争忍有离情？ 君泪盈，妾泪盈，罗带同心结未成，江边潮已平。', '宋词三百首');
INSERT INTO `poem` VALUES (656, '踏莎行·春暮', '宋代', '寇准', '春色将阑，莺声渐老，红英落尽青梅小。画堂人静雨蒙蒙，屏山半掩余香袅。 密约沉沉，离情杳杳，菱花尘满慵将照。倚楼无语欲销魂，长空黯淡连芳草。', '宋词三百首');
INSERT INTO `poem` VALUES (657, '苏幕遮·怀旧', '宋代', '范仲淹', '碧云天，黄叶地。秋色连波，波上寒烟翠。山映斜阳天接水。芳草无情，更在斜阳外。 黯乡魂，追旅思。夜夜除非，好梦留人睡。明月楼高休独倚。酒入愁肠，化作相思泪。(留人睡 一作：留人醉)', '宋词三百首');
INSERT INTO `poem` VALUES (658, '渔家傲·秋思', '宋代', '范仲淹', '塞下秋来风景异，衡阳雁去无留意。四面边声连角起，千嶂里，长烟落日孤城闭。 浊酒一杯家万里，燕然未勒归无计。羌管悠悠霜满地，人不寐，将军白发征夫泪。', '宋词三百首');
INSERT INTO `poem` VALUES (659, '蝶恋花·伫倚危楼风细细', '宋代', '柳永', '伫倚危楼风细细，望极春愁，黯黯生天际。草色烟光残照里，无言谁会凭阑意。(阑 通 栏) 拟把疏狂图一醉，对酒当歌，强乐还无味。衣带渐宽终不悔，为伊消得人憔悴。', '宋词三百首');
INSERT INTO `poem` VALUES (660, '定风波·自春来', '宋代', '柳永', '自春来、惨绿愁红，芳心是事可可。日上花梢，莺穿柳带，犹压香衾卧。暖酥消、腻云亸，终日厌厌倦梳裹。无那。恨薄情一去，音书无个。 早知恁么，悔当初、不把雕鞍锁。向鸡窗，只与蛮笺象管，拘束教吟课。镇相随、莫抛躲，针线闲拈伴伊坐。和我，免使年少，光阴虚过。', '宋词三百首');
INSERT INTO `poem` VALUES (661, '雨霖铃·寒蝉凄切', '宋代', '柳永', '寒蝉凄切，对长亭晚，骤雨初歇。都门帐饮无绪，留恋处，兰舟催发。执手相看泪眼，竟无语凝噎。念去去，千里烟波，暮霭沉沉楚天阔。 多情自古伤离别，更那堪，冷落清秋节！今宵酒醒何处？杨柳岸，晓风残月。此去经年，应是良辰好景虚设。便纵有千种风情，更与何人说？(好景 一作：美景)', '宋词三百首');
INSERT INTO `poem` VALUES (662, '望海潮·东南形胜', '宋代', '柳永', '东南形胜，三吴都会，钱塘自古繁华，烟柳画桥，风帘翠幕，参差十万人家。云树绕堤沙，怒涛卷霜雪，天堑无涯。市列珠玑，户盈罗绮，竞豪奢。 重湖叠巘清嘉。有三秋桂子，十里荷花。羌管弄晴，菱歌泛夜，嬉嬉钓叟莲娃。千骑拥高牙。乘醉听箫鼓，吟赏烟霞。异日图将好景，归去凤池夸。', '宋词三百首');
INSERT INTO `poem` VALUES (663, '迷仙引·才过笄年', '宋代', '柳永', '才过笄年，初绾云鬟，便学歌舞。席上尊前，王孙随分相许。算等闲、酬一笑，便千金慵觑。常只恐、容易蕣华偷换，光阴虚度。 已受君恩顾，好与花为主。万里丹霄，何妨携手同归去。永弃却、烟花伴侣。免教人见妾，朝云暮雨。', '宋词三百首');
INSERT INTO `poem` VALUES (664, '八声甘州·对潇潇暮雨洒江天', '宋代', '柳永', '对潇潇暮雨洒江天，一番洗清秋。渐霜风凄紧，关河冷落，残照当楼。是处红衰翠减，苒苒物华休。唯有长江水，无语东流。 不忍登高临远，望故乡渺邈，归思难收。叹年来踪迹，何事苦淹留？想佳人妆楼颙望，误几回、天际识归舟。争知我，倚阑杆处，正恁凝愁！(阑 一作：栏)', '宋词三百首');
INSERT INTO `poem` VALUES (665, '安公子·远岸收残雨', '宋代', '柳永', '远岸收残雨。雨残稍觉江天暮。拾翠汀洲人寂静，立双双鸥鹭。望几点、渔灯隐映蒹葭浦。停画桡、两两舟人语。道去程今夜，遥指前村烟树。 游宦成羁旅。短樯吟倚闲凝伫。万水千山迷远近，想乡关何处。自别后、风亭月榭孤欢聚。刚断肠、惹得离情苦。听杜宇声声，劝人不如归去。', '宋词三百首');
INSERT INTO `poem` VALUES (666, '鹤冲天·黄金榜上', '宋代', '柳永', '黄金榜上，偶失龙头望。明代暂遗贤，如何向。未遂风云便，争不恣狂荡。何须论得丧？才子词人，自是白衣卿相。 烟花巷陌，依约丹青屏障。幸有意中人，堪寻访。且恁偎红倚翠，风流事，平生畅。青春都一饷。忍把浮名，换了浅斟低唱！', '宋词三百首');
INSERT INTO `poem` VALUES (667, '天仙子·水调数声持酒听', '宋代', '张先', '水调数声持酒听，午醉醒来愁未醒。送春春去几时回？临晚镜，伤流景，往事后期空记省。 沙上并禽池上暝，云破月来花弄影。重重帘幕密遮灯，风不定，人初静，明日落红应满径。', '宋词三百首');
INSERT INTO `poem` VALUES (668, '千秋岁·数声鶗鴂', '宋代', '张先', '数声鶗鴂，又报芳菲歇。惜春更把残红折。雨轻风色暴，梅子青时节。永丰柳，无人尽日花飞雪。 莫把幺弦拨，怨极弦能说。天不老，情难绝。心似双丝网，中有千千结。夜过也，东窗未白凝残月。(凝残月 一作：孤灯灭)', '宋词三百首');
INSERT INTO `poem` VALUES (669, '青门引·春思', '宋代', '张先', '乍暖还轻冷。风雨晚来方定。庭轩寂寞近清明，残花中酒，又是去年病。 楼头画角风吹醒。入夜重门静。那堪更被明月，隔墙送过秋千影。', '宋词三百首');
INSERT INTO `poem` VALUES (670, '醉垂鞭·双蝶绣罗裙', '宋代', '张先', '双蝶绣罗裙。东池宴。初相见。朱粉不深匀。闲花淡淡春。 细看诸处好。人人道。柳腰身。昨日乱山昏。来时衣上云。', '宋词三百首');
INSERT INTO `poem` VALUES (671, '浣溪沙·一曲新词酒一杯', '宋代', '晏殊', '一曲新词酒一杯，去年天气旧亭台。夕阳西下几时回？ 无可奈何花落去，似曾相识燕归来。小园香径独徘徊。', '宋词三百首');
INSERT INTO `poem` VALUES (672, '浣溪沙·一向年光有限身', '宋代', '晏殊', '一向年光有限身，等闲离别易销魂，酒筵歌席莫辞频。 满目山河空念远，落花风雨更伤春，不如怜取眼前人。', '宋词三百首');
INSERT INTO `poem` VALUES (673, '清平乐·红笺小字', '宋代', '晏殊', '红笺小字。说尽平生意。鸿雁在云鱼在水。惆怅此情难寄。 斜阳独倚西楼。遥山恰对帘钩。人面不知何处，绿波依旧东流。', '宋词三百首');
INSERT INTO `poem` VALUES (674, '山亭柳·赠歌者', '宋代', '晏殊', '家住西秦。赌博艺随身。花柳上、斗尖新。偶学念奴声调，有时高遏行云。蜀锦缠头无数，不负辛勤。 数年来往咸京道，残杯冷炙谩消魂。衷肠事、托何人。若有知音见采，不辞遍唱阳春。一曲当筵落泪，重掩罗巾。', '宋词三百首');
INSERT INTO `poem` VALUES (675, '蝶恋花·槛菊愁烟兰泣露', '宋代', '晏殊', '槛菊愁烟兰泣露，罗幕轻寒，燕子双飞去。明月不谙离恨苦，斜光到晓穿朱户。(双飞去 一作：双来去 离恨苦 一作：离别苦) 昨夜西风凋碧树，独上高楼，望尽天涯路。欲寄彩笺兼尺素，山长水阔知何处？', '宋词三百首');
INSERT INTO `poem` VALUES (676, '破阵子·春景', '宋代', '晏殊', '燕子来时新社，梨花落后清明。池上碧苔三四点，叶底黄鹂一两声。日长飞絮轻。 巧笑东邻女伴，采桑径里逢迎。疑怪昨宵春梦好，元是今朝斗草赢。笑从双脸生。', '宋词三百首');
INSERT INTO `poem` VALUES (677, '离亭燕·一带江山如画', '宋代', '张昪', '一带江山如画，风物向秋潇洒。水浸碧天何处断？霁色冷光相射。蓼屿荻花洲，掩映竹篱茅舍。 云际客帆高挂，烟外酒旗低亚。多少六朝兴废事，尽入渔樵闲话。怅望倚层楼，寒日无言西下。', '宋词三百首');
INSERT INTO `poem` VALUES (678, '木兰春·春景', '宋代', '宋祁', '东城渐觉风光好。縠皱波纹迎客棹。绿杨烟外晓寒轻，红杏枝头春意闹。 浮生长恨欢娱少。肯爱千金轻一笑。为君持酒劝斜阳，且向花间留晚照。', '宋词三百首');
INSERT INTO `poem` VALUES (679, '贺圣朝·留别', '宋代', '叶清臣', '满斟绿醑留君住。莫匆匆归去。三分春色二分愁，更一分风雨。 花开花谢、都来几许。且高歌休诉。不知来岁牡丹时，再相逢何处。', '宋词三百首');
INSERT INTO `poem` VALUES (680, '诉衷情·眉意', '宋代', '欧阳修', '清晨帘幕卷轻霜。呵手试梅妆。都缘自有离恨，故画作远山长。 思往事，惜流芳。易成伤。拟歌先敛，欲笑还颦，最断人肠。', '宋词三百首');
INSERT INTO `poem` VALUES (681, '踏莎行·候馆梅残', '宋代', '欧阳修', '候馆梅残，溪桥柳细。草薰风暖摇征辔。离愁渐远渐无穷，迢迢不断如春水。 寸寸柔肠，盈盈粉泪。楼高莫近危阑倚。平芜尽处是春山，行人更在春山外。', '宋词三百首');
INSERT INTO `poem` VALUES (682, '生查子·元夕', '宋代', '欧阳修', '去年元夜时，花市灯如昼。 月上柳梢头，人约黄昏后。 今年元夜时，月与灯依旧。 不见去年人，泪湿春衫袖。(泪湿 ：一作：满)', '宋词三百首');
INSERT INTO `poem` VALUES (683, '蝶恋花·庭院深深深几许', '宋代', '欧阳修', '庭院深深深几许，杨柳堆烟，帘幕无重数。玉勒雕鞍游冶处，楼高不见章台路。 雨横风狂三月暮，门掩黄昏，无计留春住。泪眼问花花不语，乱红飞过秋千去。', '宋词三百首');
INSERT INTO `poem` VALUES (684, '渔家傲·花底忽闻敲两桨', '宋代', '欧阳修', '花底忽闻敲两桨。逡巡女伴来寻访。酒盏旋将荷叶当。莲舟荡。时时盏里生红浪。 花气酒香清厮酿。花腮酒面红相向。醉倚绿阴眠一饷。惊起望。船头阁在沙滩上。', '宋词三百首');
INSERT INTO `poem` VALUES (685, '渔家傲·花底忽闻敲两桨', '宋代', '欧阳修', '花底忽闻敲两桨。逡巡女伴来寻访。酒盏旋将荷叶当。莲舟荡。时时盏里生红浪。 花气酒香清厮酿。花腮酒面红相向。醉倚绿阴眠一饷。惊起望。船头阁在沙滩上。', '宋词三百首');
INSERT INTO `poem` VALUES (686, '浪淘沙·把酒祝东风', '宋代', '欧阳修', '把酒祝东风，且共从容。垂杨紫陌洛城东。总是当时携手处，游遍芳丛。 聚散苦匆匆，此恨无穷。今年花胜去年红。可惜明年花更好，知与谁同？', '宋词三百首');
INSERT INTO `poem` VALUES (687, '凤箫吟·锁离愁', '宋代', '韩缜', '锁离愁，连绵无际，来时陌上初熏。绣帏人念远，暗垂珠露，泣送征轮。长行长在眼，更重重、远水孤云。但望极楼高，尽日目断王孙。 消魂。池塘别后，曾行处、绿妒轻裙。恁时携素手，乱花飞絮里，缓步香茵。朱颜空自改，向年年、芳意长新。遍绿野，嬉游醉眠，莫负青春。', '宋词三百首');
INSERT INTO `poem` VALUES (688, '桂枝香·金陵怀古', '宋代', '王安石', '登临送目，正故国晚秋，天气初肃。千里澄江似练，翠峰如簇。归帆去棹残阳里，背西风，酒旗斜矗。彩舟云淡，星河鹭起，画图难足。(归帆 一作：征帆) 念往昔，繁华竞逐，叹门外楼头，悲恨相续。千古凭高对此，谩嗟荣辱。六朝旧事随流水，但寒烟衰草凝绿。至今商女，时时犹唱，后庭遗曲。', '宋词三百首');
INSERT INTO `poem` VALUES (689, '渔家傲·平岸小桥千嶂抱', '宋代', '王安石', '平岸小桥千嶂抱。柔蓝一水萦花草。茅屋数间窗窈窕。尘不到。时时自有春风扫。 午枕觉来闻语鸟。欹眠似听朝鸡早。忽忆故人今总老。贪梦好。茫然忘了邯郸道。', '宋词三百首');
INSERT INTO `poem` VALUES (690, '浪淘沙令·伊吕两衰翁', '宋代', '王安石', '伊吕两衰翁，历遍穷通。一为钓叟一耕佣。若使当时身不遇，老了英雄。 汤武偶相逢，风虎云龙。兴王只在谈笑中。直至如今千载后，谁与争功！', '宋词三百首');
INSERT INTO `poem` VALUES (691, '清平乐·春晚', '宋代', '王安国', '留春不住，费尽莺儿语。满地残红宫锦污，昨夜南园风雨。 小怜初上琵琶，晓来思绕天涯。不肯画堂朱户，春风自在杨花。', '宋词三百首');
INSERT INTO `poem` VALUES (692, '卜算子·送鲍浩然之浙东', '宋代', '王观', '水是眼波横，山是眉峰聚。欲问行人去那边？眉眼盈盈处。 才始送春归，又送君归去。若到江南赶上春，千万和春住。', '宋词三百首');
INSERT INTO `poem` VALUES (693, '临江仙·梦后楼台高锁', '宋代', '晏几道', '梦后楼台高锁，酒醒帘幕低垂。去年春恨却来时。落花人独立，微雨燕双飞。 记得小蘋初见，两重心字罗衣。琵琶弦上说相思。当时明月在，曾照彩云归。(蘋 通：苹)', '宋词三百首');
INSERT INTO `poem` VALUES (694, '蝶恋花·醉别西楼醒不记', '宋代', '晏几道', '醉别西楼醒不记。春梦秋云，聚散真容易。斜月半窗还少睡。画屏闲展吴山翠。 衣上酒痕诗里字。点点行行，总是凄凉意。红烛自怜无好计。夜寒空替人垂泪。', '宋词三百首');
INSERT INTO `poem` VALUES (695, '清平乐·留人不住', '宋代', '晏几道', '留人不住，醉解兰舟去。一棹碧涛春水路，过尽晓莺啼处。 渡头杨柳青青，枝枝叶叶离情。此后锦书休寄，画楼云雨无凭。', '宋词三百首');
INSERT INTO `poem` VALUES (696, '鹧鸪天·彩袖殷勤捧玉钟', '宋代', '晏几道', '彩袖殷勤捧玉钟，当年拚却醉颜红。舞低杨柳楼心月，歌尽桃花扇底风。 从别后，忆相逢，几回魂梦与君同。今宵剩把银釭照，犹恐相逢是梦中。', '宋词三百首');
INSERT INTO `poem` VALUES (697, '鹧鸪天·小令尊前见玉箫', '宋代', '晏几道', '小令尊前见玉箫。银灯一曲太妖娆。歌中醉倒谁能恨，唱罢归来酒未消。 春悄悄，夜迢迢。碧云天共楚宫遥。梦魂惯得无拘检，又踏杨花过谢桥。', '宋词三百首');
INSERT INTO `poem` VALUES (698, '阮郎归·旧香残粉似当初', '宋代', '晏几道', '旧香残粉似当初。人情恨不如。一春犹有数行书。秋来书更疏。 衾凤冷，枕鸳孤。愁肠待酒舒。梦魂纵有也成虚。那堪和梦无。', '宋词三百首');
INSERT INTO `poem` VALUES (699, '卖花声·题岳阳楼', '宋代', '张舜民', '木叶下君山。空水漫漫。十分斟酒敛芳颜。不是渭城西去客，休唱阳关。 醉袖抚危栏。天淡云闲。何人此路得生还。回首夕阳红尽处，应是长安。', '宋词三百首');
INSERT INTO `poem` VALUES (700, '水龙吟·次韵章质夫杨花词', '宋代', '苏轼', '似花还似非花，也无人惜从教坠。抛家傍路，思量却是，无情有思。萦损柔肠，困酣娇眼，欲开还闭。梦随风万里，寻郎去处，又还被、莺呼起。 不恨此花飞尽，恨西园、落红难缀。晓来雨过，遗踪何在？一池萍碎。春色三分，二分尘土，一分流水。细看来，不是杨花，点点是离人泪。', '宋词三百首');
INSERT INTO `poem` VALUES (701, '定风波·南海归赠王定国侍人寓娘', '宋代', '苏轼', '常羡人间琢玉郎，天应乞与点酥娘。尽道清歌传皓齿，风起，雪飞炎海变清凉。(尽道 一作：自作) 万里归来颜愈少，微笑，笑时犹带岭梅香。试问岭南应不好，却道：此心安处是吾乡。', '宋词三百首');
INSERT INTO `poem` VALUES (702, '水调歌头·明月几时有', '宋代', '苏轼', '丙辰中秋，欢饮达旦，大醉，作此篇，兼怀子由。 明月几时有？把酒问青天。不知天上宫阙，今夕是何年。我欲乘风归去，又恐琼楼玉宇，高处不胜寒。起舞弄清影，何似在人间？(何似 一作：何时；又恐 一作：惟 / 唯恐) 转朱阁，低绮户，照无眠。不应有恨，何事长向别时圆？人有悲欢离合，月有阴晴圆缺，此事古难全。但愿人长久，千里共婵娟。(长向 一作：偏向)', '宋词三百首');
INSERT INTO `poem` VALUES (703, '念奴娇·赤壁怀古', '宋代', '苏轼', '大江东去，浪淘尽，千古风流人物。 故垒西边，人道是，三国周郎赤壁。 乱石穿空，惊涛拍岸，卷起千堆雪。 江山如画，一时多少豪杰。 遥想公瑾当年，小乔初嫁了，雄姿英发。 羽扇纶巾，谈笑间，樯橹灰飞烟灭。(樯橹 一作：强虏) 故国神游，多情应笑我，早生华发。 人生如梦，一尊还酹江月。(人生 一作：人间；尊 通：樽)', '宋词三百首');
INSERT INTO `poem` VALUES (704, '西江月·世事一场大梦', '宋代', '苏轼', '世事一场大梦，人生几度秋凉？夜来风叶已鸣廊。看取眉头鬓上。(秋凉 一作：新凉) 酒贱常愁客少，月明多被云妨。中秋谁与共孤光。把盏凄然北望。', '宋词三百首');
INSERT INTO `poem` VALUES (705, '西江月·顷在黄州', '宋代', '苏轼', '顷在黄州，春夜行蕲水中，过酒家饮，酒醉，乘月至一溪桥上，解鞍，由肱醉卧少休。及觉已晓，乱山攒拥，流水锵然，疑非尘世也。书此语桥柱上。(由肱 一作：曲肱) 照野弥弥浅浪，横空隐隐层霄。障泥未解玉骢骄，我欲醉眠芳草。 可惜一溪风月，莫教踏碎琼瑶。解鞍欹枕绿杨桥，杜宇一声春晓。', '宋词三百首');
INSERT INTO `poem` VALUES (706, '临江仙·夜饮东坡醒复醉', '宋代', '苏轼', '夜饮东坡醒复醉，归来仿佛三更。家童鼻息已雷鸣。敲门都不应，倚杖听江声。 长恨此身非我有，何时忘却营营。夜阑风静縠纹平。小舟从此逝，江海寄余生。', '宋词三百首');
INSERT INTO `poem` VALUES (707, '定风波·莫听穿林打叶声', '宋代', '苏轼', '三月七日，沙湖道中遇雨。雨具先去，同行皆狼狈，余独不觉，已而遂晴，故作此词。 莫听穿林打叶声，何妨吟啸且徐行。竹杖芒鞋轻胜马，谁怕？一蓑烟雨任平生。 料峭春风吹酒醒，微冷，山头斜照却相迎。回首向来萧瑟处，归去，也无风雨也无晴。', '宋词三百首');
INSERT INTO `poem` VALUES (708, '卜算子·黄州定慧院寓居作', '宋代', '苏轼', '缺月挂疏桐，漏断人初静。时见幽人独往来，缥缈孤鸿影。(时见 一作：谁见) 惊起却回头，有恨无人省。拣尽寒枝不肯栖，寂寞沙洲冷。', '宋词三百首');
INSERT INTO `poem` VALUES (709, '洞仙歌·冰肌玉骨', '宋代', '苏轼', '仆七岁时，见眉州老尼，姓朱，忘其名，年九十岁。自言尝随其师入蜀主孟昶宫中，一日大热，蜀主与花蕊夫人夜纳凉摩诃池上，作一词，朱具能记之。今四十年，朱已死久矣，人无知此词者，但记其首两句，暇日寻味，岂《洞仙歌》令乎？乃为足之云。 冰肌玉骨，自清凉无汗。水殿风来暗香满。绣帘开，一点明月窥人，人未寝，欹枕钗横鬓乱。 起来携素手，庭户无声，时见疏星渡河汉。试问夜如何？夜已三更，金波淡，玉绳低转。但屈指西风几时来，又不道流年暗中偷换。', '宋词三百首');
INSERT INTO `poem` VALUES (710, '江城子·密州出猎', '宋代', '苏轼', '老夫聊发少年狂，左牵黄，右擎苍，锦帽貂裘，千骑卷平冈。为报倾城随太守，亲射虎，看孙郎。 酒酣胸胆尚开张，鬓微霜，又何妨！持节云中，何日遣冯唐？会挽雕弓如满月，西北望，射天狼。', '宋词三百首');
INSERT INTO `poem` VALUES (711, '江城子·乙卯正月二十日夜记梦', '宋代', '苏轼', '十年生死两茫茫，不思量，自难忘。千里孤坟，无处话凄凉。纵使相逢应不识，尘满面，鬓如霜。 夜来幽梦忽还乡，小轩窗，正梳妆。相顾无言，惟有泪千行。料得年年肠断处，明月夜，短松冈。(肠断 一作：断肠)', '宋词三百首');
INSERT INTO `poem` VALUES (712, '蝶恋花·春景', '宋代', '苏轼', '花褪残红青杏小。燕子飞时，绿水人家绕。枝上柳绵吹又少。天涯何处无芳草！ 墙里秋千墙外道。墙外行人，墙里佳人笑。笑渐不闻声渐悄。多情却被无情恼。', '宋词三百首');
INSERT INTO `poem` VALUES (713, '永遇乐·彭城夜宿燕子楼', '宋代', '苏轼', '彭城夜宿燕子楼，梦盼盼，因作此词。 明月如霜，好风如水，清景无限。曲港跳鱼，圆荷泻露，寂寞无人见。紞如三鼓，铿然一叶，黯黯梦云惊断。夜茫茫，重寻无处，觉来小园行遍。 天涯倦客，山中归路，望断故园心眼。燕子楼空，佳人何在，空锁楼中燕。古今如梦，何曾梦觉，但有旧欢新怨。异时对，黄楼夜景，为余浩叹。', '宋词三百首');
INSERT INTO `poem` VALUES (714, '浣溪沙·游蕲水清泉寺', '宋代', '苏轼', '游蕲水清泉寺，寺临兰溪，溪水西流。 山下兰芽短浸溪，松间沙路净无泥，潇潇暮雨子规啼。(潇潇 一作：萧萧) 谁道人生无再少？门前流水尚能西！休将白发唱黄鸡。', '宋词三百首');
INSERT INTO `poem` VALUES (715, '浣溪沙·簌簌衣巾落枣花', '宋代', '苏轼', '徐门石潭谢雨，道上作五首。潭在城东二十里，常与泗水增减清浊相应。 簌簌衣巾落枣花，村南村北响缲车，牛衣古柳卖黄瓜。(缲 通：缫) 酒困路长惟欲睡，日高人渴漫思茶。敲门试问野人家。', '宋词三百首');
INSERT INTO `poem` VALUES (716, '卜算子·我住长江头', '宋代', '李之仪', '我住长江头，君住长江尾。日日思君不见君，共饮长江水。 此水几时休，此恨何时已。只愿君心似我心，定不负相思意。', '宋词三百首');
INSERT INTO `poem` VALUES (717, '减字木兰花·竞渡', '宋代', '黄裳', '红旗高举，飞出深深杨柳渚。鼓击春雷，直破烟波远远回。 欢声震地，惊退万人争战气。金碧楼西，衔得锦标第一归。', '宋词三百首');
INSERT INTO `poem` VALUES (718, '眼儿媚·杨柳丝丝弄轻柔', '宋代', '王雱', '杨柳丝丝弄轻柔，烟缕织成愁。海棠未雨，梨花先雪，一半春休。 而今往事难重省，归梦绕秦楼。相思只在，丁香枝上，豆蔻梢头。', '宋词三百首');
INSERT INTO `poem` VALUES (719, '念奴娇·断虹霁雨', '宋代', '黄庭坚', '八月十七日，同诸生步自永安城楼，过张宽夫园待月。偶有名酒，因以金荷酌众客。客有孙彦立，善吹笛。援笔作乐府长短句，文不加点。 断虹霁雨，净秋空，山染修眉新绿。桂影扶疏，谁便道，今夕清辉不足？万里青天，姮娥何处，驾此一轮玉。寒光零乱，为谁偏照醽醁？ 年少从我追游，晚凉幽径，绕张园森木。共倒金荷，家万里，难得尊前相属。老子平生，江南江北，最爱临风笛。孙郎微笑，坐来声喷霜竹。', '宋词三百首');
INSERT INTO `poem` VALUES (720, '水调歌头·游览', '宋代', '黄庭坚', '瑶草一何碧，春入武陵溪。溪上桃花无数，枝上有黄鹂。我欲穿花寻路，直入白云深处，浩气展虹霓。只恐花深里，红露湿人衣。 坐玉石，倚玉枕，拂金徽。谪仙何处？无人伴我白螺杯。我为灵芝仙草，不为朱唇丹脸，长啸亦何为？醉舞下山去，明月逐人归。', '宋词三百首');
INSERT INTO `poem` VALUES (721, '清平乐·春归何处', '宋代', '黄庭坚', '春归何处。寂寞无行路。若有人知春去处。唤取归来同住。 春无踪迹谁知。除非问取黄鹂。百啭无人能解，因风飞过蔷薇。', '宋词三百首');
INSERT INTO `poem` VALUES (722, '鹧鸪天·座中有眉山隐客史应之和前韵即席答之', '宋代', '黄庭坚', '黄菊枝头生晓寒。人生莫放酒杯干。风前横笛斜吹雨，醉里簪花倒著冠。 身健在，且加餐。舞裙歌板尽清欢。黄花白发相牵挽，付与时人冷眼看。', '宋词三百首');
INSERT INTO `poem` VALUES (723, '洞仙歌·雪云散尽', '宋代', '李元膺', '一年春物，惟梅柳间意味最深。至莺花烂漫时，则春已衰迟，使人无复新意。予作《洞仙歌》，使探春者歌之，无后时之悔。 雪云散尽，放晓晴池院。杨柳于人便青眼。更风流多处，一点梅心，相映远，约略颦轻笑浅。 一年春好处，不在浓芳，小艳疏香最娇软。到清明时候，百紫千红，花正乱，已失春风一半。早占取韶光共追游，但莫管春寒，醉红自暖。', '宋词三百首');
INSERT INTO `poem` VALUES (724, '渔家傲·小雨纤纤风细细', '宋代', '朱服', '小雨纤纤风细细，万家杨柳青烟里。恋树湿花飞不起，愁无比，和春付与东流水。 九十光阴能有几？金龟解尽留无计。寄语东阳沽酒市，拼一醉，而今乐事他年泪。', '宋词三百首');
INSERT INTO `poem` VALUES (725, '青门饮·寄宠人', '宋代', '时彦', '胡马嘶风，汉旗翻雪，彤云又吐，一竿残照。古木连空，乱山无数，行尽暮沙衰草。星斗横幽馆，夜无眠、灯花空老。雾浓香鸭，冰凝泪烛，霜天难晓。 长记小妆才了，一杯未尽，离怀多少。醉里秋波，梦中朝雨，都是醒时烦恼。料有牵情处，忍思量、耳边曾道。甚时跃马归来，认得迎门轻笑。', '宋词三百首');
INSERT INTO `poem` VALUES (726, '望海潮·洛阳怀古', '宋代', '秦观', '梅英疏淡，冰澌溶泄，东风暗换年华。金谷俊游，铜驼巷陌，新晴细履平沙。长记误随车。正絮翻蝶舞，芳思交加。柳下桃蹊，乱分春色到人家。 西园夜饮鸣笳。有华灯碍月，飞盖妨花。兰苑未空，行人渐老，重来是事堪嗟。烟暝酒旗斜。但倚楼极目，时见栖鸦。无奈归心，暗随流水到天涯。', '宋词三百首');
INSERT INTO `poem` VALUES (727, '八六子·倚危亭', '宋代', '秦观', '倚危亭。恨如芳草，萋萋刬尽还生。念柳外青骢别后，水边红袂分时，怆然暗惊。 无端天与娉婷。夜月一帘幽梦，春风十里柔情。怎奈向、欢娱渐随流水，素弦声断，翠绡香减，那堪片片飞花弄晚，蒙蒙残雨笼晴。正销凝。黄鹂又啼数声。', '宋词三百首');
INSERT INTO `poem` VALUES (728, '满庭芳·山抹微云', '宋代', '秦观', '山抹微云，天连衰草，画角声断谯门。暂停征棹，聊共引离尊。多少蓬莱旧事，空回首、烟霭纷纷。斜阳外，寒鸦万点，流水绕孤村。(万点 一作：数点) 销魂。当此际，香囊暗解，罗带轻分。谩赢得、青楼薄幸名存。此去何时见也，襟袖上、空惹啼痕。伤情处，高城望断，灯火已黄昏。', '宋词三百首');
INSERT INTO `poem` VALUES (729, '江城子·西城杨柳弄春柔', '宋代', '秦观', '西城杨柳弄春柔，动离忧，泪难收。犹记多情、曾为系归舟。碧野朱桥当日事，人不见，水空流。 韶华不为少年留，恨悠悠，几时休？飞絮落花时候、一登楼。便作春江都是泪，流不尽，许多愁。', '宋词三百首');
INSERT INTO `poem` VALUES (730, '鹊桥仙·纤云弄巧', '宋代', '秦观', '纤云弄巧，飞星传恨，银汉迢迢暗度。金风玉露一相逢，便胜却人间无数。(度 通：渡) 柔情似水，佳期如梦，忍顾鹊桥归路。两情若是久长时，又岂在朝朝暮暮。', '宋词三百首');
INSERT INTO `poem` VALUES (731, '千秋岁·水边沙外', '宋代', '秦观', '水边沙外。城郭春寒退。花影乱，莺声碎。飘零疏酒盏，离别宽衣带。人不见，碧云暮合空相对。 忆昔西池会。鹓鹭同飞盖。携手处，今谁在。日边清梦断，镜里朱颜改。春去也，飞红万点愁如海。', '宋词三百首');
INSERT INTO `poem` VALUES (732, '踏莎行·郴州旅舍', '宋代', '秦观', '雾失楼台，月迷津渡。桃源望断无寻处。可堪孤馆闭春寒，杜鹃声里斜阳暮。 驿寄梅花，鱼传尺素。砌成此恨无重数。郴江幸自绕郴山，为谁流下潇湘去。', '宋词三百首');
INSERT INTO `poem` VALUES (733, '浣溪沙·漠漠轻寒上小楼', '宋代', '秦观', '漠漠轻寒上小楼，晓阴无赖似穷秋。淡烟流水画屏幽。 自在飞花轻似梦，无边丝雨细如愁。宝帘闲挂小银钩。', '宋词三百首');
INSERT INTO `poem` VALUES (734, '行香子·树绕村庄', '宋代', '秦观', '树绕村庄，水满陂塘。倚东风、豪兴徜徉。小园几许，收尽春光。有桃花红，李花白，菜花黄。 远远围墙，隐隐茅堂。飏青旗、流水桥旁。偶然乘兴、步过东冈。正莺儿啼，燕儿舞，蝶儿忙。', '宋词三百首');
INSERT INTO `poem` VALUES (735, '鹧鸪天·重过阊门万事非', '宋代', '贺铸', '重过阊门万事非。同来何事不同归。梧桐半死清霜后，头白鸳鸯失伴飞。 原上草，露初晞。旧栖新垅两依依。空床卧听南窗雨，谁复挑灯夜补衣。', '宋词三百首');
INSERT INTO `poem` VALUES (736, '杵声齐·砧面莹', '宋代', '贺铸', '砧面莹，杵声齐。捣就征衣泪墨题。寄到玉关应万里，戍人犹在玉关西。', '宋词三百首');
INSERT INTO `poem` VALUES (737, '芳心苦·杨柳回塘', '宋代', '贺铸', '杨柳回塘，鸳鸯别浦。绿萍涨断莲舟路。断无蜂蝶慕幽香，红衣脱尽芳心苦。 返照迎潮，行云带雨。依依似与骚人语。当年不肯嫁春风，无端却被秋风误。', '宋词三百首');
INSERT INTO `poem` VALUES (738, '青玉案·凌波不过横塘路', '宋代', '贺铸', '凌波不过横塘路，但目送、芳尘去。锦瑟华年谁与度？月桥花院，琐窗朱户，只有春知处。 飞云冉冉蘅皋暮，彩笔新题断肠句。试问闲情都几许？一川烟草，满城风絮，梅子黄时雨。(版本一) 凌波不过横塘路，但目送、芳尘去。锦瑟华年谁与度？月台花榭，琐窗朱户，只有春知处。 碧云冉冉蘅皋暮，彩笔新题断肠句。试问闲愁都几许？一川烟草，满城风絮，梅子黄时雨。(版本二)', '宋词三百首');
INSERT INTO `poem` VALUES (739, '六州歌头·少年侠气', '宋代', '贺铸', '少年侠气，交结五都雄。肝胆洞。毛发耸。立谈中。死生同。一诺千金重。推翘勇。矜豪纵。轻盖拥。联飞鞚。斗城东。轰饮酒垆，春色浮寒瓮。吸海垂虹。闲呼鹰嗾犬，白羽摘雕弓。狡穴俄空。乐匆匆。(闲呼 一作：间呼) 似黄粱梦。辞丹凤。明月共。漾孤篷。官冗從。怀倥偬。落尘笼。簿书丛。鹖弁如云众。供粗用。忽奇功。笳鼓动。渔阳弄。思悲翁。不请长缨，系取天骄种。剑吼西风。恨登山临水，手寄七弦桐。目送归鸿。', '宋词三百首');
INSERT INTO `poem` VALUES (740, '石州慢·薄雨收寒', '宋代', '贺铸', '薄雨收寒，斜照弄睛，春意空阔。长亭柳色才黄，远客一枝先折。烟横水际，映带几点归鸿，平沙消尽龙荒雪。犹记出关来，恰如今时节。 将发。画楼芳酒，红泪清歌，顿成轻别。回首经年，杳杳音尘都绝。欲知方寸，共有几许新愁？芭蕉不展丁香结。枉望断天涯，两厌厌风月', '宋词三百首');
INSERT INTO `poem` VALUES (741, '思越人·紫府东风放夜时', '宋代', '贺铸', '紫府东风放夜时。步莲秾李伴人归。五更钟动笙歌散，十里月明灯火稀。 香苒苒，梦依依。天涯寒尽减春衣。凤凰城阙知何处，寥落星河一雁飞。', '宋词三百首');
INSERT INTO `poem` VALUES (742, '南柯子·十里青山远', '宋代', '仲殊', '十里青山远，潮平路带沙。数声啼鸟怨年华。又是凄凉时候，在天涯。 白露收残月，清风散晓霞。绿杨堤畔问荷花：记得年时沽酒，那人家？', '宋词三百首');
INSERT INTO `poem` VALUES (743, '诉衷情·宝月山作', '宋代', '仲殊', '清波门外拥轻衣。杨花相送飞。西湖又还春晚，水树乱莺啼。闲院宇，小帘帏。晚初归。钟声已过，篆香才点，月到门时。', '宋词三百首');
INSERT INTO `poem` VALUES (744, '摸鱼儿·东皋寓居', '宋代', '晁补之', '买陂塘、旋栽杨柳，依稀淮岸江浦。东皋嘉雨新痕涨，沙觜鹭来鸥聚。堪爱处最好是、一川夜月光流渚。无人独舞。任翠幄张天，柔茵藉地，酒尽未能去。 青绫被，莫忆金闺故步。儒冠曾把身误。弓刀千骑成何事，荒了邵平瓜圃。君试觑。满青镜、星星鬓影今如许。功名浪语。便似得班超，封侯万里，归计恐迟暮。', '宋词三百首');
INSERT INTO `poem` VALUES (745, '盐角儿·亳社观梅', '宋代', '晁补之', '开时似雪。谢时似雪。花中奇绝。香非在蕊，香非在萼，骨中香彻。 占溪风，留溪月。堪羞损、山桃如血。直饶更、疏疏淡淡，终有一般情别。', '宋词三百首');
INSERT INTO `poem` VALUES (746, '秋蕊香·帘幕疏疏风透', '宋代', '张耒', '帘幕疏疏风透。一线香飘金兽。朱阑倚遍黄昏后。廊上月华如昼。 别离滋味浓于酒。著人瘦。此情不及墙东柳。春色年年如旧。', '宋词三百首');
INSERT INTO `poem` VALUES (747, '临江仙·未遇行藏谁肯信', '宋代', '侯蒙', '未遇行藏谁肯信，如今方表名踪。无端良匠画形容。当风轻借力，一举入高空。 才得吹嘘身渐稳，只疑远赴蟾宫。雨馀时候夕阳红。几人平地上，看我碧霄中。', '宋词三百首');
INSERT INTO `poem` VALUES (748, '瑞龙吟·大石春景', '宋代', '周邦彦', '章台路。还见褪粉梅梢，试花桃树。愔愔坊陌人家，定巢燕子，归来旧处。 黯凝伫。因念个人痴小，乍窥门户。侵晨浅约宫黄，障风映袖，盈盈笑语。 前度刘郎重到，访邻寻里，同时歌舞。唯有旧家秋娘，声价如故。吟笺赋笔，犹记燕台句。知谁伴、名园露饮，东城闲步。事与孤鸿去。探春尽是，伤离意绪。官柳低金缕。归骑晚、纤纤池塘飞雨。断肠院落，一帘风絮。', '宋词三百首');
INSERT INTO `poem` VALUES (749, '满庭芳·夏日溧水无想山作', '宋代', '周邦彦', '风老莺雏，雨肥梅子，午阴嘉树清圆。地卑山近，衣润费炉烟。人静乌鸢自乐，小桥外、新绿溅溅。凭阑久，黄芦苦竹，拟泛九江船。 年年。如社燕，飘流瀚海，来寄修椽。且莫思身外，长近尊前。憔悴江南倦客，不堪听、急管繁弦。歌筵畔，先安簟枕，容我醉时眠。', '宋词三百首');
INSERT INTO `poem` VALUES (750, '苏幕遮·燎沉香', '宋代', '周邦彦', '燎沉香，消溽暑。鸟雀呼晴，侵晓窥檐语。叶上初阳干宿雨、水面清圆，一一风荷举。 故乡遥，何日去。家住吴门，久作长安旅。五月渔郎相忆否。小楫轻舟，梦入芙蓉浦。', '宋词三百首');
INSERT INTO `poem` VALUES (751, '少年游·并刀如水', '宋代', '周邦彦', '并刀如水，吴盐胜雪，纤手破新橙。锦幄初温，兽烟不断，相对坐调笙。 低声问向谁行宿，城上已三更。马滑霜浓，不如休去，直是少人行。', '宋词三百首');
INSERT INTO `poem` VALUES (752, '夜游宫·叶下斜阳照水', '宋代', '周邦彦', '叶下斜阳照水。卷轻浪、沈沈千里。桥上酸风射眸子。立多时，看黄昏，灯火市。 古屋寒窗底。听几片、井桐飞坠。不恋单衾再三起。有谁知，为萧娘，书一纸。', '宋词三百首');
INSERT INTO `poem` VALUES (753, '解语花·上元', '宋代', '周邦彦', '风消绛蜡，露浥红莲，灯市光相射。桂华流瓦。纤云散，耿耿素娥欲下。衣裳淡雅。看楚女纤腰一把。箫鼓喧，人影参差，满路飘香麝。 因念都城放夜。望千门如昼，嬉笑游冶。钿车罗帕。相逢处，自有暗尘随马。年光是也。唯只见、旧情衰谢。清漏移，飞盖归来，从舞休歌罢。', '宋词三百首');
INSERT INTO `poem` VALUES (754, '兰陵王·柳', '宋代', '周邦彦', '柳阴直，烟里丝丝弄碧。隋堤上、曾见几番，拂水飘绵送行色。登临望故国，谁识京华倦客？长亭路，年去岁来，应折柔条过千尺。 闲寻旧踪迹，又酒趁哀弦，灯照离席。梨花榆火催寒食。愁一箭风快，半篙波暖，回头迢递便数驿，望人在天北。 凄恻，恨堆积！渐别浦萦回，津堠岑寂，斜阳冉冉春无极。念月榭携手，露桥闻笛。沉思前事，似梦里，泪暗滴。', '宋词三百首');
INSERT INTO `poem` VALUES (755, '六丑·落花', '宋代', '周邦彦', '正单衣试酒，怅客里、光阴虚掷。愿春暂留，春归如过翼。一去无迹。为问花何在，夜来风雨，葬楚宫倾国。钗钿堕处遗香泽。乱点桃蹊，轻翻柳陌。多情为谁追惜。但蜂媒蝶使，时叩窗隔。 东园岑寂。渐蒙笼暗碧。静绕珍丛底，成叹息。长条故惹行客。似牵衣待话，别情无极。残英小、强簪巾帻。终不似一朵，钗头颤袅，向人欹侧。漂流处、莫趁潮汐。恐断红、尚有相思字，何由见得。', '宋词三百首');
INSERT INTO `poem` VALUES (756, '西河·大石金陵', '宋代', '周邦彦', '佳丽地。南朝盛事谁记。山围故国绕清江，髻鬟对起。怒涛寂寞打孤城，风樯遥度天际。 断崖树，犹倒倚。莫愁艇子曾系。空余旧迹郁苍苍，雾沈半垒。夜深月过女墙来，伤心东望淮水。 酒旗戏鼓甚处市。想依稀、王谢邻里。燕子不知何世。入寻常、巷陌人家，相对如说兴亡，斜阳里。', '宋词三百首');
INSERT INTO `poem` VALUES (757, '虞美人·疏篱曲径田家小', '宋代', '周邦彦', '疏篱曲径田家小。云树开清晓。天寒山色有无中。野外一声钟起、送孤蓬。 添衣策马寻亭堠。愁抱惟宜酒。菰蒲睡鸭占陂塘。纵被行人惊散、又成双。', '宋词三百首');
INSERT INTO `poem` VALUES (758, '蝶恋花·早行', '宋代', '周邦彦', '月皎惊乌栖不定，更漏将残，辘轳牵金井。唤起两眸清炯炯。泪花落枕红绵冷。 执手霜风吹鬓影。去意徊徨，别语愁难听。楼上阑干横斗柄，露寒人远鸡相应。', '宋词三百首');
INSERT INTO `poem` VALUES (759, '玉楼春·桃溪不作从容住', '宋代', '周邦彦', '桃溪不作从容住，秋藕绝来无续处。当时相候赤阑桥，今日独寻黄叶路。 烟中列岫青无数，雁背夕阳红欲暮。人如风后入江云，情似雨馀粘地絮。', '宋词三百首');
INSERT INTO `poem` VALUES (760, '江神子·杏花村馆酒旗风', '宋代', '谢逸', '杏花村馆酒旗风。水溶溶。扬残红。野渡舟横，杨柳绿阴浓。望断江南山色远，人不见，草连空。 夕阳楼外晚烟笼。粉香融。淡眉峰。记得年时，相见画屏中。只有关山今夜月，千里外，素光同。', '宋词三百首');
INSERT INTO `poem` VALUES (761, '惜分飞·泪湿阑干花著露', '宋代', '毛滂', '泪湿阑干花著露。愁到眉峰碧聚。此恨平分取。更无言语。空相觑。 短雨残云无意绪。寂寞朝朝暮暮。今夜山深处。断魂分付。潮回去。', '宋词三百首');
INSERT INTO `poem` VALUES (762, '水调歌头·秋色渐将晚', '宋代', '叶梦得', '秋色渐将晚，霜信报黄花。小窗低户深映，微路绕欹斜。为问山翁何事，坐看流年轻度，拚却鬓双华。徙倚望沧海，天净水明霞。 念平昔，空飘荡，遍天涯。归来三径重扫，松竹本吾家。却恨悲风时起，冉冉云间新雁，边马怨胡笳。谁似东山老，谈笑静胡沙。', '宋词三百首');
INSERT INTO `poem` VALUES (763, '点绛唇·绍兴乙卯登绝顶小亭', '宋代', '叶梦得', '缥缈危亭，笑谈独在千峰上。与谁同赏。万里横烟浪。 老去情怀，犹作天涯想。空惆怅。少年豪放。莫学衰翁样。', '宋词三百首');
INSERT INTO `poem` VALUES (764, '点绛唇·新月娟娟', '宋代', '汪藻', '新月娟娟，夜寒江静山衔斗。起来搔首，梅影横窗瘦。 好个霜天，闲却传杯手。君知否？乱鸦啼后，归兴浓于酒。', '宋词三百首');
INSERT INTO `poem` VALUES (765, '蓦山溪·梅', '宋代', '曹组', '洗妆真态，不作铅花御。竹外一枝斜，想佳人，天寒日暮。黄昏院落，无处著清香，风细细，雪垂垂，何况江头路。 月边疏影，梦到消魂处。梅子欲黄时，又须作，廉纤细雨。孤芳一世，供断有情愁，消瘦损，东阳也，试问花知否？', '宋词三百首');
INSERT INTO `poem` VALUES (766, '三台·清明应制', '宋代', '万俟咏', '见梨花初带夜月，海棠半含朝雨。内苑春、不禁过青门，御沟涨、潜通南浦。东风静、细柳垂金缕。望凤阙、非烟非雾。好时代、朝野多欢，遍九陌、太平箫鼓。 乍莺儿百啭断续，燕子飞来飞去。近绿水、台榭映秋千，斗草聚、双双游女。饧香更、酒冷踏青路。会暗识、夭桃朱户。向晚骤、宝马雕鞍，醉襟惹、乱花飞絮。 正轻寒轻暖漏永，半阴半晴云暮。禁火天、已是试新妆，岁华到、三分佳处。清明看、汉宫传蜡炬。散翠烟、飞入槐府。敛兵卫、阊阖门开，住传宣、又还休务。', '宋词三百首');
INSERT INTO `poem` VALUES (767, '好事近·摇首出红尘', '宋代', '朱敦儒', '摇首出红尘，醒醉更无时节。活计绿蓑青笠，惯披霜冲雪。 晚来风定钓丝闲，上下是新月。千里水天一色，看孤鸿明灭。', '宋词三百首');
INSERT INTO `poem` VALUES (768, '相见欢·金陵城上西楼', '宋代', '朱敦儒', '金陵城上西楼，倚清秋。万里夕阳垂地，大江流。 中原乱，簪缨散，几时收？试倩悲风吹泪，过扬州。', '宋词三百首');
INSERT INTO `poem` VALUES (769, '鹧鸪天·西都作', '宋代', '朱敦儒', '我是清都山水郎，天教分付与疏狂。曾批给雨支风券，累上留云借月章。 诗万首，酒千觞。几曾着眼看侯王？玉楼金阙慵归去，且插梅花醉洛阳。', '宋词三百首');
INSERT INTO `poem` VALUES (770, '减字木兰花·题雄州驿', '宋代', '蒋氏女', '朝云横度。辘辘车声如水去。白草黄沙。月照孤村三两家。 飞鸿过也。万结愁肠无昼夜。渐近燕山。回首乡关归路难。', '宋词三百首');
INSERT INTO `poem` VALUES (771, '南柯子·山冥云阴重', '宋代', '王炎', '山冥云阴重，天寒雨意浓。数枝幽艳湿啼红。莫为惜花惆怅对东风。 蓑笠朝朝出，沟塍处处通。人间辛苦是三农。要得一犁水足望年丰。', '宋词三百首');
INSERT INTO `poem` VALUES (772, '燕山亭·北行见杏花', '宋代', '赵佶', '裁剪冰绡，轻叠数重，淡著胭脂匀注。新样靓妆，艳溢香融，羞杀蕊珠宫女。易得凋零，更多少、无情风雨。愁苦。闲院落凄凉，几番春暮。（闲院 一作：问院) 凭寄离恨重重，者双燕，何曾会人言语。天遥地远，万水千山，知他故宫何处。怎不思量，除梦里、有时曾去。无据，和梦也新来不做。', '宋词三百首');
INSERT INTO `poem` VALUES (773, '南歌子·天上星河转', '宋代', '李清照', '天上星河转，人间帘幕垂。凉生枕簟泪痕滋。起解罗衣聊问、夜何其。 翠贴莲蓬小，金销藕叶稀。旧时天气旧时衣。只有情怀不似、旧家时。', '宋词三百首');
INSERT INTO `poem` VALUES (774, '一剪梅·红藕香残玉簟秋', '宋代', '李清照', '红藕香残玉簟秋。轻解罗裳，独上兰舟。云中谁寄锦书来，雁字回时，月满西楼。 花自飘零水自流。一种相思，两处闲愁。此情无计可消除，才下眉头，却上心头。', '宋词三百首');
INSERT INTO `poem` VALUES (775, '渔家傲·天接云涛连晓雾', '宋代', '李清照', '天接云涛连晓雾，星河欲转千帆舞。仿佛梦魂归帝所。闻天语，殷勤问我归何处。 我报路长嗟日暮，学诗谩有惊人句。九万里风鹏正举。风休住，蓬舟吹取三山去！', '宋词三百首');
INSERT INTO `poem` VALUES (776, '如梦令·常记溪亭日暮', '宋代', '李清照', '常记溪亭日暮，沉醉不知归路。 兴尽晚回舟，误入藕花深处。 争渡，争渡，惊起一滩鸥鹭。', '宋词三百首');
INSERT INTO `poem` VALUES (777, '如梦令·昨夜雨疏风骤', '宋代', '李清照', '昨夜雨疏风骤，浓睡不消残酒。试问卷帘人，却道海棠依旧。知否，知否？应是绿肥红瘦。', '宋词三百首');
INSERT INTO `poem` VALUES (778, '凤凰台上忆吹箫·香冷金猊', '宋代', '李清照', '香冷金猊，被翻红浪，起来慵自梳头。任宝奁尘满，日上帘钩。生怕离怀别苦，多少事、欲说还休。新来瘦，非干病酒，不是悲秋。 休休，这回去也，千万遍《阳关》，也则难留。念武陵人远，烟锁秦楼。惟有楼前流水，应念我、终日凝眸。凝眸处，从今又添，一段新愁。(版本一) 香冷金猊，被翻红浪，起来人未梳头。任宝奁闲掩，日上帘钩。生怕闲愁暗恨，多少事、欲说还休。今年瘦，非干病酒，不是悲秋。 明朝，者回去也，千万遍阳关，也即难留。念武陵春晚，云锁重楼。记取楼前绿水，应念我、终日凝眸。凝眸处，从今更数，几段新愁。(版本二)', '宋词三百首');
INSERT INTO `poem` VALUES (779, '清平乐·年年雪里', '宋代', '李清照', '年年雪里，常插梅花醉。挼尽梅花无好意，赢得满衣清泪。 今年海角天涯，萧萧两鬓生华。看取晚来风势，故应难看梅花。', '宋词三百首');
INSERT INTO `poem` VALUES (780, '蝶恋花·暖雨晴风初破冻', '宋代', '李清照', '暖雨晴风初破冻，柳眼梅腮，已觉春心动。酒意诗情谁与共？泪融残粉花钿重。 乍试夹衫金缕缝，山枕斜欹，枕损钗头凤。独抱浓愁无好梦，夜阑犹剪灯花弄。', '宋词三百首');
INSERT INTO `poem` VALUES (781, '鹧鸪天·寒日萧萧上琐窗', '宋代', '李清照', '寒日萧萧上琐窗，梧桐应恨夜来霜。酒阑更喜团茶苦，梦断偏宜瑞脑香。 秋已尽，日犹长，仲宣怀远更凄凉。不如随分尊前醉，莫负东篱菊蕊黄。', '宋词三百首');
INSERT INTO `poem` VALUES (782, '醉花阴·薄雾浓云愁永昼', '宋代', '李清照', '薄雾浓云愁永昼，瑞脑消金兽。佳节又重阳，玉枕纱橱，半夜凉初透。(橱 通：厨) 东篱把酒黄昏后，有暗香盈袖。莫道不销魂，帘卷西风，人比黄花瘦。(人比 一作：人似；销 一作：消)', '宋词三百首');
INSERT INTO `poem` VALUES (783, '武陵春·春晚', '宋代', '李清照', '风住尘香花已尽，日晚倦梳头。物是人非事事休，欲语泪先流。 闻说双溪春尚好，也拟泛轻舟。只恐双溪舴艋舟，载不动许多愁。', '宋词三百首');
INSERT INTO `poem` VALUES (784, '点绛唇·蹴罢秋千', '宋代', '李清照', '蹴罢秋千，起来慵整纤纤手。露浓花瘦，薄汗轻衣透。 见客入来，袜刬金钗溜。和羞走，倚门回首，却把青梅嗅。', '宋词三百首');
INSERT INTO `poem` VALUES (785, '永遇乐·落日熔金', '宋代', '李清照', '落日熔金，暮云合璧，人在何处。染柳烟浓，吹梅笛怨，春意知几许。元宵佳节，融和天气，次第岂无风雨。来相召、香车宝马，谢他酒朋诗侣。(熔金 一作：镕金) 中州盛日，闺门多暇，记得偏重三五。铺翠冠儿，捻金雪柳，簇带争济楚。如今憔悴，风鬟霜鬓，怕见夜间出去。不如向、帘儿底下，听人笑语。', '宋词三百首');
INSERT INTO `poem` VALUES (786, '声声慢·寻寻觅觅', '宋代', '李清照', '寻寻觅觅，冷冷清清，凄凄惨惨戚戚。乍暖还寒时候，最难将息。三杯两盏淡酒，怎敌他、晚来风急？雁过也，正伤心，却是旧时相识。 满地黄花堆积。憔悴损，如今有谁堪摘？守着窗儿，独自怎生得黑？梧桐更兼细雨，到黄昏、点点滴滴。这次第，怎一个愁字了得！（守着窗儿 一作：守著窗儿）', '宋词三百首');
INSERT INTO `poem` VALUES (787, '蝶恋花·春涨一篙添水面', '宋代', '范成大', '春涨一篙添水面。芳草鹅儿，绿满微风岸。画舫夷犹湾百转。横塘塔近依前远。 江国多寒农事晚。村北村南，谷雨才耕遍。秀麦连冈桑叶贱。看看尝面收新茧。', '宋词三百首');
INSERT INTO `poem` VALUES (788, '采桑子·恨君不似江楼月', '宋代', '吕本中', '恨君不似江楼月，南北东西，南北东西，只有相随无别离。 恨君却似江楼月，暂满还亏，暂满还亏，待得团圆是几时？', '宋词三百首');
INSERT INTO `poem` VALUES (789, '秦楼月·芳菲歇', '宋代', '向子諲', '芳菲歇，故园目断伤心切。伤心切，无边烟水，无穷山色。 可堪更近乾龙节，眼中泪尽空啼血。空啼血，子规声外，晓风残月。', '宋词三百首');
INSERT INTO `poem` VALUES (790, '忆秦娥·与君别', '宋代', '房舜卿', '与君别。相思一夜梅花发。梅花发。凄凉南浦，断桥斜月。 盈盈微步凌波袜。东风笑倚天涯阔。天涯阔。一声羌管，暮云愁绝。', '宋词三百首');
INSERT INTO `poem` VALUES (791, '苍梧谣·天', '宋代', '蔡伸', '天。休使圆蟾照客眠。人何在，桂影自婵娟。', '宋词三百首');
INSERT INTO `poem` VALUES (792, '忆王孙·春词', '宋代', '李重元', '萋萋芳草忆王孙。柳外楼高空断魂。杜宇声声不忍闻。欲黄昏。雨打梨花深闭门。', '宋词三百首');
INSERT INTO `poem` VALUES (793, '临江仙·夜登小阁忆洛中旧游', '宋代', '陈与义', '忆昔午桥桥上饮，坐中多是豪英。长沟流月去无声。杏花疏影里，吹笛到天明。 二十余年如一梦，此身虽在堪惊。闲登小阁看新晴。古今多少事，渔唱起三更。', '宋词三百首');
INSERT INTO `poem` VALUES (794, '贺新郎·寄李伯纪丞相', '宋代', '张元干', '曳杖危楼去。斗垂天、沧波万顷，月流烟渚。扫尽浮云风不定，未放扁舟夜渡。宿雁落、寒芦深处。怅望关河空吊影，正人间、鼻息鸣鼍鼓。谁伴我，醉中舞。 十年一梦扬州路。倚高寒、愁生故国，气吞骄虏。要斩楼兰三尺剑，遗恨琵琶旧语。谩暗涩铜华尘土。唤取谪仙平章看，过苕溪、尚许垂纶否。风浩荡，欲飞举。', '宋词三百首');
INSERT INTO `poem` VALUES (795, '贺新郎·送胡邦衡待制赴新州', '宋代', '张元干', '梦绕神州路。怅秋风、连营画角，故宫离黍。底事昆仑倾砥柱，九地黄流乱注。聚万落千村狐兔。天意从来高难问，况人情老易悲难诉。更南浦，送君去。 凉生岸柳催残暑。耿斜河，疏星残月，断云微度。万里江山知何处？回首对床夜语。雁不到，书成谁与？目尽青天怀今古，肯儿曹恩怨相尔汝！举大白，听《金缕》。', '宋词三百首');
INSERT INTO `poem` VALUES (796, '柳梢青·茅舍疏篱', '宋代', '杨无咎', '茅舍疏篱。半飘残雪，斜卧低枝。可更相宜，烟笼修竹，月在寒溪。 宁宁伫立移时。判瘦损、无妨为伊。谁赋才情，画成幽思，写入新诗。', '宋词三百首');
INSERT INTO `poem` VALUES (797, '饮马歌·边头春未到', '宋代', '曹勋', '此腔自虏传至边，饮牛马即横笛吹之，不鼓不拍，声甚凄断。闻兀术每遇对阵之际，吹此则鏖战无还期也。 边头春未到，雪满交河道。暮沙明残照，塞烽云间小。断鸿悲，陇月低，泪湿征衣悄。岁华老。', '宋词三百首');
INSERT INTO `poem` VALUES (798, '满江红·写怀', '宋代', '岳飞', '怒发冲冠，凭栏处、潇潇雨歇。抬望眼，仰天长啸，壮怀激烈。三十功名尘与土，八千里路云和月。莫等闲，白了少年头，空悲切！(栏 通：阑) 靖康耻，犹未雪。臣子恨，何时灭！驾长车，踏破贺兰山缺。壮志饥餐胡虏肉，笑谈渴饮匈奴血。待从头、收拾旧山河，朝天阙。(壮志 一作：壮士；兰山缺 一作：兰山阙)', '宋词三百首');
INSERT INTO `poem` VALUES (799, '小重山·昨夜寒蛩不住鸣', '宋代', '岳飞', '昨夜寒蛩不住鸣。惊回千里梦，已三更。起来独自绕阶行。人悄悄，帘外月胧明。 白首为功名。旧山松竹老，阻归程。欲将心事付瑶琴。知音少，弦断有谁听。', '宋词三百首');
INSERT INTO `poem` VALUES (800, '鹧鸪天·一点残红欲尽时', '宋代', '周紫芝', '一点残红欲尽时。乍凉秋气满屏帏。梧桐叶上三更雨，叶叶声声是别离。 调宝瑟，拨金猊。那时同唱鹧鸪词。如今风雨西楼夜，不听清歌也泪垂。', '宋词三百首');
INSERT INTO `poem` VALUES (801, '霜天晓角·题采石蛾眉亭', '宋代', '韩元吉', '倚天绝壁，直下江千尺。天际两蛾凝黛，愁与恨，几时极！ 暮潮风正急，酒阑闻塞笛。试问谪仙何处？青山外，远烟碧。', '宋词三百首');
INSERT INTO `poem` VALUES (802, '眼儿媚·迟迟春日弄轻柔', '宋代', '朱淑真', '迟迟春日弄轻柔，花径暗香流。清明过了，不堪回首，云锁朱楼。 午窗睡起莺声巧，何处唤春愁？绿杨影里，海棠亭畔，红杏梢头。', '宋词三百首');
INSERT INTO `poem` VALUES (803, '谒金门·春半', '宋代', '朱淑真', '春已半。触目此情无限。十二阑干闲倚遍。愁来天不管。 好是风和日暖。输与莺莺燕燕。满院落花帘不卷。断肠芳草远。', '宋词三百首');
INSERT INTO `poem` VALUES (804, '蝶恋花·送春', '宋代', '朱淑真', '楼外垂杨千万缕。欲系青春，少住春还去。犹自风前飘柳絮。随春且看归何处。 绿满山川闻杜宇。便做无情，莫也愁人苦。把酒送春春不语。黄昏却下潇潇雨。', '宋词三百首');
INSERT INTO `poem` VALUES (805, '踏莎行·秋入云山', '宋代', '张抡', '秋入云山，物情潇洒。百般景物堪图画。丹枫万叶碧云边，黄花千点幽岩下。 已喜佳辰，更怜清夜。一轮明月林梢挂。松醪常与野人期，忘形共说清闲话。', '宋词三百首');
INSERT INTO `poem` VALUES (806, '瑞鹤仙·郊原初过雨', '宋代', '袁去华', '郊原初过雨。见败叶零乱，风定犹舞。斜阳挂深树。映浓愁浅黛，遥山眉妩。来时旧路。尚岩花、娇黄半吐。到而今，唯有溪边流水，见人如故。 无语。邮亭深静，下马还寻，旧曾题处。无聊倦旅。伤离恨，最愁苦。纵收香藏镜，他年重到，人面桃花在否。念沈沈、小阁幽窗，有时梦去。', '宋词三百首');
INSERT INTO `poem` VALUES (807, '钗头凤·红酥手', '宋代', '陆游', '红酥手，黄縢酒，满城春色宫墙柳。东风恶，欢情薄。一怀愁绪，几年离索。错、错、错。 春如旧，人空瘦，泪痕红浥鲛绡透。桃花落，闲池阁。山盟虽在，锦书难托。莫、莫、莫！', '宋词三百首');
INSERT INTO `poem` VALUES (808, '秋波媚·七月十六日晚登高兴亭望长安南山', '宋代', '陆游', '秋到边城角声哀，烽火照高台。悲歌击筑，凭高酹酒，此兴悠哉。 多情谁似南山月，特地暮云开。灞桥烟柳，曲江池馆，应待人来。', '宋词三百首');
INSERT INTO `poem` VALUES (809, '卜算子·咏梅', '宋代', '陆游', '驿外断桥边，寂寞开无主。已是黄昏独自愁，更著风和雨。 无意苦争春，一任群芳妒。零落成泥碾作尘，只有香如故。', '宋词三百首');
INSERT INTO `poem` VALUES (810, '夜游宫·记梦寄师伯浑', '宋代', '陆游', '雪晓清笳乱起。梦游处、不知何地。铁骑无声望似水。想关河，雁门西，青海际。 睡觉寒灯里。漏声断、月斜窗纸。自许封侯在万里。有谁知，鬓虽残，心未死。', '宋词三百首');
INSERT INTO `poem` VALUES (811, '鹊桥仙·华灯纵博', '宋代', '陆游', '华灯纵博，雕鞍驰射，谁记当年豪举。酒徒一半取封侯，独去作、江边渔父。 轻舟八尺，低篷三扇，占断苹洲烟雨。镜湖元自属闲人，又何必、君恩赐与。', '宋词三百首');
INSERT INTO `poem` VALUES (812, '诉衷情·当年万里觅封侯', '宋代', '陆游', '当年万里觅封侯，匹马戍梁州。关河梦断何处？尘暗旧貂裘。 胡未灭，鬓先秋，泪空流。此生谁料，心在天山，身老沧洲。', '宋词三百首');
INSERT INTO `poem` VALUES (813, '钗头凤·世情薄', '宋代', '唐婉', '世情薄，人情恶，雨送黄昏花易落。晓风干，泪痕残。欲笺心事，独语斜阑。难，难，难！ 人成各，今非昨，病魂常似秋千索。角声寒，夜阑珊。怕人寻问，咽泪装欢。瞒，瞒，瞒！(装欢 通：妆)', '宋词三百首');
INSERT INTO `poem` VALUES (814, '卜算子·独自上层楼', '宋代', '程垓', '独自上层楼，楼外青山远。望以斜阳欲尽时，不见西飞雁。 独自下层楼，楼下蛩声怨。待到黄昏月上时，依旧柔肠断。', '宋词三百首');
INSERT INTO `poem` VALUES (815, '昭君怨·赋松上鸥', '宋代', '杨万里', '晚饮诚斋，忽有一鸥来泊松上，已而复去，感而赋之。 偶听松梢扑鹿，知是沙鸥来宿。稚子莫喧哗，恐惊他。 俄倾忽然飞去，飞去不知何处？我已乞归休，报沙鸥。', '宋词三百首');
INSERT INTO `poem` VALUES (816, '好事近·七月十三日夜登万花川谷望月作', '宋代', '杨万里', '月未到诚斋，先到万花川谷。不是诚斋无月，隔一林修竹。 如今才是十三夜，月色已如玉。未是秋光奇绝，看十五十六。', '宋词三百首');
INSERT INTO `poem` VALUES (817, '卜算子·不是爱风尘', '宋代', '严蕊', '不是爱风尘，似被前缘误。花落花开自有时，总赖东君主。 去也终须去，住也如何住！若得山花插满头，莫问奴归处。', '宋词三百首');
INSERT INTO `poem` VALUES (818, '六州歌头·长淮望断', '宋代', '张孝祥', '长淮望断，关塞莽然平。征尘暗，霜风劲，悄边声。黯销凝。追想当年事，殆天数，非人力，洙泗上，弦歌地，亦膻腥。隔水毡乡，落日牛羊下，区脱纵横。看名王宵猎，骑火一川明。笳鼓悲鸣。遣人惊。 念腰间箭，匣中剑，空埃蠹，竟何成。时易失，心徒壮，岁将零。渺神京。干羽方怀远，静烽燧，且休兵。冠盖使，纷驰骛，若为情。闻道中原遗老，常南望、翠葆霓旌。使行人到此，忠愤气填膺。有泪如倾。', '宋词三百首');
INSERT INTO `poem` VALUES (819, '水调歌头·和庞佑父', '宋代', '张孝祥', '雪洗虏尘静，风约楚云留。何人为写悲壮，吹角古城楼。湖海平生豪气，关塞如今风景，剪烛看吴钩。剩喜燃犀处，骇浪与天浮。 忆当年，周与谢，富春秋，小乔初嫁，香囊未解，勋业故优游。赤壁矶头落照，肥水桥边衰草，渺渺唤人愁。我欲乘风去，击楫誓中流。', '宋词三百首');
INSERT INTO `poem` VALUES (820, '念奴娇·过洞庭', '宋代', '张孝祥', '洞庭青草，近中秋，更无一点风色。玉界琼田三万顷，着我扁舟一叶。素月分辉，明河共影，表里俱澄澈。悠然心会，妙处难与君说。(著 同：着；玉界 一作：玉鉴) 应念岭表经年，孤光自照，肝胆皆冰雪。短发萧骚襟袖冷，稳泛沧浪空阔。尽挹西江，细斟北斗，万象为宾客。扣舷独啸，不知今夕何夕。(沧浪 一作：沧冥；岭表 一作：岭海)', '宋词三百首');
INSERT INTO `poem` VALUES (821, '西江月·问讯湖边春色', '宋代', '张孝祥', '问讯湖边春色，重来又是三年。东风吹我过湖船，杨柳丝丝拂面。 世路如今已惯，此心到处悠然。寒光亭下水如天，飞起沙鸥一片。', '宋词三百首');
INSERT INTO `poem` VALUES (822, '西江月·阻风山峰下', '宋代', '张孝祥', '满载一船秋色，平铺十里湖光。波神留我看斜阳，放起鳞鳞细浪。 明日风回更好，今宵露宿何妨？水晶宫里奏霓裳，准拟岳阳楼上。', '宋词三百首');
INSERT INTO `poem` VALUES (823, '临江仙·暮春', '宋代', '赵长卿', '过尽征鸿来尽燕，故园消息茫然。一春憔悴有谁怜。怀家寒食夜，中酒落花天。 见说江头春浪渺，殷勤欲送归船。别来此处最萦牵。短篷南浦雨，疏柳断桥烟。', '宋词三百首');
INSERT INTO `poem` VALUES (824, '摸鱼儿·更能消几番风雨', '宋代', '辛弃疾', '淳熙己亥，自湖北漕移湖南，同官王正之置酒小山亭，为赋。 更能消、几番风雨，匆匆春又归去。惜春长怕花开早，何况落红无数。春且住，见说道、天涯芳草无归路。怨春不语。算只有殷勤，画檐蛛网，尽日惹飞絮。(怕 一作：恨) (无归路 一作：迷归路） 长门事，准拟佳期又误。蛾眉曾有人妒。千金纵买相如赋，脉脉此情谁诉？君莫舞，君不见、玉环飞燕皆尘土！闲愁最苦！休去倚危栏，斜阳正在，烟柳断肠处。', '宋词三百首');
INSERT INTO `poem` VALUES (825, '水龙吟·楚天千里无云', '宋代', '曾觌', '楚天千里无云，露华洗出秋容净。银蟾台榭，玉壶天地，参差桂影。鸳瓦寒生，画檐光射，碧梧金井。听韶华半夜，江梅三弄，风袅袅、良宵永。 携手西园宴罢，下瑶台、醉魂初醒。吹箫仙子，骖鸾归路，一襟清兴。鳷鹊楼高，建章门迥，星河耿耿。看沧江潮上，丹枫叶落，浸关山冷。', '宋词三百首');
INSERT INTO `poem` VALUES (826, '菩萨蛮·书江西造口壁', '宋代', '辛弃疾', '郁孤台下清江水，中间多少行人泪。西北望长安，可怜无数山。 青山遮不住，毕竟东流去。江晚正愁余，山深闻鹧鸪。 (愁余 一作：愁予)', '宋词三百首');
INSERT INTO `poem` VALUES (827, '青玉案·元夕', '宋代', '辛弃疾', '东风夜放花千树，更吹落，星如雨。宝马雕车香满路。凤箫声动，玉壶光转，一夜鱼龙舞。 蛾儿雪柳黄金缕，笑语盈盈暗香去。众里寻他千百度，蓦然回首，那人却在，灯火阑珊处。', '宋词三百首');
INSERT INTO `poem` VALUES (828, '清平乐·村居', '宋代', '辛弃疾', '茅檐低小，溪上青青草。醉里吴音相媚好，白发谁家翁媪？ 大儿锄豆溪东，中儿正织鸡笼。最喜小儿亡赖，溪头卧剥莲蓬。(亡 通：无)', '宋词三百首');
INSERT INTO `poem` VALUES (829, '清平乐·独宿博山王氏庵', '宋代', '辛弃疾', '绕床饥鼠，蝙蝠翻灯舞。屋上松风吹急雨，破纸窗间自语。 平生塞北江南，归来华发苍颜。布被秋宵梦觉，眼前万里江山。', '宋词三百首');
INSERT INTO `poem` VALUES (830, '水龙吟·过南剑双溪楼', '宋代', '辛弃疾', '举头西北浮云，倚天万里须长剑。人言此地，夜深长见，斗牛光焰。我觉山高，潭空水冷，月明星淡。待燃犀下看，凭栏却怕，风雷怒，鱼龙惨。 峡束苍江对起，过危楼，欲飞还敛。元龙老矣！不妨高卧，冰壶凉簟。千古兴亡，百年悲笑，一时登览。问何人又卸，片帆沙岸，系斜阳缆？', '宋词三百首');
INSERT INTO `poem` VALUES (831, '西江月·夜行黄沙道中', '宋代', '辛弃疾', '明月别枝惊鹊，清风半夜鸣蝉。稻花香里说丰年，听取蛙声一片。 七八个星天外，两三点雨山前。旧时茅店社林边，路转溪桥忽见。(溪桥 一作：溪头)', '宋词三百首');
INSERT INTO `poem` VALUES (832, '贺新郎·别茂嘉十二弟', '宋代', '辛弃疾', '别茂嘉十二弟。鹈鴂、杜鹃实两种，见《离骚补注》 绿树听鹈鴂。更那堪、鹧鸪声住，杜鹃声切。啼到春归无寻处，苦恨芳菲都歇。算未抵、人间离别。马上琵琶关塞黑，更长门、翠辇辞金阙。看燕燕，送归妾。 将军百战身名裂。向河梁、回头万里，故人长绝。易水萧萧西风冷，满座衣冠似雪。正壮士、悲歌未彻。啼鸟还知如许恨，料不啼清泪长啼血。谁共我，醉明月。', '宋词三百首');
INSERT INTO `poem` VALUES (833, '贺新郎·甚矣吾衰矣', '宋代', '辛弃疾', '邑中园亭，仆皆为赋此词。一日，独坐停云，水声山色，竞来相娱。意溪山欲援例者，遂作数语，庶几仿佛渊明思亲友之意云。 甚矣吾衰矣。怅平生、交游零落，只今馀几！白发空垂三千丈，一笑人间万事。问何物、能令公喜？我见青山多妩媚，料青山见我应如是。情与貌，略相似。 一尊搔首东窗里。想渊明《停云》诗就，此时风味。江左沉酣求名者，岂识浊醪妙理。回首叫、云飞风起。不恨古人吾不见，恨古人不见吾狂耳。知我者，二三子。', '宋词三百首');
INSERT INTO `poem` VALUES (834, '丑奴儿·书博山道中壁', '宋代', '辛弃疾', '少年不识愁滋味，爱上层楼。爱上层楼，为赋新词强说愁。 而今识尽愁滋味，欲说还休。欲说还休，却道天凉好个秋。', '宋词三百首');
INSERT INTO `poem` VALUES (835, '太常引·建康中秋夜为吕叔潜赋', '宋代', '辛弃疾', '一轮秋影转金波，飞镜又重磨。把酒问姮娥：被白发、欺人奈何？ 乘风好去，长空万里，直下看山河。斫去桂婆娑，人道是、清光更多。', '宋词三百首');
INSERT INTO `poem` VALUES (836, '破阵子·为陈同甫赋壮词以寄之', '宋代', '辛弃疾', '醉里挑灯看剑，梦回吹角连营。八百里分麾下炙，五十弦翻塞外声。沙场秋点兵。 马作的卢飞快，弓如霹雳弦惊。了却君王天下事，赢得生前身后名。可怜白发生！', '宋词三百首');
INSERT INTO `poem` VALUES (837, '鹧鸪天·有客慨然谈功名因追念少年时事戏作', '宋代', '辛弃疾', '壮岁旌旗拥万夫，锦襜突骑渡江初。燕兵夜娖银胡觮，汉箭朝飞金仆姑。 追往事，叹今吾，春风不染白髭须。却将万字平戎策，换得东家种树书。', '宋词三百首');
INSERT INTO `poem` VALUES (838, '西江月·遣兴', '宋代', '辛弃疾', '醉里且贪欢笑，要愁那得工夫。近来始觉古人书，信著全无是处。 昨夜松边醉倒，问松我醉何如。只疑松动要来扶，以手推松曰去。', '宋词三百首');
INSERT INTO `poem` VALUES (839, '永遇乐·京口北固亭怀古', '宋代', '辛弃疾', '千古江山，英雄无觅，孙仲谋处。舞榭歌台，风流总被雨打风吹去。斜阳草树，寻常巷陌，人道寄奴曾住。想当年，金戈铁马，气吞万里如虎。 元嘉草草，封狼居胥，赢得仓皇北顾。四十三年，望中犹记，烽火扬州路。可堪回首，佛狸祠下，一片神鸦社鼓。凭谁问，廉颇老矣，尚能饭否？', '宋词三百首');
INSERT INTO `poem` VALUES (840, '南乡子·登京口北固亭有怀', '宋代', '辛弃疾', '何处望神州？满眼风光北固楼。千古兴亡多少事？悠悠。不尽长江滚滚流。 年少万兜鍪，坐断东南战未休。天下英雄谁敌手？曹刘。生子当如孙仲谋。', '宋词三百首');
INSERT INTO `poem` VALUES (841, '卜算子·见也如何暮', '宋代', '石孝友', '见也如何暮。别也如何遽。别也应难见也难，后会难凭据。 去也如何去。住也如何住。住也应难去也难，此际难分付。', '宋词三百首');
INSERT INTO `poem` VALUES (842, '水调歌头·送章德茂大卿使虏', '宋代', '陈亮', '不见南师久，漫说北群空。当场只手，毕竟还我万夫雄。自笑堂堂汉使，得似洋洋河水，依旧只流东？且复穹庐拜，会向藁街逢！ 尧之都，舜之壤，禹之封。于中应有，一个半个耻臣戎！万里腥膻如许，千古英灵安在，磅礴几时通？胡运何须问，赫日自当中！', '宋词三百首');
INSERT INTO `poem` VALUES (843, '水龙吟·春恨', '宋代', '陈亮', '闹花深处层楼，画帘半卷东风软。春归翠陌，平莎茸嫩，垂杨金浅。迟日催花，淡云阁雨，轻寒轻暖。恨芳菲世界，游人未赏，都付与、莺和燕。 寂寞凭高念远。向南楼、一声归雁。金钗斗草，青丝勒马，风流云散。罗绶分香，翠绡对泪，几多幽怨。正销魂，又是疏烟淡月，子规声断。', '宋词三百首');
INSERT INTO `poem` VALUES (844, '唐多令·芦叶满汀洲', '宋代', '刘过', '安远楼小集，侑觞歌板之姬黄其姓者，乞词于龙洲道人，为赋此《唐多令》。同柳阜之、刘去非、石民瞻、周嘉仲、陈孟参、孟容。时八月五日也。 芦叶满汀洲，寒沙带浅流。二十年重过南楼。柳下系船犹未稳，能几日，又中秋。 黄鹤断矶头，故人今在否？旧江山浑是新愁。欲买桂花同载酒，终不似，少年游。(在否 一作：在不)', '宋词三百首');
INSERT INTO `poem` VALUES (845, '西江月·堂上谋臣尊俎', '宋代', '刘过', '堂上谋臣尊俎，边头将士干戈。天时地利与人和，“燕可伐欤？”曰：“可”。 今日楼台鼎鼐，明年带砺山河。大家齐唱《大风歌》，不日四方来贺。', '宋词三百首');
INSERT INTO `poem` VALUES (846, '点绛唇·丁未冬过吴松作', '宋代', '姜夔', '燕雁无心，太湖西畔随云去。数峰清苦。商略黄昏雨。 第四桥边，拟共天随住。今何许。凭阑怀古。残柳参差舞。', '宋词三百首');
INSERT INTO `poem` VALUES (847, '踏莎行·自沔东来丁未元日至金陵江上感梦而作', '宋代', '姜夔', '燕燕轻盈，莺莺娇软，分明又向华胥见。夜长争得薄情知？春初早被相思染。 别后书辞，别时针线，离魂暗逐郎行远。淮南皓月冷千山，冥冥归去无人管。', '宋词三百首');
INSERT INTO `poem` VALUES (848, '鹧鸪天·己酉之秋苕溪记所见', '宋代', '姜夔', '京洛风流绝代人。因何风絮落溪津。笼鞋浅出鸦头袜，知是凌波缥缈身。 红乍笑，绿长嚬。与谁同度可怜春。鸳鸯独宿何曾惯，化作西楼一缕云。', '宋词三百首');
INSERT INTO `poem` VALUES (849, '念奴娇·闹红一舸', '宋代', '姜夔', '余客武陵。湖北宪治在焉：古城野水，乔木参天。余与二三友，日荡舟其间。薄荷花而饮，意象幽闲，不类人境。秋水且涸。荷叶出地寻丈，因列坐其下，上不见日。清风徐来，绿云自动。间于疏处，窥见游人画船，亦一乐也。朅来吴兴。数得相羊荷花中，又夜泛西湖，光景奇绝。故以此句写之。 闹红一舸，记来时，尝与鸳鸯为侣，三十六陂人未到，水佩风裳无数。翠叶吹凉，玉容消酒，更洒菇蒲雨。嫣然摇动，冷香飞上诗句。 日暮，青盖亭亭，情人不见，争忍凌波去？只恐舞衣寒易落，愁人西风南浦。高柳垂阴，老鱼吹浪，留我花间住。田田多少，几回沙际归路。(愁人 一作：愁入)', '宋词三百首');
INSERT INTO `poem` VALUES (850, '齐天乐·蟋蟀', '宋代', '姜夔', '丙辰岁，与张功父会饮张达可之堂。闻屋壁间蟋蟀有声，功父约予同赋，以授歌者。功父先成，辞甚美。予裴回末利花间，仰见秋月，顿起幽思，寻亦得此。蟋蟀，中都呼为促织，善斗。好事者或以三二十万钱致一枚，镂象齿为楼观以贮之。 庾郎先自吟愁赋，凄凄更闻私语。露湿铜铺，苔侵石井，都是曾听伊处。哀音似诉。正思妇无眠，起寻机杼。曲曲屏山，夜凉独自甚情绪？ 西窗又吹暗雨。为谁频断续，相和砧杵？候馆迎秋，离宫吊月，别有伤心无数。豳诗漫与。笑篱落呼灯，世间儿女。写入琴丝，一声声更苦。', '宋词三百首');
INSERT INTO `poem` VALUES (851, '扬州慢·淮左名都', '宋代', '姜夔', '淳熙丙申至日，予过维扬。夜雪初霁，荠麦弥望。入其城，则四顾萧条，寒水自碧，暮色渐起，戍角悲吟。予怀怆然，感慨今昔，因自度此曲。千岩老人以为有“黍离”之悲也。 淮左名都，竹西佳处，解鞍少驻初程。过春风十里。尽荠麦青青。自胡马窥江去后，废池乔木，犹厌言兵。渐黄昏，清角吹寒。都在空城。 杜郎俊赏，算而今、重到须惊。纵豆蔻词工，青楼梦好，难赋深情。二十四桥仍在，波心荡、冷月无声。念桥边红药，年年知为谁生。', '宋词三百首');
INSERT INTO `poem` VALUES (852, '长亭怨慢·渐吹尽', '宋代', '姜夔', '余颇喜自制曲。初率意为长短句，然后协以律，故前后阕多不同。桓大司马云：“昔年种柳，依依汉南。今看摇落，凄怆江潭：树犹如此，人何以堪？”此语余深爱之。 渐吹尽，枝头香絮，是处人家，绿深门户。远浦萦回，暮帆零乱向何许？阅人多矣，谁得似长亭树？树若有情时，不会得青青如此！ 日暮，望高城不见，只见乱山无数。韦郎去也，怎忘得、玉环分付：第一是早早归来，怕红萼无人为主。算空有并刀，难剪离愁千缕。', '宋词三百首');
INSERT INTO `poem` VALUES (853, '暗香·旧时月色', '宋代', '姜夔', '辛亥之冬，余载雪诣石湖。止既月，授简索句，且征新声，作此两曲，石湖把玩不已，使二妓肆习之，音节谐婉，乃名之曰《暗香》、《疏影》。 旧时月色，算几番照我，梅边吹笛？唤起玉人，不管清寒与攀摘。何逊而今渐老，都忘却春风词笔。但怪得竹外疏花，香冷入瑶席。 江国，正寂寂，叹寄与路遥，夜雪初积。翠尊易泣，红萼无言耿相忆。长记曾携手处，千树压、西湖寒碧。又片片、吹尽也，几时见得？', '宋词三百首');
INSERT INTO `poem` VALUES (854, '疏影·苔枝缀玉', '宋代', '姜夔', '辛亥之冬，余载雪诣石湖。止既月，授简索句，且征新声，作此两曲，石湖把玩不已，使二妓肆习之，音节谐婉，乃名之曰《暗香》、《疏影》。 苔枝缀玉，有翠禽小小，枝上同宿。客里相逢，篱角黄昏，无言自倚修竹。昭君不惯胡沙远，但暗忆、江南江北。想佩环、月夜归来，化作此花幽独。 犹记深宫旧事，那人正睡里，飞近蛾绿。莫似春风，不管盈盈，早与安排金屋。还教一片随波去，又却怨、玉龙哀曲。等恁时、重觅幽香，已入小窗横幅。', '宋词三百首');
INSERT INTO `poem` VALUES (855, '风入松·一春长费买花钱', '宋代', '俞国宝', '一春长费买花钱，日日醉湖边。玉骢惯识西湖路，骄嘶过、沽酒楼前。红杏香中箫鼓，绿杨影里秋千。 暖风十里丽人天，花压鬓云偏。画船载取春归去，馀情付、湖水湖烟。明日重扶残醉，来寻陌上花钿。', '宋词三百首');
INSERT INTO `poem` VALUES (856, '柳梢青·岳阳楼', '宋代', '戴复古', '袖剑飞吟。洞庭青草，秋水深深。万顷波光，岳阳楼上，一快披襟。 不须携酒登临。问有酒、何人共斟？变尽人间，君山一点，自古如今。', '宋词三百首');
INSERT INTO `poem` VALUES (857, '减字木兰花·莎衫筠笠', '宋代', '卢炳', '莎衫筠笠。正是村村农务急。绿水千畦。惭愧秧针出得齐。 风斜雨细。麦欲黄时寒又至。馌妇耕夫。画作今年稔岁图。', '宋词三百首');
INSERT INTO `poem` VALUES (858, '双双燕·咏燕', '宋代', '史达祖', '过春社了，度帘幕中间，去年尘冷。差池欲住，试入旧巢相并。还相雕梁藻井。又软语、商量不定。飘然快拂花梢，翠尾分开红影。 芳径。芹泥雨润。爱贴地争飞，竞夸轻俊。红楼归晚，看足柳昏花暝。应自栖香正稳。便忘了、天涯芳信。愁损翠黛双蛾，日日画阑独凭。', '宋词三百首');
INSERT INTO `poem` VALUES (859, '绮罗香·咏春雨', '宋代', '史达祖', '做冷欺花，将烟困柳，千里偷催春暮。尽日冥迷，愁里欲飞还住。惊粉重、蝶宿西园，喜泥润、燕归南浦。最妨它、佳约风流，钿车不到杜陵路。 沉沉江上望极，还被春潮晚急，难寻官渡。隐约遥峰，和泪谢娘眉妩。临断岸、新绿生时，是落红、带愁流处。记当日、门掩梨花，剪灯深夜语。', '宋词三百首');
INSERT INTO `poem` VALUES (860, '江城子·画楼帘幕卷新晴', '宋代', '卢祖皋', '画楼帘幕卷新晴。掩银屏。晓寒轻。坠粉飘香，日日唤愁生。暗数十年湖上路，能几度，著娉婷。 年华空自感飘零。拥春酲。对谁醒。天阔云间，无处觅箫声。载酒买花年少事，浑不似，旧心情。', '宋词三百首');
INSERT INTO `poem` VALUES (861, '宴清都·初春', '宋代', '卢祖皋', '春讯飞琼管。风日薄、度墙啼鸟声乱。江城次第，笙歌翠合，绮罗香暖。溶溶涧渌冰泮。醉梦里、年华暗换。料黛眉重锁隋堤，芳心还动梁苑。 新来雁阔云音，鸾分鉴影，无计重见。啼春细雨，笼愁澹月，恁时庭院。离肠未语先断。算犹有、凭高望眼。更那堪、芳草连天，飞梅弄晚。', '宋词三百首');
INSERT INTO `poem` VALUES (862, '浪淘沙·莫上玉楼看', '宋代', '韩疁', '莫上玉楼看。花雨斑斑。四垂罗幕护朝寒。燕子不知人去也，飞认阑干。 回首几关山。后会应难。相逢祗有梦魂间，可奈梦随春漏短，不到江南。', '宋词三百首');
INSERT INTO `poem` VALUES (863, '霜天晓角·仪真江上夜泊', '宋代', '黄机', '寒江夜宿。长啸江之曲。水底鱼龙惊动，风卷地、浪翻屋。 诗情吟未足。酒兴断还续。草草兴亡休问，功名泪、欲盈掬。', '宋词三百首');
INSERT INTO `poem` VALUES (864, '玉楼春·春思', '宋代', '严仁', '春风只在园西畔，荠菜花繁胡蝶乱。冰池晴绿照还空，香径落红吹已断。 意长翻恨游丝短，尽日相思罗带缓。宝奁明月不欺人，明日归来君试看。', '宋词三百首');
INSERT INTO `poem` VALUES (865, '沁园春·答九华叶贤良', '宋代', '刘克庄', '一卷阴符，二石硬弓，百斤宝刀。更玉花骢喷，鸣鞭电抹，乌丝阑展，醉墨龙跳。牛角书生，虬髯豪客，谈笑皆堪折简招。依稀记，曾请缨系粤，草檄征辽。 当年目视云霄。谁信道凄凉今折腰。怅燕然未勒，南归草草，长安不见，北望迢迢。老去胸中，有些磊块，歌罢犹须著酒浇。休休也，但帽边鬓改，镜里颜凋。', '宋词三百首');
INSERT INTO `poem` VALUES (866, '长相思·惜梅', '宋代', '刘克庄', '寒相催。暖相催。催了开时催谢时。丁宁花放迟。 角声吹。笛声吹。吹了南枝吹北枝。明朝成雪飞。', '宋词三百首');
INSERT INTO `poem` VALUES (867, '贺新郎·送陈真州子华', '宋代', '刘克庄', '北望神州路。试平章、这场公事，怎生分付。记得太行山百万，曾入宗爷驾驭。今把作、握蛇骑虎。君去京东豪杰喜，想投戈、下拜真吾父。谈笑里，定齐鲁。 两河萧瑟惟狐兔。问当年、祖生去后，有人来否。多少新亭挥泪客，谁梦中原块土。算事业、须由人做。应笑书生心胆怯，向车中、闭置如新妇。空目送，塞鸿去。', '宋词三百首');
INSERT INTO `poem` VALUES (868, '贺新郎·九日', '宋代', '刘克庄', '湛湛长空黑。更那堪、斜风细雨，乱愁如织。老眼平生空四海，赖有高楼百尺。看浩荡、千崖秋色。白发书生神州泪，尽凄凉、不向牛山滴。追往事，去无迹。 少年自负凌云笔。到而今、春华落尽，满怀萧瑟。常恨世人新意少，爱说南朝狂客。把破帽、年年拈出。若对黄花孤负酒，怕黄花、也笑人岑寂。鸿北去，日西匿。', '宋词三百首');
INSERT INTO `poem` VALUES (869, '玉楼春·戏林推', '宋代', '刘克庄', '年年跃马长安市。客舍似家家似寄。青钱换酒日无何，红烛呼卢宵不寐。 易挑锦妇机中字。难得玉人心下事。男儿西北有神州，莫滴水西桥畔泪。', '宋词三百首');
INSERT INTO `poem` VALUES (870, '卜算子·片片蝶衣轻', '宋代', '刘克庄', '片片蝶衣轻，点点猩红小。道是天公不惜花，百种千般巧。 朝见树头繁，暮见枝头少。道是天公果惜花，雨洗风吹了。', '宋词三百首');
INSERT INTO `poem` VALUES (871, '一剪梅·舟过吴江', '宋代', '蒋捷', '一片春愁待酒浇。江上舟摇，楼上帘招。秋娘渡与泰娘桥，风又飘飘，雨又萧萧。（渡 一作：度。桥 一作：娇） 何日归家洗客袍？银字笙调，心字香烧。流光容易把人抛，红了樱桃，绿了芭蕉。', '宋词三百首');
INSERT INTO `poem` VALUES (872, '虞美人·听雨', '宋代', '蒋捷', '少年听雨歌楼上。红烛昏罗帐。壮年听雨客舟中。江阔云低、断雁叫西风。 而今听雨僧庐下。鬓已星星也。悲欢离合总无情。一任阶前、点滴到天明。', '宋词三百首');
INSERT INTO `poem` VALUES (873, '满江红·送李御带珙', '宋代', '吴潜', '红玉阶前，问何事、翩然引去。湖海上、一汀鸥鹭，半帆烟雨。报国无门空自怨，济时有策从谁吐。过垂虹亭下系扁舟，鲈堪煮。 拚一醉，留君住。歌一曲，送君路。遍江南江北，欲归何处。世事悠悠浑未了，年光冉冉今如许。试举头、一笑问青天，天无语。', '宋词三百首');
INSERT INTO `poem` VALUES (874, '江城子·示表侄刘国华', '宋代', '吴潜', '家园十亩屋头边。正春妍，酿花天。杨柳多情，拂拂带轻烟。别馆闲亭随分有，时策杖，小盘旋。 采山钓水美而鲜。饮中仙，醉中禅。闲处光阴，赢得日高眠。一品高官人道好，多少事，碎心田。', '宋词三百首');
INSERT INTO `poem` VALUES (875, '蓦山溪·自述', '宋代', '宋自逊', '壶山居士，未老心先懒。爱学道人家，办竹几、蒲团茗碗。青山可买，小结屋三间，开一径，俯清溪，修竹栽教满。 客来便请，随分家常饭。若肯小留连，更薄酒，三杯两盏，吟诗度曲，风月任招呼。身外事，不关心，自有天公管。', '宋词三百首');
INSERT INTO `poem` VALUES (876, '谒金门·花过雨', '宋代', '李好古', '花过雨，又是一番红素。燕子归来愁不语，旧巢无觅处。 谁在玉关劳苦？谁在玉楼歌舞？若使胡尘吹得去，东风侯万户。', '宋词三百首');
INSERT INTO `poem` VALUES (877, '青玉案·年年社日停针线', '宋代', '黄公绍', '年年社日停针线。怎忍见、双飞燕。今日江城春已半。一身犹在，乱山深处，寂寞溪桥畔。 春衫著破谁针线。点点行行泪痕满。落日解鞍芳草岸。花无人戴，酒无人劝，醉也无人管。', '宋词三百首');
INSERT INTO `poem` VALUES (878, '湘春夜月·近清明', '宋代', '黄孝迈', '近清明。翠禽枝上消魂。可惜一片清歌，都付与黄昏。欲共柳花低诉，怕柳花轻薄，不解伤春。念楚乡旅宿，柔情别绪，谁与温存。 空樽夜泣，青山不语，残月当门。翠玉楼前，惟是有、一波湘水，摇荡湘云。天长梦短，问甚时、重见桃根。这次第，算人间没个并刀，剪断心上愁痕。', '宋词三百首');
INSERT INTO `poem` VALUES (879, '长相思·花深深', '宋代', '陈东甫', '花深深。柳阴阴。度柳穿花觅信音。君心负妾心。 怨呜琴。恨孤衾。钿誓钗盟何处寻。当初谁料今。', '宋词三百首');
INSERT INTO `poem` VALUES (880, '水调歌头·平山堂用东坡韵', '宋代', '方岳', '秋雨一何碧，山色倚晴空。江南江北愁思，分付酒螺红。芦叶蓬舟千重，菰菜莼羹一梦，无语寄归鸿。醉眼渺河洛，遗恨夕阳中。 苹洲外，山欲暝，敛眉峰。人间俯仰陈迹，叹息两仙翁。不见当时杨柳，只是从前烟雨，磨灭几英雄。天地一孤啸，匹马又西风。', '宋词三百首');
INSERT INTO `poem` VALUES (881, '浣溪沙·门隔花深梦旧游', '宋代', '吴文英', '门隔花深梦旧游。夕阳无语燕归愁。玉纤香动小帘钩。 落絮无声春堕泪，行云有影月含羞。东风临夜冷于秋。', '宋词三百首');
INSERT INTO `poem` VALUES (882, '风入松·听风听雨过清明', '宋代', '吴文英', '听风听雨过清明。愁草瘗花铭。楼前绿暗分携路，一丝柳、一寸柔情。料峭春寒中酒，交加晓梦啼莺。 西园日日扫林亭。依旧赏新晴。黄蜂频扑秋千索，有当时、纤手香凝。惆怅双鸳不到，幽阶一夜苔生。', '宋词三百首');
INSERT INTO `poem` VALUES (883, '莺啼序·春晚感怀', '宋代', '吴文英', '　　残寒正欺病酒，掩沉香绣户。燕来晚、飞入西城，似说春事迟暮。画船载、清明过却，晴烟冉冉吴宫树。念羁情、游荡随风，化为轻絮。 　　十载西湖，傍柳系马，趁娇尘软雾。溯红渐招入仙溪，锦儿偷寄幽素，倚银屏、春宽梦窄，断红湿、歌纨金缕。暝堤空，轻把斜阳，总还鸥鹭。 　　幽兰旋老，杜若还生，水乡尚寄旅。别后访、六桥无信，事往花委，瘗玉埋香，几番风雨。长波妒盼，遥山羞黛，渔灯分影春江宿。记当时、短楫桃根渡，青楼仿佛，临分败壁题诗，泪墨惨淡尘土。危亭望极，草色天涯，叹鬓侵半苎。暗点检、离痕欢唾，尚染鲛绡，亸凤迷归，破鸾慵舞。殷勤待写，书中长恨，蓝霞辽海沉过雁。漫相思、弹入哀筝柱。伤心千里江南，怨曲重招，断魂在否？', '宋词三百首');
INSERT INTO `poem` VALUES (884, '八声甘州·灵岩陪庾幕诸公游', '宋代', '吴文英', '渺空烟四远，是何年、青天坠长星？幻苍崖云树，名娃金屋，残霸宫城。箭径酸风射眼，腻水染花腥。时靸双鸳响，廊叶秋声。 宫里吴王沉醉，倩五湖倦客，独钓醒醒。问苍波无语，华发奈山青。水涵空、阑干高处，送乱鸦斜日落渔汀。连呼酒、上琴台去，秋与云平。', '宋词三百首');
INSERT INTO `poem` VALUES (885, '唐多令·惜别', '宋代', '吴文英', '何处合成愁。离人心上秋。纵芭蕉、不雨也飕飕。都道晚凉天气好，有明月、怕登楼。 年事梦中休。花空烟水流。燕辞归、客尚淹留。垂柳不萦裙带住。漫长是、系行舟。', '宋词三百首');
INSERT INTO `poem` VALUES (886, '清平乐·宫怨', '宋代', '黄升', '珠帘寂寂。愁背银缸泣。记得少年初选入。三十六宫第一。 当年掌上承恩。而今冷落长门。又是羊车过也，月明花落黄昏。', '宋词三百首');
INSERT INTO `poem` VALUES (887, '贺新郎·西湖', '宋代', '文及翁', '一勺西湖水。渡江来，百年歌舞，百年酣醉。回首洛阳花石尽，烟渺黍离之地。更不复、新亭堕泪。簇乐红妆摇画舫，问中流、击楫何人是？千古恨，几时洗？ 余生自负澄清志。更有谁、磻溪未遇，傅岩未起。国事如今谁倚仗，衣带一江而已！便都道、江神堪恃。借问孤山林处士，但掉头、笑指梅花蕊。天下事，可知矣！', '宋词三百首');
INSERT INTO `poem` VALUES (888, '西江月·新秋写兴', '宋代', '刘辰翁', '天上低昂似旧，人间儿女成狂。夜来处处试新妆。却是人间天上。 不觉新凉似水，相思两鬓如霜。梦从海底跨枯桑。阅尽银河风浪。', '宋词三百首');
INSERT INTO `poem` VALUES (889, '兰陵王·丙子送春', '宋代', '刘辰翁', '送春去。春去人间无路。秋千外，芳草连天，谁遣风沙暗南浦。依依甚意绪。漫忆海门飞絮。乱鸦过，斗转城荒，不见来时试灯处。 春去。最谁苦。但箭雁沉边，梁燕无主。杜鹃声里长门暮。想玉树凋土，泪盘如露。咸阳送客屡回顾。斜日未能度。 春去。尚来否。正江令恨别，庾信愁赋。二人皆北去。苏堤尽日风和雨。叹神游故国，花记前度。人生流落，顾孺子，共夜语。', '宋词三百首');
INSERT INTO `poem` VALUES (890, '满江红·题南京夷山驿', '宋代', '王清惠', '太液芙蓉，浑不似、旧时颜色。 曾记得、春风雨露，玉楼金阙。名播兰馨妃后里，晕潮莲脸君王侧。忽一声、鼙鼓揭天来，繁华歇。 龙虎散，风云灭。千古恨，凭谁说。对山河百二，泪盈襟血。客馆夜惊尘土梦，宫车晓碾关山月。问嫦娥、于我肯从容，同圆缺？', '宋词三百首');
INSERT INTO `poem` VALUES (891, '八声甘州·记玉关踏雪事清游', '宋代', '张炎', '辛卯岁，沈尧道同余北归，各处杭、越。逾岁，尧道来问寂寞，语笑数日。又复别去。赋此曲，并寄赵学舟。 记玉关踏雪事清游，寒气脆貂裘。傍枯林古道，长河饮马，此意悠悠。短梦依然江表，老泪洒西州。一字无题处，落叶都愁。 载取白云归去，问谁留楚佩，弄影中洲？折芦花赠远，零落一身秋。向寻常、野桥流水，待招来，不是旧沙鸥。空怀感，有斜阳处，却怕登楼。', '宋词三百首');
INSERT INTO `poem` VALUES (892, '清平乐·候蛩凄断', '宋代', '张炎', '候蛩凄断，人语西风岸。月落沙平江似练，望尽芦花无雁。 暗教愁损兰成，可怜夜夜关情。只有一枝梧叶，不知多少秋声！', '宋词三百首');
INSERT INTO `poem` VALUES (893, '水调歌头·明月几时有', '宋代', '苏轼', '丙辰中秋，欢饮达旦，大醉，作此篇，兼怀子由。 明月几时有？把酒问青天。不知天上宫阙，今夕是何年。我欲乘风归去，又恐琼楼玉宇，高处不胜寒。起舞弄清影，何似在人间？(何似 一作：何时；又恐 一作：惟 / 唯恐) 转朱阁，低绮户，照无眠。不应有恨，何事长向别时圆？人有悲欢离合，月有阴晴圆缺，此事古难全。但愿人长久，千里共婵娟。(长向 一作：偏向)', '宋词精选');
INSERT INTO `poem` VALUES (894, '卜算子·咏梅', '宋代', '陆游', '驿外断桥边，寂寞开无主。已是黄昏独自愁，更著风和雨。 无意苦争春，一任群芳妒。零落成泥碾作尘，只有香如故。', '宋词精选');
INSERT INTO `poem` VALUES (895, '声声慢·寻寻觅觅', '宋代', '李清照', '寻寻觅觅，冷冷清清，凄凄惨惨戚戚。乍暖还寒时候，最难将息。三杯两盏淡酒，怎敌他、晚来风急？雁过也，正伤心，却是旧时相识。 满地黄花堆积。憔悴损，如今有谁堪摘？守着窗儿，独自怎生得黑？梧桐更兼细雨，到黄昏、点点滴滴。这次第，怎一个愁字了得！（守着窗儿 一作：守著窗儿）', '宋词精选');
INSERT INTO `poem` VALUES (896, '武陵春·春晚', '宋代', '李清照', '风住尘香花已尽，日晚倦梳头。物是人非事事休，欲语泪先流。 闻说双溪春尚好，也拟泛轻舟。只恐双溪舴艋舟，载不动许多愁。', '宋词精选');
INSERT INTO `poem` VALUES (897, '雨霖铃·寒蝉凄切', '宋代', '柳永', '寒蝉凄切，对长亭晚，骤雨初歇。都门帐饮无绪，留恋处，兰舟催发。执手相看泪眼，竟无语凝噎。念去去，千里烟波，暮霭沉沉楚天阔。 多情自古伤离别，更那堪，冷落清秋节！今宵酒醒何处？杨柳岸，晓风残月。此去经年，应是良辰好景虚设。便纵有千种风情，更与何人说？(好景 一作：美景)', '宋词精选');
INSERT INTO `poem` VALUES (898, '浣溪沙·一曲新词酒一杯', '宋代', '晏殊', '一曲新词酒一杯，去年天气旧亭台。夕阳西下几时回？ 无可奈何花落去，似曾相识燕归来。小园香径独徘徊。', '宋词精选');
INSERT INTO `poem` VALUES (899, '如梦令·常记溪亭日暮', '宋代', '李清照', '常记溪亭日暮，沉醉不知归路。 兴尽晚回舟，误入藕花深处。 争渡，争渡，惊起一滩鸥鹭。', '宋词精选');
INSERT INTO `poem` VALUES (900, '一剪梅·红藕香残玉簟秋', '宋代', '李清照', '红藕香残玉簟秋。轻解罗裳，独上兰舟。云中谁寄锦书来，雁字回时，月满西楼。 花自飘零水自流。一种相思，两处闲愁。此情无计可消除，才下眉头，却上心头。', '宋词精选');
INSERT INTO `poem` VALUES (901, '如梦令·昨夜雨疏风骤', '宋代', '李清照', '昨夜雨疏风骤，浓睡不消残酒。试问卷帘人，却道海棠依旧。知否，知否？应是绿肥红瘦。', '宋词精选');
INSERT INTO `poem` VALUES (902, '鹊桥仙·纤云弄巧', '宋代', '秦观', '纤云弄巧，飞星传恨，银汉迢迢暗度。金风玉露一相逢，便胜却人间无数。(度 通：渡) 柔情似水，佳期如梦，忍顾鹊桥归路。两情若是久长时，又岂在朝朝暮暮。', '宋词精选');
INSERT INTO `poem` VALUES (903, '醉花阴·薄雾浓云愁永昼', '宋代', '李清照', '薄雾浓云愁永昼，瑞脑消金兽。佳节又重阳，玉枕纱橱，半夜凉初透。(橱 通：厨) 东篱把酒黄昏后，有暗香盈袖。莫道不销魂，帘卷西风，人比黄花瘦。(人比 一作：人似；销 一作：消)', '宋词精选');
INSERT INTO `poem` VALUES (904, '丑奴儿·书博山道中壁', '宋代', '辛弃疾', '少年不识愁滋味，爱上层楼。爱上层楼，为赋新词强说愁。 而今识尽愁滋味，欲说还休。欲说还休，却道天凉好个秋。', '宋词精选');
INSERT INTO `poem` VALUES (905, '苏幕遮·怀旧', '宋代', '范仲淹', '碧云天，黄叶地。秋色连波，波上寒烟翠。山映斜阳天接水。芳草无情，更在斜阳外。 黯乡魂，追旅思。夜夜除非，好梦留人睡。明月楼高休独倚。酒入愁肠，化作相思泪。(留人睡 一作：留人醉)', '宋词精选');
INSERT INTO `poem` VALUES (906, '钗头凤·红酥手', '宋代', '陆游', '红酥手，黄縢酒，满城春色宫墙柳。东风恶，欢情薄。一怀愁绪，几年离索。错、错、错。 春如旧，人空瘦，泪痕红浥鲛绡透。桃花落，闲池阁。山盟虽在，锦书难托。莫、莫、莫！', '宋词精选');
INSERT INTO `poem` VALUES (907, '卜算子·我住长江头', '宋代', '李之仪', '我住长江头，君住长江尾。日日思君不见君，共饮长江水。 此水几时休，此恨何时已。只愿君心似我心，定不负相思意。', '宋词精选');
INSERT INTO `poem` VALUES (908, '蝶恋花·庭院深深深几许', '宋代', '欧阳修', '庭院深深深几许，杨柳堆烟，帘幕无重数。玉勒雕鞍游冶处，楼高不见章台路。 雨横风狂三月暮，门掩黄昏，无计留春住。泪眼问花花不语，乱红飞过秋千去。', '宋词精选');
INSERT INTO `poem` VALUES (909, '渔家傲·天接云涛连晓雾', '宋代', '李清照', '天接云涛连晓雾，星河欲转千帆舞。仿佛梦魂归帝所。闻天语，殷勤问我归何处。 我报路长嗟日暮，学诗谩有惊人句。九万里风鹏正举。风休住，蓬舟吹取三山去！', '宋词精选');
INSERT INTO `poem` VALUES (910, '生查子·元夕', '宋代', '欧阳修', '去年元夜时，花市灯如昼。 月上柳梢头，人约黄昏后。 今年元夜时，月与灯依旧。 不见去年人，泪湿春衫袖。(泪湿 ：一作：满)', '宋词精选');
INSERT INTO `poem` VALUES (911, '诉衷情·当年万里觅封侯', '宋代', '陆游', '当年万里觅封侯，匹马戍梁州。关河梦断何处？尘暗旧貂裘。 胡未灭，鬓先秋，泪空流。此生谁料，心在天山，身老沧洲。', '宋词精选');
INSERT INTO `poem` VALUES (912, '钗头凤·世情薄', '宋代', '唐婉', '世情薄，人情恶，雨送黄昏花易落。晓风干，泪痕残。欲笺心事，独语斜阑。难，难，难！ 人成各，今非昨，病魂常似秋千索。角声寒，夜阑珊。怕人寻问，咽泪装欢。瞒，瞒，瞒！(装欢 通：妆)', '宋词精选');
INSERT INTO `poem` VALUES (913, '水龙吟·登建康赏心亭', '宋代', '辛弃疾', '楚天千里清秋，水随天去秋无际。遥岑远目，献愁供恨，玉簪螺髻。落日楼头，断鸿声里，江南游子。把吴钩看了，栏杆拍遍，无人会，登临意。(栏杆 一作：阑干) 休说鲈鱼堪脍，尽西风，季鹰归未？求田问舍，怕应羞见，刘郎才气。可惜流年，忧愁风雨，树犹如此！倩何人唤取，红巾翠袖，揾英雄泪！', '宋词精选');
INSERT INTO `poem` VALUES (914, '虞美人·听雨', '宋代', '蒋捷', '少年听雨歌楼上。红烛昏罗帐。壮年听雨客舟中。江阔云低、断雁叫西风。 而今听雨僧庐下。鬓已星星也。悲欢离合总无情。一任阶前、点滴到天明。', '宋词精选');
INSERT INTO `poem` VALUES (915, '阳关曲·中秋月', '宋代', '苏轼', '中秋作本名小秦王，入腔即阳关曲 暮云收尽溢清寒，银汉无声转玉盘。 此生此夜不长好，明月明年何处看。', '宋词精选');
INSERT INTO `poem` VALUES (916, '清平乐·年年雪里', '宋代', '李清照', '年年雪里，常插梅花醉。挼尽梅花无好意，赢得满衣清泪。 今年海角天涯，萧萧两鬓生华。看取晚来风势，故应难看梅花。', '宋词精选');
INSERT INTO `poem` VALUES (917, '西江月·世事一场大梦', '宋代', '苏轼', '世事一场大梦，人生几度秋凉？夜来风叶已鸣廊。看取眉头鬓上。(秋凉 一作：新凉) 酒贱常愁客少，月明多被云妨。中秋谁与共孤光。把盏凄然北望。', '宋词精选');
INSERT INTO `poem` VALUES (918, '水龙吟·次韵章质夫杨花词', '宋代', '苏轼', '似花还似非花，也无人惜从教坠。抛家傍路，思量却是，无情有思。萦损柔肠，困酣娇眼，欲开还闭。梦随风万里，寻郎去处，又还被、莺呼起。 不恨此花飞尽，恨西园、落红难缀。晓来雨过，遗踪何在？一池萍碎。春色三分，二分尘土，一分流水。细看来，不是杨花，点点是离人泪。', '宋词精选');
INSERT INTO `poem` VALUES (919, '临江仙·梦后楼台高锁', '宋代', '晏几道', '梦后楼台高锁，酒醒帘幕低垂。去年春恨却来时。落花人独立，微雨燕双飞。 记得小蘋初见，两重心字罗衣。琵琶弦上说相思。当时明月在，曾照彩云归。(蘋 通：苹)', '宋词精选');
INSERT INTO `poem` VALUES (920, '点绛唇·蹴罢秋千', '宋代', '李清照', '蹴罢秋千，起来慵整纤纤手。露浓花瘦，薄汗轻衣透。 见客入来，袜刬金钗溜。和羞走，倚门回首，却把青梅嗅。', '宋词精选');
INSERT INTO `poem` VALUES (921, '小重山·昨夜寒蛩不住鸣', '宋代', '岳飞', '昨夜寒蛩不住鸣。惊回千里梦，已三更。起来独自绕阶行。人悄悄，帘外月胧明。 白首为功名。旧山松竹老，阻归程。欲将心事付瑶琴。知音少，弦断有谁听。', '宋词精选');
INSERT INTO `poem` VALUES (922, '玉楼春·尊前拟把归期说', '宋代', '欧阳修', '尊前拟把归期说，欲语春容先惨咽。人生自是有情痴，此恨不关风与月。 离歌且莫翻新阕，一曲能教肠寸结。直须看尽洛城花，始共春风容易别。', '宋词精选');
INSERT INTO `poem` VALUES (923, '鹧鸪天·送人', '宋代', '辛弃疾', '唱彻《阳关》泪未干，功名馀事且加餐。浮天水送无穷树，带雨云埋一半山。 今古恨，几千般，只应离合是悲欢？江头未是风波恶，别有人间行路难！', '宋词精选');
INSERT INTO `poem` VALUES (924, '鹧鸪天·桂花', '宋代', '李清照', '暗淡轻黄体性柔，情疏迹远只香留。何须浅碧深红色，自是花中第一流。(深红 一作：轻) 梅定妒，菊应羞，画阑开处冠中秋。骚人可煞无情思，何事当年不见收。(阑 通：栏)', '宋词精选');
INSERT INTO `poem` VALUES (925, '木兰春·春景', '宋代', '宋祁', '东城渐觉风光好。縠皱波纹迎客棹。绿杨烟外晓寒轻，红杏枝头春意闹。 浮生长恨欢娱少。肯爱千金轻一笑。为君持酒劝斜阳，且向花间留晚照。', '宋词精选');
INSERT INTO `poem` VALUES (926, '浣溪沙·闺情', '宋代', '李清照', '绣面芙蓉一笑开，斜飞宝鸭衬香腮。眼波才动被人猜。 一面风情深有韵，半笺娇恨寄幽怀。月移花影约重来。', '宋词精选');
INSERT INTO `poem` VALUES (927, '蝶恋花·暖雨晴风初破冻', '宋代', '李清照', '暖雨晴风初破冻，柳眼梅腮，已觉春心动。酒意诗情谁与共？泪融残粉花钿重。 乍试夹衫金缕缝，山枕斜欹，枕损钗头凤。独抱浓愁无好梦，夜阑犹剪灯花弄。', '宋词精选');
INSERT INTO `poem` VALUES (928, '千秋岁·数声鶗鴂', '宋代', '张先', '数声鶗鴂，又报芳菲歇。惜春更把残红折。雨轻风色暴，梅子青时节。永丰柳，无人尽日花飞雪。 莫把幺弦拨，怨极弦能说。天不老，情难绝。心似双丝网，中有千千结。夜过也，东窗未白凝残月。(凝残月 一作：孤灯灭)', '宋词精选');
INSERT INTO `poem` VALUES (929, '卜算子·不是爱风尘', '宋代', '严蕊', '不是爱风尘，似被前缘误。花落花开自有时，总赖东君主。 去也终须去，住也如何住！若得山花插满头，莫问奴归处。', '宋词精选');
INSERT INTO `poem` VALUES (930, '湘春夜月·近清明', '宋代', '黄孝迈', '近清明。翠禽枝上消魂。可惜一片清歌，都付与黄昏。欲共柳花低诉，怕柳花轻薄，不解伤春。念楚乡旅宿，柔情别绪，谁与温存。 空樽夜泣，青山不语，残月当门。翠玉楼前，惟是有、一波湘水，摇荡湘云。天长梦短，问甚时、重见桃根。这次第，算人间没个并刀，剪断心上愁痕。', '宋词精选');
INSERT INTO `poem` VALUES (931, '满庭芳·山抹微云', '宋代', '秦观', '山抹微云，天连衰草，画角声断谯门。暂停征棹，聊共引离尊。多少蓬莱旧事，空回首、烟霭纷纷。斜阳外，寒鸦万点，流水绕孤村。(万点 一作：数点) 销魂。当此际，香囊暗解，罗带轻分。谩赢得、青楼薄幸名存。此去何时见也，襟袖上、空惹啼痕。伤情处，高城望断，灯火已黄昏。', '宋词精选');
INSERT INTO `poem` VALUES (932, '江城子·西城杨柳弄春柔', '宋代', '秦观', '西城杨柳弄春柔，动离忧，泪难收。犹记多情、曾为系归舟。碧野朱桥当日事，人不见，水空流。 韶华不为少年留，恨悠悠，几时休？飞絮落花时候、一登楼。便作春江都是泪，流不尽，许多愁。', '宋词精选');
INSERT INTO `poem` VALUES (933, '永遇乐·落日熔金', '宋代', '李清照', '落日熔金，暮云合璧，人在何处。染柳烟浓，吹梅笛怨，春意知几许。元宵佳节，融和天气，次第岂无风雨。来相召、香车宝马，谢他酒朋诗侣。(熔金 一作：镕金) 中州盛日，闺门多暇，记得偏重三五。铺翠冠儿，捻金雪柳，簇带争济楚。如今憔悴，风鬟霜鬓，怕见夜间出去。不如向、帘儿底下，听人笑语。', '宋词精选');
INSERT INTO `poem` VALUES (934, '点绛唇·闺思', '宋代', '李清照', '寂寞深闺，柔肠一寸愁千缕。惜春春去。几点催花雨。 倚遍阑干，只是无情绪。人何处。连天衰草，望断归来路。(衰草 一作：芳)', '宋词精选');
INSERT INTO `poem` VALUES (935, '浣溪沙·淡荡春光寒食天', '宋代', '李清照', '淡荡春光寒食天，玉炉沉水袅残烟。梦回山枕隐花钿。 海燕未来人斗草，江梅已过柳生绵。黄昏疏雨湿秋千。', '宋词精选');
INSERT INTO `poem` VALUES (936, '玉蝴蝶·望处雨收云断', '宋代', '柳永', '望处雨收云断，凭阑悄悄，目送秋光。晚景萧疏，堪动宋玉悲凉。水风轻，蘋花渐老，月露冷、梧叶飘黄。遣情伤。故人何在，烟水茫茫。 难忘，文期酒会，几孤风月，屡变星霜。海阔山遥，未知何处是潇湘。念双燕、难凭远信，指暮天、空识归航。黯相望。断鸿声里，立尽斜阳。', '宋词精选');
INSERT INTO `poem` VALUES (937, '玉楼春·别后不知君远近', '宋代', '欧阳修', '别后不知君远近。触目凄凉多少闷。渐行渐远渐无书，水阔鱼沉何处问。 夜深风竹敲秋韵。万叶千声皆是恨。故欹单枕梦中寻，梦又不成灯又烬。', '宋词精选');
INSERT INTO `poem` VALUES (938, '御街行·秋日怀旧', '宋代', '范仲淹', '纷纷坠叶飘香砌。夜寂静，寒声碎。真珠帘卷玉楼空，天淡银河垂地。年年今夜，月华如练，长是人千里。 愁肠已断无由醉，酒未到，先成泪。残灯明灭枕头欹，谙尽孤眠滋味。都来此事，眉间心上，无计相回避。', '宋词精选');
INSERT INTO `poem` VALUES (939, '兰陵王·柳', '宋代', '周邦彦', '柳阴直，烟里丝丝弄碧。隋堤上、曾见几番，拂水飘绵送行色。登临望故国，谁识京华倦客？长亭路，年去岁来，应折柔条过千尺。 闲寻旧踪迹，又酒趁哀弦，灯照离席。梨花榆火催寒食。愁一箭风快，半篙波暖，回头迢递便数驿，望人在天北。 凄恻，恨堆积！渐别浦萦回，津堠岑寂，斜阳冉冉春无极。念月榭携手，露桥闻笛。沉思前事，似梦里，泪暗滴。', '宋词精选');
INSERT INTO `poem` VALUES (940, '采桑子·恨君不似江楼月', '宋代', '吕本中', '恨君不似江楼月，南北东西，南北东西，只有相随无别离。 恨君却似江楼月，暂满还亏，暂满还亏，待得团圆是几时？', '宋词精选');
INSERT INTO `poem` VALUES (941, '贺新郎·别茂嘉十二弟', '宋代', '辛弃疾', '别茂嘉十二弟。鹈鴂、杜鹃实两种，见《离骚补注》 绿树听鹈鴂。更那堪、鹧鸪声住，杜鹃声切。啼到春归无寻处，苦恨芳菲都歇。算未抵、人间离别。马上琵琶关塞黑，更长门、翠辇辞金阙。看燕燕，送归妾。 将军百战身名裂。向河梁、回头万里，故人长绝。易水萧萧西风冷，满座衣冠似雪。正壮士、悲歌未彻。啼鸟还知如许恨，料不啼清泪长啼血。谁共我，醉明月。', '宋词精选');
INSERT INTO `poem` VALUES (942, '行香子·七夕', '宋代', '李清照', '草际鸣蛩。惊落梧桐。正人间、天上愁浓。云阶月地，关锁千重。纵浮槎来，浮槎去，不相逢。 星桥鹊驾，经年才见，想离情、别恨难穷。牵牛织女，莫是离中。甚霎儿晴，霎儿雨，霎儿风。', '宋词精选');
INSERT INTO `poem` VALUES (943, '长相思·花似伊', '宋代', '欧阳修', '花似伊，柳似伊。花柳青春人别离。低头双泪垂。 长江东，长江西。两岸鸳鸯两处飞。相逢知几时。', '宋词精选');
INSERT INTO `poem` VALUES (944, '凤凰台上忆吹箫·香冷金猊', '宋代', '李清照', '香冷金猊，被翻红浪，起来慵自梳头。任宝奁尘满，日上帘钩。生怕离怀别苦，多少事、欲说还休。新来瘦，非干病酒，不是悲秋。 休休，这回去也，千万遍《阳关》，也则难留。念武陵人远，烟锁秦楼。惟有楼前流水，应念我、终日凝眸。凝眸处，从今又添，一段新愁。(版本一) 香冷金猊，被翻红浪，起来人未梳头。任宝奁闲掩，日上帘钩。生怕闲愁暗恨，多少事、欲说还休。今年瘦，非干病酒，不是悲秋。 明朝，者回去也，千万遍阳关，也即难留。念武陵春晚，云锁重楼。记取楼前绿水，应念我、终日凝眸。凝眸处，从今更数，几段新愁。(版本二)', '宋词精选');
INSERT INTO `poem` VALUES (945, '念奴娇·春情', '宋代', '李清照', '萧条庭院，又斜风细雨，重门须闭。宠柳娇花寒食近，种种恼人天气。险韵诗成，扶头酒醒，别是闲滋味。征鸿过尽，万千心事难寄。 楼上几日春寒，帘垂四面，玉阑干慵倚。被冷香消新梦觉，不许愁人不起。清露晨流，新桐初引，多少游春意。日高烟敛，更看今日晴未。', '宋词精选');
INSERT INTO `poem` VALUES (946, '相思令·吴山青', '宋代', '林逋', '吴山青，越山青，两岸青山相送迎，争忍有离情？ 君泪盈，妾泪盈，罗带同心结未成，江边潮已平。', '宋词精选');
INSERT INTO `poem` VALUES (947, '蝶恋花·送春', '宋代', '朱淑真', '楼外垂杨千万缕。欲系青春，少住春还去。犹自风前飘柳絮。随春且看归何处。 绿满山川闻杜宇。便做无情，莫也愁人苦。把酒送春春不语。黄昏却下潇潇雨。', '宋词精选');
INSERT INTO `poem` VALUES (948, '南歌子·天上星河转', '宋代', '李清照', '天上星河转，人间帘幕垂。凉生枕簟泪痕滋。起解罗衣聊问、夜何其。 翠贴莲蓬小，金销藕叶稀。旧时天气旧时衣。只有情怀不似、旧家时。', '宋词精选');
INSERT INTO `poem` VALUES (949, '减字木兰花·卖花担上', '宋代', '李清照', '卖花担上。买得一枝春欲放。泪染轻匀。犹带彤霞晓露痕。 怕郎猜道。奴面不如花面好。云鬓斜簪。徒要教郎比并看。', '宋词精选');
INSERT INTO `poem` VALUES (950, '诉衷情·眉意', '宋代', '欧阳修', '清晨帘幕卷轻霜。呵手试梅妆。都缘自有离恨，故画作远山长。 思往事，惜流芳。易成伤。拟歌先敛，欲笑还颦，最断人肠。', '宋词精选');
INSERT INTO `poem` VALUES (951, '少年游·草', '宋代', '高观国', '春风吹碧，春云映绿，晓梦入芳裀。软衬飞花，远连流水，一望隔香尘。 萋萋多少江南恨，翻忆翠罗裙。冷落闲门，凄迷古道，烟雨正愁人。', '宋词精选');
INSERT INTO `poem` VALUES (952, '南乡子·冬夜', '宋代', '黄升', '万籁寂无声。衾铁稜稜近五更。香断灯昏吟未稳，凄清。只有霜华伴月明。 应是夜寒凝。恼得梅花睡不成。我念梅花花念我，关情。起看清冰满玉瓶。', '宋词精选');
INSERT INTO `poem` VALUES (953, '唐多令·惜别', '宋代', '吴文英', '何处合成愁。离人心上秋。纵芭蕉、不雨也飕飕。都道晚凉天气好，有明月、怕登楼。 年事梦中休。花空烟水流。燕辞归、客尚淹留。垂柳不萦裙带住。漫长是、系行舟。', '宋词精选');
INSERT INTO `poem` VALUES (954, '昼夜乐·洞房记得初相遇', '宋代', '柳永', '洞房记得初相遇。便只合、长相聚。何期小会幽欢，变作离情别绪。况值阑珊春色暮，对满目、乱花狂絮。直恐好风光，尽随伊归去。 一场寂寞凭谁诉。算前言、总轻负。早知恁地难拚，悔不当时留住。其奈风流端正外，更别有，系人心处。一日不思量，也攒眉千度。', '宋词精选');
INSERT INTO `poem` VALUES (955, '蝶恋花·梦入江南烟水路', '宋代', '晏几道', '梦入江南烟水路，行尽江南，不与离人遇。睡里消魂无说处，觉来惆怅消魂误。 欲尽此情书尺素，浮雁沉鱼，终了无凭据。却倚缓弦歌别绪，断肠移破秦筝柱。', '宋词精选');
INSERT INTO `poem` VALUES (956, '贺新郎·夏景', '宋代', '苏轼', '乳燕飞华屋。悄无人、桐阴转午，晚凉新浴。手弄生绡白团扇，扇手一时似玉。渐困倚、孤眠清熟。帘外谁来推绣户，枉教人、梦断瑶台曲。又却是，风敲竹。 石榴半吐红巾蹙。待浮花、浪蕊都尽，伴君幽独。秾艳一枝细看取，芳心千重似束。又恐被、秋风惊绿。若待得君来向此，花前对酒不忍触。共粉泪，两簌簌。', '宋词精选');
INSERT INTO `poem` VALUES (957, '定风波·自春来', '宋代', '柳永', '自春来、惨绿愁红，芳心是事可可。日上花梢，莺穿柳带，犹压香衾卧。暖酥消、腻云亸，终日厌厌倦梳裹。无那。恨薄情一去，音书无个。 早知恁么，悔当初、不把雕鞍锁。向鸡窗，只与蛮笺象管，拘束教吟课。镇相随、莫抛躲，针线闲拈伴伊坐。和我，免使年少，光阴虚过。', '宋词精选');
INSERT INTO `poem` VALUES (958, '暗香·旧时月色', '宋代', '姜夔', '辛亥之冬，余载雪诣石湖。止既月，授简索句，且征新声，作此两曲，石湖把玩不已，使二妓肆习之，音节谐婉，乃名之曰《暗香》、《疏影》。 旧时月色，算几番照我，梅边吹笛？唤起玉人，不管清寒与攀摘。何逊而今渐老，都忘却春风词笔。但怪得竹外疏花，香冷入瑶席。 江国，正寂寂，叹寄与路遥，夜雪初积。翠尊易泣，红萼无言耿相忆。长记曾携手处，千树压、西湖寒碧。又片片、吹尽也，几时见得？', '宋词精选');
INSERT INTO `poem` VALUES (959, '江南春·波渺渺', '宋代', '寇准', '波渺渺，柳依依。孤村芳草远，斜日杏花飞。江南春尽离肠断，苹满汀洲人未归。', '宋词精选');
INSERT INTO `poem` VALUES (960, '鹧鸪天·元夕有所梦', '宋代', '姜夔', '肥水东流无尽期。当初不合种相思。梦中未比丹青见，暗里忽惊山鸟啼。 春未绿，鬓先丝。人间别久不成悲。谁教岁岁红莲夜，两处沉吟各自知。(沉 通：沈)', '宋词精选');
INSERT INTO `poem` VALUES (961, '鹧鸪天·醉拍春衫惜旧香', '宋代', '晏几道', '醉拍春衫惜旧香。天将离恨恼疏狂。年年陌上生秋草，日日楼中到夕阳。 云渺渺，水茫茫。征人归路许多长。相思本是无凭语，莫向花笺费泪行。', '宋词精选');
INSERT INTO `poem` VALUES (962, '清平乐·题上卢桥', '宋代', '辛弃疾', '清溪奔快。不管青山碍。千里盘盘平世界。更著溪山襟带。 古今陵谷茫茫。市朝往往耕桑。此地居然形胜，似曾小小兴亡。', '宋词精选');
INSERT INTO `poem` VALUES (963, '眼儿媚·杨柳丝丝弄轻柔', '宋代', '王雱', '杨柳丝丝弄轻柔，烟缕织成愁。海棠未雨，梨花先雪，一半春休。 而今往事难重省，归梦绕秦楼。相思只在，丁香枝上，豆蔻梢头。', '宋词精选');
INSERT INTO `poem` VALUES (964, '忆帝京·薄衾小枕凉天气', '宋代', '柳永', '薄衾小枕凉天气，乍觉别离滋味。展转数寒更，起了还重睡。毕竟不成眠，一夜长如岁。 也拟待、却回征辔；又争奈、已成行计。万种思量，多方开解，只恁寂寞厌厌地。系我一生心，负你千行泪。', '宋词精选');
INSERT INTO `poem` VALUES (965, '点绛唇·感兴', '宋代', '王禹偁', '雨恨云愁，江南依旧称佳丽。水村渔市。一缕孤烟细。 天际征鸿，遥认行如缀。平生事。此时凝睇。谁会凭栏意。(栏 通：阑)', '宋词精选');
INSERT INTO `poem` VALUES (966, '怨王孙·湖上风来波浩渺', '宋代', '李清照', '湖上风来波浩渺。秋已暮、红稀香少。水光山色与人亲，说不尽、无穷好。 莲子已成荷叶老。青露洗、蘋花汀草。眠沙鸥鹭不回头，似也恨、人归早。', '宋词精选');
INSERT INTO `poem` VALUES (967, '风入松·听风听雨过清明', '宋代', '吴文英', '听风听雨过清明。愁草瘗花铭。楼前绿暗分携路，一丝柳、一寸柔情。料峭春寒中酒，交加晓梦啼莺。 西园日日扫林亭。依旧赏新晴。黄蜂频扑秋千索，有当时、纤手香凝。惆怅双鸳不到，幽阶一夜苔生。', '宋词精选');
INSERT INTO `poem` VALUES (968, '蝶恋花·醉别西楼醒不记', '宋代', '晏几道', '醉别西楼醒不记。春梦秋云，聚散真容易。斜月半窗还少睡。画屏闲展吴山翠。 衣上酒痕诗里字。点点行行，总是凄凉意。红烛自怜无好计。夜寒空替人垂泪。', '宋词精选');
INSERT INTO `poem` VALUES (969, '临江仙·斗草阶前初见', '宋代', '晏几道', '斗草阶前初见，穿针楼上曾逢。罗裙香露玉钗风。靓妆眉沁绿，羞脸粉生红。 流水便随春远，行云终与谁同。酒醒长恨锦屏空。相寻梦里路，飞雨落花中。', '宋词精选');
INSERT INTO `poem` VALUES (970, '燕山亭·北行见杏花', '宋代', '赵佶', '裁剪冰绡，轻叠数重，淡著胭脂匀注。新样靓妆，艳溢香融，羞杀蕊珠宫女。易得凋零，更多少、无情风雨。愁苦。闲院落凄凉，几番春暮。（闲院 一作：问院) 凭寄离恨重重，者双燕，何曾会人言语。天遥地远，万水千山，知他故宫何处。怎不思量，除梦里、有时曾去。无据，和梦也新来不做。', '宋词精选');
INSERT INTO `poem` VALUES (971, '长相思·长相思', '宋代', '晏几道', '长相思，长相思。若问相思甚了期，除非相见时。 长相思，长相思。欲把相思说似谁，浅情人不知。', '宋词精选');
INSERT INTO `poem` VALUES (972, '思远人·红叶黄花秋意晚', '宋代', '晏几道', '红叶黄花秋意晚，千里念行客。飞云过尽，归鸿无信，何处寄书得。 泪弹不尽临窗滴。就砚旋研墨。渐写到别来，此情深处，红笺为无色。', '宋词精选');
INSERT INTO `poem` VALUES (973, '长相思·雨', '宋代', '万俟咏', '一声声，一更更。窗外芭蕉窗里灯，此时无限情。 梦难成，恨难平。不道愁人不喜听，空阶滴到明。', '宋词精选');
INSERT INTO `poem` VALUES (974, '南乡子·重九涵辉楼呈徐君猷', '宋代', '苏轼', '霜降水痕收。浅碧鳞鳞露远洲。酒力渐消风力软，飕飕。破帽多情却恋头。 佳节若为酬。但把清尊断送秋。万事到头都是梦，休休。明日黄花蝶也愁。', '宋词精选');
INSERT INTO `poem` VALUES (975, '木兰花·城上风光莺语乱', '宋代', '钱惟演', '城上风光莺语乱，城下烟波春拍岸。绿杨芳草几时休，泪眼愁肠先已断。 情怀渐觉成衰晚，鸾镜朱颜惊暗换。昔年多病厌芳尊，今日芳尊惟恐浅。', '宋词精选');
INSERT INTO `poem` VALUES (976, '踏莎行·雪似梅花', '宋代', '吕本中', '雪似梅花，梅花似雪。似和不似都奇绝。恼人风味阿谁知？请君问取南楼月。 记得去年，探梅时节。老来旧事无人说。为谁醉倒为谁醒？到今犹恨轻离别。', '宋词精选');
INSERT INTO `poem` VALUES (977, '清平乐·春晚', '宋代', '王安国', '留春不住，费尽莺儿语。满地残红宫锦污，昨夜南园风雨。 小怜初上琵琶，晓来思绕天涯。不肯画堂朱户，春风自在杨花。', '宋词精选');
INSERT INTO `poem` VALUES (978, '阮郎归·天边金掌露成霜', '宋代', '晏几道', '天边金掌露成霜。云随雁字长。绿杯红袖趁重阳。人情似故乡。 兰佩紫，菊簪黄。殷勤理旧狂。欲将沉醉换悲凉。清歌莫断肠。', '宋词精选');
INSERT INTO `poem` VALUES (979, '鹧鸪天·家住苍烟落照间', '宋代', '陆游', '家住苍烟落照间，丝毫尘事不相关。斟残玉瀣行穿竹，卷罢《黄庭》卧看山。 贪啸傲，任衰残，不妨随处一开颜。元知造物心肠别，老却英雄似等闲！', '宋词精选');
INSERT INTO `poem` VALUES (980, '凤箫吟·锁离愁', '宋代', '韩缜', '锁离愁，连绵无际，来时陌上初熏。绣帏人念远，暗垂珠露，泣送征轮。长行长在眼，更重重、远水孤云。但望极楼高，尽日目断王孙。 消魂。池塘别后，曾行处、绿妒轻裙。恁时携素手，乱花飞絮里，缓步香茵。朱颜空自改，向年年、芳意长新。遍绿野，嬉游醉眠，莫负青春。', '宋词精选');
INSERT INTO `poem` VALUES (981, '卜算子·兰', '宋代', '曹组', '松竹翠萝寒，迟日江山暮。幽径无人独自芳，此恨凭谁诉。似共梅花语。尚有寻芳侣。着意闻时不肯香，香在无心处。', '宋词精选');
INSERT INTO `poem` VALUES (982, '千秋岁·水边沙外', '宋代', '秦观', '水边沙外。城郭春寒退。花影乱，莺声碎。飘零疏酒盏，离别宽衣带。人不见，碧云暮合空相对。 忆昔西池会。鹓鹭同飞盖。携手处，今谁在。日边清梦断，镜里朱颜改。春去也，飞红万点愁如海。', '宋词精选');
INSERT INTO `poem` VALUES (983, '虞美人·碧桃天上栽和露', '宋代', '秦观', '碧桃天上栽和露，不是凡花数。乱山深处水萦回，可惜一枝如画为谁开？ 轻寒细雨情何限！不道春难管。为君沉醉又何妨，只怕酒醒时候断人肠。', '宋词精选');
INSERT INTO `poem` VALUES (984, '踏莎行·春暮', '宋代', '寇准', '春色将阑，莺声渐老，红英落尽青梅小。画堂人静雨蒙蒙，屏山半掩余香袅。 密约沉沉，离情杳杳，菱花尘满慵将照。倚楼无语欲销魂，长空黯淡连芳草。', '宋词精选');
INSERT INTO `poem` VALUES (985, '虞美人·寄公度', '宋代', '舒亶', '芙蓉落尽天涵水，日暮沧波起。背飞双燕贴云寒，独向小楼东畔、倚阑看。 浮生只合尊前老，雪满长安道。故人早晚上高台，赠我江南春色、一枝梅。', '宋词精选');
INSERT INTO `poem` VALUES (986, '阮郎归·南园春半踏青时', '宋代', '欧阳修', '南园春半踏青时，风和闻马嘶。青梅如豆柳如眉，日长蝴蝶飞。 花露重，草烟低，人家帘幕垂。秋千慵困解罗衣，画堂双燕归。', '宋词精选');
INSERT INTO `poem` VALUES (987, '贺圣朝·留别', '宋代', '叶清臣', '满斟绿醑留君住。莫匆匆归去。三分春色二分愁，更一分风雨。 花开花谢、都来几许。且高歌休诉。不知来岁牡丹时，再相逢何处。', '宋词精选');
INSERT INTO `poem` VALUES (988, '减字木兰花·天涯旧恨', '宋代', '秦观', '天涯旧恨，独自凄凉人不问。欲见回肠，断尽金炉小篆香。 黛蛾长敛，任是春风吹不展。困倚危楼，过尽飞鸿字字愁。', '宋词精选');
INSERT INTO `poem` VALUES (989, '鹧鸪天·座中有眉山隐客史应之和前韵即席答之', '宋代', '黄庭坚', '黄菊枝头生晓寒。人生莫放酒杯干。风前横笛斜吹雨，醉里簪花倒著冠。 身健在，且加餐。舞裙歌板尽清欢。黄花白发相牵挽，付与时人冷眼看。', '宋词精选');
INSERT INTO `poem` VALUES (990, '鹧鸪天·一点残红欲尽时', '宋代', '周紫芝', '一点残红欲尽时。乍凉秋气满屏帏。梧桐叶上三更雨，叶叶声声是别离。 调宝瑟，拨金猊。那时同唱鹧鸪词。如今风雨西楼夜，不听清歌也泪垂。', '宋词精选');
INSERT INTO `poem` VALUES (991, '菩萨蛮·金陵赏心亭为叶丞相赋', '宋代', '辛弃疾', '青山欲共高人语。联翩万马来无数。烟雨却低回。望来终不来。 人言头上发。总向愁中白。拍手笑沙鸥。一身都是愁。', '宋词精选');
INSERT INTO `poem` VALUES (992, '点绛唇·金谷年年', '宋代', '林逋', '金谷年年，乱生春色谁为主？余花落处，满地和烟雨。 又是离歌，一阕长亭暮。王孙去，萋萋无数，南北东西路。', '宋词精选');
INSERT INTO `poem` VALUES (993, '渔家傲·画鼓声中昏又晓', '宋代', '晏殊', '画鼓声中昏又晓。时光只解催人老。求得浅欢风日好。齐揭调。神仙一曲渔家傲。 绿水悠悠天杳杳。浮生岂得长年少。莫惜醉来开口笑。须信道。人间万事何时了。', '宋词精选');
INSERT INTO `poem` VALUES (994, '浣溪沙·门隔花深梦旧游', '宋代', '吴文英', '门隔花深梦旧游。夕阳无语燕归愁。玉纤香动小帘钩。 落絮无声春堕泪，行云有影月含羞。东风临夜冷于秋。', '宋词精选');
INSERT INTO `poem` VALUES (995, '贺新郎·九日', '宋代', '刘克庄', '湛湛长空黑。更那堪、斜风细雨，乱愁如织。老眼平生空四海，赖有高楼百尺。看浩荡、千崖秋色。白发书生神州泪，尽凄凉、不向牛山滴。追往事，去无迹。 少年自负凌云笔。到而今、春华落尽，满怀萧瑟。常恨世人新意少，爱说南朝狂客。把破帽、年年拈出。若对黄花孤负酒，怕黄花、也笑人岑寂。鸿北去，日西匿。', '宋词精选');
INSERT INTO `poem` VALUES (996, '安公子·远岸收残雨', '宋代', '柳永', '远岸收残雨。雨残稍觉江天暮。拾翠汀洲人寂静，立双双鸥鹭。望几点、渔灯隐映蒹葭浦。停画桡、两两舟人语。道去程今夜，遥指前村烟树。 游宦成羁旅。短樯吟倚闲凝伫。万水千山迷远近，想乡关何处。自别后、风亭月榭孤欢聚。刚断肠、惹得离情苦。听杜宇声声，劝人不如归去。', '宋词精选');
INSERT INTO `poem` VALUES (997, '水调歌头·江上春山远', '宋代', '葛长庚', '江上春山远，山下暮云长。相留相送，时见双燕语风樯。满目飞花万点，回首故人千里，把酒沃愁肠。回雁峰前路，烟树正苍苍。 漏声残，灯焰短，马蹄香。浮云飞絮，一身将影向潇湘。多少风前月下，迤逦天涯海角，魂梦亦凄凉。又是春将暮，无语对斜阳。', '宋词精选');
INSERT INTO `poem` VALUES (998, '贺新郎·把酒长亭说', '宋代', '辛弃疾', '陈同父自东阳来过余，留十日。与之同游鹅湖，且会朱晦庵于紫溪，不至，飘然东归。既别之明日，余意中殊恋恋，复欲追路。至鹭鸶林，则雪深泥滑，不得前矣。独饮方村，怅然久之，颇恨挽留之正是遂也。夜半投宿吴氏泉湖四望楼，闻邻笛悲甚，为赋《贺新郎》以见意。又五日，同父书来索词，心所同然者如此，可发千里一笑。 把酒长亭说。看渊明、风流酷似，卧龙诸葛。何处飞来林间鹊，蹙踏松梢微雪。要破帽多添华发。剩水残山无态度，被疏梅料理成风月。两三雁，也萧瑟。 佳人重约还轻别。怅清江、天寒不渡，水深冰合。路断车轮生四角，此地行人销骨。问谁使、君来愁绝？铸就而今相思错，料当初、费尽人间铁。长夜笛，莫吹裂。', '宋词精选');
INSERT INTO `poem` VALUES (999, '祝英台近·晚春', '宋代', '辛弃疾', '宝钗分，桃叶渡，烟柳暗南浦。怕上层楼，十日九风雨。断肠片片飞红，都无人管，更谁劝、啼莺声住？ 鬓边觑，试把花卜归期，才簪又重数。罗帐灯昏，哽咽梦中语：是他春带愁来，春归何处？却不解、带将愁去。', '宋词精选');
INSERT INTO `poem` VALUES (1000, '苍梧谣·天', '宋代', '蔡伸', '天。休使圆蟾照客眠。人何在，桂影自婵娟。', '宋词精选');
INSERT INTO `poem` VALUES (1001, '谢池春·残寒销尽', '宋代', '李之仪', '残寒销尽，疏雨过，清明后。花径敛余红，风沼萦新皱。乳燕穿庭户，飞絮沾襟袖。正佳时，仍晚昼。著人滋味，真个浓如酒。 频移带眼，空只恁、厌厌瘦。不见又思量，见了还依旧。为问频相见，何似长相守？天不老，人未偶。且将此恨，分付庭前柳。', '宋词精选');
INSERT INTO `poem` VALUES (1002, '蝶恋花·欲减罗衣寒未去', '宋代', '赵令畤', '欲减罗衣寒未去，不卷珠帘，人在深深处。红杏枝头花几许？啼痕止恨清明雨。 尽日沉烟香一缕，宿酒醒迟，恼破春情绪。飞燕又将归信误，小屏风上西江路。', '宋词精选');
INSERT INTO `poem` VALUES (1003, '谒金门·春半', '宋代', '朱淑真', '春已半。触目此情无限。十二阑干闲倚遍。愁来天不管。 好是风和日暖。输与莺莺燕燕。满院落花帘不卷。断肠芳草远。', '宋词精选');
INSERT INTO `poem` VALUES (1004, '子夜歌·三更月', '宋代', '贺铸', '三更月，中庭恰照梨花雪。梨花雪，不胜凄断，杜鹃啼血。 王孙何许音尘绝，柔桑陌上吞声别。吞声别，陇头流水，替人呜咽。', '宋词精选');
INSERT INTO `poem` VALUES (1005, '鹧鸪天·枝上流莺和泪闻', '宋代', '秦观', '枝上流莺和泪闻，新啼痕间旧啼痕。一春鱼鸟无消息，千里关山劳梦魂。 无一语，对芳尊。安排肠断到黄昏。甫能炙得灯儿了，雨打梨花深闭门。', '宋词精选');
INSERT INTO `poem` VALUES (1006, '减字木兰花·春月', '宋代', '苏轼', '二月十五夜，与赵德麟小酌聚星堂 春庭月午，摇荡香醪光欲舞。步转回廊，半落梅花婉娩香。 轻云薄雾，总是少年行乐处。不似秋光，只与离人照断肠。', '宋词精选');
INSERT INTO `poem` VALUES (1007, '武陵春·人道有情须有梦', '宋代', '连静女', '人道有情须有梦，无梦岂无情？夜夜相思直到明，有梦怎生成？ 伊若忽然来梦里，邻笛又还惊。笛里声声不忍听，浑是断肠声。', '宋词精选');
INSERT INTO `poem` VALUES (1008, '水调歌头·和马叔度游月波楼', '宋代', '辛弃疾', '客子久不到，好景为君留。西楼着意吟赏，何必问更筹？唤起一天明月，照我满怀冰雪，浩荡百川流。鲸饮未吞海，剑气已横秋。 野光浮，天宇回，物华幽。中州遗恨，不知今夜几人愁？谁念英雄老矣？不道功名蕞尔，决策尚悠悠。此事费分说，来日且扶头！', '宋词精选');
INSERT INTO `poem` VALUES (1009, '浣溪沙·霜日明霄水蘸空', '宋代', '张孝祥', '霜日明霄水蘸空。鸣鞘声里绣旗红。淡烟衰草有无中。 万里中原烽火北，一尊浊酒戍楼东。酒阑挥泪向悲风。', '宋词精选');
INSERT INTO `poem` VALUES (1010, '秦楼月·芳菲歇', '宋代', '向子諲', '芳菲歇，故园目断伤心切。伤心切，无边烟水，无穷山色。 可堪更近乾龙节，眼中泪尽空啼血。空啼血，子规声外，晓风残月。', '宋词精选');
INSERT INTO `poem` VALUES (1011, '霜天晓角·题采石蛾眉亭', '宋代', '韩元吉', '倚天绝壁，直下江千尺。天际两蛾凝黛，愁与恨，几时极！ 暮潮风正急，酒阑闻塞笛。试问谪仙何处？青山外，远烟碧。', '宋词精选');
INSERT INTO `poem` VALUES (1012, '阮郎归·绍兴乙卯大雪行鄱阳道中', '宋代', '向子諲', '江南江北雪漫漫，遥知易水寒。同云深处望三关，断肠山又山。 天可老，海能翻，消除此恨难。频闻遣使问平安，几时鸾辂还？', '宋词精选');
INSERT INTO `poem` VALUES (1013, '鹊桥仙·说盟说誓', '宋代', '蜀妓', '说盟说誓。说情说意。动便春愁满纸。多应念得脱空经，是那个、先生教底。 不茶不饭，不言不语，一味供他憔悴。相思已是不曾闲，又那得、工夫咒你。', '宋词精选');
INSERT INTO `poem` VALUES (1014, '踏莎行·情似游丝', '宋代', '周紫芝', '情似游丝，人如飞絮。泪珠阁定空相觑。一溪烟柳万丝垂，无因系得兰舟住。 雁过斜阳，草迷烟渚。如今已是愁无数。明朝且做莫思量，如何过得今宵去。', '宋词精选');
INSERT INTO `poem` VALUES (1015, '石州慢·寒水依痕', '宋代', '张元干', '寒水依痕，春意渐回，沙际烟阔。溪梅晴照生香，冷蕊数枝争发。天涯旧恨，试看几许消魂，长亭门外山重叠。不尽眼中青，是愁来时节。 情切。画楼深闭，想见东风，暗销肌雪。辜负枕前云雨，尊前花月。心期切处，更有多少凄凉，殷勤留与归时说。到得却相逢，恰经年离别。', '宋词精选');
INSERT INTO `poem` VALUES (1016, '解连环·孤雁', '宋代', '张炎', '楚江空晚。怅离群万里，恍然惊散。自顾影、欲下寒塘，正沙净草枯，水平天远。写不成书，只寄得、相思一点。料因循误了，残毡拥雪，故人心眼。 谁怜旅愁荏苒。谩长门夜悄，锦筝弹怨。想伴侣、犹宿芦花，也曾念春前，去程应转。暮雨相呼，怕蓦地、玉关重见。未羞他、双燕归来，画帘半卷。', '宋词精选');
INSERT INTO `poem` VALUES (1017, '满江红·题南京夷山驿', '宋代', '王清惠', '太液芙蓉，浑不似、旧时颜色。 曾记得、春风雨露，玉楼金阙。名播兰馨妃后里，晕潮莲脸君王侧。忽一声、鼙鼓揭天来，繁华歇。 龙虎散，风云灭。千古恨，凭谁说。对山河百二，泪盈襟血。客馆夜惊尘土梦，宫车晓碾关山月。问嫦娥、于我肯从容，同圆缺？', '宋词精选');
INSERT INTO `poem` VALUES (1018, '长亭怨慢·渐吹尽', '宋代', '姜夔', '余颇喜自制曲。初率意为长短句，然后协以律，故前后阕多不同。桓大司马云：“昔年种柳，依依汉南。今看摇落，凄怆江潭：树犹如此，人何以堪？”此语余深爱之。 渐吹尽，枝头香絮，是处人家，绿深门户。远浦萦回，暮帆零乱向何许？阅人多矣，谁得似长亭树？树若有情时，不会得青青如此！ 日暮，望高城不见，只见乱山无数。韦郎去也，怎忘得、玉环分付：第一是早早归来，怕红萼无人为主。算空有并刀，难剪离愁千缕。', '宋词精选');
INSERT INTO `poem` VALUES (1019, '卜算子·见也如何暮', '宋代', '石孝友', '见也如何暮。别也如何遽。别也应难见也难，后会难凭据。 去也如何去。住也如何住。住也应难去也难，此际难分付。', '宋词精选');
INSERT INTO `poem` VALUES (1020, '浣溪沙·髻子伤春慵更梳', '宋代', '李清照', '髻子伤春慵更梳。晚风庭院落梅初。淡云来往月疏疏。 玉鸭熏炉闲瑞脑，朱樱斗帐掩流苏。通犀还解辟寒无。', '宋词精选');
INSERT INTO `poem` VALUES (1021, '忆秦娥·用太白韵', '宋代', '李之仪', '清溪咽。霜风洗出山头月。山头月。迎得云归，还送云别。 不知今是何时节。凌歊望断音尘绝。音尘绝。帆来帆去，天际双阙。', '宋词精选');
INSERT INTO `poem` VALUES (1022, '霜天晓角·梅', '宋代', '范成大', '晚晴风歇。一夜春威折。脉脉花疏天淡，云来去、数枝雪。 胜绝。愁亦绝。此情谁共说。惟有两行低雁，知人倚、画楼月。', '宋词精选');
INSERT INTO `poem` VALUES (1023, '西江月·新秋写兴', '宋代', '刘辰翁', '天上低昂似旧，人间儿女成狂。夜来处处试新妆。却是人间天上。 不觉新凉似水，相思两鬓如霜。梦从海底跨枯桑。阅尽银河风浪。', '宋词精选');
INSERT INTO `poem` VALUES (1024, '最高楼·旧时心事', '宋代', '程垓', '旧时心事，说着两眉羞。长记得、凭肩游。缃裙罗袜桃花岸，薄衫轻扇杏花楼。几番行，几番醉，几番留。 也谁料、春风吹已断。又谁料、朝云飞亦散。天易老，恨难酬。蜂儿不解知人苦，燕儿不解说人愁。旧情怀，消不尽，几时休。', '宋词精选');
INSERT INTO `poem` VALUES (1025, '庆清朝·禁幄低张', '宋代', '李清照', '禁幄低张，彤阑巧护，就中独占残春。容华淡伫，绰约俱见天真。待得群花过后，一番风露晓妆新。妖娆艳态，妒风笑月，长殢东君。 东城边，南陌上，正日烘池馆，竟走香轮。绮筵散日，谁人可继芳尘。更好明光宫殿，几枝先近日边匀。金尊倒，拚了尽烛，不管黄昏。', '宋词精选');
INSERT INTO `poem` VALUES (1026, '减字木兰花·题雄州驿', '宋代', '蒋氏女', '朝云横度。辘辘车声如水去。白草黄沙。月照孤村三两家。 飞鸿过也。万结愁肠无昼夜。渐近燕山。回首乡关归路难。', '宋词精选');
INSERT INTO `poem` VALUES (1027, '夜半乐·冻云黯淡天气', '宋代', '柳永', '冻云黯淡天气，扁舟一叶，乘兴离江渚。渡万壑千岩，越溪深处。怒涛渐息，樵风乍起，更闻商旅相呼。片帆高举。泛画鹢、翩翩过南浦。 望中酒旆闪闪，一簇烟村，数行霜树。残日下，渔人鸣榔归去。败荷零落，衰杨掩映，岸边两两三三，浣沙游女。避行客、含羞笑相语。 到此因念，绣阁轻抛，浪萍难驻。叹后约丁宁竟何据。惨离怀，空恨岁晚归期阻。凝泪眼、杳杳神京路。断鸿声远长天暮。', '宋词精选');
INSERT INTO `poem` VALUES (1028, '撼庭秋·别来音信千里', '宋代', '晏殊', '别来音信千里，恨此情难寄。碧纱秋月，梧桐夜雨，几回无寐。 楼高目断，天遥云黯，只堪憔悴。念兰堂红烛，心长焰短，向人垂泪。', '宋词精选');
INSERT INTO `poem` VALUES (1029, '点绛唇·试灯夜初晴', '宋代', '吴文英', '卷尽愁云，素娥临夜新梳洗。暗尘不起。酥润凌波地。 辇路重来，仿佛灯前事。情如水。小楼熏被。春梦笙歌里。', '宋词精选');
INSERT INTO `poem` VALUES (1030, '减字木兰花·淮山隐隐', '宋代', '淮上女', '淮山隐隐，千里云峰千里恨。淮水悠悠，万顷烟波万顷愁。 山长水远，遮断行人东望眼。恨旧愁新，有泪无言对晚春。', '宋词精选');
INSERT INTO `poem` VALUES (1031, '清商怨·葭萌驿作', '宋代', '陆游', '江头日暮痛饮。乍雪晴犹凛。山驿凄凉，灯昏人独寝。 鸳机新寄断锦。叹往事、不堪重省。梦破南楼，绿云堆一枕。', '宋词精选');
INSERT INTO `poem` VALUES (1032, '临江仙·忆昔西池池上饮', '宋代', '晁冲之', '忆昔西池池上饮，年年多少欢娱。别来不寄一行书。寻常相见了，犹道不如初。 安稳锦裘今夜梦，月明好渡江湖。相思休问定何如。情知春去后，管得落花无。', '宋词精选');
INSERT INTO `poem` VALUES (1033, '西江月·携手看花深径', '宋代', '贺铸', '携手看花深径，扶肩待月斜廊。临分少伫已伥伥，此段不堪回想。 欲寄书如天远，难销夜似年长。小窗风雨碎人肠，更在孤舟枕上。', '宋词精选');
INSERT INTO `poem` VALUES (1034, '眼儿媚·愁云淡淡雨潇潇', '宋代', '石孝友', '愁云淡淡雨潇潇。暮暮复朝朝。别来应是，眉峰翠减，腕玉香销。 小轩独坐相思处，情绪好无聊。一丛萱草，数竿修竹，数叶芭蕉。', '宋词精选');
INSERT INTO `poem` VALUES (1035, '霜叶飞·重九', '宋代', '吴文英', '断烟离绪。关心事，斜阳红隐霜树。半壶秋水荐黄花，香噀西风雨。纵玉勒、轻飞迅羽，凄凉谁吊荒台古？记醉踏南屏， 彩扇咽寒蝉，倦梦不知蛮素。 聊对旧节传杯，尘笺蠹管，断阕经岁慵赋。小蟾斜影转东篱，夜冷残蛩语。早白发、缘愁万缕。惊飙从卷乌纱去。谩细将、茱萸看，但约明年，翠微高处。', '宋词精选');
INSERT INTO `poem` VALUES (1036, '尾犯·夜雨滴空阶', '宋代', '柳永', '夜雨滴空阶，孤馆梦回，情绪萧索。一片闲愁，想丹青难貌。秋渐老、蛩声正苦，夜将阑、灯花旋落。最无端处，总把良宵，只恁孤眠却。 佳人应怪我，别后寡信轻诺。记得当初，翦香云为约。甚时向、幽闺深处，按新词、流霞共酌。再同欢笑，肯把金玉珠珍博。', '宋词精选');
INSERT INTO `poem` VALUES (1037, '惜分飞·泪湿阑干花著露', '宋代', '毛滂', '泪湿阑干花著露。愁到眉峰碧聚。此恨平分取。更无言语。空相觑。 短雨残云无意绪。寂寞朝朝暮暮。今夜山深处。断魂分付。潮回去。', '宋词精选');
INSERT INTO `poem` VALUES (1038, '蝶恋花·卷絮风头寒欲尽', '宋代', '赵令畤', '卷絮风头寒欲尽。坠粉飘红，日日香成阵。新酒又添残酒困。今春不减前春恨。 蝶去莺飞无处问。隔水高楼，望断双鱼信。恼乱横波秋一寸。斜阳只与黄昏近。(横波秋 一作：层波横)', '宋词精选');
INSERT INTO `poem` VALUES (1039, '木兰花慢·莺啼啼不尽', '宋代', '戴复古', '莺啼啼不尽，任燕语、语难通。这一点闲愁，十年不断，恼乱春风。重来故人不见，但依然、杨柳小楼东。记得同题粉壁，而今壁破无踪。 兰皋新涨绿溶溶。流恨落花红。念着破春衫，当时送别，灯下裁缝。相思谩然自苦，算云烟、过眼总成空。落日楚天无际，凭栏目送飞鸿。', '宋词精选');
INSERT INTO `poem` VALUES (1040, '后庭花·一春不识西湖面', '宋代', '许棐', '一春不识西湖面。翠羞红倦。雨窗和泪摇湘管。意长笺短。 知心惟有雕梁燕。自来相伴。东风不管琵琶怨。落花吹遍。', '宋词精选');
INSERT INTO `poem` VALUES (1041, '减字木兰花·去年今夜', '宋代', '吕本中', '去年今夜，同醉月明花树下。此夜江边，月暗长堤柳暗船。 故人何处？带我离愁江外去。来岁花前，又是今年忆去年。', '宋词精选');
INSERT INTO `poem` VALUES (1042, '江城子·平沙浅草接天长', '宋代', '李好古', '平沙浅草接天长。路茫茫。几兴亡。昨夜波声。洗岸骨如霜。千古英雄成底事，徒感慨，谩悲凉。 少年有意伏中行。馘名王。扫沙场。击楫中流，曾记泪沾裳。欲上治安双阙远，空怅望，过维扬。', '宋词精选');
INSERT INTO `poem` VALUES (1043, '忆秦娥·中斋上元客散感旧', '宋代', '刘辰翁', '中斋上元客散感旧，赋《忆秦娥》见属，一读凄然。随韵寄情，不觉悲甚。 烧灯节，朝京道上风和雪。风和雪，江山如旧，朝京人绝。 百年短短兴亡别，与君犹对当时月。当时月，照人烛泪，照人梅发。', '宋词精选');
INSERT INTO `poem` VALUES (1044, '少年游·朝云漠漠散轻丝', '宋代', '周邦彦', '朝云漠漠散轻丝。楼阁淡春姿。柳泣花啼，九街泥重，门外燕飞迟。 而今丽日明金屋，春色在桃枝。不似当时，小桥冲雨，幽恨两人知。', '宋词精选');
INSERT INTO `poem` VALUES (1045, '解连环·怨怀无托', '宋代', '周邦彦', '怨怀无托。嗟情人断绝，信音辽邈。纵妙手、能解连环，似风散雨收，雾轻云薄。燕子楼空，暗尘锁、一床弦索。想移根换叶。尽是旧时，手种红药。 汀洲渐生杜若。料舟依岸曲，人在天角。谩记得、当日音书，把闲语闲言，待总烧却。水驿春回，望寄我、江南梅萼。拚今生，对花对酒，为伊泪落。', '宋词精选');
INSERT INTO `poem` VALUES (1046, '菩萨蛮·彩舟载得离愁动', '宋代', '贺铸', '彩舟载得离愁动，无端更借樵风送。波渺夕阳迟，销魂不自持。 良宵谁与共，赖有窗间梦。可奈梦回时，一番新别离！', '宋词精选');
INSERT INTO `poem` VALUES (1047, '蝶恋花·笑艳秋莲生绿浦', '宋代', '晏几道', '笑艳秋莲生绿浦。红脸青腰，旧识凌波女。照影弄妆娇欲语。西风岂是繁花主。 可恨良辰天不与。才过斜阳，又是黄昏雨。朝落暮开空自许。竟无人解知心苦。', '宋词精选');
INSERT INTO `poem` VALUES (1048, '望江南·三月暮', '宋代', '吴文英', '三月暮，花落更情浓。人去秋千闲挂月，马停杨柳倦嘶风。堤畔画船空。 恹恹醉，长日小帘栊。宿燕夜归银烛外，啼莺声在绿阴中。无处觅残红。', '宋词精选');
INSERT INTO `poem` VALUES (1049, '忆秦娥·花深深', '宋代', '郑文妻', '花深深。一钩罗袜行花阴。行花阴。闲将柳带，细结同心。 日边消息空沈沈。画眉楼上愁登临。愁登临。海棠开后，望到如今。', '宋词精选');
INSERT INTO `poem` VALUES (1050, '满江红·送李御带珙', '宋代', '吴潜', '红玉阶前，问何事、翩然引去。湖海上、一汀鸥鹭，半帆烟雨。报国无门空自怨，济时有策从谁吐。过垂虹亭下系扁舟，鲈堪煮。 拚一醉，留君住。歌一曲，送君路。遍江南江北，欲归何处。世事悠悠浑未了，年光冉冉今如许。试举头、一笑问青天，天无语。', '宋词精选');
INSERT INTO `poem` VALUES (1051, '鹧鸪天·手捻香笺忆小莲', '宋代', '晏几道', '手捻香笺忆小莲。欲将遗恨倩谁传。归来独卧逍遥夜，梦里相逢酩酊天。 花易落，月难圆。只应花月似欢缘。秦筝算有心情在，试写离声入旧弦。', '宋词精选');
INSERT INTO `poem` VALUES (1052, '一剪梅·怀旧', '宋代', '汪元量', '十年愁眼泪巴巴。今日思家，明日思家。一团燕月明窗纱。楼上胡笳，塞上胡笳。 玉人劝我酌流霞。急捻琵琶，缓捻琵琶。一从别后各天涯。欲寄梅花，莫寄梅花。', '宋词精选');
INSERT INTO `poem` VALUES (1053, '茶瓶儿·去年相逢深院宇', '宋代', '李元膺', '去年相逢深院宇，海棠下、曾歌《金缕》。歌罢花如雨。 翠罗衫上，点点红无数。 今岁重寻携手处，空物是人非春暮。回首青门路。乱红飞絮，相逐东风去。', '宋词精选');
INSERT INTO `poem` VALUES (1054, '绮寮怨·上马人扶残醉', '宋代', '周邦彦', '上马人扶残醉，晓风吹未醒。映水曲、翠瓦朱檐，垂杨里、乍见津亭。当时曾题败壁，蛛丝罩、淡墨苔晕青。念去来、岁月如流，徘徊久、叹息愁思盈。 去去倦寻路程。江陵旧事，何曾再问杨琼。旧曲凄清。敛愁黛、与谁听。尊前故人如在，想念我、最关情。何须渭城。歌声未尽处，先泪零。', '宋词精选');
INSERT INTO `poem` VALUES (1055, '望江南·江南月', '宋代', '王琪', '江南月，清夜满西楼。云落开时冰吐鉴，浪花深处玉沉钩。圆缺几时休。 星汉迥，风露入新秋。丹桂不知摇落恨，素娥应信别离愁。天上共悠悠。', '宋词精选');
INSERT INTO `poem` VALUES (1056, '水调歌头·把酒对斜日', '宋代', '杨炎正', '把酒对斜日，无语问西风。胭脂何事，都做颜色染芙蓉。放眼暮江千顷，中有离愁万斛，无处落征鸿。天在阑干角，人倚醉醒中。 千万里，江南北，浙西东。吾生如寄，尚想三径菊花丛。谁是中州豪杰，借我五湖舟楫，去作钓鱼翁。故国且回首，此意莫匆匆。', '宋词精选');
INSERT INTO `poem` VALUES (1057, '江梅引·人间离别易多时', '宋代', '姜夔', '丙辰之冬，予留梁溪，将诣淮南不得，因梦思以述志。 人间离别易多时。见梅枝，忽相思。几度小窗幽梦手同携。今夜梦中无觅处，漫徘徊，寒侵被，尚未知。 湿红恨墨浅封题。宝筝空，无雁飞。俊游巷陌，算空有、古木斜晖。旧约扁舟，心事已成非。歌罢淮南春草赋，又萋萋。漂零客，泪满衣。', '宋词精选');
INSERT INTO `poem` VALUES (1058, '满江红·豫章滕王阁', '宋代', '吴潜', '万里西风，吹我上、滕王高阁。正槛外、楚山云涨，楚江涛作。何处征帆木末去，有时野鸟沙边落。近帘钩、暮雨掩空来，今犹昨。 秋渐紧，添离索。天正远，伤飘泊。叹十年心事，休休莫莫。岁月无多人易老，乾坤虽大愁难著。向黄昏、断送客魂消，城头角。', '宋词精选');
INSERT INTO `poem` VALUES (1059, '诉衷情·出林杏子落金盘', '宋代', '周邦彦', '出林杏子落金盘。齿软怕尝酸。可惜半残青紫，犹有小唇丹。 南陌上，落花闲。雨斑斑。不言不语，一段伤春，都在眉间。', '宋词精选');
INSERT INTO `poem` VALUES (1060, '花心动·春词', '宋代', '阮逸女', '仙苑春浓，小桃开，枝枝已堪攀折。乍雨乍晴，轻暖轻寒，渐近赏花时节。柳摇台榭东风软，帘栊静，幽禽调舌。断魂远，闲寻翠径，顿成愁结。 此恨无人共说。还立尽黄昏，寸心空切。强整绣衾，独掩朱扉，枕簟为谁铺设。夜长更漏传声远，纱窗映、银缸明灭。梦回处，梅梢半笼残月。', '宋词精选');
INSERT INTO `poem` VALUES (1061, '八声甘州·摘青梅荐酒', '宋代', '汤恢', '摘青梅荐酒，甚残寒，犹怯苎萝衣。正柳腴花瘦，绿云冉冉，红雪霏霏。隔屋秦筝依约，谁品春词？回首繁华梦，流水斜晖。 寄隐孤山山下，但一瓢饮水，深掩苔扉。羡青山有思，白鹤忘机。怅年华、不禁搔首，又天涯、弹泪送春归。销魂远，千山啼鴂，十里荼麋。', '宋词精选');
INSERT INTO `poem` VALUES (1062, '传言玉女·钱塘元夕', '宋代', '汪元量', '一片风流，今夕与谁同乐。月台花馆，慨尘埃漠漠。豪华荡尽，只有青山如洛。钱塘依旧，潮生潮落。 万点灯光，羞照舞钿歌箔。玉梅消瘦，恨东皇命薄。昭君泪流，手捻琵琶弦索。离愁聊寄，画楼哀角。', '宋词精选');
INSERT INTO `poem` VALUES (1063, '采桑子·彭浪矶', '宋代', '朱敦儒', '扁舟去作江南客，旅雁孤云。万里烟尘。回首中原泪满巾。 碧山对晚汀洲冷，枫叶芦根。日落波平。愁损辞乡去国人。', '宋词精选');
INSERT INTO `poem` VALUES (1064, '醉落魄·预赏景龙门追悼明节皇后', '宋代', '赵佶', '无言哽噎。看灯记得年时节。行行指月行行说。愿月常圆，休要暂时缺。 今年华市灯罗列。好灯争奈人心别。人前不敢分明说。不忍抬头，羞见旧时月。', '宋词精选');
INSERT INTO `poem` VALUES (1065, '好事近·汴京赐宴闻教坊乐有感', '宋代', '韩元吉', '凝碧旧池头，一听管弦凄切。多少梨园声在，总不堪华发。 杏花无处避春愁，也傍野烟发。惟有御沟声断，似知人呜咽。', '宋词精选');
INSERT INTO `poem` VALUES (1066, '谒金门·春又老', '宋代', '王安石', '春又老。南陌酒香梅小。遍地落花浑不扫。梦回情意悄。红笺寄与添烦恼。细写相思多少。醉后几行书字小。泪痕都揾了。', '宋词精选');
INSERT INTO `poem` VALUES (1067, '浣溪沙·闲弄筝弦懒系裙', '宋代', '晏几道', '闲弄筝弦懒系裙。铅华消尽见天真。眼波低处事还新。 怅恨不逢如意酒。寻思难值有情人。可怜虚度琐窗春。', '宋词精选');
INSERT INTO `poem` VALUES (1068, '倦寻芳慢·露晞向晚', '宋代', '王雱', '露晞向晚，帘幕风轻，小院闲昼。翠迳莺来，惊下乱红铺绣。倚危墙，登高榭，海棠经雨胭脂透。算韶华，又因循过了，清明时候。 倦游燕、风光满目，好景良辰，谁共携手。恨被榆钱，买断两眉长斗。忆高阳，人散后。落花流水仍依旧。这情怀，对东风、尽成消瘦。', '宋词精选');
INSERT INTO `poem` VALUES (1069, '唐多令·秋暮有感', '宋代', '陈允平', '休去采芙蓉。秋江烟水空。带斜阳、一片征鸿。欲顿闲愁无顿处，都著在两眉峰。 心事寄题红。画桥流水东。断肠人、无奈秋浓。回首层楼归去懒，早新月、挂梧桐。', '宋词精选');
INSERT INTO `poem` VALUES (1070, '浪淘沙·赋虞美人草', '宋代', '辛弃疾', '不肯过江东。玉帐匆匆。至今草木忆英雄。唱著虞兮当日曲，便舞春风。 儿女此情同。往事朦胧。湘娥竹上泪痕浓。舜盖重瞳堪痛恨，羽又重瞳。', '宋词精选');
INSERT INTO `poem` VALUES (1071, '望江南·春睡起', '宋代', '金德淑', '春睡起，积雪满燕山。万里长城横玉带，六街灯火已阑珊，人立蓟楼间。 空懊恼，独客此时还。辔压马头金错落？鞍笼驼背锦斓班，肠断唱阳关。', '宋词精选');
INSERT INTO `poem` VALUES (1072, '踏莎行·寒草烟光阔', '宋代', '寇准', '寒草烟光阔，渭水波声咽。春潮雨霁轻尘歇。征鞍发。指青青杨柳，又是轻攀折。动黯然，知有后会甚时节？ 更进一杯酒，歌一阕。叹人生，最难欢聚易离别。且莫辞沉醉，听取阳关彻。念故人，千里至此共明月。', '宋词精选');
INSERT INTO `poem` VALUES (1073, '蝶恋花·移得绿杨栽后院', '宋代', '张先', '移得绿杨栽后院。学舞宫腰，二月青犹短。不比灞陵多送远，残丝乱絮东西岸。 几叶小眉寒不展。莫唱阳关，真个肠先断。分付与春休细看，条条尽是离人怨。', '宋词精选');
INSERT INTO `poem` VALUES (1074, '南乡子·风雨满苹洲', '宋代', '陈亮', '风雨满苹洲。绣阁银屏一夜秋。当日袜尘何处去，溪楼。怎对烟波不泪流。 天际目归舟。浪卷涛翻一叶浮。也似我侬魂不定，悠悠。宋玉方悲庾信愁。', '宋词精选');
INSERT INTO `poem` VALUES (1075, '酷相思·月挂霜林寒欲坠', '宋代', '程垓', '月挂霜林寒欲坠。正门外、催人起。奈离别如今真个是。欲住也、留无计。欲去也、来无计。 马上离魂衣上泪。各自个、供憔悴。问江路梅花开也未？春到也、须频寄。人到也、须频寄。', '宋词精选');
INSERT INTO `poem` VALUES (1076, '木兰花慢·恨莺花渐老', '宋代', '万俟咏', '恨莺花渐老，但芳草、绿汀洲。纵岫壁千寻，榆钱万叠，难买春留。梅花向来始别，又匆匆、结子满枝头。门外垂杨岸侧，画桥谁系兰舟。 悠悠。岁月如流。叹水覆、杳难收。凭画阑，往往抬头举眼，都是春愁。东风晚来更恶，怕飞红、怕絮入书楼。双燕归来问我，怎生不上帘钩。', '宋词精选');
INSERT INTO `poem` VALUES (1077, '虞美人·扁舟三日秋塘路', '宋代', '陈与义', '余甲寅岁自春官出守湖州。秋杪，道中荷花无复存者。乙卯岁，自琐闼以病得请奉祠，卜居青墩镇。立秋后三日行。舟之前后如朝霞相映，望之不断也。以长短句记之。 扁舟三日秋塘路，平度荷花去。病夫因病得来游，更值满川微雨洗新秋。 去年长恨拏舟晚，空见残荷满。今年何以报君恩，一路繁花相送过青墩。', '宋词精选');
INSERT INTO `poem` VALUES (1078, '高阳台·落梅', '宋代', '吴文英', '宫粉雕痕，仙云堕影，无人野水荒湾。古石埋香，金沙锁骨连环。南楼不恨吹横笛，恨晓风、千里关山。半飘零，庭上黄昏，月冷阑干。 寿阳空理愁鸾。问谁调玉髓，暗补香瘢。细雨归鸿，孤山无限春寒。离魂难倩招清此，梦缟衣、解佩溪边。最愁人，啼鸟清明，叶底青圆。', '宋词精选');
INSERT INTO `poem` VALUES (1079, '夜游宫·人去西楼雁杳', '宋代', '吴文英', '人去西楼雁杳。叙别梦、扬州一觉。云澹星疏楚山晓。听啼乌，立河桥，话未了。 雨外蛩声早。细织就、霜丝多少。说与萧娘未知道。向长安，对秋灯，几人老。', '宋词精选');
INSERT INTO `poem` VALUES (1080, '念奴娇·避地溢江书于新亭', '宋代', '王澜', '凭高远望，见家乡、只在白云深处。镇日思归归未得，孤负殷勤杜宇。故国伤心，新亭泪眼，更洒潇潇雨。长江万里，难将此恨流去。 遥想江口依然，鸟啼花谢，今日谁为主。燕子归来，雕梁何处，底事呢喃语。最苦金沙，十万户尽，作血流漂杵。横空剑气，要当一洗残虏。', '宋词精选');
INSERT INTO `poem` VALUES (1081, '浪淘沙慢·晓阴重', '宋代', '周邦彦', '晓阴重，霜凋岸草，雾隐城堞。南陌脂车待发，东门帐饮乍阕。正拂面、垂杨堪揽结。掩红泪、玉手亲折。念汉浦、离鸿去何许？经时信音绝。 情切，望中地远天阔。向露冷风清，无人处，耿耿寒漏咽。嗟万事难忘，惟是轻别。翠尊未竭，凭断云、留取西楼残月。 罗带光消纹衾叠，连环解、旧香顿歇。怨歌永、琼壶敲尽缺。恨春去、不与人期，弄夜色、空馀满地梨花雪。', '宋词精选');
INSERT INTO `poem` VALUES (1082, '金缕歌·陪履斋先生沧浪看梅', '宋代', '吴文英', '乔木生云气。访中兴、英雄陈迹，暗追前事。战舰东风慳借便，梦断神州故里。旋小筑、吴宫闲地。华表月明归夜鹤，叹当时、花竹今如此。枝上露，溅清泪。 遨头小簇行春队。步苍苔、寻幽别坞，问梅开未。重唱梅边新度曲，催发寒梢冻蕊。此心与、东君同意。后不如今今非昔，两无言、相对沧浪水。怀此恨，寄残醉。', '宋词精选');
INSERT INTO `poem` VALUES (1083, '楚天遥过清江引·有意送春归', '宋代', '如晦', '有意送春归，无计留春住。毕竟年年用着来，何似休归去。 目断楚天遥，不见春归路。风急桃花也似愁，点点飞红雨。', '宋词精选');
INSERT INTO `poem` VALUES (1084, '菩萨蛮·春来春去催人老', '宋代', '张元干', '三月晦，送春有集，坐中偶书。 春来春去催人老，老夫争肯输年少。醉后少年狂，白髭殊未妨。 插花还起舞，管领风光处。把酒共留春，莫教花笑人。', '宋词精选');
INSERT INTO `poem` VALUES (1085, '献仙音·吊雪香亭梅', '宋代', '周密', '松雪飘寒，岭云吹冻，红破数椒春浅。衬舞台荒，浣妆池冷，凄凉市朝轻换。叹花与人凋谢，依依岁华晚。 共凄黯。问东风、几番吹梦，应惯识、当年翠屏金辇。一片古今愁，但废绿、平烟空远。无语销魂，对斜阳、衰草泪满。又西泠残笛，低送数声春怨。', '宋词精选');
INSERT INTO `poem` VALUES (1086, '望江南·燕塞雪', '宋代', '华清淑', '燕塞雪，片片大如拳。蓟上酒楼喧鼓吹，帝城车马走骈阗。羁馆独凄然。 燕塞月，缺了又还圆。万里妾心愁更苦，十春和泪看婵娟。何日是归年。', '宋词精选');
INSERT INTO `poem` VALUES (1087, '恋绣衾·柳丝空有千万条', '宋代', '赵汝茪', '柳丝空有千万条。系不住、溪桡。想今宵、也对新月，过轻寒、何处小桥。 玉箫榭春多少，溜啼红、脸霞未消。怪别来、胭脂慵傅，被东风、偷在杏梢。', '宋词精选');
INSERT INTO `poem` VALUES (1088, '昭君怨·牡丹', '宋代', '刘克庄', '曾看洛阳旧谱，只许姚黄独步。若比广陵花，太亏他。 旧日王侯园圃，今日荆榛狐兔。君莫说中州，怕花愁。', '宋词精选');
INSERT INTO `poem` VALUES (1089, '鹧鸪天·镇日无心扫黛眉', '宋代', '夏竦', '镇日无心扫黛眉，临行愁见理征衣。尊前只恐伤郎意，阁泪汪汪不敢垂。 停宝马，捧瑶卮，相斟相劝忍分离？不如饮待奴先醉，图得不知郎去时。', '宋词精选');
INSERT INTO `poem` VALUES (1090, '清平乐·凤城春浅', '宋代', '陈允平', '凤城春浅，寒压花梢颤。有约不来梁上燕，十二绣帘空卷。 去年共倚秋千，今年独倚阑干。误了海棠时候，不成直待花残。(独倚 一作：独上)', '宋词精选');
INSERT INTO `poem` VALUES (1091, '吴山青·金璞明', '宋代', '赵溍', '金璞明。玉璞明。小小杯柈翠袖擎。满将春色盛。仙珮鸣。玉珮鸣。雪月花中过洞庭。此时人独清。', '宋词精选');
INSERT INTO `poem` VALUES (1092, '玉京秋·烟水阔', '宋代', '周密', '长安独客，又见西风，素月丹枫凄然其为秋也，因调夹钟羽一解 烟水阔。高林弄残照，晚蜩凄切。碧碪度韵，银床飘叶。衣湿桐阴露冷，采凉花、时赋秋雪。叹轻别。一襟幽事，砌蛩能说。 客思吟商还怯。怨歌长、琼壶暗缺。翠扇恩疏，红衣香褪，翻成消歇。玉骨西风，恨最恨、闲却新凉时节。楚箫咽。谁倚西楼淡月。', '宋词精选');
INSERT INTO `poem` VALUES (1093, '南浦·旅怀', '宋代', '孔夷', '风悲画角，听单于、三弄落谯门。投宿骎骎征骑，飞雪满孤村。酒市渐闲灯火，正敲窗、乱叶舞纷纷。送数声惊雁，下离烟水，嘹唳度寒云。 好在半胧溪月，到如今、无处不销魂。故国梅花归梦，愁损绿罗裙。为问暗香闲艳，也相思、万点付啼痕。算翠屏应是，两眉馀恨倚黄昏。', '宋词精选');
INSERT INTO `poem` VALUES (1094, '渔家傲引·子月水寒风又烈', '宋代', '洪适', '子月水寒风又烈。巨鱼漏网成虚设。圉圉从它归丙穴。谋自拙。空归不管旁人说。 昨夜醉眠西浦月。今宵独钓南溪雪。妻子一船衣百结。长欢悦。不知人世多离别。', '宋词精选');
INSERT INTO `poem` VALUES (1095, '忆君王·依依宫柳拂宫墙', '宋代', '谢克家', '依依宫柳拂宫墙，楼殿无人春昼长。燕子归来依旧忙。忆君王，月破黄昏人断肠。', '宋词精选');
INSERT INTO `poem` VALUES (1096, '菩萨蛮·湘东驿', '宋代', '范成大', '客行忽到湘东驿。明朝真是潇湘客。晴碧万重云。几时逢故人。 江南如塞北。别后书难得。先自雁来稀。那堪春半时。', '宋词精选');
INSERT INTO `poem` VALUES (1097, '菩萨蛮·画船捶鼓催君去', '宋代', '舒亶', '画船捶鼓催君去，高楼把酒留君住。去住若为情，西江潮欲平。 江潮容易得，只是人南北。今日此樽空，知君何日同！', '宋词精选');
INSERT INTO `poem` VALUES (1098, '醉桃源·赠卢长笛', '宋代', '吴文英', '沙河塘上旧游嬉。卢郎年少时。一声长笛月中吹。和云和雁飞。 惊物换，叹星移。相看两鬓丝。断肠吴苑草凄凄。倚楼人未归。', '宋词精选');
INSERT INTO `poem` VALUES (1099, '绣鸾凤花犯·赋水仙', '宋代', '周密', '楚江湄，湘娥乍见，无言洒清泪。淡然春意。空独倚东风，芳思谁寄。凌波路冷秋无际，香云随步起。谩记得、汉宫仙掌，亭亭明月底。 冰弦写怨更多情，骚人恨，枉赋芳兰幽芷。春思远，谁叹赏、国香风味。相将共、岁寒伴侣，小窗净、沉烟熏翠袂。幽梦觉，涓涓清露，一枝灯影里。', '宋词精选');
INSERT INTO `poem` VALUES (1100, '菩萨蛮·举头忽见衡阳雁', '宋代', '陈达叟', '举头忽见衡阳雁。千声万字情何限。叵而薄情夫。一行书也无。 泣归香阁恨。和泪掩红粉。待雁却回时。也无书寄伊。', '宋词精选');
INSERT INTO `poem` VALUES (1101, '满江红·小院深深', '宋代', '岳珂', '小院深深，悄镇日、阴晴无据。春未足，闺愁难寄，琴心谁与？曲径穿花寻蛱蝶，虚阑傍日教鹦鹉。笑十三杨柳女儿腰，东风舞。 云外月，风前絮。情与恨，长如许。想绮窗今夜，为谁凝伫？洛浦梦回留佩客，秦楼声断吹箫侣。正黄昏时候杏花寒，廉纤雨。', '宋词精选');
INSERT INTO `poem` VALUES (1102, '望江南·幽州九日', '宋代', '汪元量', '官舍悄，坐到月西斜。永夜角声悲自语，客心愁破正思家。南北各天涯。 肠断裂，搔首一长嗟。绮席象床寒玉枕，美人何处醉黄花。和泪捻琵琶。', '宋词精选');
INSERT INTO `poem` VALUES (1103, '江梅引·忆江梅', '宋代', '洪皓', '岁在壬戌，甫临长至，张总侍御邀饮。众宾皆退，独留少款。侍婢歌江梅引，有“念此情、家万里”之句，仆曰：此词殆为我作也。又闻本朝使命将至，感慨久之。既归，不寝，追和四章，多用古人诗赋，各有一笑字，聊以自宽。如暗香、疏影、相思等语，虽甚奇，经前人用者众，嫌其一律，故辄略之。卒押吹字，非风即笛，不可易也。此方无梅花，士人罕有知梅事者，故皆注所出（旧注：阙一首。此录示乡人者，北人谓之四笑江梅引） 天涯除馆忆江梅。几枝开。使南来。还带馀杭、春信到燕台。准拟寒英聊慰远，隔山水，应销落，赴诉谁。 空恁遐想笑摘蕊。断回肠，思故里。漫弹绿绮。引三弄、不觉魂飞。更听胡笳、哀怨泪沾衣。乱插繁花须异日，待孤讽，怕东风，一夜吹。', '宋词精选');
INSERT INTO `poem` VALUES (1104, '临江仙·千里长安名利客', '宋代', '刘彤', '千里长安名利客，轻离轻散寻常。难禁三月好风光。满阶芳草绿，一片杏花香。 记得年时临上马，看人眼泪汪汪。如今不忍更思量。恨无千日酒，空断九回肠。', '宋词精选');
INSERT INTO `poem` VALUES (1105, '青玉案·和贺方回韵送山谷弟贬宜州', '宋代', '黄大临', '千峰百嶂宜州路。天黯淡、知人去。晓别吾家黄叔度。弟兄华发，远山修水，异日同归处。 樽罍饮散长亭暮。别语缠绵不成句。已断离肠能几许。水村山馆，夜阑无寐，听尽空阶雨。', '宋词精选');
INSERT INTO `poem` VALUES (1106, '临江仙·登凌歊台感怀', '宋代', '李之仪', '偶向凌歊台上望，春光已过三分。江山重叠倍销魂。风花飞有态，烟絮坠无痕。 已是年来伤感甚，那堪旧恨仍存。清愁满眼共谁论。却应台下草，不解忆王孙。', '宋词精选');
INSERT INTO `poem` VALUES (1107, '菩萨蛮·商妇怨', '宋代', '江开', '春时江上帘纤雨。张帆打鼓开船去。秋晚恰归来。看看船又开。 嫁郎如未嫁。长是凄凉夜。情少利心多。郎如年少何。', '宋词精选');
INSERT INTO `poem` VALUES (1108, '鹧鸪天·卫县道中有怀其人', '宋代', '史达祖', '雁足无书古塞幽。一程烟草一程愁。帽檐尘重风吹野，帐角香销月满楼。 情思乱，梦魂浮。缃裙多忆敝貂裘。官河水静阑干暖，徙倚斜阳怨晚秋。', '宋词精选');
INSERT INTO `poem` VALUES (1109, '长相思·南高峰', '宋代', '袁正真', '南高峰。北高峰。南北高峰云淡浓。湖山图画中。采芙容。小小红船西复东。相思无路通。', '宋词精选');
INSERT INTO `poem` VALUES (1110, '西河·和王潜斋韵', '宋代', '曹豳', '今日事。何人弄得如此。漫漫白骨蔽川原，恨何日已。关河万里寂无烟，月明空照芦苇。 谩哀痛，无及矣。无情莫问江水。西风落日惨新亭，几人坠泪。战和何者是良筹，扶危但看天意。 只今寂寞薮泽里。岂无人、高卧闾里。试问安危谁寄。定相将、有诏催公起。须信前书言犹未。', '宋词精选');
INSERT INTO `poem` VALUES (1111, '长相思·村姑儿', '宋代', '蔡伸', '村姑儿，红袖衣，初发黄梅插秧时，双双女伴随。 长歌诗，短歌诗，歌里真情恨别离，休言伊不知。', '宋词精选');
INSERT INTO `poem` VALUES (1112, '清平乐·怀人', '宋代', '赵崇', '莺歌蝶舞。池馆春多处。满架花云留不住。散作一川香雨。 相思夜夜情＿。青衫泪满啼红。料想故园桃李，也应怨月愁风。', '宋词精选');
INSERT INTO `poem` VALUES (1113, '玉楼春·华堂帘幕飘香雾', '宋代', '周端臣', '华堂帘幕飘香雾。一搦楚腰轻束素。翩跹舞态燕还鹭，绰约妆容花尽妒。樽前谩咏高唐赋巫峡云深留不住。重来花畔倚阑干，愁满阑干无倚处。', '宋词精选');
INSERT INTO `poem` VALUES (1114, '三姝媚·过都城旧居有感', '宋代', '吴文英', '湖山经醉惯。渍春衫、啼痕酒痕无限。又客长安，叹断襟零袂，涴尘谁浣。紫曲门荒，沿败井、风摇青蔓。对语东邻，犹是曾巢，谢堂双燕。 春梦人间须断。但怪得、当年梦缘能短。绣屋秦筝，傍海棠偏爱，夜深开宴。舞歇歌沈，花未减、红颜先变。伫久河桥欲去，斜阳泪满。', '宋词精选');
INSERT INTO `poem` VALUES (1115, '行行重行行', '两汉', '佚名', '行行重行行，与君生别离。 相去万余里，各在天一涯。 道路阻且长，会面安可知？ 胡马依北风，越鸟巢南枝。 相去日已远，衣带日已缓。 浮云蔽白日，游子不顾返。 思君令人老，岁月忽已晚。 弃捐勿复道，努力加餐饭。', '古诗十九首');
INSERT INTO `poem` VALUES (1116, '青青河畔草', '两汉', '佚名', '青青河畔草，郁郁园中柳。 盈盈楼上女，皎皎当窗牖。 娥娥红粉妆，纤纤出素手。 昔为倡家女，今为荡子妇。 荡子行不归，空床难独守。', '古诗十九首');
INSERT INTO `poem` VALUES (1117, '青青陵上柏', '两汉', '佚名', '青青陵上柏，磊磊涧中石。 人生天地间，忽如远行客。 斗酒相娱乐，聊厚不为薄。 驱车策驽马，游戏宛与洛。 洛中何郁郁，冠带自相索。 长衢罗夹巷，王侯多第宅。 两宫遥相望，双阙百余尺。 极宴娱心意，戚戚何所迫？', '古诗十九首');
INSERT INTO `poem` VALUES (1118, '今日良宴会', '两汉', '佚名', '今日良宴会，欢乐难具陈。 弹筝奋逸响，新声妙入神。 令德唱高言，识曲听其真。 齐心同所愿，含意俱未申。 人生寄一世，奄忽若飙尘。 何不策高足，先据要路津。 无为守穷贱，坎坷长苦辛。', '古诗十九首');
INSERT INTO `poem` VALUES (1119, '西北有高楼', '两汉', '佚名', '西北有高楼，上与浮云齐。 交疏结绮窗，阿阁三重阶。 上有弦歌声，音响一何悲！ 谁能为此曲，无乃杞梁妻。 清商随风发，中曲正徘徊。 一弹再三叹，慷慨有余哀。 不惜歌者苦，但伤知音稀。 愿为双鸿鹄，奋翅起高飞。', '古诗十九首');
INSERT INTO `poem` VALUES (1120, '涉江采芙蓉', '两汉', '佚名', '涉江采芙蓉，兰泽多芳草。 采之欲遗谁，所思在远道。 还顾望旧乡，长路漫浩浩。 同心而离居，忧伤以终老。', '古诗十九首');
INSERT INTO `poem` VALUES (1121, '明月何皎皎', '两汉', '佚名', '明月何皎皎，照我罗床帏。 忧愁不能寐，揽衣起徘徊。 客行虽云乐，不如早旋归。 出户独彷徨，愁思当告谁！ 引领还入房，泪下沾裳衣。', '古诗十九首');
INSERT INTO `poem` VALUES (1122, '冉冉孤生竹', '两汉', '佚名', '冉冉孤生竹，结根泰山阿。 与君为新婚，菟丝附女萝。 菟丝生有时，夫妇会有宜。 千里远结婚，悠悠隔山陂。 思君令人老，轩车来何迟！ 伤彼蕙兰花，含英扬光辉。 过时而不采，将随秋草萎。 君亮执高节，贱妾亦何为！', '古诗十九首');
INSERT INTO `poem` VALUES (1123, '庭中有奇树', '两汉', '佚名', '庭中有奇树，绿叶发华滋。 攀条折其荣，将以遗所思。 馨香盈怀袖，路远莫致之。 此物何足贵，但感别经时。', '古诗十九首');
INSERT INTO `poem` VALUES (1124, '迢迢牵牛星', '两汉', '佚名', '迢迢牵牛星，皎皎河汉女。 纤纤擢素手，札札弄机杼。 终日不成章，泣涕零如雨。 河汉清且浅，相去复几许！ 盈盈一水间，脉脉不得语。', '古诗十九首');
INSERT INTO `poem` VALUES (1125, '回车驾言迈', '两汉', '佚名', '回车驾言迈，悠悠涉长道。 四顾何茫茫，东风摇百草。 所遇无故物，焉得不速老。 盛衰各有时，立身苦不早。 人生非金石，岂能长寿考？ 奄忽随物化，荣名以为宝。', '古诗十九首');
INSERT INTO `poem` VALUES (1126, '东城高且长', '两汉', '佚名', '东城高且长，逶迤自相属。 回风动地起，秋草萋已绿。 四时更变化，岁暮一何速！ 晨风怀苦心，蟋蟀伤局促。 荡涤放情志，何为自结束？ 燕赵多佳人，美者颜如玉。 被服罗裳衣，当户理清曲。 音响一何悲！弦急知柱促。 驰情整巾带，沉吟聊踯躅。 思为双飞燕，衔泥巢君屋。', '古诗十九首');
INSERT INTO `poem` VALUES (1127, '驱车上东门', '两汉', '佚名', '驱车上东门，遥望郭北墓。 白杨何萧萧，松柏夹广路。 下有陈死人，杳杳即长暮。 潜寐黄泉下，千载永不寤。 浩浩阴阳移，年命如朝露。 人生忽如寄，寿无金石固。 万岁更相送，贤圣莫能度。 服食求神仙，多为药所误。 不如饮美酒，被服纨与素。', '古诗十九首');
INSERT INTO `poem` VALUES (1128, '去者日以疏', '两汉', '佚名', '去者日以疏，来者日以亲。 出郭门直视，但见丘与坟。 古墓犁为田，松柏摧为薪。 白杨多悲风，萧萧愁杀人。 思还故里闾，欲归道无因。', '古诗十九首');
INSERT INTO `poem` VALUES (1129, '生年不满百', '两汉', '佚名', '生年不满百，常怀千岁忧。 昼短苦夜长，何不秉烛游！ 为乐当及时，何能待来兹？ 愚者爱惜费，但为後世嗤。 仙人王子乔，难可与等期。', '古诗十九首');
INSERT INTO `poem` VALUES (1130, '凛凛岁云暮', '两汉', '佚名', '凛凛岁云暮，蝼蛄夕鸣悲。 凉风率已厉，游子寒无衣。 锦衾遗洛浦，同袍与我违。 独宿累长夜，梦想见容辉。 良人惟古欢，枉驾惠前绥。 愿得常巧笑，携手同车归。 既来不须臾，又不处重闱。 亮无晨风翼，焉能凌风飞？ 眄睐以适意，引领遥相睎。 徙倚怀感伤，垂涕沾双扉。', '古诗十九首');
INSERT INTO `poem` VALUES (1131, '孟冬寒气至', '两汉', '佚名', '孟冬寒气至，北风何惨栗。 愁多知夜长，仰观众星列。 三五明月满，四五蟾兔缺。 客从远方来，遗我一书札。 上言长相思，下言久离别。 置书怀袖中，三岁字不灭。 一心抱区区，惧君不识察。', '古诗十九首');
INSERT INTO `poem` VALUES (1132, '客从远方来', '两汉', '佚名', '客从远方来，遗我一端绮。 相去万余里，故人心尚尔。 文采双鸳鸯，裁为合欢被。 著以长相思，缘以结不解。 以胶投漆中，谁能别离此？', '古诗十九首');
INSERT INTO `poem` VALUES (1133, '明月皎夜光', '两汉', '佚名', '明月皎夜光，促织鸣东壁。 玉衡指孟冬，众星何历历。 白露沾野草，时节忽复易。 秋蝉鸣树间，玄鸟逝安适。 昔我同门友，高举振六翮。 不念携手好，弃我如遗迹。 南箕北有斗，牵牛不负轭。 良无盘石固，虚名复何益？', '古诗十九首');
INSERT INTO `poem` VALUES (1134, '关雎', '先秦', '佚名', '关关雎鸠，在河之洲。窈窕淑女，君子好逑。 参差荇菜，左右流之。窈窕淑女，寤寐求之。 求之不得，寤寐思服。悠哉悠哉，辗转反侧。 参差荇菜，左右采之。窈窕淑女，琴瑟友之。 参差荇菜，左右芼之。窈窕淑女，钟鼓乐之。', '诗经');
INSERT INTO `poem` VALUES (1135, '葛覃', '先秦', '佚名', '葛之覃兮，施于中谷，维叶萋萋。黄鸟于飞，集于灌木，其鸣喈喈。 葛之覃兮，施于中谷，维叶莫莫。是刈是濩，为絺为綌，服之无斁。 言告师氏，言告言归。薄污我私，薄浣我衣。害浣害否？归宁父母。', '诗经');
INSERT INTO `poem` VALUES (1136, '卷耳', '先秦', '佚名', '采采卷耳，不盈顷筐。嗟我怀人，寘彼周行。(寘 通：置) 陟彼崔嵬，我马虺隤。我姑酌彼金罍，维以不永怀。 陟彼高冈，我马玄黄。我姑酌彼兕觥，维以不永伤。 陟彼砠矣，我马瘏矣，我仆痡矣，云何吁矣。', '诗经');
INSERT INTO `poem` VALUES (1137, '樛木', '先秦', '佚名', '南有樛木，葛藟累之。乐只君子，福履绥之。 南有樛木，葛藟荒之。乐只君子，福履将之。 南有樛木，葛藟萦之。乐只君子，福履成之。', '诗经');
INSERT INTO `poem` VALUES (1138, '螽斯', '先秦', '佚名', '螽斯羽，诜诜兮。宜尔子孙，振振兮。 螽斯羽，薨薨兮。宜尔子孙，绳绳兮。 螽斯羽，揖揖兮。宜尔子孙，蛰蛰兮。', '诗经');
INSERT INTO `poem` VALUES (1139, '桃夭', '先秦', '佚名', '桃之夭夭，灼灼其华。之子于归，宜其室家。 桃之夭夭，有蕡其实。之子于归，宜其家室。 桃之夭夭，其叶蓁蓁。之子于归，宜其家人。', '诗经');
INSERT INTO `poem` VALUES (1140, '国风·周南·兔罝', '先秦', '佚名', '肃肃兔罝，椓之丁丁。赳赳武夫，公侯干城。 肃肃兔罝，施于中逵。赳赳武夫，公侯好仇。 肃肃兔罝，施于中林。赳赳武夫，公侯腹心。', '诗经');
INSERT INTO `poem` VALUES (1141, '国风·周南·芣苢', '先秦', '佚名', '采采芣苢，薄言采之。 采采芣苢，薄言有之。 采采芣苢，薄言掇之。 采采芣苢，薄言捋之。 采采芣苢，薄言袺之。 采采芣苢，薄言襭之。', '诗经');
INSERT INTO `poem` VALUES (1142, '汉广', '先秦', '佚名', '南有乔木，不可休思。 汉有游女，不可求思。 汉之广矣，不可泳思。 江之永矣，不可方思。 翘翘错薪，言刈其楚。 之子于归，言秣其马。 汉之广矣，不可泳思。 江之永矣，不可方思。 翘翘错薪，言刈其蒌。 之子于归，言秣其驹。 汉之广矣，不可泳思。 江之永矣，不可方思。', '诗经');
INSERT INTO `poem` VALUES (1143, '国风·周南·汝坟', '先秦', '佚名', '遵彼汝坟，伐其条枚。未见君子，惄如调饥。 遵彼汝坟，伐其条肄。既见君子，不我遐弃。 鲂鱼赪尾，王室如毁。虽则如毁，父母孔迩。', '诗经');
INSERT INTO `poem` VALUES (1144, '国风·周南·麟之趾', '先秦', '佚名', '麟之趾，振振公子，于嗟麟兮。 麟之定，振振公姓，于嗟麟兮。 麟之角，振振公族，于嗟麟兮。', '诗经');
INSERT INTO `poem` VALUES (1145, '国风·召南·鹊巢', '先秦', '佚名', '维鹊有巢，维鸠居之。之子于归，百两御之。 维鹊有巢，维鸠方之。之子于归，百两将之。 维鹊有巢，维鸠盈之。之子于归，百两成之。', '诗经');
INSERT INTO `poem` VALUES (1146, '采蘩', '先秦', '佚名', '于以采蘩？于沼于沚。于以用之？公侯之事。 于以采蘩？于涧之中。于以用之？公侯之宫。 被之僮僮，夙夜在公。被之祁祁，薄言还归。', '诗经');
INSERT INTO `poem` VALUES (1147, '国风·召南·草虫', '先秦', '佚名', '喓喓草虫，趯趯阜螽。未见君子，忧心忡忡。亦既见止，亦既觏止，我心则降。 陟彼南山，言采其蕨。未见君子，忧心惙惙。亦既见止，亦既觏止，我心则说。 陟彼南山，言采其薇。未见君子，我心伤悲。亦既见止，亦既觏止，我心则夷。', '诗经');
INSERT INTO `poem` VALUES (1148, '采蘋', '先秦', '佚名', '于以采蘋？南涧之滨。于以采藻？于彼行潦。 于以盛之？维筐及筥。于以湘之？维锜及釜。 于以奠之？宗室牖下。谁其尸之？有齐季女。', '诗经');
INSERT INTO `poem` VALUES (1149, '甘棠', '先秦', '佚名', '蔽芾甘棠，勿翦勿伐，召伯所茇。 蔽芾甘棠，勿翦勿败，召伯所憩。 蔽芾甘棠，勿翦勿拜，召伯所说。', '诗经');
INSERT INTO `poem` VALUES (1150, '行露', '先秦', '佚名', '厌浥行露，岂不夙夜，谓行多露。 谁谓雀无角？何以穿我屋？谁谓女无家？何以速我狱？虽速我狱，室家不足！ 谁谓鼠无牙？何以穿我墉？谁谓女无家？何以速我讼？虽速我讼，亦不女从！', '诗经');
INSERT INTO `poem` VALUES (1151, '羔羊', '先秦', '佚名', '羔羊之皮，素丝五紽。退食自公，委蛇委蛇。 羔羊之革，素丝五緎。委蛇委蛇，自公退食。 羔羊之缝，素丝五总。委蛇委蛇，退食自公。', '诗经');
INSERT INTO `poem` VALUES (1152, '殷其雷', '先秦', '佚名', '殷其雷，在南山之阳。何斯违斯，莫敢或遑？振振君子，归哉归哉！ 殷其雷，在南山之侧。何斯违斯，莫敢遑息？振振君子，归哉归哉！ 殷其雷，在南山之下。何斯违斯，莫或遑处？振振君子，归哉归哉！', '诗经');
INSERT INTO `poem` VALUES (1153, '摽有梅', '先秦', '佚名', '摽有梅，其实七兮。求我庶士，迨其吉兮。 摽有梅，其实三兮。求我庶士，迨其今兮。 摽有梅，顷筐塈之。求我庶士，迨其谓之。', '诗经');
INSERT INTO `poem` VALUES (1154, '小星', '先秦', '佚名', '嘒彼小星，三五在东。肃肃宵征，夙夜在公。寔命不同。 嘒彼小星，维参与昴。肃肃宵征，抱衾与裯。寔命不犹。', '诗经');
INSERT INTO `poem` VALUES (1155, '江有汜', '先秦', '佚名', '江有汜，之子归，不我以。不我以，其后也悔。 江有渚，之子归，不我与。不我与，其后也处。 江有沱，之子归，不我过。不我过，其啸也歌。', '诗经');
INSERT INTO `poem` VALUES (1156, '国风·召南·野有死麕', '先秦', '佚名', '野有死麕，白茅包之。有女怀春，吉士诱之。 林有朴樕，野有死鹿。白茅纯束，有女如玉。 舒而脱脱兮！无感我帨兮！无使尨也吠！', '诗经');
INSERT INTO `poem` VALUES (1157, '何彼襛矣', '先秦', '佚名', '何彼襛矣，唐棣之华？曷不肃雍？王姬之车。 何彼襛矣，华如桃李？平王之孙，齐侯之子。 其钓维何？维丝伊缗。齐侯之子，平王之孙。', '诗经');
INSERT INTO `poem` VALUES (1158, '驺虞', '先秦', '佚名', '彼茁者葭，壹发五豝，于嗟乎驺虞！ 彼茁者蓬，壹发五豵，于嗟乎驺虞！', '诗经');
INSERT INTO `poem` VALUES (1159, '国风·邶风·柏舟', '先秦', '佚名', '泛彼柏舟，亦泛其流。耿耿不寐，如有隐忧。微我无酒，以敖以游。 我心匪鉴，不可以茹。亦有兄弟，不可以据。薄言往愬，逢彼之怒。 我心匪石，不可转也。我心匪席，不可卷也。威仪棣棣，不可选也。 忧心悄悄，愠于群小。觏闵既多，受侮不少。静言思之，寤辟有摽。 日居月诸，胡迭而微？心之忧矣，如匪浣衣。静言思之，不能奋飞。', '诗经');
INSERT INTO `poem` VALUES (1160, '国风·邶风·绿衣', '先秦', '佚名', '绿兮衣兮，绿衣黄里。心之忧矣，曷维其已？(黄里 一作：黄裹) 绿兮衣兮，绿衣黄裳。心之忧矣，曷维其亡？ 绿兮丝兮，女所治兮。我思古人，俾无訧兮。 絺兮绤兮，凄其以风。我思古人，实获我心。', '诗经');
INSERT INTO `poem` VALUES (1161, '国风·邶风·燕燕', '先秦', '佚名', '燕燕于飞，差池其羽。之子于归，远送于野。瞻望弗及，泣涕如雨。 燕燕于飞，颉之颃之。之子于归，远于将之。瞻望弗及，伫立以泣。 燕燕于飞，下上其音。之子于归，远送于南。瞻望弗及，实劳我心。 仲氏任只，其心塞渊。终温且惠，淑慎其身。先君之思，以勖寡人。', '诗经');
INSERT INTO `poem` VALUES (1162, '国风·邶风·日月', '先秦', '佚名', '日居月诸，照临下土。乃如之人兮，逝不古处？胡能有定？宁不我顾。 日居月诸，下土是冒。乃如之人兮，逝不相好。胡能有定？宁不我报。 日居月诸，出自东方。乃如之人兮，德音无良。胡能有定？俾也可忘。 日居月诸，东方自出。父兮母兮，畜我不卒。胡能有定？报我不述。', '诗经');
INSERT INTO `poem` VALUES (1163, '终风', '先秦', '佚名', '终风且暴，顾我则笑，谑浪笑敖，中心是悼。 终风且霾，惠然肯来，莫往莫来，悠悠我思。 终风且曀，不日有曀，寤言不寐，愿言则嚏。 曀曀其阴，虺虺其雷，寤言不寐，愿言则怀。', '诗经');
INSERT INTO `poem` VALUES (1164, '击鼓', '先秦', '佚名', '击鼓其镗，踊跃用兵。土国城漕，我独南行。 从孙子仲，平陈与宋。不我以归，忧心有忡。 爰居爰处？爰丧其马？于以求之？于林之下。 死生契阔，与子成说。执子之手，与子偕老。 于嗟阔兮，不我活兮。于嗟洵兮，不我信兮。', '诗经');
INSERT INTO `poem` VALUES (1165, '国风·邶风·凯风', '先秦', '佚名', '凯风自南，吹彼棘心。棘心夭夭，母氏劬劳。 凯风自南，吹彼棘薪。母氏圣善，我无令人。 爰有寒泉？在浚之下。有子七人，母氏劳苦。 睍睆黄鸟，载好其音。有子七人，莫慰母心。', '诗经');
INSERT INTO `poem` VALUES (1166, '雄雉', '先秦', '佚名', '雄雉于飞，泄泄其羽。我之怀矣，自诒伊阻。 雄雉于飞，下上其音。展矣君子，实劳我心。 瞻彼日月，悠悠我思。道之云远，曷云能来？ 百尔君子，不知德行。不忮不求，何用不臧？', '诗经');
INSERT INTO `poem` VALUES (1167, '匏有苦叶', '先秦', '佚名', '匏有苦叶，济有深涉。深则厉，浅则揭。 有瀰济盈，有鷕雉鸣。济盈不濡轨，雉鸣求其牡。 雍雍鸣雁，旭日始旦。士如归妻，迨冰未泮。 招招舟子，人涉卬否。人涉卬否，卬须我友。', '诗经');
INSERT INTO `poem` VALUES (1168, '国风·邶风·谷风', '先秦', '佚名', '习习谷风，以阴以雨。黾勉同心，不宜有怒。采葑采菲，无以下体？德音莫违，及尔同死。 行道迟迟，中心有违。不远伊迩，薄送我畿。谁谓荼苦，其甘如荠。宴尔新婚，如兄如弟。 泾以渭浊，湜湜其沚。宴尔新婚，不我屑以。毋逝我梁，毋发我笱。我躬不阅，遑恤我后。 就其深矣，方之舟之。就其浅矣，泳之游之。何有何亡，黾勉求之。凡民有丧，匍匐救之。 不我能慉，反以我为仇。既阻我德，贾用不售。昔育恐育鞫，及尔颠覆。既生既育，比予于毒。 我有旨蓄，亦以御冬。宴尔新婚，以我御穷。有洸有溃，既诒我肄。不念昔者，伊余来塈。', '诗经');
INSERT INTO `poem` VALUES (1169, '国风·邶风·式微', '先秦', '佚名', '式微，式微，胡不归？微君之故，胡为乎中露！ 式微，式微，胡不归？微君之躬，胡为乎泥中！', '诗经');
INSERT INTO `poem` VALUES (1170, '国风·邶风·旄丘', '先秦', '佚名', '旄丘之葛兮，何诞之节兮。叔兮伯兮，何多日也？ 何其处也？必有与也！何其久也？必有以也！ 狐裘蒙戎，匪车不东。叔兮伯兮，靡所与同。 琐兮尾兮，流离之子。叔兮伯兮，褎如充耳。', '诗经');
INSERT INTO `poem` VALUES (1171, '简兮', '先秦', '佚名', '简兮简兮，方将万舞。日之方中，在前上处。 硕人俣俣，公庭万舞。有力如虎，执辔如组。 左手执龠，右手秉翟。赫如渥赭，公言锡爵。 山有榛，隰有苓。云谁之思？西方美人。彼美人兮，西方之人兮。', '诗经');
INSERT INTO `poem` VALUES (1172, '国风·邶风·泉水', '先秦', '佚名', '毖彼泉水，亦流于淇。有怀于卫，靡日不思。娈彼诸姬，聊与之谋。 出宿于泲，饮饯于祢，女子有行，远父母兄弟。问我诸姑，遂及伯姊。 出宿于干，饮饯于言。载脂载舝，还车言迈。遄臻于卫，不瑕有害？ 我思肥泉，兹之永叹。思须与漕，我心悠悠。驾言出游，以写我忧。', '诗经');
INSERT INTO `poem` VALUES (1173, '北门', '先秦', '佚名', '出自北门，忧心殷殷。终窭且贫，莫知我艰。已焉哉！天实为之，谓之何哉！ 王事适我，政事一埤益我。我入自外，室人交徧谪我。已焉哉！天实为之，谓之何哉！ 王事敦我，政事一埤遗我。我入自外，室人交徧摧我。已焉哉！天实为之，谓之何哉！', '诗经');
INSERT INTO `poem` VALUES (1174, '北风', '先秦', '佚名', '北风其凉，雨雪其雱。惠而好我，携手同行。其虚其邪？既亟只且！ 北风其喈，雨雪其霏。惠而好我，携手同归。其虚其邪？既亟只且！ 莫赤匪狐，莫黑匪乌。惠而好我，携手同车。其虚其邪？既亟只且！', '诗经');
INSERT INTO `poem` VALUES (1175, '静女', '先秦', '佚名', '静女其姝，俟我于城隅。爱而不见，搔首踟蹰。 静女其娈，贻我彤管。彤管有炜，说怿女美。 自牧归荑，洵美且异。匪女之为美，美人之贻。', '诗经');
INSERT INTO `poem` VALUES (1176, '国风·邶风·新台', '先秦', '佚名', '新台有泚，河水弥弥。燕婉之求，蘧篨不鲜。 新台有洒，河水浼浼。燕婉之求，蘧篨不殄。 鱼网之设，鸿则离之。燕婉之求，得此戚施。', '诗经');
INSERT INTO `poem` VALUES (1177, '二子乘舟', '先秦', '佚名', '二子乘舟，泛泛其景。愿言思子，中心养养！ 二子乘舟，泛泛其逝。愿言思子，不瑕有害！', '诗经');
INSERT INTO `poem` VALUES (1178, '国风·鄘风·柏舟', '先秦', '佚名', '泛彼柏舟，在彼中河。髧彼两髦，实维我仪。之死矢靡它。母也天只！不谅人只！ 泛彼柏舟，在彼河侧。髧彼两髦，实维我特。之死矢靡慝。母也天只！不谅人只！', '诗经');
INSERT INTO `poem` VALUES (1179, '国风·鄘风·墙有茨', '先秦', '佚名', '墙有茨，不可扫也。中冓之言，不可道也。所可道也，言之丑也。 墙有茨，不可襄也。中冓之言，不可详也。所可详也，言之长也。 墙有茨，不可束也。中冓之言，不可读也。所可读也，言之辱也。', '诗经');
INSERT INTO `poem` VALUES (1180, '国风·鄘风·君子偕老', '先秦', '佚名', '君子偕老，副笄六珈。委委佗佗，如山如河，象服是宜。子之不淑，云如之何？ 玼兮玼兮，其之翟也。鬒发如云，不屑髢也；玉之瑱也，象之揥也，扬且之皙也。胡然而天也？胡然而帝也？ 瑳兮瑳兮，其之展也。蒙彼绉絺，是绁袢也。子之清扬，扬且之颜也。展如之人兮，邦之媛也！', '诗经');
INSERT INTO `poem` VALUES (1181, '国风·鄘风·桑中', '先秦', '佚名', '爰采唐矣？沬之乡矣。云谁之思？美孟姜矣。期我乎桑中，要我乎上宫，送我乎淇之上矣。 爰采麦矣？沬之北矣。云谁之思？美孟弋矣。期我乎桑中，要我乎上宫，送我乎淇之上矣。 爰采葑矣？沬之东矣。云谁之思？美孟庸矣。期我乎桑中，要我乎上宫，送我乎淇之上矣。', '诗经');
INSERT INTO `poem` VALUES (1182, '鹑之奔奔', '先秦', '佚名', '鹑之奔奔，鹊之彊彊。人之无良，我以为兄！ 鹊之彊彊，鹑之奔奔。人之无良，我以为君！', '诗经');
INSERT INTO `poem` VALUES (1183, '鄘风·定之方中', '先秦', '佚名', '定之方中，作于楚宫。揆之以日，作于楚室。树之榛栗，椅桐梓漆，爰伐琴瑟。 升彼虚矣，以望楚矣。望楚与堂，景山与京。降观于桑。卜云其吉，终焉允臧。 灵雨既零，命彼倌人。星言夙驾，说于桑田。匪直也人，秉心塞渊。騋牝三千。', '诗经');
INSERT INTO `poem` VALUES (1184, '蝃蝀', '先秦', '佚名', '蝃蝀在东，莫之敢指。女子有行，远父母兄弟。 朝隮于西，崇朝其雨。女子有行，远兄弟父母。 乃如之人也，怀婚姻也。大无信也，不知命也！', '诗经');
INSERT INTO `poem` VALUES (1185, '国风·鄘风·相鼠', '先秦', '佚名', '相鼠有皮，人而无仪！人而无仪，不死何为？ 相鼠有齿，人而无止！人而无止，不死何俟？ 相鼠有体，人而无礼，人而无礼！胡不遄死？', '诗经');
INSERT INTO `poem` VALUES (1186, '干旄', '先秦', '佚名', '孑孑干旄，在浚之郊。素丝纰之，良马四之。彼姝者子，何以畀之？ 孑孑干旟，在浚之都。素丝组之，良马五之。彼姝者子，何以予之？ 孑孑干旌，在浚之城。素丝祝之，良马六之。彼姝者子，何以告之？', '诗经');
INSERT INTO `poem` VALUES (1187, '载驰', '先秦', '佚名', '载驰载驱，归唁卫侯。驱马悠悠，言至于漕。大夫跋涉，我心则忧。 既不我嘉，不能旋反。视尔不臧，我思不远。 既不我嘉，不能旋济？视尔不臧，我思不閟。 陟彼阿丘，言采其蝱。女子善怀，亦各有行。许人尤之，众稚且狂。 我行其野，芃芃其麦。控于大邦，谁因谁极？大夫君子，无我有尤。百尔所思，不如我所之。', '诗经');
INSERT INTO `poem` VALUES (1188, '国风·卫风·淇奥', '先秦', '佚名', '瞻彼淇奥，绿竹猗猗。有匪君子，如切如磋，如琢如磨。瑟兮僩兮，赫兮咺兮。有匪君子，终不可谖兮。 瞻彼淇奥，绿竹青青。有匪君子，充耳琇莹，会弁如星。瑟兮僩兮，赫兮咺兮。有匪君子，终不可谖兮。 瞻彼淇奥，绿竹如箦。有匪君子，如金如锡，如圭如璧。宽兮绰兮，猗重较兮。善戏谑兮，不为虐兮。', '诗经');
INSERT INTO `poem` VALUES (1189, '考槃', '先秦', '佚名', '考槃在涧，硕人之宽。独寐寤言，永矢弗谖。 考槃在阿，硕人之薖。独寐寤歌，永矢弗过。 考槃在陆，硕人之轴。独寐寤宿，永矢弗告。', '诗经');
INSERT INTO `poem` VALUES (1190, '硕人', '先秦', '佚名', '硕人其颀，衣锦褧衣。齐侯之子，卫侯之妻。东宫之妹，邢侯之姨，谭公维私。 手如柔荑，肤如凝脂，领如蝤蛴，齿如瓠犀，螓首蛾眉，巧笑倩兮，美目盼兮。 硕人敖敖，说于农郊。四牡有骄，朱幩镳镳。翟茀以朝。大夫夙退，无使君劳。 河水洋洋，北流活活。施罛濊濊，鳣鲔发发。葭菼揭揭，庶姜孽孽，庶士有朅。', '诗经');
INSERT INTO `poem` VALUES (1191, '氓', '先秦', '佚名', '氓之蚩蚩，抱布贸丝。匪来贸丝，来即我谋。送子涉淇，至于顿丘。匪我愆期，子无良媒。将子无怒，秋以为期。 乘彼垝垣，以望复关。不见复关，泣涕涟涟。既见复关，载笑载言。尔卜尔筮，体无咎言。以尔车来，以我贿迁。(尔 一作：尓) 桑之未落，其叶沃若。于嗟鸠兮！无食桑葚。于嗟女兮！无与士耽。士之耽兮，犹可说也。女之耽兮，不可说也。 桑之落矣，其黄而陨。自我徂尔，三岁食贫。淇水汤汤，渐车帷裳。女也不爽，士贰其行。士也罔极，二三其德。 三岁为妇，靡室劳矣。夙兴夜寐，靡有朝矣。言既遂矣，至于暴矣。兄弟不知，咥其笑矣。静言思之，躬自悼矣。 及尔偕老，老使我怨。淇则有岸，隰则有泮。总角之宴，言笑晏晏，信誓旦旦，不思其反。反是不思，亦已焉哉！', '诗经');
INSERT INTO `poem` VALUES (1192, '竹竿', '先秦', '佚名', '籊籊竹竿，以钓于淇。岂不尔思？远莫致之。 泉源在左，淇水在右。女子有行，远兄弟父母。 淇水在右，泉源在左。巧笑之瑳，佩玉之傩。 淇水滺滺，桧楫松舟。驾言出游，以写我忧。', '诗经');
INSERT INTO `poem` VALUES (1193, '芄兰', '先秦', '佚名', '芄兰之支，童子佩觿。虽则佩觿，能不我知。容兮遂兮，垂带悸兮。 芄兰之叶，童子佩韘。虽则佩韘，能不我甲。容兮遂兮，垂带悸兮。', '诗经');
INSERT INTO `poem` VALUES (1194, '国风·卫风·河广', '先秦', '佚名', '谁谓河广？一苇杭之。谁谓宋远？跂予望之。 谁谓河广？曾不容刀。谁谓宋远？曾不崇朝。', '诗经');
INSERT INTO `poem` VALUES (1195, '国风·卫风·伯兮', '先秦', '佚名', '伯兮朅兮，邦之桀兮。伯也执殳，为王前驱。 自伯之东，首如飞蓬。岂无膏沐？谁适为容！ 其雨其雨，杲杲出日。愿言思伯，甘心首疾。 焉得谖草？言树之背。愿言思伯。使我心痗。', '诗经');
INSERT INTO `poem` VALUES (1196, '有狐', '先秦', '佚名', '有狐绥绥，在彼淇梁。心之忧矣，之子无裳。 有狐绥绥，在彼淇厉。心之忧矣，之子无带。 有狐绥绥，在彼淇侧。心之忧矣，之子无服。', '诗经');
INSERT INTO `poem` VALUES (1197, '国风·卫风·木瓜', '先秦', '佚名', '投我以木瓜，报之以琼琚。匪报也，永以为好也！ 投我以木桃，报之以琼瑶。匪报也，永以为好也！ 投我以木李，报之以琼玖。匪报也，永以为好也！', '诗经');
INSERT INTO `poem` VALUES (1198, '黍离', '先秦', '佚名', '彼黍离离，彼稷之苗。行迈靡靡，中心摇摇。知我者，谓我心忧；不知我者，谓我何求。悠悠苍天，此何人哉？ 彼黍离离，彼稷之穗。行迈靡靡，中心如醉。知我者，谓我心忧；不知我者，谓我何求。悠悠苍天，此何人哉？ 彼黍离离，彼稷之实。行迈靡靡，中心如噎。知我者，谓我心忧；不知我者，谓我何求。悠悠苍天，此何人哉？', '诗经');
INSERT INTO `poem` VALUES (1199, '君子于役', '先秦', '佚名', '君子于役，不知其期，曷至哉？鸡栖于埘，日之夕矣，羊牛下来。君子于役，如之何勿思！ 君子于役，不日不月，曷其有佸？鸡栖于桀，日之夕矣，羊牛下括。君子于役，苟无饥渴！', '诗经');
INSERT INTO `poem` VALUES (1200, '君子阳阳', '先秦', '佚名', '君子阳阳，左执簧，右招我由房，其乐只且！ 君子陶陶，左执翿，右招我由敖，其乐只且！', '诗经');
INSERT INTO `poem` VALUES (1201, '国风·王风·扬之水', '先秦', '佚名', '扬之水，不流束薪。彼其之子，不与我戍申。怀哉怀哉，曷月予还归哉？ 扬之水，不流束楚。彼其之子，不与我戍甫。怀哉怀哉，曷月予还归哉？ 扬之水，不流束蒲。彼其之子，不与我戍许。怀哉怀哉，曷月予还归哉？', '诗经');
INSERT INTO `poem` VALUES (1202, '国风·王风·中谷有蓷', '先秦', '佚名', '中谷有蓷，暵其乾矣。有女仳离，嘅其叹矣。嘅其叹矣，遇人之艰难矣！ 中谷有蓷，暵其修矣。有女仳离，条其啸矣。条其啸矣，遇人之不淑矣！ 中谷有蓷，暵其湿矣。有女仳离，啜其泣矣。啜其泣矣，何嗟及矣！', '诗经');
INSERT INTO `poem` VALUES (1203, '国风·王风·兔爰', '先秦', '佚名', '有兔爰爰，雉离于罗。我生之初，尚无为；我生之后，逢此百罹。尚寐无吪！ 有兔爰爰，雉离于罦。我生之初，尚无造；我生之后，逢此百忧。尚寐无觉！ 有兔爰爰，雉离于罿。我生之初，尚无庸；我生之后，逢此百凶。尚寐无聪！', '诗经');
INSERT INTO `poem` VALUES (1204, '葛藟', '先秦', '佚名', '绵绵葛藟，在河之浒。终远兄弟，谓他人父。谓他人父，亦莫我顾。 绵绵葛藟，在河之涘。终远兄弟，谓他人母。谓他人母，亦莫我有。 绵绵葛藟，在河之漘。终远兄弟，谓他人昆。谓他人昆，亦莫我闻。', '诗经');
INSERT INTO `poem` VALUES (1205, '采葛', '先秦', '佚名', '彼采葛兮，一日不见，如三月兮！ 彼采萧兮，一日不见，如三秋兮！ 彼采艾兮！一日不见，如三岁兮！', '诗经');
INSERT INTO `poem` VALUES (1206, '大车', '先秦', '佚名', '大车槛槛，毳衣如菼。岂不尔思？畏子不敢。 大车啍啍，毳衣如璊。岂不尔思？畏子不奔。 榖则异室，死则同穴。谓予不信，有如皎日。', '诗经');
INSERT INTO `poem` VALUES (1207, '丘中有麻', '先秦', '佚名', '丘中有麻，彼留子嗟。彼留子嗟，将其来施施。 丘中有麦，彼留子国。彼留子国，将其来食。 丘中有李，彼留之子。彼留之子，贻我佩玖。', '诗经');
INSERT INTO `poem` VALUES (1208, '缁衣', '先秦', '佚名', '缁衣之宜兮，敝予又改为兮。适子之馆兮。还予授子之粲兮。 缁衣之好兮，敝予又改造兮。适子之馆兮，还予授子之粲兮。 缁衣之席兮，敝予又改作兮。适子之馆兮，还予授子之粲兮。', '诗经');
INSERT INTO `poem` VALUES (1209, '将仲子', '先秦', '佚名', '将仲子兮，无逾我里，无折我树杞。岂敢爱之？畏我父母。仲可怀也，父母之言亦可畏也。 将仲子兮，无逾我墙，无折我树桑。岂敢爱之？畏我诸兄。仲可怀也，诸兄之言亦可畏也。 将仲子兮，无逾我园，无折我树檀。岂敢爱之？畏人之多言。仲可怀也，人之多言亦可畏也。', '诗经');
INSERT INTO `poem` VALUES (1210, '叔于田', '先秦', '佚名', '叔于田，巷无居人。岂无居人？不如叔也。洵美且仁。 叔于狩，巷无饮酒。岂无饮酒？不如叔也。洵美且好。 叔适野，巷无服马。岂无服马？不如叔也。洵美且武。', '诗经');
INSERT INTO `poem` VALUES (1211, '大叔于田', '先秦', '佚名', '叔于田，乘乘马。执辔如组，两骖如舞。叔在薮，火烈具举。袒裼暴虎，献于公所。将叔勿狃，戒其伤女。 叔于田，乘乘黄。两服上襄，两骖雁行。叔在薮，火烈具扬。叔善射忌，又良御忌。抑罄控忌，抑纵送忌。 叔于田，乘乘鸨。两服齐首，两骖如手。叔在薮，火烈具阜。叔马慢忌，叔发罕忌，抑释掤忌，抑鬯弓忌。', '诗经');
INSERT INTO `poem` VALUES (1212, '清人', '先秦', '佚名', '清人在彭，驷介旁旁。二矛重英，河上乎翱翔。 清人在消，驷介麃麃。二矛重乔，河上乎逍遥。 清人在轴，驷介陶陶。左旋右抽，中军作好。', '诗经');
INSERT INTO `poem` VALUES (1213, '羔裘', '先秦', '佚名', '羔裘如濡，洵直且侯。彼其之子，舍命不渝。 羔裘豹饰，孔武有力。彼其之子，邦之司直。 羔裘晏兮，三英粲兮。彼其之子，邦之彦兮。', '诗经');
INSERT INTO `poem` VALUES (1214, '国风·郑风·遵大路', '先秦', '佚名', '遵大路兮，掺执子之祛兮，无我恶兮，不寁故也！ 遵大路兮，掺执子之手兮，无我魗兮，不寁好也！', '诗经');
INSERT INTO `poem` VALUES (1215, '国风·郑风·女曰鸡鸣', '先秦', '佚名', '女曰鸡鸣，士曰昧旦。子兴视夜，明星有烂。将翱将翔，弋凫与雁。 弋言加之，与子宜之。宜言饮酒，与子偕老。琴瑟在御，莫不静好。 知子之来之，杂佩以赠之。知子之顺之，杂佩以问之。知子之好之，杂佩以报之。', '诗经');
INSERT INTO `poem` VALUES (1216, '国风·郑风·有女同车', '先秦', '佚名', '有女同车，颜如舜华。将翱将翔，佩玉琼琚。彼美孟姜，洵美且都。 有女同行，颜如舜英。将翱将翔，佩玉将将。彼美孟姜，德音不忘。', '诗经');
INSERT INTO `poem` VALUES (1217, '国风·郑风·山有扶苏', '先秦', '佚名', '山有扶苏，隰有荷华。不见子都，乃见狂且。 山有乔松，隰有游龙，不见子充，乃见狡童。', '诗经');
INSERT INTO `poem` VALUES (1218, '萚兮', '先秦', '佚名', '萚兮萚兮，风其吹女。叔兮伯兮，倡予和女。 萚兮萚兮，风其漂女。叔兮伯兮，倡予要女。', '诗经');
INSERT INTO `poem` VALUES (1219, '狡童', '先秦', '佚名', '彼狡童兮，不与我言兮。维子之故，使我不能餐兮。 彼狡童兮，不与我食兮。维子之故，使我不能息兮。', '诗经');
INSERT INTO `poem` VALUES (1220, '国风·郑风·褰裳', '先秦', '佚名', '子惠思我，褰裳涉溱。子不我思，岂无他人？狂童之狂也且！ 子惠思我，褰裳涉洧。子不我思，岂无他士？狂童之狂也且！', '诗经');
INSERT INTO `poem` VALUES (1221, '丰', '先秦', '佚名', '子之丰兮，俟我乎巷兮，悔予不送兮。 子之昌兮，俟我乎堂兮，悔予不将兮。 衣锦褧衣，裳锦褧裳。叔兮伯兮，驾予与行。 裳锦褧裳，衣锦褧衣。叔兮伯兮，驾予与归。', '诗经');
INSERT INTO `poem` VALUES (1222, '东门之墠', '先秦', '佚名', '东门之墠，茹藘在阪。其室则迩，其人甚远。 东门之栗，有践家室。岂不尔思？子不我即！', '诗经');
INSERT INTO `poem` VALUES (1223, '国风·郑风·风雨', '先秦', '佚名', '风雨凄凄，鸡鸣喈喈。既见君子，云胡不夷。 风雨潇潇，鸡鸣胶胶。既见君子，云胡不瘳。 风雨如晦，鸡鸣不已。既见君子，云胡不喜。', '诗经');
INSERT INTO `poem` VALUES (1224, '国风·郑风·子衿', '先秦', '佚名', '青青子衿，悠悠我心。纵我不往，子宁不嗣音？ 青青子佩，悠悠我思。纵我不往，子宁不来？ 挑兮达兮，在城阙兮。一日不见，如三月兮。', '诗经');
INSERT INTO `poem` VALUES (1225, '郑风·扬之水', '先秦', '佚名', '扬之水，不流束楚。终鲜兄弟，维予与女。无信人之言，人实诳女。 扬之水，不流束薪。终鲜兄弟，维予二人。无信人之言，人实不信。', '诗经');
INSERT INTO `poem` VALUES (1226, '出其东门', '先秦', '佚名', '出其东门，有女如云。虽则如云，匪我思存。缟衣綦巾，聊乐我员。 出其闉闍，有女如荼。虽则如荼，匪我思且。缟衣茹藘，聊可与娱。', '诗经');
INSERT INTO `poem` VALUES (1227, '国风·郑风·野有蔓草', '先秦', '佚名', '野有蔓草，零露漙兮。有美一人，清扬婉兮。邂逅相遇，适我愿兮。 野有蔓草，零露瀼瀼。有美一人，婉如清扬。邂逅相遇，与子偕臧。', '诗经');
INSERT INTO `poem` VALUES (1228, '溱洧', '先秦', '佚名', '溱与洧，方涣涣兮。 士与女，方秉蕑兮。 女曰观乎？士曰既且，且往观乎？ 洧之外，洵訏且乐。 维士与女，伊其相谑，赠之以勺药。 溱与洧，浏其清矣。 士与女，殷其盈矣。 女曰观乎？士曰既且，且往观乎？ 洧之外，洵訏且乐。 维士与女，伊其将谑，赠之以勺药。', '诗经');
INSERT INTO `poem` VALUES (1229, '鸡鸣', '先秦', '佚名', '鸡既鸣矣，朝既盈矣。匪鸡则鸣，苍蝇之声。 东方明矣，朝既昌矣。匪东方则明，月出之光。 虫飞薨薨，甘与子同梦。会且归矣，无庶予子憎。', '诗经');
INSERT INTO `poem` VALUES (1230, '还', '先秦', '佚名', '子之还兮，遭我乎峱之间兮。并驱从两肩兮，揖我谓我儇兮。 子之茂兮，遭我乎峱之道兮。并驱从两牡兮，揖我谓我好兮。 子之昌兮，遭我乎峱之阳兮。并驱从两狼兮，揖我谓我臧兮。', '诗经');
INSERT INTO `poem` VALUES (1231, '著', '先秦', '佚名', '俟我于著乎而，充耳以素乎而，尚之以琼华乎而。 俟我于庭乎而，充耳以青乎而，尚之以琼莹乎而。 俟我于堂乎而，充耳以黄乎而，尚之以琼英乎而。', '诗经');
INSERT INTO `poem` VALUES (1232, '东方之日', '先秦', '佚名', '东方之日兮，彼姝者子，在我室兮。在我室兮，履我即兮。 东方之月兮，彼姝者子，在我闼兮。在我闼兮，履我发兮。', '诗经');
INSERT INTO `poem` VALUES (1233, '东方未明', '先秦', '佚名', '东方未明，颠倒衣裳。颠之倒之，自公召之。 东方未晞，颠倒裳衣。倒之颠之，自公令之。 折柳樊圃，狂夫瞿瞿。不能辰夜，不夙则莫。', '诗经');
INSERT INTO `poem` VALUES (1234, '南山', '先秦', '佚名', '南山崔崔，雄狐绥绥。鲁道有荡，齐子由归。既曰归止，曷又怀止？ 葛屦五两，冠緌双止。鲁道有荡，齐子庸止。既曰庸止，曷又从止？ 蓺麻如之何？衡从其亩。取妻如之何？必告父母。既曰告止，曷又鞠止？ 析薪如之何？匪斧不克。取妻如之何？匪媒不得。既曰得止，曷又极止？', '诗经');
INSERT INTO `poem` VALUES (1235, '甫田', '先秦', '佚名', '无田甫田，维莠骄骄。无思远人，劳心忉忉。 无田甫田，维莠桀桀。无思远人，劳心怛怛。 婉兮娈兮。总角丱兮。未几见兮，突而弁兮！', '诗经');
INSERT INTO `poem` VALUES (1236, '卢令', '先秦', '佚名', '卢令令，其人美且仁。 卢重环，其人美且鬈。 卢重鋂，其人美且偲。', '诗经');
INSERT INTO `poem` VALUES (1237, '敝笱', '先秦', '佚名', '敝笱在梁，其鱼鲂鳏。齐子归止，其从如云。 敝笱在梁，其鱼鲂鱮。齐子归止，其从如雨。 敝笱在梁，其鱼唯唯。齐子归止，其从如水。', '诗经');
INSERT INTO `poem` VALUES (1238, '载驱', '先秦', '佚名', '载驱薄薄，簟茀朱鞹。 鲁道有荡，齐子发夕。 四骊济济，垂辔沵沵。 鲁道有荡，齐子岂弟。 汶水汤汤，行人彭彭。 鲁道有荡，齐子翱翔。 汶水滔滔，行人儦儦。 鲁道有荡，齐子游遨。', '诗经');
INSERT INTO `poem` VALUES (1239, '猗嗟', '先秦', '佚名', '猗嗟昌兮，颀而长兮。抑若扬兮，美目扬兮。巧趋跄兮，射则臧兮。 猗嗟名兮，美目清兮，仪既成兮。终日射侯，不出正兮，展我甥兮。 猗嗟娈兮，清扬婉兮。舞则选兮，射则贯兮。四矢反兮，以御乱兮。', '诗经');
INSERT INTO `poem` VALUES (1240, '葛屦', '先秦', '佚名', '纠纠葛屦，可以履霜？掺掺女手，可以缝裳？要之襋之，好人服之。 好人提提，宛然左辟，佩其象揥。维是褊心，是以为刺。', '诗经');
INSERT INTO `poem` VALUES (1241, '汾沮洳', '先秦', '佚名', '彼汾沮洳，言采其莫。彼其之子，美无度。美无度，殊异乎公路。 彼汾一方，言采其桑。彼其之子，美如英。美如英，殊异乎公行。 彼汾一曲，言采其藚。彼其之子，美如玉。美如玉，殊异乎公族。', '诗经');
INSERT INTO `poem` VALUES (1242, '园有桃', '先秦', '佚名', '园有桃，其实之肴。心之忧矣，我歌且谣。不知我者，谓我士也骄。彼人是哉，子曰何其？心之忧矣，其谁知之？其谁知之，盖亦勿思！ 园有棘，其实之食。心之忧矣，聊以行国。不知我者，谓我士也罔极。彼人是哉，子曰何其？心之忧矣，其谁知之？其谁知之，盖亦勿思！', '诗经');
INSERT INTO `poem` VALUES (1243, '陟岵', '先秦', '佚名', '陟彼岵兮，瞻望父兮。父曰：嗟！予子行役，夙夜无已。上慎旃哉，犹来！无止！ 陟彼屺兮，瞻望母兮。母曰：嗟！予季行役，夙夜无寐。上慎旃哉，犹来！无弃！ 陟彼冈兮，瞻望兄兮。兄曰：嗟！予弟行役，夙夜必偕。上慎旃哉，犹来！无死！', '诗经');
INSERT INTO `poem` VALUES (1244, '十亩之间', '先秦', '佚名', '十亩之间兮，桑者闲闲兮，行与子还兮。 十亩之外兮，桑者泄泄兮，行与子逝兮。', '诗经');
INSERT INTO `poem` VALUES (1245, '伐檀', '先秦', '佚名', '坎坎伐檀兮，置之河之干兮。河水清且涟猗。不稼不穑，胡取禾三百廛兮？不狩不猎，胡瞻尔庭有县貆兮？彼君子兮，不素餐兮！ 坎坎伐辐兮，置之河之侧兮。河水清且直猗。不稼不穑，胡取禾三百亿兮？不狩不猎，胡瞻尔庭有县特兮？彼君子兮，不素食兮！ 坎坎伐轮兮，置之河之漘兮。河水清且沦猗。不稼不穑，胡取禾三百囷兮？不狩不猎，胡瞻尔庭有县鹑兮？彼君子兮，不素飧兮！', '诗经');
INSERT INTO `poem` VALUES (1246, '国风·魏风·硕鼠', '先秦', '佚名', '硕鼠硕鼠，无食我黍！三岁贯女，莫我肯顾。逝将去女，适彼乐土。乐土乐土，爰得我所。 硕鼠硕鼠，无食我麦！三岁贯女，莫我肯德。逝将去女，适彼乐国。乐国乐国，爰得我直。 硕鼠硕鼠，无食我苗！三岁贯女，莫我肯劳。逝将去女，适彼乐郊。乐郊乐郊，谁之永号？(女 通 汝)', '诗经');
INSERT INTO `poem` VALUES (1247, '蟋蟀', '先秦', '佚名', '蟋蟀在堂，岁聿其莫。今我不乐，日月其除。无已大康，职思其居。好乐无荒，良士瞿瞿。 蟋蟀在堂，岁聿其逝。今我不乐，日月其迈。无已大康，职思其外。好乐无荒，良士蹶蹶。 蟋蟀在堂，役车其休。今我不乐，日月其慆。无以大康，职思其忧。好乐无荒，良士休休。', '诗经');
INSERT INTO `poem` VALUES (1248, '国风·唐风·山有枢', '先秦', '佚名', '山有枢，隰有榆。子有衣裳，弗曳弗娄。子有车马，弗驰弗驱。宛其死矣，他人是愉。 山有栲，隰有杻。子有廷内，弗洒弗扫。子有钟鼓，弗鼓弗考。宛其死矣，他人是保。 山有漆，隰有栗。子有酒食，何不日鼓瑟？且以喜乐，且以永日。宛其死矣，他人入室。', '诗经');
INSERT INTO `poem` VALUES (1249, '唐风·扬之水', '先秦', '佚名', '扬之水，白石凿凿。素衣朱襮，从子于沃。既见君子，云何不乐？ 扬之水，白石皓皓。素衣朱绣，从子于鹄。既见君子，云何其忧？ 扬之水，白石粼粼。我闻有命，不敢以告人。', '诗经');
INSERT INTO `poem` VALUES (1250, '椒聊', '先秦', '佚名', '椒聊之实，蕃衍盈升。彼其之子，硕大无朋。椒聊且，远条且。 椒聊之实，蕃衍盈匊。彼其之子，硕大且笃。椒聊且，远条且。', '诗经');
INSERT INTO `poem` VALUES (1251, '绸缪', '先秦', '佚名', '绸缪束薪，三星在天。今夕何夕，见此良人？子兮子兮，如此良人何？ 绸缪束刍，三星在隅。今夕何夕，见此邂逅？子兮子兮，如此邂逅何？ 绸缪束楚，三星在户。今夕何夕，见此粲者？子兮子兮，如此粲者何？', '诗经');
INSERT INTO `poem` VALUES (1252, '杕杜', '先秦', '佚名', '有杕之杜，其叶湑湑。独行踽踽。岂无他人？不如我同父。嗟行之人，胡不比焉？人无兄弟，胡不佽焉？ 有杕之杜，其叶菁菁。独行睘睘。岂无他人？不如我同姓。嗟行之人，胡不比焉？人无兄弟，胡不佽焉？', '诗经');
INSERT INTO `poem` VALUES (1253, '国风·唐风·羔裘', '先秦', '佚名', '羔裘豹祛，自我人居居。岂无他人？维子之故。 羔裘豹褎，自我人究究。岂无他人？维子之好。', '诗经');
INSERT INTO `poem` VALUES (1254, '鸨羽', '先秦', '佚名', '肃肃鸨羽，集于苞栩。王事靡盬，不能蓺稷黍。父母何怙？悠悠苍天，曷其有所？ 肃肃鸨翼，集于苞棘。王事靡盬，不能蓺黍稷。父母何食？悠悠苍天，曷其有极？ 肃肃鸨行，集于苞桑，王事靡盬，不能蓺稻梁。父母何尝？悠悠苍天，曷其有常？', '诗经');
INSERT INTO `poem` VALUES (1255, '无衣', '先秦', '佚名', '岂曰无衣？七兮。不如子之衣，安且吉兮。 岂曰无衣？六兮。不如子之衣，安且燠兮。', '诗经');
INSERT INTO `poem` VALUES (1256, '有杕之杜', '先秦', '佚名', '有杕之杜，生于道左。彼君子兮，噬肯适我？中心好之，曷饮食之？ 有杕之杜，生于道周。彼君子兮，噬肯来游？中心好之，曷饮食之？', '诗经');
INSERT INTO `poem` VALUES (1257, '葛生', '先秦', '佚名', '葛生蒙楚，蔹蔓于野。予美亡此，谁与？独处？ 葛生蒙棘，蔹蔓于域。予美亡此，谁与？独息？ 角枕粲兮，锦衾烂兮。予美亡此，谁与？独旦？ 夏之日，冬之夜。百岁之后，归于其居。 冬之夜，夏之日。百岁之后，归于其室。', '诗经');
INSERT INTO `poem` VALUES (1258, '采苓', '先秦', '佚名', '采苓采苓，首阳之巅。人之为言，苟亦无信。舍旃舍旃，苟亦无然。人之为言，胡得焉？ 采苦采苦，首阳之下。人之为言，苟亦无与。舍旃舍旃，苟亦无然。人之为言，胡得焉？ 采葑采葑，首阳之东。人之为言，苟亦无从。舍旃舍旃，苟亦无然。人之为言，胡得焉？', '诗经');
INSERT INTO `poem` VALUES (1259, '车邻', '先秦', '佚名', '有车邻邻，有马白颠。未见君子，寺人之令。 阪有漆，隰有栗。既见君子，并坐鼓瑟。今者不乐，逝者其耋。 阪有桑，隰有杨。既见君子，并坐鼓簧。今者不乐，逝者其亡。', '诗经');
INSERT INTO `poem` VALUES (1260, '国风·秦风·驷驖', '先秦', '佚名', '驷驖孔阜，六辔在手。公之媚子，从公于狩。 奉时辰牡，辰牡孔硕。公曰左之，舍拔则获。 游于北园，四马既闲。輶车鸾镳，载猃歇骄。', '诗经');
INSERT INTO `poem` VALUES (1261, '国风·秦风·小戎', '先秦', '佚名', '小戎俴收，五楘梁辀。游环胁驱，阴靷鋈续。文茵畅毂，驾我骐馵。言念君子，温其如玉。在其板屋，乱我心曲。 四牡孔阜，六辔在手。骐骝是中，騧骊是骖。龙盾之合，鋈以觼軜。言念君子，温其在邑。方何为期？胡然我念之。 俴驷孔群，厹矛鋈錞。蒙伐有苑，虎韔镂膺。交韔二弓，竹闭绲滕。言念君子，载寝载兴。厌厌良人，秩秩德音。', '诗经');
INSERT INTO `poem` VALUES (1262, '蒹葭', '先秦', '佚名', '蒹葭苍苍，白露为霜。所谓伊人，在水一方。 溯洄从之，道阻且长。溯游从之，宛在水中央。 蒹葭萋萋，白露未晞。所谓伊人，在水之湄。 溯洄从之，道阻且跻。溯游从之，宛在水中坻。 蒹葭采采，白露未已。所谓伊人，在水之涘。 溯洄从之，道阻且右。溯游从之，宛在水中沚。', '诗经');
INSERT INTO `poem` VALUES (1263, '终南', '先秦', '佚名', '终南何有？有条有梅。君子至止，锦衣狐裘。颜如渥丹，其君也哉！ 终南何有？有纪有堂。君子至止，黻衣绣裳。佩玉将将，寿考不忘！', '诗经');
INSERT INTO `poem` VALUES (1264, '国风·秦风·黄鸟', '先秦', '佚名', '交交黄鸟，止于棘。谁从穆公？子车奄息。维此奄息，百夫之特。临其穴，惴惴其栗。彼苍者天，歼我良人！如可赎兮，人百其身！ 交交黄鸟，止于桑。谁从穆公？子车仲行。维此仲行，百夫之防。临其穴，惴惴其栗。彼苍者天，歼我良人！如可赎兮，人百其身！ 交交黄鸟，止于楚。谁从穆公？子车针虎。维此针虎，百夫之御。临其穴，惴惴其栗。彼苍者天，歼我良人！如可赎兮，人百其身！', '诗经');
INSERT INTO `poem` VALUES (1265, '国风·秦风·晨风', '先秦', '佚名', '鴥彼晨风，郁彼北林。未见君子，忧心钦钦。如何如何，忘我实多！ 山有苞栎，隰有六驳。未见君子，忧心靡乐。如何如何，忘我实多！ 山有苞棣，隰有树檖。未见君子，忧心如醉。如何如何，忘我实多！', '诗经');
INSERT INTO `poem` VALUES (1266, '秦风·无衣', '先秦', '佚名', '岂曰无衣？与子同袍。王于兴师，修我戈矛。与子同仇！ 岂曰无衣？与子同泽。王于兴师，修我矛戟。与子偕作！ 岂曰无衣？与子同裳。王于兴师，修我甲兵。与子偕行！', '诗经');
INSERT INTO `poem` VALUES (1267, '渭阳', '先秦', '佚名', '我送舅氏，曰至渭阳。何以赠之？路车乘黄。 我送舅氏，悠悠我思。何以赠之？琼瑰玉佩。', '诗经');
INSERT INTO `poem` VALUES (1268, '权舆', '先秦', '佚名', '於我乎，夏屋渠渠，今也每食无余。于嗟乎，不承权舆！ 於我乎，每食四簋，今也每食不饱。于嗟乎，不承权舆！', '诗经');
INSERT INTO `poem` VALUES (1269, '宛丘', '先秦', '佚名', '子之汤兮，宛丘之上兮。洵有情兮，而无望兮。 坎其击鼓，宛丘之下。无冬无夏，值其鹭羽。 坎其击缶，宛丘之道。无冬无夏，值其鹭翿。', '诗经');
INSERT INTO `poem` VALUES (1270, '东门之枌', '先秦', '佚名', '东门之枌，宛丘之栩。子仲之子，婆娑其下。 穀旦于差，南方之原。不绩其麻，市也婆娑。 穀旦于逝，越以鬷迈。视尔如荍，贻我握椒。', '诗经');
INSERT INTO `poem` VALUES (1271, '衡门', '先秦', '佚名', '衡门之下，可以栖迟。泌之洋洋，可以乐饥。 岂其食鱼，必河之鲂？岂其取妻，必齐之姜？ 岂其食鱼，必河之鲤？岂其取妻，必宋之子？', '诗经');
INSERT INTO `poem` VALUES (1272, '国风·陈风·东门之池', '先秦', '佚名', '东门之池，可以沤麻。彼美淑姬，可与晤歌。 东门之池，可以沤纻。彼美淑姬，可与晤语。 东门之池，可以沤菅。彼美淑姬，可与晤言。', '诗经');
INSERT INTO `poem` VALUES (1273, '东门之杨', '先秦', '佚名', '东门之杨，其叶牂牂。昏以为期，明星煌煌。 东门之杨，其叶肺肺。昏以为期，明星晢晢。', '诗经');
INSERT INTO `poem` VALUES (1274, '墓门', '先秦', '佚名', '墓门有棘，斧以斯之。夫也不良，国人知之。知而不已，谁昔然矣。 墓门有梅，有鸮萃止。夫也不良，歌以讯之。讯予不顾，颠倒思予。', '诗经');
INSERT INTO `poem` VALUES (1275, '防有鹊巢', '先秦', '佚名', '防有鹊巢，邛有旨苕。谁侜予美？心焉忉忉。 中唐有甓，邛有旨鷊。谁侜予美？心焉惕惕。', '诗经');
INSERT INTO `poem` VALUES (1276, '诗经·陈风·月出', '先秦', '佚名', '月出皎兮，佼人僚兮。舒窈纠兮，劳心悄兮。 月出皓兮，佼人懰兮。舒忧受兮，劳心慅兮。 月出照兮，佼人燎兮。舒夭绍兮，劳心惨兮。', '诗经');
INSERT INTO `poem` VALUES (1277, '株林', '先秦', '佚名', '胡为乎株林？从夏南！匪适株林，从夏南！ 驾我乘马，说于株野。乘我乘驹，朝食于株！', '诗经');
INSERT INTO `poem` VALUES (1278, '国风·陈风·泽陂', '先秦', '佚名', '彼泽之陂，有蒲与荷。有美一人，伤如之何？寤寐无为，涕泗滂沱。 彼泽之陂，有蒲与蕳。有美一人，硕大且卷。寤寐无为，中心悁悁。 彼泽之陂，有蒲菡萏。有美一人，硕大且俨。寤寐无为，辗转伏枕。', '诗经');
INSERT INTO `poem` VALUES (1279, '桧风·羔裘', '先秦', '佚名', '羔裘逍遥，狐裘以朝。岂不尔思？劳心忉忉。 羔裘翱翔，狐裘在堂。岂不尔思？我心忧伤。 羔裘如膏，日出有曜。岂不尔思？中心是悼。', '诗经');
INSERT INTO `poem` VALUES (1280, '素冠', '先秦', '佚名', '庶见素冠兮？棘人栾栾兮，劳心慱慱兮。 庶见素衣兮？我心伤悲兮，聊与子同归兮。 庶见素韠兮？我心蕴结兮，聊与子如一兮。', '诗经');
INSERT INTO `poem` VALUES (1281, '国风·桧风·隰有苌楚', '先秦', '佚名', '隰有苌楚，猗傩其枝，夭之沃沃，乐子之无知。 隰有苌楚，猗傩其华，夭之沃沃。乐子之无家。 隰有苌楚，猗傩其实，夭之沃沃。乐子之无室。', '诗经');
INSERT INTO `poem` VALUES (1282, '匪风', '先秦', '佚名', '匪风发兮，匪车偈兮。顾瞻周道，中心怛兮。 匪风飘兮，匪车嘌兮。顾瞻周道，中心吊兮。 谁能亨鱼？溉之釜鬵。谁将西归？怀之好音。', '诗经');
INSERT INTO `poem` VALUES (1283, '蜉蝣', '先秦', '佚名', '蜉蝣之羽，衣裳楚楚。心之忧矣，於我归处。 蜉蝣之翼，采采衣服。心之忧矣，於我归息。 蜉蝣掘阅，麻衣如雪。心之忧矣，於我归说。', '诗经');
INSERT INTO `poem` VALUES (1284, '候人', '先秦', '佚名', '彼候人兮，何戈与祋。彼其之子，三百赤芾。 维鹈在梁，不濡其翼。彼其之子，不称其服。 维鹈在梁，不濡其咮。彼其之子，不遂其媾。 荟兮蔚兮，南山朝隮。婉兮娈兮，季女斯饥。', '诗经');
INSERT INTO `poem` VALUES (1285, '鳲鸠', '先秦', '佚名', '鳲鸠在桑，其子七兮。淑人君子，其仪一兮。其仪一兮，心如结兮。 鳲鸠在桑，其子在梅。淑人君子，其带伊丝。其带伊丝，其弁伊骐。 鳲鸠在桑，其子在棘。淑人君子，其仪不忒。其仪不忒，正是四国。 鳲鸠在桑，其子在榛。淑人君子，正是国人，正是国人。胡不万年？', '诗经');
INSERT INTO `poem` VALUES (1286, '下泉', '先秦', '佚名', '冽彼下泉，浸彼苞稂。忾我寤叹，念彼周京。 冽彼下泉，浸彼苞萧。忾我寤叹，念彼京周。 冽彼下泉，浸彼苞蓍。忾我寤叹，念彼京师。 芃芃黍苗，阴雨膏之。四国有王，郇伯劳之。', '诗经');
INSERT INTO `poem` VALUES (1287, '国风·豳风·七月', '先秦', '佚名', '　　七月流火，九月授衣。一之日觱发，二之日栗烈。无衣无褐，何以卒岁。三之日于耜，四之日举趾。同我妇子，馌彼南亩，田畯至喜。 　　七月流火，九月授衣。春日载阳，有鸣仓庚。女执懿筐，遵彼微行，爰求柔桑。春日迟迟，采蘩祁祁。女心伤悲，殆及公子同归。 　　七月流火，八月萑苇。蚕月条桑，取彼斧斨，以伐远扬，猗彼女桑。七月鸣鵙，八月载绩。载玄载黄，我朱孔阳，为公子裳。 　　四月秀葽，五月鸣蜩。八月其获，十月陨箨。一之日于貉，取彼狐狸，为公子裘。二之日其同，载缵武功，言私其豵，献豜于公。 　　五月斯螽动股，六月莎鸡振羽，七月在野，八月在宇，九月在户，十月蟋蟀入我床下。穹窒熏鼠，塞向墐户。嗟我妇子，曰为改岁，入此室处。 　　六月食郁及薁，七月亨葵及菽，八月剥枣，十月获稻，为此春酒，以介眉寿。七月食瓜，八月断壶，九月叔苴，采荼薪樗，食我农夫。 　　九月筑场圃，十月纳禾稼。黍稷重穋，禾麻菽麦。嗟我农夫，我稼既同，上入执宫功。昼尔于茅，宵尔索綯。亟其乘屋，其始播百谷。 　　二之日凿冰冲冲，三之日纳于凌阴。四之日其蚤，献羔祭韭。九月肃霜，十月涤场。朋酒斯飨，曰杀羔羊。跻彼公堂，称彼兕觥，万寿无疆。', '诗经');
INSERT INTO `poem` VALUES (1288, '鸱鸮', '先秦', '佚名', '鸱鸮鸱鸮，既取我子，无毁我室。恩斯勤斯，鬻子之闵斯。 迨天之未阴雨，彻彼桑土，绸缪牖户。今女下民，或敢侮予？ 予手拮据，予所捋荼。予所蓄租，予口卒瘏，曰予未有室家。 予羽谯谯，予尾翛翛，予室翘翘。风雨所漂摇，予维音哓哓！', '诗经');
INSERT INTO `poem` VALUES (1289, '东山', '先秦', '佚名', '我徂东山，慆慆不归。我来自东，零雨其濛。我东曰归，我心西悲。制彼裳衣，勿士行枚。蜎蜎者蠋，烝在桑野。敦彼独宿，亦在车下。 我徂东山，慆慆不归。我来自东，零雨其濛。果臝之实，亦施于宇。伊威在室，蠨蛸在户。町畽鹿场，熠耀宵行。不可畏也，伊可怀也。 我徂东山，慆慆不归。我来自东，零雨其濛。鹳鸣于垤，妇叹于室。洒扫穹窒，我征聿至。有敦瓜苦，烝在栗薪。自我不见，于今三年。 我徂东山，慆慆不归。我来自东，零雨其濛。仓庚于飞，熠耀其羽。之子于归，皇驳其马。亲结其缡，九十其仪。其新孔嘉，其旧如之何？', '诗经');
INSERT INTO `poem` VALUES (1290, '国风·豳风·破斧', '先秦', '佚名', '既破我斧，又缺我斨。周公东征，四国是皇。哀我人斯，亦孔之将。 既破我斧，又缺我锜。周公东征，四国是吪。哀我人斯，亦孔之嘉。 既破我斧，又缺我銶。周公东征，四国是遒。哀我人斯，亦孔之休。', '诗经');
INSERT INTO `poem` VALUES (1291, '伐柯', '先秦', '佚名', '伐柯如何？匪斧不克。取妻如何？匪媒不得。 伐柯伐柯，其则不远。我觏之子，笾豆有践。', '诗经');
INSERT INTO `poem` VALUES (1292, '九罭', '先秦', '佚名', '九罭之鱼，鳟鲂。我觏之子，衮衣绣裳。 鸿飞遵渚，公归无所，於女信处。 鸿飞遵陆，公归不复，於女信宿。 是以有衮衣兮，无以我公归兮，无使我心悲兮。', '诗经');
INSERT INTO `poem` VALUES (1293, '狼跋', '先秦', '佚名', '狼跋其胡，载疐其尾。公孙硕肤，赤舄几几。 狼疐其尾，载跋其胡。公孙硕肤，德音不瑕？', '诗经');
INSERT INTO `poem` VALUES (1294, '小雅·鹿鸣', '先秦', '佚名', '呦呦鹿鸣，食野之苹。我有嘉宾，鼓瑟吹笙。吹笙鼓簧，承筐是将。人之好我，示我周行。 呦呦鹿鸣，食野之蒿。我有嘉宾，德音孔昭。视民不恌，君子是则是效。我有旨酒，嘉宾式燕以敖。 呦呦鹿鸣，食野之芩。我有嘉宾，鼓瑟鼓琴。鼓瑟鼓琴，和乐且湛。我有旨酒，以燕乐嘉宾之心。', '诗经');
INSERT INTO `poem` VALUES (1295, '小雅·四牡', '先秦', '佚名', '四牡騑騑，周道倭迟。岂不怀归？王事靡盬，我心伤悲。 四牡騑騑，啴啴骆马。岂不怀归？王事靡盬，不遑启处。 翩翩者鵻，载飞载下，集于苞栩。王事靡盬，不遑将父。 翩翩者鵻，载飞载止，集于苞杞。王事靡盬，不遑将母。 驾彼四骆，载骤骎骎。岂不怀归？是用作歌，将母来谂。', '诗经');
INSERT INTO `poem` VALUES (1296, '皇皇者华', '先秦', '佚名', '皇皇者华，于彼原隰。駪駪征夫，每怀靡及。 我马维驹，六辔如濡。载驰载驱，周爰咨诹。 我马维骐，六辔如丝。载驰载驱，周爰咨谋。 我马维骆，六辔沃若。载驰载驱，周爰咨度。 我马维骃，六辔既均。载驰载驱，周爰咨询。', '诗经');
INSERT INTO `poem` VALUES (1297, '常棣', '先秦', '佚名', '常棣之华，鄂不韡韡。凡今之人，莫如兄弟。 死丧之威，兄弟孔怀。原隰裒矣，兄弟求矣。 脊令在原，兄弟急难。每有良朋，况也永叹。 兄弟阋于墙，外御其务。每有良朋，烝也无戎。 丧乱既平，既安且宁。虽有兄弟，不如友生？ 傧尔笾豆，饮酒之饫。兄弟既具，和乐且孺。 妻子好合，如鼓瑟琴。兄弟既翕，和乐且湛。 宜尔室家，乐尔妻帑。是究是图，亶其然乎？', '诗经');
INSERT INTO `poem` VALUES (1298, '小雅·伐木', '先秦', '佚名', '伐木丁丁，鸟鸣嘤嘤。出自幽谷，迁于乔木。嘤其鸣矣，求其友声。 相彼鸟矣，犹求友声。矧伊人矣，不求友生？神之听之，终和且平。 伐木许许，酾酒有藇！既有肥羜，以速诸父。宁适不来，微我弗顾。 於粲洒扫，陈馈八簋。既有肥牡，以速诸舅。宁适不来，微我有咎。 伐木于阪，酾酒有衍。笾豆有践，兄弟无远。民之失德，乾餱以愆。 有酒湑我，无酒酤我。坎坎鼓我，蹲蹲舞我。迨我暇矣，饮此湑矣。', '诗经');
INSERT INTO `poem` VALUES (1299, '天保', '先秦', '佚名', '天保定尔，亦孔之固。俾尔单厚，何福不除？俾尔多益，以莫不庶。 天保定尔，俾尔戬穀。罄无不宜，受天百禄。降尔遐福，维日不足。 天保定尔，以莫不兴。如山如阜，如冈如陵，如川之方至，以莫不增。 吉蠲为饎，是用孝享。禴祠烝尝，于公先王。君曰：卜尔，万寿无疆。 神之吊矣，诒尔多福。民之质矣，日用饮食。群黎百姓，遍为尔德。 如月之恒，如日之升。如南山之寿，不骞不崩。如松柏之茂，无不尔或承。', '诗经');
INSERT INTO `poem` VALUES (1300, '采薇', '先秦', '佚名', '采薇采薇，薇亦作止。曰归曰归，岁亦莫止。 靡室靡家，猃狁之故。不遑启居，猃狁之故。 采薇采薇，薇亦柔止。曰归曰归，心亦忧止。 忧心烈烈，载饥载渴。我戍未定，靡使归聘。 采薇采薇，薇亦刚止。曰归曰归，岁亦阳止。 王事靡盬，不遑启处。忧心孔疚，我行不来！ 彼尔维何？维常之华。彼路斯何？君子之车。 戎车既驾，四牡业业。岂敢定居？一月三捷。 驾彼四牡，四牡骙骙。君子所依，小人所腓。 四牡翼翼，象弭鱼服。岂不日戒？猃狁孔棘！ 昔我往矣，杨柳依依。今我来思，雨雪霏霏。 行道迟迟，载渴载饥。我心伤悲，莫知我哀！', '诗经');
INSERT INTO `poem` VALUES (1301, '小雅·出车', '先秦', '佚名', '我出我车，于彼牧矣。自天子所，谓我来矣。召彼仆夫，谓之载矣。王事多难，维其棘矣。 我出我车，于彼郊矣。设此旐矣，建彼旄矣。彼旟旐斯，胡不旆旆？忧心悄悄，仆夫况瘁。 王命南仲，往城于方。出车彭彭，旂旐央央。天子命我，城彼朔方。赫赫南仲，玁狁于襄。 昔我往矣，黍稷方华。今我来思，雨雪载途。王事多难，不遑启居。岂不怀归？畏此简书。 喓喓草虫，趯趯阜螽。未见君子，忧心忡忡。既见君子，我心则降。赫赫南仲，薄伐西戎。 春日迟迟，卉木萋萋。仓庚喈喈，采蘩祁祁。执讯获丑，薄言还归。赫赫南仲，玁狁于夷。', '诗经');
INSERT INTO `poem` VALUES (1302, '小雅·杕杜', '先秦', '佚名', '有杕之杜，有睆其实。王事靡盬，继嗣我日。日月阳止，女心伤止，征夫遑止。 有杕之杜，其叶萋萋。王事靡盬，我心伤悲。卉木萋止，女心悲止，征夫归止！ 陟彼北山，言采其杞。王事靡盬，忧我父母。檀车幝幝，四牡痯痯，征夫不远！ 匪载匪来，忧心孔疚。期逝不至，而多为恤。卜筮偕止，会言近止，征夫迩止。', '诗经');
INSERT INTO `poem` VALUES (1303, '鱼丽', '先秦', '佚名', '鱼丽于罶，鲿鲨。君子有酒，旨且多。 鱼丽于罶，鲂鳢。君子有酒，多且旨。 鱼丽于罶，鰋鲤。君子有酒，旨且有。物其多矣， 维其嘉矣！物其旨矣，维其偕矣！物其有矣，维其时矣！', '诗经');
INSERT INTO `poem` VALUES (1304, '小雅·南有嘉鱼', '先秦', '佚名', '南有嘉鱼，烝然罩罩。君子有酒，嘉宾式燕以乐。 南有嘉鱼，烝然汕汕。君子有酒，嘉宾式燕以衎。 南有樛木，甘瓠累之。君子有酒，嘉宾式燕绥之。 翩翩者鵻，烝然来思。君子有酒，嘉宾式燕又思。', '诗经');
INSERT INTO `poem` VALUES (1305, '小雅·南山有台', '先秦', '佚名', '南山有台，北山有莱。乐只君子，邦家之基。乐只君子，万寿无期。 南山有桑，北山有杨。乐只君子，邦家之光。乐只君子，万寿无疆。 南山有杞，北山有李。乐只君子，民之父母。乐只君子，德音不已。 南山有栲，北山有杻。乐只君子，遐不眉寿。乐只君子，德音是茂。 南山有枸，北山有楰。乐只君子，遐不黄耇。乐只君子，保艾尔后。', '诗经');
INSERT INTO `poem` VALUES (1306, '小雅·蓼萧', '先秦', '佚名', '蓼彼萧斯，零露湑兮。既见君子，我心写兮。燕笑语兮，是以有誉处兮。 蓼彼萧斯，零露瀼瀼。既见君子，为龙为光。其德不爽，寿考不忘。 蓼彼萧斯，零露泥泥。既见君子，孔燕岂弟。宜兄宜弟，令德寿岂。 蓼彼萧斯，零露浓浓。既见君子，鞗革忡忡。和鸾雍雍，万福攸同。', '诗经');
INSERT INTO `poem` VALUES (1307, '小雅·湛露', '先秦', '佚名', '湛湛露斯，匪阳不晞。厌厌夜饮，不醉无归。 湛湛露斯，在彼丰草。厌厌夜饮，在宗载考。 湛湛露斯，在彼杞棘。显允君子，莫不令德。 其桐其椅，其实离离。岂弟君子，莫不令仪。', '诗经');
INSERT INTO `poem` VALUES (1308, '小雅·彤弓', '先秦', '佚名', '彤弓弨兮，受言藏之。我有嘉宾，中心贶之。钟鼓既设，一朝飨之。 彤弓弨兮，受言载之。我有嘉宾，中心喜之。钟鼓既设，一朝右之。 彤弓弨兮，受言櫜之。我有嘉宾，中心好之。钟鼓既设，一朝酬之。', '诗经');
INSERT INTO `poem` VALUES (1309, '菁菁者莪', '先秦', '佚名', '菁菁者莪，在彼中阿。既见君子，乐且有仪。 菁菁者莪，在彼中沚。既见君子，我心则喜。 菁菁者莪，在彼中陵。既见君子，锡我百朋。 泛泛杨舟，载沉载浮。既见君子，我心则休。', '诗经');
INSERT INTO `poem` VALUES (1310, '小雅·六月', '先秦', '佚名', '六月栖栖，戎车既饬。四牡骙骙，载是常服。玁狁孔炽，我是用急。王于出征，以匡王国。 比物四骊，闲之维则。维此六月，既成我服。我服既成，于三十里。王于出征，以佐天子。 四牡修广，其大有颙。薄伐玁狁，以奏肤公。有严有翼，共武之服。共武之服，以定王国。 玁狁匪茹，整居焦获。侵镐及方，至于泾阳。织文鸟章，白旆央央。元戎十乘，以先启行。 戎车既安，如轾如轩。四牡既佶，既佶且闲。薄伐玁狁，至于大原。文武吉甫，万邦为宪。 吉甫燕喜，既多受祉。来归自镐，我行永久。饮御诸友，炰鳖脍鲤。侯谁在矣？张仲孝友。', '诗经');
INSERT INTO `poem` VALUES (1311, '采芑', '先秦', '佚名', '薄言采芑，于彼新田，呈此菑亩。方叔涖止，其车三千。师干之试，方叔率止。乘其四骐，四骐翼翼。路车有奭，簟茀鱼服，钩膺鞗革。 薄言采芑，于彼新田，于此中乡。方叔涖止，其车三千。旂旐央央，方叔率止。约軧错衡，八鸾玱玱。服其命服，朱芾斯皇，有玱葱珩。 鴥彼飞隼，其飞戾天，亦集爰止。方叔涖止，其车三千。师干之试，方叔率止。钲人伐鼓，陈师鞠旅。显允方叔，伐鼓渊渊，振旅阗阗。 蠢尔蛮荆，大邦为仇。方叔元老，克壮其犹。方叔率止，执讯获丑。戎车啴啴，啴啴焞焞，如霆如雷。显允方叔，征伐玁狁，蛮荆来威。', '诗经');
INSERT INTO `poem` VALUES (1312, '小雅·车攻', '先秦', '佚名', '我车既攻，我马既同。四牡庞庞，驾言徂东。 田车既好，田牡孔阜。东有甫草，驾言行狩。 之子于苗，选徒嚣嚣。建旐设旄，搏兽于敖。 驾彼四牡，四牡奕奕。赤芾金舄，会同有绎。 决拾既佽，弓矢既调。射夫既同，助我举柴。 四黄既驾，两骖不猗。不失其驰，舍矢如破。 萧萧马鸣，悠悠旆旌。徒御不惊，大庖不盈。 之子于征，有闻无声。允矣君子，展也大成。', '诗经');
INSERT INTO `poem` VALUES (1313, '小雅·吉日', '先秦', '佚名', '吉日维戊，既伯既祷。田车既好，四牡孔阜。升彼大阜，从其群丑。 吉日庚午，既差我马。兽之所同，麀鹿麌麌。漆沮之从，天子之所。 瞻彼中原，其祁孔有。儦儦俟俟，或群或友。悉率左右，以燕天子。 既张我弓，既挟我矢。发彼小豝，殪此大兕。以御宾客，且以酌醴。', '诗经');
INSERT INTO `poem` VALUES (1314, '鸿雁', '先秦', '佚名', '鸿雁于飞，肃肃其羽。之子于征，劬劳于野。爰及矜人，哀此鳏寡。 鸿雁于飞，集于中泽。之子于垣，百堵皆作。虽则劬劳，其究安宅？ 鸿雁于飞，哀鸣嗷嗷。维此哲人，谓我劬劳。维彼愚人，谓我宣骄。', '诗经');
INSERT INTO `poem` VALUES (1315, '庭燎', '先秦', '佚名', '夜如何其？夜未央，庭燎之光。君子至止，鸾声将将。 夜如何其？夜未艾，庭燎晣晣。君子至止，鸾声哕哕。 夜如何其？夜乡晨，庭燎有辉。君子至止，言观其旂。', '诗经');
INSERT INTO `poem` VALUES (1316, '沔水', '先秦', '佚名', '沔彼流水，朝宗于海。鴥彼飞隼，载飞载止。嗟我兄弟，邦人诸友。莫肯念乱，谁无父母？ 沔彼流水，其流汤汤。鴥彼飞隼，载飞载扬。念彼不迹，载起载行。心之忧矣，不可弭忘。 鴥彼飞隼，率彼中陵。民之讹言，宁莫之惩？我友敬矣，谗言其兴。', '诗经');
INSERT INTO `poem` VALUES (1317, '小雅·鹤鸣', '先秦', '佚名', '鹤鸣于九皋，声闻于野。鱼潜在渊，或在于渚。乐彼之园，爰有树檀，其下维萚。它山之石，可以为错。 鹤鸣于九皋，声闻于天。鱼在于渚，或潜在渊。乐彼之园，爰有树檀，其下维榖。它山之石，可以攻玉。', '诗经');
INSERT INTO `poem` VALUES (1318, '祈父', '先秦', '佚名', '祈父，予王之爪牙。胡转予于恤，靡所止居？ 祈父，予王之爪士。胡转予于恤，靡所厎止？ 祈父，亶不聪。胡转予于恤？有母之尸饔。', '诗经');
INSERT INTO `poem` VALUES (1319, '小雅·白驹', '先秦', '佚名', '皎皎白驹，食我场苗。絷之维之，以永今朝。所谓伊人，于焉逍遥？ 皎皎白驹，食我场藿。絷之维之，以永今夕。所谓伊人，于焉嘉客？ 皎皎白驹，贲然来思。尔公尔侯，逸豫无期？慎尔优游，勉尔遁思。 皎皎白驹，在彼空谷。生刍一束，其人如玉。毋金玉尔音，而有遐心。', '诗经');
INSERT INTO `poem` VALUES (1320, '小雅·黄鸟', '先秦', '佚名', '黄鸟黄鸟，无集于穀，无啄我粟。此邦之人，不我肯穀。言旋言归，复我邦族。 黄鸟黄鸟，无集于桑，无啄我粱。此邦之人，不可与明。言旋言归，复我诸兄。 黄鸟黄鸟，无集于栩，无啄我黍。此邦之人，不可与处。言旋言归，复我诸父。', '诗经');
INSERT INTO `poem` VALUES (1321, '我行其野', '先秦', '佚名', '我行其野，蔽芾其樗。婚姻之故，言就尔居。尔不我畜，复我邦家。 我行其野，言采其蓫。婚姻之故，言就尔宿。尔不我畜，言归斯复。 我行其野，言采其葍。不思旧姻，求尔新特。成不以富，亦祗以异。', '诗经');
INSERT INTO `poem` VALUES (1322, '小雅·斯干', '先秦', '佚名', '秩秩斯干，幽幽南山。如竹苞矣，如松茂矣。兄及弟矣，式相好矣，无相犹矣。 似续妣祖，筑室百堵，西南其户。爰居爰处，爰笑爰语。 约之阁阁，椓之橐橐。风雨攸除，鸟鼠攸去，君子攸芋。 如跂斯翼，如矢斯棘，如鸟斯革，如翚斯飞，君子攸跻。 殖殖其庭，有觉其楹。哙哙其正，哕哕其冥。君子攸宁。 下莞上簟，乃安斯寝。乃寝乃兴，乃占我梦。吉梦维何？维熊维罴，维虺维蛇。 大人占之：维熊维罴，男子之祥；维虺维蛇，女子之祥。 乃生男子，载寝之床。载衣之裳，载弄之璋。其泣喤喤，朱芾斯皇，室家君王。 乃生女子，载寝之地。载衣之裼，载弄之瓦。无非无仪，唯酒食是议，无父母诒罹。', '诗经');
INSERT INTO `poem` VALUES (1323, '小雅·无羊', '先秦', '佚名', '谁谓尔无羊？三百维群。谁谓尔无牛？九十其犉。尔羊来思，其角濈濈。尔牛来思，其耳湿湿。 或降于阿，或饮于池，或寝或讹。尔牧来思，何蓑何笠，或负其餱。三十维物，尔牲则具。 尔牧来思，以薪以蒸，以雌以雄。尔羊来思，矜矜兢兢，不骞不崩。麾之以肱，毕来既升。 牧人乃梦，众维鱼矣，旐维旟矣，大人占之；众维鱼矣，实维丰年；旐维旟矣，室家溱溱。', '诗经');
INSERT INTO `poem` VALUES (1324, '小雅·节南山', '先秦', '佚名', '节彼南山，维石岩岩。赫赫师尹，民具尔瞻。忧心如惔，不敢戏谈。国既卒斩，何用不监！ 节彼南山，有实其猗。赫赫师尹，不平谓何。天方荐瘥，丧乱弘多。民言无嘉，憯莫惩嗟。 尹氏大师，维周之氐；秉国之钧，四方是维。天子是毗，俾民不迷。不吊昊天，不宜空我师。 弗躬弗亲，庶民弗信。弗问弗仕，勿罔君子。式夷式已，无小人殆。琐琐姻亚，则无膴仕。 昊天不佣，降此鞠訩。昊天不惠，降此大戾。君子如届，俾民心阕。君子如夷，恶怒是违。 不吊昊天，乱靡有定。式月斯生，俾民不宁。忧心如酲，谁秉国成？不自为政，卒劳百姓。 驾彼四牡，四牡项领。我瞻四方，蹙蹙靡所骋。 方茂尔恶，相尔矛矣。既夷既怿，如相酬矣。 昊天不平，我王不宁。不惩其心，覆怨其正。 家父作诵，以究王訩。式讹尔心，以畜万邦。', '诗经');
INSERT INTO `poem` VALUES (1325, '小雅·正月', '先秦', '佚名', '正月繁霜，我心忧伤。民之讹言，亦孔之将。念我独兮，忧心京京。哀我小心，癙忧以痒。 父母生我，胡俾我瘉？不自我先，不自我后。好言自口，莠言自口。忧心愈愈，是以有侮。 忧心惸惸，念我无禄。民之无辜，并其臣仆。哀我人斯，于何从禄？瞻乌爰止？于谁之屋？ 瞻彼中林，侯薪侯蒸。民今方殆，视天梦梦。既克有定，靡人弗胜。有皇上帝，伊谁云憎？ 谓山盖卑，为冈为陵。民之讹言，宁莫之惩。召彼故老，讯之占梦。具曰予圣，谁知乌之雌雄！ 谓天盖高，不敢不局。谓地盖厚，不敢不蹐。维号斯言，有伦有脊。哀今之人，胡为虺蜴？ 瞻彼阪田，有菀其特。天之杌我，如不我克。彼求我则，如不我得。执我仇仇，亦不我力。 心之忧矣，如或结之。今兹之正，胡然厉矣？燎之方扬，宁或灭之？赫赫宗周，褒姒灭之！ 终其永怀，又窘阴雨。其车既载，乃弃尔辅。载输尔载，将伯助予！ 无弃尔辅，员于尔辐。屡顾尔仆，不输尔载。终逾绝险，曾是不意。 鱼在于沼，亦匪克乐。潜虽伏矣，亦孔之炤。忧心惨惨，念国之为虐！ 彼有旨酒，又有嘉肴。洽比其邻，婚姻孔云。念我独兮，忧心殷殷。 佌佌彼有屋，蔌蔌方有谷。民今之无禄，天夭是椓。哿矣富人，哀此惸独。', '诗经');
INSERT INTO `poem` VALUES (1326, '小雅·十月之交', '先秦', '佚名', '十月之交，朔月辛卯。日有食之，亦孔之丑。彼月而微，此日而微；今此下民，亦孔之哀。 日月告凶，不用其行。四国无政，不用其良。彼月而食，则维其常；此日而食，于何不臧。 烨烨震电，不宁不令。百川沸腾，山冢崒崩。高岸为谷，深谷为陵。哀今之人，胡憯莫惩？ 皇父卿士，番维司徒。家伯维宰，仲允膳夫。棸子内史，蹶维趣马。楀维师氏，醘妻煽方处。 抑此皇父，岂曰不时？胡为我作，不即我谋？彻我墙屋，田卒污莱。曰予不戕，礼则然矣。 皇父孔圣，作都于向。择三有事，亶侯多藏。不慭遗一老，俾守我王。择有车马，以居徂向。 黾勉从事，不敢告劳。无罪无辜，谗口嚣嚣。下民之孽，匪降自天。噂沓背憎，职竞由人。 悠悠我里，亦孔之痗。四方有羡，我独居忧。民莫不逸，我独不敢休。天命不彻，我不敢效我友自逸。', '诗经');
INSERT INTO `poem` VALUES (1327, '雨无正', '先秦', '佚名', '浩浩昊天，不骏其德。降丧饥馑，斩伐四国。旻天疾威，弗虑弗图。舍彼有罪，既伏其辜。若此无罪，沦胥以铺。 周宗既灭，靡所止戾。正大夫离居，莫知我勚。三事大夫，莫肯夙夜。邦君诸侯，莫肯朝夕。庶曰式臧，覆出为恶。 如何昊天，辟言不信。如彼行迈，则靡所臻。凡百君子，各敬尔身。胡不相畏，不畏于天？ 戎成不退，饥成不遂。曾我暬御，憯憯日瘁。凡百君子，莫肯用讯。听言则答，谮言则退。 哀哉不能言，匪舌是出，维躬是瘁。哿矣能言，巧言如流，俾躬处休！ 维曰予仕，孔棘且殆。云不何使，得罪于天子；亦云可使，怨及朋友。 谓尔迁于王都。曰予未有室家。鼠思泣血，无言不疾。昔尔出居，谁从作尔室？', '诗经');
INSERT INTO `poem` VALUES (1328, '小雅·小旻', '先秦', '佚名', '旻天疾威，敷于下土。谋犹回遹，何日斯沮？谋臧不从，不臧覆用。我视谋犹，亦孔之邛。 潝潝訿訿，亦孔之哀。谋之其臧，则具是违。谋之不臧，则具是依。我视谋犹，伊于胡厎。 我龟既厌，不我告犹。谋夫孔多，是用不集。发言盈庭，谁敢执其咎？如匪行迈谋，是用不得于道。 哀哉为犹，匪先民是程，匪大犹是经。维迩言是听，维迩言是争。如彼筑室于道谋，是用不溃于成。 国虽靡止，或圣或否。民虽靡膴，或哲或谋，或肃或艾。如彼泉流，无沦胥以败。 不敢暴虎，不敢冯河。人知其一，莫知其他。战战兢兢，如临深渊，如履薄冰。', '诗经');
INSERT INTO `poem` VALUES (1329, '小雅·小宛', '先秦', '佚名', '宛彼鸣鸠，翰飞戾天。我心忧伤，念昔先人。明发不寐，有怀二人。 人之齐圣，饮酒温克。彼昏不知，壹醉日富。各敬尔仪，天命不又。 中原有菽，庶民采之。螟蛉有子，蜾蠃负之。教诲尔子，式穀似之。 题彼脊令，载飞载鸣。我日斯迈，而月斯征。夙兴夜寐，毋忝尔所生。 交交桑扈，率场啄粟。哀我填寡，宜岸宜狱。握粟出卜，自何能穀？ 温温恭人，如集于木。惴惴小心，如临于谷。战战兢兢，如履薄冰。', '诗经');
INSERT INTO `poem` VALUES (1330, '小雅·小弁', '先秦', '佚名', '弁彼鸴斯，归飞提提。民莫不穀，我独于罹。何辜于天？我罪伊何？心之忧矣，云如之何？ 踧踧周道，鞫为茂草。我心忧伤，惄焉如捣。假寐永叹，维忧用老。心之忧矣，疢如疾首。 维桑与梓，必恭敬止。靡瞻匪父，靡依匪母。不属于毛？不罹于里？天之生我，我辰安在？ 菀彼柳斯，鸣蜩嘒嘒，有漼者渊，萑苇淠淠。譬彼舟流，不知所届，心之忧矣，不遑假寐。 鹿斯之奔，维足伎伎。雉之朝雊，尚求其雌。譬彼坏木，疾用无枝。心之忧矣，宁莫之知？ 相彼投兔，尚或先之。行有死人，尚或墐之。君子秉心，维其忍之。心之忧矣，涕既陨之。 君子信谗，如或酬之。君子不惠，不舒究之。伐木掎矣，析薪扡矣。舍彼有罪，予之佗矣。 莫高匪山，莫浚匪泉。君子无易由言，耳属于垣。无逝我梁，无发我笱。我躬不阅，遑恤我后。', '诗经');
INSERT INTO `poem` VALUES (1331, '小雅·巧言', '先秦', '佚名', '悠悠昊天，曰父母且。无罪无辜，乱如此幠。昊天已威，予慎无罪。昊天大幠，予慎无辜。 乱之初生，僭始既涵。乱之又生，君子信谗。君子如怒，乱庶遄沮。君子如祉，乱庶遄已。 君子屡盟，乱是用长。君子信盗，乱是用暴。盗言孔甘，乱是用餤。匪其止共，维王之邛。 奕奕寝庙，君子作之。秩秩大猷，圣人莫之。他人有心，予忖度之。跃跃毚兔，遇犬获之。 荏染柔木，君子树之。往来行言，心焉数之。蛇蛇硕言，出自口矣。巧言如簧，颜之厚矣。 彼何人斯？居河之麋。无拳无勇，职为乱阶。既微且尰，尔勇伊何？为犹将多，尔居徒几何？', '诗经');
INSERT INTO `poem` VALUES (1332, '小雅·何人斯', '先秦', '佚名', '彼何人斯？其心孔艰。胡逝我梁，不入我门？伊谁云从？维暴之云。 二人从行，谁为此祸？胡逝我梁，不入唁我？始者不如今，云不我可 彼何人斯？胡逝我陈？我闻其声，不见其身。不愧于人？不畏于天？ 彼何人斯？其为飘风。胡不自北？胡不自南？胡逝我梁？祗搅我心。 尔之安行，亦不遑舍。尔之亟行，遑脂尔车。壹者之来，云何其盱。 尔还而入，我心易也。还而不入，否难知也。壹者之来，俾我祗也。 伯氏吹埙，仲氏吹篪。及尔如贯，谅不我知，出此三物，以诅尔斯。 为鬼为蜮，则不可得。有靦面目，视人罔极。作此好歌，以极反侧。', '诗经');
INSERT INTO `poem` VALUES (1333, '小雅·巷伯', '先秦', '佚名', '萋兮斐兮，成是贝锦。彼谮人者，亦已大甚！ 哆兮侈兮，成是南箕。彼谮人者，谁适与谋。 缉缉翩翩，谋欲谮人。慎尔言也，谓尔不信。 捷捷幡幡，谋欲谮言。岂不尔受？既其女迁。 骄人好好，劳人草草。苍天苍天，视彼骄人，矜此劳人。 彼谮人者，谁适与谋？取彼谮人，投畀豺虎。豺虎不食，投畀有北。有北不受，投畀有昊！ 杨园之道，猗于亩丘。寺人孟子，作为此诗。凡百君子，敬而听之。', '诗经');
INSERT INTO `poem` VALUES (1334, '小雅·谷风', '先秦', '佚名', '习习谷风，维风及雨。将恐将惧，维予与女。将安将乐，女转弃予。 习习谷风，维风及颓。将恐将惧，置予于怀。将安将乐，弃予如遗。 习习谷风，维山崔嵬。无草不死，无木不萎。忘我大德，思我小怨。', '诗经');
INSERT INTO `poem` VALUES (1335, '蓼莪', '先秦', '佚名', '蓼蓼者莪，匪莪伊蒿。哀哀父母，生我劬劳。 蓼蓼者莪，匪莪伊蔚。哀哀父母，生我劳瘁。 瓶之罄矣，维罍之耻。鲜民之生，不如死之久矣。无父何怙？无母何恃？出则衔恤，入则靡至。 父兮生我，母兮鞠我。抚我畜我，长我育我，顾我复我，出入腹我。欲报之德。昊天罔极！ 南山烈烈，飘风发发。民莫不穀，我独何害！南山律律，飘风弗弗。民莫不穀，我独不卒！', '诗经');
INSERT INTO `poem` VALUES (1336, '小雅·大东', '先秦', '佚名', '有饛簋飧，有捄棘匕。周道如砥，其直如矢。君子所履，小人所视。眷言顾之，潸焉出涕。 小东大东，杼柚其空。纠纠葛屦，可以履霜。佻佻公子，行彼周行。既往既来，使我心疚。 有冽氿泉，无浸获薪。契契寤叹，哀我惮人。薪是获薪，尚可载也。哀我惮人，亦可息也。 东人之子，职劳不来。西人之子，粲粲衣服。舟人之子，熊罴是裘。私人之子，百僚是试。 或以其酒，不以其浆。鞙鞙佩璲，不以其长。维天有汉，监亦有光。跂彼织女，终日七襄。 虽则七襄，不成报章。睆彼牵牛，不以服箱。东有启明，西有长庚。有捄天毕，载施之行。 维南有箕，不可以簸扬。维北有斗，不可以挹酒浆。维南有箕，载翕其舌。维北有斗，西柄之揭。', '诗经');
INSERT INTO `poem` VALUES (1337, '小雅·四月', '先秦', '佚名', '四月维夏，六月徂署。先祖匪人，胡宁忍予？ 秋日凄凄，百卉具腓。乱离瘼矣，爰其适归？ 冬日烈烈，飘风发发。民莫不穀，我独何害？ 山有嘉卉，侯栗侯梅。废为残贼，莫知其尤！ 相彼泉水，载清载浊。我日构祸，曷云能穀？ 滔滔江汉，南国之纪。尽瘁以仕，宁莫我有？ 匪鹑匪鸢，翰飞戾天。匪鳣匪鲔，潜逃于渊。 山有蕨薇，隰有杞桋。君子作歌，维以告哀。', '诗经');
INSERT INTO `poem` VALUES (1338, '小雅·北山', '先秦', '佚名', '陟彼北山，言采其杞。偕偕士子，朝夕从事。王事靡盬，忧我父母。 溥天之下，莫非王土；率土之滨，莫非王臣。大夫不均，我从事独贤。 四牡彭彭，王事傍傍。嘉我未老，鲜我方将。旅力方刚，经营四方。 或燕燕居息，或尽瘁事国；或息偃在床，或不已于行。 或不知叫号，或惨惨劬劳；或栖迟偃仰，或王事鞅掌。 或湛乐饮酒，或惨惨畏咎；或出入风议，或靡事不为。', '诗经');
INSERT INTO `poem` VALUES (1339, '无将大车', '先秦', '佚名', '无将大车，祇自尘兮。无思百忧，祇自疧兮。 无将大车，维尘冥冥。无思百忧，不出于颎。 无将大车，维尘雍兮。无思百忧，祇自重兮。', '诗经');
INSERT INTO `poem` VALUES (1340, '小明', '先秦', '佚名', '明明上天，照临下土。我征徂西，至于艽野。二月初吉，载离寒暑。 心之忧矣，其毒大苦。念彼共人，涕零如雨。岂不怀归？畏此罪罟！ 昔我往矣，日月方除。曷云其还？岁聿云莫。念我独兮，我事孔庶。 心之忧矣，惮我不暇。念彼共人，眷眷怀顾！岂不怀归？畏此谴怒。 昔我往矣，日月方奥。曷云其还？政事愈蹙。岁聿云莫，采萧获菽。 心之忧矣，自诒伊戚。念彼共人，兴言出宿。岂不怀归？畏此反覆。 嗟尔君子，无恒安处。靖共尔位，正直是与。神之听之，式穀以女。 嗟尔君子，无恒安息。靖共尔位，好是正直。神之听之，介尔景福。', '诗经');
INSERT INTO `poem` VALUES (1341, '小雅·鼓钟', '先秦', '佚名', '鼓钟将将，淮水汤汤，忧心且伤。淑人君子，怀允不忘。 鼓钟喈喈，淮水湝湝，忧心且悲。淑人君子，其德不回。 鼓钟伐鼛，淮有三洲，忧心且妯。淑人君子，其德不犹。 鼓钟钦钦，鼓瑟鼓琴，笙磬同音。以雅以南，以籥不僭。', '诗经');
INSERT INTO `poem` VALUES (1342, '小雅·楚茨', '先秦', '佚名', '楚楚者茨，言抽其棘。自昔何为，我艺黍稷。我黍与与，我稷翼翼。 我仓既盈，我庾维亿。以为酒食，以享以祀。以妥以侑，以介景福。 济济跄跄，絜尔牛羊，以往烝尝。或剥或亨，或肆或将。 祝祭于祊，祀事孔明。先祖是皇，神保是飨。孝孙有庆，报以介福，万寿无疆。 执爨踖踖，为俎孔硕。或燔或炙，君妇莫莫。为豆孔庶，为宾为客。 献酬交错，礼仪卒度，笑语卒获。神保是格，报以介福，万寿攸酢。 我孔熯矣，式礼莫愆。工祝致告，徂赉孝孙。苾芬孝祀，神嗜饮食。 卜尔百福，如畿如式。既齐既稷，既匡既敕。永锡尔极，时万时亿。 礼仪既备，钟鼓既戒。孝孙徂位，工祝致告。神具醉止，皇尸载起。 钟鼓送尸，神保聿归。诸宰君妇，废彻不迟。诸父兄弟，备言燕私。 乐具入奏，以绥后禄。尔肴既将，莫怨具庆。既醉既饱，小大稽首。 神嗜饮食，使君寿考。孔惠孔时，维其尽之。子子孙孙，勿替引之。', '诗经');
INSERT INTO `poem` VALUES (1343, '小雅·信南山', '先秦', '佚名', '信彼南山，维禹甸之。畇畇原隰，曾孙田之。我疆我理，南东其亩。 上天同云。雨雪雰雰，益之以霡霂。既优既渥，既沾既足。生我百谷。 疆埸翼翼，黍稷彧彧。曾孙之穑，以为酒食。畀我尸宾，寿考万年。 中田有庐，疆埸有瓜。是剥是菹，献之皇祖。曾孙寿考，受天之祜。 祭以清酒，从以骍牡，享于祖考。执其鸾刀，以启其毛，取其血膋。 是烝是享，苾苾芬芬。祀事孔明，先祖是皇。报以介福。万寿无疆。', '诗经');
INSERT INTO `poem` VALUES (1344, '小雅·甫田', '先秦', '佚名', '倬彼甫田，岁取十千。我取其陈，食我农人。自古有年。今适南亩，或耘或耔。黍稷薿薿，攸介攸止，烝我髦士。 以我齐明，与我牺羊，以社以方。我田既臧，农夫之庆。琴瑟击鼓，以御田祖。以祈甘雨，以介我稷黍，以穀我士女。 曾孙来止，以其妇子。馌彼南亩，田畯至喜。攘其左右，尝其旨否。禾易长亩，终善且有。曾孙不怒，农夫克敏。 曾孙之稼，如茨如梁。曾孙之庾，如坻如京。乃求千斯仓，乃求万斯箱。黍稷稻粱，农夫之庆。报以介福，万寿无疆。', '诗经');
INSERT INTO `poem` VALUES (1345, '小雅·大田', '先秦', '佚名', '大田多稼，既种既戒，既备乃事。以我覃耜，俶载南亩。播厥百谷，既庭且硕，曾孙是若。 既方既皁，既坚既好，不稂不莠。去其螟螣，及其蟊贼，无害我田稚。田祖有神，秉畀炎火。 有渰萋萋，兴雨祈祈。雨我公田，遂及我私。彼有不获稚，此有不敛穧，彼有遗秉，此有滞穗，伊寡妇之利。 曾孙来止，以其妇子。馌彼南亩，田畯至喜。来方禋祀，以其骍黑，与其黍稷。以享以祀，以介景福。', '诗经');
INSERT INTO `poem` VALUES (1346, '瞻彼洛矣', '先秦', '佚名', '瞻彼洛矣，维水泱泱。君子至止，福禄如茨。韎韐有奭，以作六师。 瞻彼洛矣，维水泱泱。君子至止，鞸琫有珌。君子万年，保其家室。 瞻彼洛矣，维水泱泱。君子至止，福禄既同。君子万年，保其家邦。', '诗经');
INSERT INTO `poem` VALUES (1347, '小雅·裳裳者华', '先秦', '佚名', '裳裳者华，其叶湑兮。我觏之子，我心写兮。我心写兮，是以有誉处兮。 裳裳者华，芸其黄矣。我觏之子，维其有章矣。维其有章矣，是以有庆矣。 裳裳者华，或黄或白。我觏之子，乘其四骆。乘其四骆，六辔沃若。 左之左之，君子宜之。右之右之，君子有之。维其有之，是以似之。', '诗经');
INSERT INTO `poem` VALUES (1348, '小雅·桑扈', '先秦', '佚名', '交交桑扈，有莺其羽。君子乐胥，受天之祜。 交交桑扈，有莺其领。君子乐胥，万邦之屏。 之屏之翰，百辟为宪。不戢不难，受福不那。 兕觥其觩，旨酒思柔。彼交匪敖，万福来求。', '诗经');
INSERT INTO `poem` VALUES (1349, '鸳鸯', '先秦', '佚名', '鸳鸯于飞，毕之罗之。君子万年，福禄宜之。 鸳鸯在梁，戢其左翼。君子万年，宜其遐福。 乘马在厩，摧之秣之。君子万年，福禄艾之。 乘马在厩，秣之摧之。君子万年，福禄绥之。', '诗经');
INSERT INTO `poem` VALUES (1350, '頍弁', '先秦', '佚名', '有頍者弁，实维伊何？尔酒既旨，尔肴既嘉。岂伊异人？兄弟匪他。 茑与女萝，施于松柏。未见君子，忧心奕奕；既见君子，庶几说怿。 有頍者弁，实维何期？尔酒既旨，尔肴既时。岂伊异人？兄弟具来。 茑与女萝，施于松上。未见君子，忧心怲怲；既见君子，庶几有臧。 有頍者弁，实维在首。尔酒既旨，尔肴既阜。岂伊异人？兄弟甥舅。 如彼雨雪，先集维霰。死丧无日，无几相见。乐酒今夕，君子维宴。', '诗经');
INSERT INTO `poem` VALUES (1351, '小雅·车舝', '先秦', '佚名', '间关车之舝兮，思娈季女逝兮。匪饥匪渴，德音来括。虽无好友？式燕且喜。 依彼平林，有集维鷮。辰彼硕女，令德来教。式燕且誉，好尔无射。 虽无旨酒？式饮庶几。虽无嘉肴？式食庶几。虽无德与女？式歌且舞？ 陟彼高冈，析其柞薪。析其柞薪，其叶湑兮。鲜我觏尔，我心写兮。 高山仰止，景行行止。四牡騑騑，六辔如琴。觏尔新婚，以慰我心。', '诗经');
INSERT INTO `poem` VALUES (1352, '青蝇', '先秦', '佚名', '营营青蝇，止于樊。岂弟君子，无信谗言。 营营青蝇，止于棘。谗人罔极，交乱四国。 营营青蝇，止于榛。谗人罔极，构我二人。', '诗经');
INSERT INTO `poem` VALUES (1353, '宾之初筵', '先秦', '佚名', '宾之初筵，左右秩秩。笾豆有楚，殽核维旅。酒既和旨，饮酒孔偕。钟鼓既设，举酬逸逸。大侯既抗，弓矢斯张。射夫既同，献尔发功。发彼有的，以祈尔爵。 籥舞笙鼓，乐既和奏。烝衎烈祖，以洽百礼。百礼既至，有壬有林。锡尔纯嘏，子孙其湛。其湛曰乐，各奏尔能。宾载手仇，室人入又。酌彼康爵，以奏尔时。 宾之初筵，温温其恭。其未醉止，威仪反反。曰既醉止，威仪幡幡。舍其坐迁，屡舞仙仙。其未醉止，威仪抑抑。曰既醉止，威仪怭怭。是曰既醉，不知其秩。 宾既醉止，载号载呶。乱我笾豆，屡舞僛僛。是曰既醉，不知其邮。侧弁之俄，屡舞傞傞。既醉而出，并受其福。醉而不出，是谓伐德。饮酒孔嘉，维其令仪。 凡此饮酒，或醉或否。既立之监，或佐之史。彼醉不臧，不醉反耻。式勿从谓，无俾大怠。匪言勿言，匪由勿语。由醉之言，俾出童羖。三爵不识，矧敢多又。', '诗经');
INSERT INTO `poem` VALUES (1354, '鱼藻', '先秦', '佚名', '鱼在在藻，有颁其首。王在在镐，岂乐饮酒。 鱼在在藻，有莘其尾。王在在镐，饮酒乐岂。 鱼在在藻，依于其蒲。王在在镐，有那其居。', '诗经');
INSERT INTO `poem` VALUES (1355, '采菽', '先秦', '佚名', '采菽采菽，筐之莒之。君子来朝，何锡予之？虽无予之？路车乘马。又何予之？玄衮及黼。 觱沸槛泉，言采其芹。君子来朝，言观其旂。其旂淠淠，鸾声嘒嘒。载骖载驷，君子所届。 赤芾在股，邪幅在下。彼交匪纾，天子所予。乐只君子，天子命之。乐只君子，福禄申之。 维柞之枝，其叶蓬蓬。乐只君子，殿天子之邦。乐只君子，万福攸同。平平左右，亦是率从。 汎汎杨舟，绋纚维之。乐只君子，天子葵之。乐只君子，福禄膍之。优哉游哉，亦是戾矣。', '诗经');
INSERT INTO `poem` VALUES (1356, '角弓', '先秦', '佚名', '骍骍角弓，翩其反矣。兄弟婚姻，无胥远矣。 尔之远矣，民胥然矣。尔之教矣，民胥效矣。 此令兄弟，绰绰有裕。不令兄弟，交相为愈。 民之无良，相怨一方。受爵不让，至于已斯亡。 老马反为驹，不顾其后。如食宜饇，如酌孔取。 毋教猱升木，如涂涂附。君子有徽猷，小人与属。 雨雪瀌瀌，见晛曰消。莫肯下遗，式居娄骄。 雨雪浮浮，见晛曰流。如蛮如髦，我是用忧。', '诗经');
INSERT INTO `poem` VALUES (1357, '菀柳', '先秦', '佚名', '有菀者柳，不尚息焉。上帝甚蹈，无自暱焉。俾予靖之，后予极焉。 有菀者柳，不尚愒焉。上帝甚蹈，无自瘵焉。俾予靖之，后予迈焉。 有鸟高飞，亦傅于天。彼人之心，于何其臻。曷予靖之，居以凶矜。', '诗经');
INSERT INTO `poem` VALUES (1358, '都人士', '先秦', '佚名', '彼都人士，狐裘黄黄。其容不改，出言有章。行归于周，万民所望。 彼都人士，台笠缁撮。彼君子女，绸直如发。我不见兮，我心不说。 彼都人士，充耳琇实。彼君子女，谓之尹吉。我不见兮，我心苑结。 彼都人士，垂带而厉。彼君子女，卷发如虿。我不见兮，言从之迈。 匪伊垂之，带则有余。匪伊卷之，发则有旟。我不见兮，云何盱矣。', '诗经');
INSERT INTO `poem` VALUES (1359, '采绿', '先秦', '佚名', '终朝采绿，不盈一匊。予发曲局，薄言归沐。 终朝采蓝，不盈一襜。五日为期，六日不詹。 之子于狩，言韔其弓。之子于钓，言纶之绳。 其钓维何？维鲂及鱮。维鲂及鱮，薄言观者。', '诗经');
INSERT INTO `poem` VALUES (1360, '小雅·黍苗', '先秦', '佚名', '芃芃黍苗，阴雨膏之。悠悠南行，召伯劳之。 我任我辇，我车我牛。我行既集，盖云归哉。 我徒我御，我师我旅。我行既集，盖云归处。 肃肃谢功，召伯营之。烈烈征师，召伯成之。 原隰既平，泉流既清。召伯有成，王心则宁。', '诗经');
INSERT INTO `poem` VALUES (1361, '隰桑', '先秦', '佚名', '隰桑有阿，其叶有难。既见君子，其乐如何。 隰桑有阿，其叶有沃。既见君子，云何不乐。 隰桑有阿，其叶有幽。既见君子，德音孔胶。 心乎爱矣，遐不谓矣？中心藏之，何日忘之！', '诗经');
INSERT INTO `poem` VALUES (1362, '白华', '先秦', '佚名', '白华菅兮，白茅束兮。之子之远，俾我独兮。 英英白云，露彼菅茅。天步艰难，之子不犹。 滮池北流，浸彼稻田。啸歌伤怀，念彼硕人。 樵彼桑薪，卬烘于煁。维彼硕人，实劳我心。 鼓钟于宫，声闻于外。念子懆懆，视我迈迈。 有鹙在梁，有鹤在林。维彼硕人，实劳我心。 鸳鸯在梁，戢其左翼。之子无良，二三其德。 有扁斯石，履之卑兮。之子之远，俾我疧兮。', '诗经');
INSERT INTO `poem` VALUES (1363, '绵蛮', '先秦', '佚名', '绵蛮黄鸟，止于丘阿。道之云远，我劳如何。饮之食之，教之诲之。命彼后车，谓之载之。 绵蛮黄鸟，止于丘隅。岂敢惮行，畏不能趋。饮之食之。教之诲之。命彼后车，谓之载之。 绵蛮黄鸟，止于丘侧。岂敢惮行，畏不能极。饮之食之，教之诲之。命彼后车，谓之载之。', '诗经');
INSERT INTO `poem` VALUES (1364, '小雅·瓠叶', '先秦', '佚名', '幡幡瓠叶，采之亨之。君子有酒，酌言尝之。 有兔斯首，炮之燔之。君子有酒，酌言献之。 有兔斯首，燔之炙之。君子有酒，酌言酢之。 有兔斯首，燔之炮之。君子有酒，酌言酬之。', '诗经');
INSERT INTO `poem` VALUES (1365, '小雅·渐渐之石', '先秦', '佚名', '渐渐之石，维其高矣。山川悠远，维其劳矣。武人东征，不皇朝矣。 渐渐之石，维其卒矣。山川悠远，曷其没矣？武人东征，不皇出矣。 有豕白蹢，烝涉波矣。月离于毕，俾滂沱矣。武人东征，不皇他矣。', '诗经');
INSERT INTO `poem` VALUES (1366, '小雅·苕之华', '先秦', '佚名', '苕之华，芸其黄矣。心之忧矣，维其伤矣！ 苕之华，其叶青青。知我如此，不如无生！ 牂羊坟首，三星在罶。人可以食，鲜可以饱！', '诗经');
INSERT INTO `poem` VALUES (1367, '何草不黄', '先秦', '佚名', '何草不黄？何日不行？何人不将？经营四方。 何草不玄？何人不矜？哀我征夫，独为匪民。 匪兕匪虎，率彼旷野。哀我征夫，朝夕不暇。 有芃者狐，率彼幽草。有栈之车，行彼周道。', '诗经');
INSERT INTO `poem` VALUES (1368, '大雅·文王', '先秦', '佚名', '文王在上，於昭於天。周虽旧邦，其命维新。有周不显，帝命不时。文王陟降，在帝左右。 亹亹文王，令闻不已。陈锡哉周，侯文王孙子。文王孙子，本支百世，凡周之士，不显亦世。 世之不显，厥犹翼翼。思皇多士，生此王国。王国克生，维周之桢；济济多士，文王以宁。 穆穆文王，于缉熙敬止。假哉天命，有商孙子。商之孙子，其丽不亿。上帝既命，侯于周服。 侯服于周，天命靡常。殷士肤敏，祼将于京。厥作祼将，常服黼冔。王之荩臣，无念尔祖。 无念尔祖，聿修厥德。永言配命，自求多福。殷之未丧师，克配上帝。宜鉴于殷，骏命不易！ 命之不易，无遏尔躬。宣昭义问，有虞殷自天。上天之载，无声无臭。仪刑文王，万邦作孚。', '诗经');
INSERT INTO `poem` VALUES (1369, '大雅·大明', '先秦', '佚名', '明明在下，赫赫在上。天难忱斯，不易维王。天位殷适，使不挟四方。 挚仲氏任，自彼殷商，来嫁于周，曰嫔于京。乃及王季，维德之行。 大任有身，生此文王。维此文王，小心翼翼。昭事上帝，聿怀多福。厥德不回，以受方国。 天监在下，有命既集。文王初载，天作之合。在洽之阳，在渭之涘。 文王嘉止，大邦有子。大邦有子，伣天之妹。文定厥祥，亲迎于渭。造舟为梁，不显其光。 有命自天，命此文王。于周于京，缵女维莘。长子维行，笃生武王。保右命尔，燮伐大商。 殷商之旅，其会如林。矢于牧野，维予侯兴。上帝临女，无贰尔心。 牧野洋洋，檀车煌煌，驷騵彭彭。维师尚父，时维鹰扬。凉彼武王，肆伐大商，会朝清明。', '诗经');
INSERT INTO `poem` VALUES (1370, '绵', '先秦', '佚名', '绵绵瓜瓞。民之初生，自土沮漆。古公亶父，陶复陶冗，未有家室。 古公亶父，来朝走马。率西水浒，至于岐下。爰及姜女，聿来胥宇。 周原膴々，堇荼如饴。爰始爰谋，爰契我龟，曰止曰时，筑室于兹。 乃慰乃止，乃左乃右，乃疆乃理，乃宣乃亩。自西徂东，周爰执事。 乃召司空，乃召司徒，俾立室家。其绳则直，缩版以载，作庙翼翼。 捄之陾，度之薨薨，筑之登登，削屡冯冯。百堵皆兴，鼛鼓弗胜。 乃立皋门，皋门有伉。乃立应门，应门将将。乃立冢土，戎丑攸行。 肆不殄厥愠，亦不陨厥问。柞棫拔矣，行道兑矣。混夷駾矣，维其喙矣！ 虞芮质厥成，文王蹶厥生。予曰有疏附，予曰有先后。予曰有奔奏，予曰有御侮！', '诗经');
INSERT INTO `poem` VALUES (1371, '棫朴', '先秦', '佚名', '芃芃棫朴，薪之槱之。济济辟王，左右趣之。 济济辟王，左右奉璋。奉璋峨峨，髦士攸宜。 淠彼泾舟，烝徒楫之。周王于迈，六师及之。 倬彼云汉，为章于天。周王寿考，遐不作人？ 追琢其章，金玉其相。勉勉我王，纲纪四方。', '诗经');
INSERT INTO `poem` VALUES (1372, '大雅·旱麓', '先秦', '佚名', '瞻彼旱麓，榛楛济济。岂弟君子，干禄岂弟。 瑟彼玉瓒，黄流在中。岂弟君子，福禄攸降。 鸢飞戾天，鱼跃于渊。岂弟君子，遐不作人？ 清酒既载，骍牡既备。以享以祀，以介景福。 瑟彼柞棫，民所燎矣。岂弟君子，神所劳矣。 莫莫葛藟，施于条枚。岂弟君子，求福不回。', '诗经');
INSERT INTO `poem` VALUES (1373, '大雅·思齐', '先秦', '佚名', '思齐大任，文王之母，思媚周姜，京室之妇。大姒嗣徽音，则百斯男。 惠于宗公，神罔时怨，神罔时恫。刑于寡妻，至于兄弟，以御于家邦。 雍雍在宫，肃肃在庙。不显亦临，无射亦保。 肆戎疾不殄，烈假不瑕。不闻亦式，不谏亦入。肆成人有德，小子有造。古之人无斁，誉髦斯士。', '诗经');
INSERT INTO `poem` VALUES (1374, '皇矣', '先秦', '佚名', '皇矣上帝，临下有赫。监观四方，求民之莫。维此二国，其政不获。维彼四国，爰究爰度。上帝耆之，憎其式廓。乃眷西顾，此维与宅。 作之屏之，其菑其翳。修之平之，其灌其栵。启之辟之，其柽其椐。攘之剔之，其檿其柘。帝迁明德，串夷载路。天立厥配，受命既固。 帝省其山，柞棫斯拔，松柏斯兑。帝作邦作对，自大伯王季。维此王季，因心则友。则友其兄，则笃其庆，载锡之光。受禄无丧，奄有四方。 维此王季，帝度其心。貊其德音，其德克明。克明克类，克长克君。王此大邦，克顺克比。比于文王，其德靡悔。既受帝祉，施于孙子。 帝谓文王：无然畔援，无然歆羡，诞先登于岸。密人不恭，敢距大邦，侵阮徂共。王赫斯怒，爰整其旅，以按徂旅。以笃于周祜，以对于天下。 依其在京，侵自阮疆。陟我高冈，无矢我陵。我陵我阿，无饮我泉，我泉我池。度其鲜原，居岐之阳，在渭之将。万邦之方，下民之王。 帝谓文王：予怀明德，不大声以色，不长夏以革。不识不知，顺帝之则。帝谓文王：訽尔仇方，同尔弟兄。以尔钩援，与尔临冲，以伐崇墉。 临冲闲闲，崇墉言言。执讯连连，攸馘安安。是类是禡，是致是附，四方以无侮。临冲茀茀，崇墉仡仡。是伐是肆，是绝是忽。四方以无拂。', '诗经');
INSERT INTO `poem` VALUES (1375, '大雅·灵台', '先秦', '佚名', '经始灵台，经之营之。庶民攻之，不日成之。经始勿亟，庶民子来。 王在灵囿，麀鹿攸伏。麀鹿濯濯，白鸟翯翯。王在灵沼，於牣鱼跃。 虡业维枞，贲鼓维镛。於论鼓钟，於乐辟廱。 於论鼓钟，於乐辟廱。鼍鼓逢逢。蒙瞍奏公。', '诗经');
INSERT INTO `poem` VALUES (1376, '下武', '先秦', '佚名', '下武维周，世有哲王。三后在天，王配于京。 王配于京，世德作求。永言配命，成王之孚。 成王之孚，下土之式。永言孝思，孝思维则。 媚兹一人，应侯顺德。永言孝思，昭哉嗣服。 昭兹来许，绳其祖武。於万斯年，受天之祜。 受天之祜，四方来贺。於万斯年，不遐有佐。', '诗经');
INSERT INTO `poem` VALUES (1377, '大雅·文王有声', '先秦', '佚名', '文王有声，遹骏有声。遹求厥宁，遹观厥成。文王烝哉！ 文王受命，有此武功。既伐于崇，作邑于丰。文王烝哉！ 筑城伊淢，作丰伊匹。匪棘其欲，遹追来孝。王后烝哉！ 王公伊濯，维丰之垣。四方攸同，王后维翰。王后烝哉！ 丰水东注，维禹之绩。四方攸同，皇王维辟。皇王烝哉！ 镐京辟雍，自西自东，自南自北，无思不服。皇王烝哉！ 考卜维王，宅是镐京。维龟正之，武王成之。武王烝哉！ 丰水有芑，武王岂不仕？诒厥孙谋，以燕翼子。武王烝哉！', '诗经');
INSERT INTO `poem` VALUES (1378, '大雅·生民', '先秦', '佚名', '厥初生民，时维姜嫄。生民如何？克禋克祀，以弗无子。履帝武敏歆，攸介攸止，载震载夙。载生载育，时维后稷。 诞弥厥月，先生如达。不坼不副，无菑无害，以赫厥灵。上帝不宁，不康禋祀，居然生子。 诞寘之隘巷，牛羊腓字之。诞寘之平林，会伐平林。诞寘之寒冰，鸟覆翼之。鸟乃去矣，后稷呱矣。实覃实訏，厥声载路。 诞实匍匐，克岐克嶷，以就口食。蓺之荏菽，荏菽旆旆。禾役穟穟，麻麦幪幪，瓜瓞唪唪。 诞后稷之穑，有相之道。茀厥丰草，种之黄茂。实方实苞，实种实褎。实发实秀，实坚实好。实颖实栗，即有邰家室。 诞降嘉种，维秬维秠，维穈维芑。恒之秬秠，是获是亩。恒之穈芑，是任是负，以归肇祀。 诞我祀如何？或舂或揄，或簸或蹂。释之叟叟，烝之浮浮。载谋载惟，取萧祭脂。取羝以軷，载燔载烈，以兴嗣岁。 昂盛于豆，于豆于登，其香始升。上帝居歆，胡臭亶时。后稷肇祀，庶无罪悔，以迄于今。', '诗经');
INSERT INTO `poem` VALUES (1379, '行苇', '先秦', '佚名', '敦彼行苇，牛羊勿践履。方苞方体，维叶泥泥。戚戚兄弟，莫远具尔。或肆之筵，或授之几。 肆筵设席，授几有缉御。或献或酢，洗爵奠斝。醓醢以荐，或燔或炙。嘉肴脾臄，或歌或咢。 敦弓既坚，四鍭既钧，舍矢既均，序宾以贤。敦弓既句，既挟四鍭。四鍭如树，序宾以不侮。 曾孙维主，酒醴维醹，酌以大斗，以祈黄耇。黄耇台背，以引以翼。寿考维祺，以介景福。', '诗经');
INSERT INTO `poem` VALUES (1380, '大雅·既醉', '先秦', '佚名', '既醉以酒，既饱以德。君子万年，介尔景福。 既醉以酒，尔肴既将。君子万年，介尔昭明。 昭明有融，高朗令终，令终有俶。公尸嘉告。 其告维何？笾豆静嘉。朋友攸摄，摄以威仪。 威仪孔时，君子有孝子。孝子不匮，永锡尔类。 其类维何？室家之壶。君子万年，永锡祚胤。 其胤维何？天被尔禄。君子万年，景命有仆。 其仆维何？釐尔女士。釐尔女士，从以孙子。', '诗经');
INSERT INTO `poem` VALUES (1381, '大雅·凫鹥', '先秦', '佚名', '凫鹥在泾，公尸来燕来宁。尔酒既清，尔肴既馨。公尸燕饮，福禄来成。 凫鹥在沙，公尸来燕来宜。尔酒既多，尔肴既嘉。公尸燕饮，福禄来为。 凫鹥在渚，公尸来燕来处。尔酒既湑，尔肴伊脯。公尸燕饮，福禄来下。 凫鹥在潀，公尸来燕来宗，既燕于宗，福禄攸降。公尸燕饮，福禄来崇。 凫鹥在亹，公尸来止熏熏。旨酒欣欣，燔炙芬芬。公尸燕饮，无有后艰。', '诗经');
INSERT INTO `poem` VALUES (1382, '大雅·假乐', '先秦', '佚名', '假乐君子，显显令德，宜民宜人。受禄于天，保右命之，自天申之。 干禄百福，子孙千亿。穆穆皇皇，宜君宜王。不愆不忘，率由旧章。 威仪抑抑，德音秩秩。无怨无恶，率由群匹。受福无疆，四方之纲。 之纲之纪，燕及朋友。百辟卿士，媚于天子。不解于位，民之攸塈。', '诗经');
INSERT INTO `poem` VALUES (1383, '大雅·公刘', '先秦', '佚名', '笃公刘，匪居匪康。乃埸乃疆，乃积乃仓；乃裹餱粮，于橐于囊。思辑用光，弓矢斯张；干戈戚扬，爰方启行。 笃公刘，于胥斯原。既庶既繁，既顺乃宣，而无永叹。陟则在巘，复降在原。何以舟之？维玉及瑶，鞞琫容刀。 笃公刘，逝彼百泉。瞻彼溥原，乃陟南冈。乃觏于京，京师之野。于时处处，于时庐旅，于时言言，于时语语。 笃公刘，于京斯依。跄跄济济，俾筵俾几。既登乃依，乃造其曹。执豕于牢，酌之用匏。食之饮之，君之宗之。 笃公刘，既溥既长。既景乃冈，相其阴阳，观其流泉。其军三单，度其隰原。彻田为粮，度其夕阳。豳居允荒。 笃公刘，于豳斯馆。涉渭为乱，取厉取锻，止基乃理。爰众爰有，夹其皇涧。溯其过涧。止旅乃密，芮鞫之即。', '诗经');
INSERT INTO `poem` VALUES (1384, '泂酌', '先秦', '佚名', '泂酌彼行潦，挹彼注兹，可以餴饎。岂弟君子，民之父母。 泂酌彼行潦，挹彼注兹，可以濯罍。岂弟君子，民之攸归。 泂酌彼行潦，挹彼注兹，可以濯溉。岂弟君子，民之攸塈。', '诗经');
INSERT INTO `poem` VALUES (1385, '卷阿', '先秦', '佚名', '有卷者阿，飘风自南。岂弟君子，来游来歌，以矢其音。 伴奂尔游矣，优游尔休矣。岂弟君子，俾尔弥尔性，似先公酋矣。 尔土宇昄章，亦孔之厚矣。岂弟君子，俾尔弥尔性，百神尔主矣。 尔受命长矣，茀禄尔康矣。岂弟君子，俾尔弥尔性，纯嘏尔常矣。 有冯有翼，有孝有德，以引以翼。岂弟君子，四方为则。 颙颙卬卬，如圭如璋，令闻令望。岂弟君子，四方为纲。 凤凰于飞，翙翙其羽，亦集爰止。蔼蔼王多吉士，维君子使，媚于天子。 凤凰于飞，翙翙其羽，亦傅于天。蔼蔼王多吉人，维君子命，媚于庶人。 凤凰鸣矣，于彼高冈。梧桐生矣，于彼朝阳。菶菶萋萋，雍雍喈喈。 君子之车，既庶且多。君子之马，既闲且驰。矢诗不多，维以遂歌。', '诗经');
INSERT INTO `poem` VALUES (1386, '大雅·民劳', '先秦', '佚名', '民亦劳止，汔可小康。惠此中国，以绥四方。无纵诡随，以谨无良。式遏寇虐，憯不畏明。柔远能迩，以定我王。 民亦劳止，汔可小休。惠此中国，以为民逑。无纵诡随，以谨惛怓。式遏寇虐，无俾民忧。无弃尔劳，以为王休。 民亦劳止，汔可小息。惠此京师，以绥四国。无纵诡随，以谨罔极。式遏寇虐，无俾作慝。敬慎威仪，以近有德。 民亦劳止，汔可小愒。惠此中国，俾民忧泄。无纵诡随，以谨丑厉。式遏寇虐，无俾正败。戎虽小子，而式弘大。 民亦劳止，汔可小安。惠此中国，国无有残。无纵诡随，以谨缱绻。式遏寇虐，无俾正反。王欲玉女，是用大谏。', '诗经');
INSERT INTO `poem` VALUES (1387, '大雅·板', '先秦', '佚名', '上帝板板，下民卒瘅。出话不然，为犹不远。靡圣管管。不实于亶。犹之未远，是用大谏。 天之方难，无然宪宪。天之方蹶，无然泄泄。辞之辑矣，民之洽矣。辞之怿矣，民之莫矣。 我虽异事，及尔同僚。我即尔谋，听我嚣嚣。我言维服，勿以为笑。先民有言，询于刍荛。 天之方虐，无然谑谑。老夫灌灌，小子蹻蹻。匪我言耄，尔用忧谑。多将熇熇，不可救药。 天之方懠。无为夸毗。威仪卒迷，善人载尸。民之方殿屎，则莫我敢葵？丧乱蔑资，曾莫惠我师？ 天之牖民，如埙如篪，如璋如圭，如取如携。携无曰益，牖民孔易。民之多辟，无自立辟。 价人维藩，大师维垣，大邦维屏，大宗维翰，怀德维宁，宗子维城。无俾城坏，无独斯畏。 敬天之怒，无敢戏豫。敬天之渝，无敢驰驱。昊天曰明，及尔出王。昊天曰旦，及尔游衍。', '诗经');
INSERT INTO `poem` VALUES (1388, '荡', '先秦', '佚名', '荡荡上帝，下民之辟。疾威上帝，其命多辟。天生烝民，其命匪谌。靡不有初，鲜克有终。 文王曰咨，咨汝殷商。曾是彊御？曾是掊克？曾是在位？曾是在服？天降滔德，女兴是力。 文王曰咨，咨女殷商。而秉义类，彊御多怼。流言以对。寇攘式内。侯作侯祝，靡届靡究。 文王曰咨，咨女殷商。女炰烋于中国。敛怨以为德。不明尔德，时无背无侧。尔德不明，以无陪无卿。 文王曰咨，咨女殷商。天不湎尔以酒，不义从式。既愆尔止。靡明靡晦。式号式呼。俾昼作夜。 文王曰咨，咨女殷商。如蜩如螗，如沸如羹。小大近丧，人尚乎由行。内奰于中国，覃及鬼方。 文王曰咨，咨女殷商。匪上帝不时，殷不用旧。虽无老成人，尚有典刑。曾是莫听，大命以倾。 文王曰咨，咨女殷商。人亦有言：颠沛之揭，枝叶未有害，本实先拨。殷鉴不远，在夏后之世。', '诗经');
INSERT INTO `poem` VALUES (1389, '大雅·抑', '先秦', '佚名', '抑抑威仪，维德之隅。人亦有言：靡哲不愚，庶人之愚，亦职维疾。哲人之愚，亦维斯戾。 无竞维人，四方其训之。有觉德行，四国顺之。訏谟定命，远犹辰告。敬慎威仪，维民之则。 其在于今，兴迷乱于政。颠覆厥德，荒湛于酒。女虽湛乐从，弗念厥绍。罔敷求先王，克共明刑。 肆皇天弗尚，如彼泉流，无沦胥以亡。夙兴夜寐，洒扫庭内，维民之章。修尔车马，弓矢戎兵，用戒戎作，用逷蛮方。 质尔人民，谨尔侯度，用戒不虞。慎尔出话，敬尔威仪，无不柔嘉。白圭之玷，尚可磨也；斯言之玷，不可为也！ 无易由言，无曰苟矣，莫扪朕舌，言不可逝矣。无言不雠，无德不报。惠于朋友，庶民小子。子孙绳绳，万民靡不承。 视尔友君子，辑柔尔颜，不遐有愆。相在尔室，尚不愧于屋漏。无曰不显，莫予云觏。神之格思，不可度思，矧可射思！ 辟尔为德，俾臧俾嘉。淑慎尔止，不愆于仪。不僭不贼，鲜不为则。投我以桃，报之以李。彼童而角，实虹小子。 荏染柔木，言缗之丝。温温恭人，维德之基。其维哲人，告之话言，顺德之行。其维愚人，覆谓我僭。民各有心。 於乎小子，未知臧否。匪手携之，言示之事。匪面命之，言提其耳。借曰未知，亦既抱子。民之靡盈，谁夙知而莫成？ 昊天孔昭，我生靡乐。视尔梦梦，我心惨惨。诲尔谆谆，听我藐藐。匪用为教，覆用为虐。借曰未知，亦聿既耄。 于乎，小子，告尔旧止。听用我谋，庶无大悔。天方艰难，曰丧厥国。取譬不远，昊天不忒。回遹其德，俾民大棘。', '诗经');
INSERT INTO `poem` VALUES (1390, '桑柔', '先秦', '佚名', '菀彼桑柔，其下侯旬，捋采其刘，瘼此下民。不殄心忧，仓兄填兮。倬彼昊天，宁不我矜？ 四牡骙骙，旟旐有翩。乱生不夷，靡国不泯。民靡有黎，具祸以烬。於乎有哀，国步斯频。 国步蔑资，天不我将。靡所止疑，云徂何往？君子实维，秉心无竞。谁生厉阶，至今为梗？ 忧心慇慇，念我土宇。我生不辰，逢天僤怒。自西徂东，靡所定处。多我觏痻，孔棘我圉。 为谋为毖，乱况斯削。告尔忧恤，诲尔序爵。谁能执热，逝不以濯？其何能淑，载胥及溺。 如彼遡风，亦孔之僾。民有肃心，荓云不逮。好是稼穑，力民代食。稼穑维宝，代食维好？ 天降丧乱，灭我立王。降此蟊贼，稼穑卒痒。哀恫中国，具赘卒荒。靡有旅力，以念穹苍。 维此惠君，民人所瞻。秉心宣犹，考慎其相。维彼不顺，自独俾臧。自有肺肠，俾民卒狂。 瞻彼中林，甡甡其鹿。朋友已谮，不胥以谷。人亦有言：进退维谷。 维此圣人，瞻言百里。维彼愚人，覆狂以喜。匪言不能，胡斯畏忌？ 维此良人，弗求弗迪。维彼忍心，是顾是复。民之贪乱，宁为荼毒。 大风有隧，有空大谷。维此良人，作为式谷。维彼不顺，征以中垢。 大风有隧，贪人败类。听言则对，诵言如醉。匪用其良，复俾我悖。 嗟尔朋友，予岂不知而作。如彼飞虫，时亦弋获。既之阴女，反予来赫。 民之罔极，职凉善背。为民不利，如云不克。民之回遹，职竞用力。 民之未戾，职盗为寇。凉曰不可，覆背善詈。虽曰匪予，既作尔歌！', '诗经');
INSERT INTO `poem` VALUES (1391, '云汉', '先秦', '佚名', '倬彼云汉，昭回于天。王曰：於乎！何辜今之人？天降丧乱，饥馑荐臻。靡神不举，靡爱斯牲。圭壁既卒，宁莫我听？ 旱既大甚，蕴隆虫虫。不殄禋祀，自郊徂宫。上下奠瘗，靡神不宗。后稷不克，上帝不临。耗斁下土，宁丁我梗。 旱既大甚，则不可推。兢兢业业，如霆如雷。周余黎民，靡有孑遗。昊天上帝，则不我遗。胡不相畏？先祖于摧。 旱既大甚，则不可沮。赫赫炎炎，云我无所。大命近止，靡瞻靡顾。群公先正，则不我助。父母先祖，胡宁忍予？ 旱既大甚，涤涤山川。旱魃为虐，如惔如焚。我心惮暑，忧心如熏。群公先正，则不我闻。昊天上帝，宁俾我遯？ 旱既大甚，黾勉畏去。胡宁瘨我以旱？憯不知其故。祈年孔夙，方社不莫。昊天上帝，则不我虞。敬恭明神，宜无悔怒。 旱既大甚，散无友纪。鞫哉庶正，疚哉冢宰。趣马师氏，膳夫左右。靡人不周。无不能止，瞻昂昊天，云如何里！ 瞻昂昊天，有嘒其星。大夫君子，昭假无赢。大命近止，无弃尔成。何求为我。以戾庶正。瞻昂昊天，曷惠其宁？', '诗经');
INSERT INTO `poem` VALUES (1392, '诗经·大雅', '先秦', '佚名', '崧高维岳，骏极于天。维岳降神，生甫及申。维申及甫，维周之翰。四国于蕃。四方于宣。 亹亹申伯，王缵之事。于邑于谢，南国是式。王命召伯，定申伯之宅。登是南邦，世执其功。 王命申伯，式是南邦。因是谢人，以作尔庸。王命召伯，彻申伯土田。王命傅御，迁其私人。 申伯之功，召伯是营。有俶其城，寝庙既成。既成藐藐，王锡申伯。四牡蹻蹻，钩膺濯濯。 王遣申伯，路车乘马。我图尔居，莫如南土。锡尔介圭，以作尔宝。往近王舅，南土是保。 申伯信迈，王饯于郿。申伯还南，谢于诚归。王命召伯，彻申伯土疆。以峙其粻，式遄其行。 申伯番番，既入于谢。徒御啴啴。周邦咸喜，戎有良翰。不显申伯，王之元舅，文武是宪。 申伯之德，柔惠且直。揉此万邦，闻于四国。吉甫作诵，其诗孔硕。其风肆好，以赠申伯。', '诗经');
INSERT INTO `poem` VALUES (1393, '烝民', '先秦', '佚名', '天生烝民，有物有则。民之秉彝，好是懿德。天监有周，昭假于下。保兹天子，生仲山甫。 仲山甫之德，柔嘉维则。令仪令色，小心翼翼。古训是式，威仪是力。天子是若，明命使赋。 王命仲山甫，式是百辟，缵戎祖考，王躬是保。出纳王命，王之喉舌。赋政于外，四方爰发。 肃肃王命，仲山甫将之。邦国若否，仲山甫明之。既明且哲，以保其身。夙夜匪解，以事一人。 人亦有言，柔则茹之，刚则吐之。维仲山甫，柔亦不茹，刚亦不吐。不侮矜寡，不畏强御。 人亦有言，德輶如毛，民鲜克举之。我仪图之，维仲山甫举之。爱莫助之。衮职有阙，维仲山甫补之。 仲山甫出祖。四牡业业。征夫捷捷，每怀靡及。四牡彭彭，八鸾锵锵。王命仲山甫，城彼东方。 四牡骙骙，八鸾喈喈。仲山甫徂齐，式遄其归。吉甫作诵，穆如清风。仲山甫永怀，以慰其心。', '诗经');
INSERT INTO `poem` VALUES (1394, '韩奕', '先秦', '佚名', '奕奕梁山，维禹甸之，有倬其道。韩侯受命，王亲命之：缵戎祖考，无废朕命。夙夜匪解，虔共尔位，朕命不易。榦不庭方，以佐戎辟。 四牡奕奕，孔脩且张。韩侯入觐，以其介圭，入觐于王。王锡韩侯，淑旂绥章，簟茀错衡，玄衮赤舄，钩膺镂鍚，鞹鞃浅幭，鞗革金厄。 韩侯出祖，出宿于屠。显父饯之，清酒百壶。其肴维何？炰鳖鲜鱼。其蔌维何？维笋及蒲。其赠维何？乘马路车。笾豆有且。侯氏燕胥。 韩侯取妻，汾王之甥，蹶父之子。韩侯迎止，于蹶之里。百两彭彭，八鸾锵锵，不显其光。诸娣从之，祁祁如云。韩侯顾之，烂其盈门。 蹶父孔武，靡国不到。为韩姞相攸，莫如韩乐。孔乐韩土，川泽訏訏，鲂鱮甫甫，麀鹿噳噳，有熊有罴，有猫有虎。庆既令居，韩姞燕誉。 溥彼韩城，燕师所完。以先祖受命，因时百蛮。王锡韩侯，其追其貊。奄受北国，因以其伯。实墉实壑，实亩实藉。献其貔皮，赤豹黄罴。', '诗经');
INSERT INTO `poem` VALUES (1395, '大雅·江汉', '先秦', '佚名', '江汉浮浮，武夫滔滔。匪安匪游，淮夷来求。既出我车，既设我旟。匪安匪舒，淮夷来铺。 江汉汤汤，武夫洸洸。经营四方，告成于王。四方既平，王国庶定。时靡有争，王心载宁。 江汉之浒，王命召虎：式辟四方，彻我疆土。匪疚匪棘，王国来极。于疆于理，至于南海。 王命召虎：来旬来宣。文武受命，召公维翰。无曰予小子，召公是似。肇敏戎公，用锡尔祉。 釐尔圭瓒，秬鬯一卣。告于文人，锡山土田。于周受命，自召祖命，虎拜稽首：天子万年！ 虎拜稽首，对扬王休。作召公考：天子万寿！明明天子，令闻不已，矢其文德，洽此四国。', '诗经');
INSERT INTO `poem` VALUES (1396, '大雅·常武', '先秦', '佚名', '赫赫明明。王命卿士，南仲大祖，大师皇父。整我六师，以脩我戎。既敬既戒，惠此南国。 王谓尹氏，命程伯休父，左右陈行。戒我师旅，率彼淮浦，省此徐土。不留不处，三事就绪。 赫赫业业，有严天子。王舒保作，匪绍匪游。徐方绎骚，震惊徐方。如雷如霆，徐方震惊。 王奋厥武，如震如怒。进厥虎臣，阚如虓虎。铺敦淮濆，仍执丑虏。截彼淮浦，王师之所。 王旅啴啴，如飞如翰。如江如汉，如山之苞。如川之流，绵绵翼翼。不测不克，濯征徐国。 王犹允塞，徐方既来。徐方既同，天子之功。四方既平，徐方来庭。徐方不回，王曰还归。', '诗经');
INSERT INTO `poem` VALUES (1397, '大雅·瞻卬', '先秦', '佚名', '瞻卬昊天，则不我惠？孔填不宁，降此大厉。邦靡有定，士民其瘵。蟊贼蟊疾，靡有夷届。罪罟不收，靡有夷瘳！ 人有土田，女反有之。人有民人，女覆夺之。此宜无罪，女反收之。彼宜有罪，女覆说之。 哲夫成城，哲妇倾城。懿厥哲妇，为枭为鸱。妇有长舌，维厉之阶！乱匪降自天，生自妇人。匪教匪诲，时维妇寺。 鞫人忮忒，谮始竟背。岂曰不极，伊胡为慝？如贾三倍，君子是识。妇无公事，休其蚕织。 天何以刺？何神不富？舍尔介狄，维予胥忌。不吊不祥，威仪不类。人之云亡，邦国殄瘁！ 天之降罔，维其优矣。人之云亡，心之忧矣。天之降罔，维其几矣。人之云亡，心之悲矣！ 觱沸槛泉，维其深矣。心之忧矣，宁自今矣？不自我先，不自我后。藐藐昊天，无不可巩。无忝皇祖，式救尔后。', '诗经');
INSERT INTO `poem` VALUES (1398, '大雅·召旻', '先秦', '佚名', '旻天疾威，天笃降丧。瘨我饥馑，民卒流亡。我居圉卒荒。 天降罪罟，蟊贼内讧。昏椓靡共，溃溃回遹，实靖夷我邦。 皋皋訿訿，曾不知其玷。兢兢业业，孔填不宁，我位孔贬。 如彼岁旱，草不溃茂，如彼栖苴。我相此邦，无不溃止。 维昔之富不如时，维今之疚不如兹。彼疏斯粺，胡不自替？职兄斯引。 池之竭矣，不云自频。泉之竭矣，不云自中。溥斯害矣，职兄斯弘，不烖我躬。 昔先王受命，有如召公，日辟国百里，今也日蹙国百里。於乎哀哉！维今之人，不尚有旧！', '诗经');
INSERT INTO `poem` VALUES (1399, '周颂·清庙', '先秦', '佚名', '於穆清庙，肃雍显相。 济济多士，秉文之德。 对越在天，骏奔走在庙。 不显不承，无射于人斯！', '诗经');
INSERT INTO `poem` VALUES (1400, '周颂·维天之命', '先秦', '佚名', '维天之命，于穆不已。 于乎不显，文王之德之纯。 假以溢我，我其收之。 骏惠我文王，曾孙笃之。', '诗经');
INSERT INTO `poem` VALUES (1401, '周颂·维清', '先秦', '佚名', '维清缉熙，文王之典。肇禋，迄用有成，维周之祯。', '诗经');
INSERT INTO `poem` VALUES (1402, '周颂·烈文', '先秦', '佚名', '烈文辟公，锡兹祉福。惠我无疆，子孙保之。无封靡于尔邦，维王其崇之。 念兹戎功，继序其皇之。无竞维人，四方其训之。不显维德，百辟其刑之。於乎，前王不忘！', '诗经');
INSERT INTO `poem` VALUES (1403, '周颂·天作', '先秦', '佚名', '天作高山，大王荒之。彼作矣，文王康之。彼徂矣，岐有夷之行。子孙保之。', '诗经');
INSERT INTO `poem` VALUES (1404, '周颂·昊天有成命', '先秦', '佚名', '昊天有成命，二后受之。成王不敢康，夙夜基命宥密。於缉熙！单厥心，肆其靖之。', '诗经');
INSERT INTO `poem` VALUES (1405, '周颂·我将', '先秦', '佚名', '我将我享，维羊维牛，维天其右之。仪式刑文王之典，日靖四方。伊嘏文王，既右飨之。我其夙夜，畏天之威，于时保之。', '诗经');
INSERT INTO `poem` VALUES (1406, '周颂·时迈', '先秦', '佚名', '时迈其邦，昊天其子之，实右序有周。薄言震之，莫不震叠。怀柔百神，及河乔岳，允王维后。 明昭有周，式序在位。载戢干戈，载橐弓矢。我求懿德，肆于时夏，允王保之。', '诗经');
INSERT INTO `poem` VALUES (1407, '周颂·执竞', '先秦', '佚名', '执竞武王，无竞维烈。不显成康，上帝是皇。自彼成康，奄有四方，斤斤其明。 钟鼓喤喤，磬莞将将，降福穰穰。降福简简，威仪反反。既醉既饱，福禄来反。', '诗经');
INSERT INTO `poem` VALUES (1408, '周颂·思文', '先秦', '佚名', '思文后稷，克配彼天。立我烝民，莫菲尔极。贻我来牟，帝命率育，无此疆尔界。陈常于时夏。', '诗经');
INSERT INTO `poem` VALUES (1409, '周颂·臣工', '先秦', '佚名', '嗟嗟臣工，敬尔在公。王厘尔成，来咨来茹。嗟嗟保介，维莫之春，亦又何求？如何新畲？於皇来牟，将受厥明。明昭上帝，迄用康年。命我众人：庤乃钱镈，奄观铚艾。', '诗经');
INSERT INTO `poem` VALUES (1410, '周颂·噫嘻', '先秦', '佚名', '噫嘻成王，既昭假尔。率时农夫，播厥百谷。骏发尔私，终三十里。亦服尔耕，十千维耦。', '诗经');
INSERT INTO `poem` VALUES (1411, '周颂·振鹭', '先秦', '佚名', '振鹭于飞，于彼西雍。我客戾止，亦有斯容。在彼无恶，在此无斁。庶几夙夜，以永终誉。', '诗经');
INSERT INTO `poem` VALUES (1412, '周颂·丰年', '先秦', '佚名', '丰年多黍多稌，亦有高廪，万亿及秭。为酒为醴，烝畀祖妣。以洽百礼，降福孔皆。', '诗经');
INSERT INTO `poem` VALUES (1413, '周颂·有瞽', '先秦', '佚名', '有瞽有瞽，在周之庭。设业设虡，崇牙树羽。应田县鼓，鞉磬柷圉。既备乃奏，箫管备举。喤喤厥声，肃雍和鸣，先祖是听。我客戾止，永观厥成。', '诗经');
INSERT INTO `poem` VALUES (1414, '周颂·潜', '先秦', '佚名', '猗与漆沮，潜有多鱼。有鳣有鲔，鲦鲿鰋鲤。以享以祀，以介景福。', '诗经');
INSERT INTO `poem` VALUES (1415, '周颂·雝', '先秦', '佚名', '有来雝雝，至止肃肃。相维辟公，天子穆穆。於荐广牡，相予肆祀。假哉皇考！绥予孝子。 宣哲维人，文武维后。燕及皇天，克昌厥后。绥我眉寿，介以繁祉，既右烈考，亦右文母。', '诗经');
INSERT INTO `poem` VALUES (1416, '周颂·载见', '先秦', '佚名', '载见辟王，曰求厥章。龙旂阳阳，和铃央央。鞗革有鸧，休有烈光。率见昭考，以孝以享。以介眉寿，永言保之，思皇多祜。烈文辟公，绥以多福，俾缉熙于纯嘏。', '诗经');
INSERT INTO `poem` VALUES (1417, '周颂·有客', '先秦', '佚名', '有客有客，亦白其马。有萋有且，敦琢其旅。有客宿宿，有客信信。 言授之絷，以絷其马。薄言追之，左右绥之。既有淫威，降福孔夷。', '诗经');
INSERT INTO `poem` VALUES (1418, '周颂·武', '先秦', '佚名', '於皇武王！无竞维烈。允文文王，克开厥后。嗣武受之，胜殷遏刘，耆定尔功。', '诗经');
INSERT INTO `poem` VALUES (1419, '周颂·闵予小子', '先秦', '佚名', '闵予小子，遭家不造，嬛嬛在疚。於乎皇考，永世克孝。念兹皇祖，陟降庭止。维予小子，夙夜敬止。于乎皇王，继序思不忘。', '诗经');
INSERT INTO `poem` VALUES (1420, '周颂·访落', '先秦', '佚名', '访予落止，率时昭考。於乎悠哉，朕未有艾。将予就之，继犹判涣。维予小子，未堪家多难。绍庭上下，陟降厥家。休矣皇考，以保明其身。', '诗经');
INSERT INTO `poem` VALUES (1421, '周颂·敬之', '先秦', '佚名', '敬之敬之，天维显思，命不易哉。无曰高高在上，陟降厥士，日监在兹。维予小子，不聪敬止。日就月将，学有缉熙于光明。佛时仔肩，示我显德行。', '诗经');
INSERT INTO `poem` VALUES (1422, '周颂·小毖', '先秦', '佚名', '予其惩，而毖后患。莫予荓蜂，自求辛螫。肇允彼桃虫，拚飞维鸟。未堪家多难，予又集于蓼。', '诗经');
INSERT INTO `poem` VALUES (1423, '周颂·载芟', '先秦', '佚名', '载芟载柞，其耕泽泽。千耦其耘，徂隰徂畛。侯主侯伯，侯亚侯旅，侯彊侯以。有嗿其馌，思媚其妇，有依其士。有略其耜，俶载南亩，播厥百谷。实函斯活，驿驿其达。有厌其杰，厌厌其苗，绵绵其麃。载获济济，有实其积，万亿及秭。为酒为醴，烝畀祖妣，以洽百礼。有飶其香。邦家之光。有椒其馨，胡考之宁。匪且有且，匪今斯今，振古如兹。', '诗经');
INSERT INTO `poem` VALUES (1424, '周颂·良耜', '先秦', '佚名', '畟畟良耜，俶载南亩。播厥百谷，实函斯活。或来瞻女，载筐及莒，其饟伊黍。其笠伊纠，其镈斯赵，以薅荼蓼。荼蓼朽止，黍稷茂止。获之挃挃，积之栗栗。其崇如墉，其比如栉。以开百室，百室盈止，妇子宁止。杀时犉牡，有捄其角。以似以续，续古之人。', '诗经');
INSERT INTO `poem` VALUES (1425, '周颂·丝衣', '先秦', '佚名', '丝衣其紑，载弁俅俅。自堂徂基，自羊徂牛，鼐鼎及鼒，兕觥其觩。旨酒思柔。不吴不敖，胡考之休。', '诗经');
INSERT INTO `poem` VALUES (1426, '周颂·酌', '先秦', '佚名', '於铄王师，遵养时晦。 时纯熙矣，是用大介。 我龙受之，蹻蹻王之造。 载用有嗣，实维尔公允师。', '诗经');
INSERT INTO `poem` VALUES (1427, '周颂·桓', '先秦', '佚名', '绥万邦，屡丰年。天命匪解，桓桓武王。保有厥士，于以四方，克定厥家。于昭于天，皇以间之。', '诗经');
INSERT INTO `poem` VALUES (1428, '周颂·赉', '先秦', '佚名', '文王既勤止，我应受之。敷时绎思，我徂维求定。时周之命，於绎思。', '诗经');
INSERT INTO `poem` VALUES (1429, '周颂·般', '先秦', '佚名', '於皇时周！陟其高山，嶞山乔岳，允犹翕河。敷天之下，裒时之对。时周之命。', '诗经');
INSERT INTO `poem` VALUES (1430, '鲁颂·駉', '先秦', '佚名', '駉駉牡马，在坰之野。薄言駉者，有驈有皇，有骊有黄，以车彭彭。思无疆思，马斯臧。 駉駉牡马，在坰之野。薄言駉者，有骓有駓，有骍有骐，以车伾伾。思无期思，马斯才。 駉駉牡马，在坰之野。溥言駉者，有驒有骆，有骝有雒，以车绎绎。思无斁思，马斯作。 駉駉牡马，在坰之野。薄言駉者，有骃有騢，有驔有鱼，以车祛祛。思无邪思，马斯徂。', '诗经');
INSERT INTO `poem` VALUES (1431, '鲁颂·有駜', '先秦', '佚名', '有駜有駜，駜彼乘黄。夙夜在公，在公明明。振振鹭，鹭于下。鼓咽咽，醉言舞。于胥乐兮！ 有駜有駜，駜彼乘牡。夙夜在公，在公饮酒。振振鹭，鹭于飞。鼓咽咽，醉言归。于胥乐兮！ 有駜有駜，駜彼乘駽。夙夜在公，在公载燕。自今以始，岁其有。君子有穀，诒孙子。于胥乐兮！', '诗经');
INSERT INTO `poem` VALUES (1432, '鲁颂·泮水', '先秦', '佚名', '思乐泮水，薄采其芹。鲁侯戾止，言观其旂。其旂茷茷，鸾声哕哕。无小无大，从公于迈。 思乐泮水，薄采其藻。鲁侯戾止，其马蹻蹻。其马蹻蹻，其音昭昭。载色载笑，匪怒伊教。 思乐泮水，薄采其茆。鲁侯戾止，在泮饮酒。既饮旨酒，永锡难老。顺彼长道，屈此群丑。 穆穆鲁侯，敬明其德。敬慎威仪，维民之则。允文允武，昭假烈祖。靡有不孝，自求伊祜。 明明鲁侯，克明其德。既作泮宫，淮夷攸服。矫矫虎臣，在泮献馘。淑问如皋陶，在泮献囚。 济济多士，克广德心。桓桓于征，狄彼东南。烝烝皇皇，不吴不扬。不告于訩，在泮献功。 角弓其觩。束矢其搜。戎车孔博。徒御无斁。既克淮夷，孔淑不逆。式固尔犹，淮夷卒获。 翩彼飞鸮，集于泮林。食我桑葚，怀我好音。憬彼淮夷，来献其琛。元龟象齿，大赂南金。', '诗经');
INSERT INTO `poem` VALUES (1433, '鲁颂·閟宫', '先秦', '佚名', '閟宫有侐，实实枚枚。赫赫姜嫄，其德不回。上帝是依，无灾无害。弥月不迟，是生后稷。降之百福。黍稷重穋，稙稚菽麦。奄有下国，俾民稼穑。有稷有黍，有稻有秬。奄有下土，缵禹之绪。后稷之孙，实维大王。居岐之阳，实始剪商。至于文武，缵大王之绪，致天之届，于牧之野。无贰无虞，上帝临女。敦商之旅，克咸厥功。王曰叔父，建尔元子，俾侯于鲁。大启尔宇，为周室辅。 乃命鲁公，俾侯于东。锡之山川，土田附庸。周公之孙，庄公之子。龙旂承祀。六辔耳耳。春秋匪解，享祀不忒。皇皇后帝！皇祖后稷！享以骍牺，是飨是宜。降福既多，周公皇祖，亦其福女。 秋而载尝，夏而楅衡，白牡骍刚。牺尊将将，毛炰胾羹。笾豆大房，万舞洋洋。孝孙有庆。俾尔炽而昌，俾尔寿而臧。保彼东方，鲁邦是尝。不亏不崩，不震不腾。三寿作朋，如冈如陵。 公车千乘，朱英绿縢。二矛重弓。公徒三万，贝胄朱綅。烝徒增增，戎狄是膺，荆舒是惩，则莫我敢承！俾尔昌而炽，俾尔寿而富。黄发台背，寿胥与试。俾尔昌而大，俾尔耆而艾。万有千岁，眉寿无有害。 泰山岩岩，鲁邦所詹。奄有龟蒙，遂荒大东。至于海邦，淮夷来同。莫不率从，鲁侯之功。 保有凫绎，遂荒徐宅。至于海邦，淮夷蛮貊。及彼南夷，莫不率从。莫敢不诺，鲁侯是若。 天锡公纯嘏，眉寿保鲁。居常与许，复周公之宇。鲁侯燕喜，令妻寿母。宜大夫庶士，邦国是有。既多受祉，黄发儿齿。 徂徠之松，新甫之柏。是断是度，是寻是尺。松桷有舄，路寝孔硕，新庙奕奕。奚斯所作，孔曼且硕，万民是若。', '诗经');
INSERT INTO `poem` VALUES (1434, '商颂·那', '先秦', '佚名', '猗与那与！置我鞉鼓。 奏鼓简简，衎我烈祖。 汤孙奏假，绥我思成。 鞉鼓渊渊，嘒嘒管声。 既和且平，依我磬声。 於赫汤孙！穆穆厥声。 庸鼓有斁，万舞有奕。 我有嘉客，亦不夷怿。 自古在昔，先民有作。 温恭朝夕，执事有恪， 顾予烝尝，汤孙之将。', '诗经');
INSERT INTO `poem` VALUES (1435, '商颂·烈祖', '先秦', '佚名', '嗟嗟烈祖！有秩斯祜。 申锡无疆，及尔斯所。 既载清酤，赉我思成。 亦有和羹，既戒既平。 鬷假无言，时靡有争。 绥我眉寿，黄耇无疆。 约軧错衡，八鸾鸧鸧。 以假以享，我受命溥将。 自天降康，丰年穰穰。 来假来飨，降福无疆。 顾予烝尝，汤孙之将。', '诗经');
INSERT INTO `poem` VALUES (1436, '商颂·玄鸟', '先秦', '佚名', '天命玄鸟，降而生商，宅殷土芒芒。古帝命武汤，正域彼四方。 方命厥后，奄有九有。商之先后，受命不殆，在武丁孙子。武丁孙子，武王靡不胜。 龙旂十乘，大糦是承。邦畿千里，维民所止，肇域彼四海。 四海来假，来假祁祁。景员维河。殷受命咸宜，百禄是何。', '诗经');
INSERT INTO `poem` VALUES (1437, '商颂·长发', '先秦', '佚名', '濬哲维商，长发其祥。洪水芒芒，禹敷下土方。外大国是疆，幅陨既长。有娀方将，帝立子生商。 玄王桓拨，受小国是达，受大国是达。率履不越，遂视既发。相土烈烈。海外有截。 帝命不违，至于汤齐。汤降不迟，圣敬日跻。昭假迟迟，上帝是祗，帝命式于九围。 受小球大球，为下国缀旒，何天之休。不竞不絿，不刚不柔。敷政优优，百禄是遒。 受小共大共，为下国骏厖。何天之龙，敷奏其勇。不震不动，不戁不竦，百禄是总。 武王载旆，有虔秉钺。如火烈烈，则莫我敢曷。苞有三蘖，莫遂莫达。九有有截，韦顾既伐，昆吾夏桀。 昔在中叶，有震且业。允也天子，降予卿士。实维阿衡，实左右商王。', '诗经');
INSERT INTO `poem` VALUES (1438, '商颂·殷武', '先秦', '佚名', '挞彼殷武，奋伐荆楚。深入其阻，裒荆之旅。有截其所，汤孙之绪。 维女荆楚，居国南乡。昔有成汤，自彼氐羌，莫敢不来享，莫敢不来王。曰商是常。 天命多辟，设都于禹之绩。岁事来辟，勿予祸适，稼穑匪解。 天命降监，下民有严。不僭不滥，不敢怠遑。命于下国，封建厥福。 商邑翼翼，四方之极。赫赫厥声，濯濯厥灵。寿考且宁，以保我后生。 陟彼景山，松伯丸丸。是断是迁，方斫是虔。松桷有梴，旅楹有闲，寝成孔安。', '诗经');
INSERT INTO `poem` VALUES (1439, '离骚', '先秦', '屈原', '帝高阳之苗裔兮，朕皇考曰伯庸。 摄提贞于孟陬兮，惟庚寅吾以降。 皇览揆余初度兮，肇锡余以嘉名： 名余曰正则兮，字余曰灵均。 纷吾既有此内美兮，又重之以修能。 扈江离与辟芷兮，纫秋兰以为佩。 汩余若将不及兮，恐年岁之不吾与。 朝搴阰之木兰兮，夕揽洲之宿莽。 日月忽其不淹兮，春与秋其代序。 惟草木之零落兮，恐美人之迟暮。(惟 通：唯) 不抚壮而弃秽兮，何不改乎此度？ 乘骐骥以驰骋兮，来吾道夫先路！ 昔三后之纯粹兮，固众芳之所在。 杂申椒与菌桂兮，岂惟纫夫蕙茝！ 彼尧、舜之耿介兮，既遵道而得路。 何桀纣之猖披兮，夫惟捷径以窘步。 惟夫党人之偷乐兮，路幽昧以险隘。 岂余身之殚殃兮，恐皇舆之败绩！ 忽奔走以先后兮，及前王之踵武。 荃不查余之中情兮，反信谗而齌怒。 余固知謇謇之为患兮，忍而不能舍也。 指九天以为正兮，夫惟灵修之故也。 曰黄昏以为期兮，羌中道而改路！ 初既与余成言兮，后悔遁而有他。 余既不难夫离别兮，伤灵修之数化。 余既滋兰之九畹兮，又树蕙之百亩。 畦留夷与揭车兮，杂杜衡与芳芷。 冀枝叶之峻茂兮，愿俟时乎吾将刈。 虽萎绝其亦何伤兮，哀众芳之芜秽。 众皆竞进以贪婪兮，凭不厌乎求索。 羌内恕己以量人兮，各兴心而嫉妒。 忽驰骛以追逐兮，非余心之所急。 老冉冉其将至兮，恐修名之不立。 朝饮木兰之坠露兮，夕餐秋菊之落英。 苟余情其信姱以练要兮，长顑颔亦何伤。 掔木根以结茝兮，贯薜荔之落蕊。 矫菌桂以纫蕙兮，索胡绳之纚纚。 謇吾法夫前修兮，非世俗之所服。 虽不周于今之人兮，愿依彭咸之遗则。 长太息以掩涕兮，哀民生之多艰。 余虽好修姱以鞿羁兮，謇朝谇而夕替。 既替余以蕙纕兮，又申之以揽茝。 亦余心之所善兮，虽九死其犹未悔。 怨灵修之浩荡兮，终不察夫民心。 众女嫉余之蛾眉兮，谣诼谓余以善淫。 固时俗之工巧兮，偭规矩而改错。 背绳墨以追曲兮，竞周容以为度。 忳郁邑余侘傺兮，吾独穷困乎此时也。 宁溘死以流亡兮，余不忍为此态也。 鸷鸟之不群兮，自前世而固然。 何方圜之能周兮，夫孰异道而相安？ 屈心而抑志兮，忍尤而攘诟。 伏清白以死直兮，固前圣之所厚。 悔相道之不察兮，延伫乎吾将反。 回朕车以复路兮，及行迷之未远。 步余马于兰皋兮，驰椒丘且焉止息。 进不入以离尤兮，退将复修吾初服。 制芰荷以为衣兮，集芙蓉以为裳。 不吾知其亦已兮，苟余情其信芳。 高余冠之岌岌兮，长余佩之陆离。 芳与泽其杂糅兮，唯昭质其犹未亏。 忽反顾以游目兮，将往观乎四荒。 佩缤纷其繁饰兮，芳菲菲其弥章。 民生各有所乐兮，余独好修以为常。 虽体解吾犹未变兮，岂余心之可惩。 女嬃之婵媛兮，申申其詈予，曰： 「鲧婞直以亡身兮，终然夭乎羽之野。 汝何博謇而好修兮，纷独有此姱节？ 薋菉葹以盈室兮，判独离而不服。」 众不可户说兮，孰云察余之中情？ 世并举而好朋兮，夫何茕独而不予听？ 依前圣以节中兮，喟凭心而历兹。 济沅、湘以南征兮，就重华而敶词： 启《九辩》与《九歌》兮，夏康娱以自纵。 不顾难以图后兮，五子用失乎家衖。 羿淫游以佚畋兮，又好射夫封狐。 固乱流其鲜终兮，浞又贪夫厥家。 浇身被服强圉兮，纵欲而不忍。 日康娱而自忘兮，厥首用夫颠陨。 夏桀之常违兮，乃遂焉而逢殃。 后辛之菹醢兮，殷宗用而不长。 汤、禹俨而祗敬兮，周论道而莫差。 举贤才而授能兮，循绳墨而不颇。 皇天无私阿兮，览民德焉错辅。 夫维圣哲以茂行兮，苟得用此下土。 瞻前而顾后兮，相观民之计极。 夫孰非义而可用兮？孰非善而可服？ 阽余身而危死兮，览余初其犹未悔。 不量凿而正枘兮，固前修以菹醢。 曾歔欷余郁邑兮，哀朕时之不当。 揽茹蕙以掩涕兮，沾余襟之浪浪。 跪敷衽以陈辞兮，耿吾既得此中正。 驷玉虬以桀鹥兮，溘埃风余上征。 朝发轫于苍梧兮，夕余至乎县圃。 欲少留此灵琐兮，日忽忽其将暮。 吾令羲和弭节兮，望崦嵫而勿迫。 路漫漫其修远兮，吾将上下而求索。 饮余马于咸池兮，总余辔乎扶桑。 折若木以拂日兮，聊逍遥以相羊。 前望舒使先驱兮，后飞廉使奔属。 鸾皇为余先戒兮，雷师告余以未具。 吾令凤鸟飞腾兮，继之以日夜。 飘风屯其相离兮，帅云霓而来御。 纷总总其离合兮，斑陆离其上下。 吾令帝阍开关兮，倚阊阖而望予。 时暧暧其将罢兮，结幽兰而延伫。 世溷浊而不分兮，好蔽美而嫉妒。 朝吾将济于白水兮，登阆风而绁马。 忽反顾以流涕兮，哀高丘之无女。 溘吾游此春宫兮，折琼枝以继佩。 及荣华之未落兮，相下女之可诒。 吾令丰隆乘云兮，求宓妃之所在。 解佩纕以结言兮，吾令謇修以为理。 纷总总其离合兮，忽纬繣其难迁。 夕归次于穷石兮，朝濯发乎洧盘。 保厥美以骄傲兮，日康娱以淫游。 虽信美而无礼兮，来违弃而改求。 览相观于四极兮，周流乎天余乃下。 望瑶台之偃蹇兮，见有娀之佚女。 吾令鸩为媒兮，鸩告余以不好。 雄鸠之鸣逝兮，余犹恶其佻巧。 心犹豫而狐疑兮，欲自适而不可。 凤皇既受诒兮，恐高辛之先我。 欲远集而无所止兮，聊浮游以逍遥。 及少康之未家兮，留有虞之二姚。 理弱而媒拙兮，恐导言之不固。 世溷浊而嫉贤兮，好蔽美而称恶。 闺中既以邃远兮，哲王又不寤。 怀朕情而不发兮，余焉能忍而与此终古？ 索琼茅以筳篿兮，命灵氛为余占之。 曰：「两美其必合兮，孰信修而慕之？ 思九州之博大兮，岂惟是其有女？」 曰：「勉远逝而无狐疑兮，孰求美而释女？ 何所独无芳草兮，尔何怀乎故宇？」 世幽昧以昡曜兮，孰云察余之善恶？ 民好恶其不同兮，惟此党人其独异！ 户服艾以盈要兮，谓幽兰其不可佩。 览察草木其犹未得兮，岂珵美之能当？ 苏粪壤以充祎兮，谓申椒其不芳。 欲从灵氛之吉占兮，心犹豫而狐疑。 巫咸将夕降兮，怀椒糈而要之。 百神翳其备降兮，九疑缤其并迎。 皇剡剡其扬灵兮，告余以吉故。 曰：「勉升降以上下兮，求矩矱之所同。 汤、禹俨而求合兮，挚、咎繇而能调。 苟中情其好修兮，又何必用夫行媒？ 说操筑于傅岩兮，武丁用而不疑。 吕望之鼓刀兮，遭周文而得举。 宁戚之讴歌兮，齐桓闻以该辅。 及年岁之未晏兮，时亦犹其未央。 恐鹈鴃之先鸣兮，使夫百草为之不芳。」 何琼佩之偃蹇兮，众薆然而蔽之。 惟此党人之不谅兮，恐嫉妒而折之。 时缤纷其变易兮，又何可以淹留？ 兰芷变而不芳兮，荃蕙化而为茅。 何昔日之芳草兮，今直为此萧艾也？ 岂其有他故兮，莫好修之害也！ 余以兰为可恃兮，羌无实而容长。 委厥美以从俗兮，苟得列乎众芳。 椒专佞以慢慆兮，樧又欲充夫佩帏。 既干进而务入兮，又何芳之能祗？ 固时俗之流从兮，又孰能无变化？ 览椒兰其若兹兮，又况揭车与江离？ 惟兹佩之可贵兮，委厥美而历兹。 芳菲菲而难亏兮，芬至今犹未沬。 和调度以自娱兮，聊浮游而求女。 及余饰之方壮兮，周流观乎上下。 灵氛既告余以吉占兮，历吉日乎吾将行。 折琼枝以为羞兮，精琼爢以为粻。 为余驾飞龙兮，杂瑶象以为车。 何离心之可同兮？吾将远逝以自疏。 邅吾道夫昆仑兮，路修远以周流。 扬云霓之晻蔼兮，鸣玉鸾之啾啾。 朝发轫于天津兮，夕余至乎西极。 凤皇翼其承旗兮，高翱翔之翼翼。 忽吾行此流沙兮，遵赤水而容与。 麾蛟龙使梁津兮，诏西皇使涉予。 路修远以多艰兮，腾众车使径待。 路不周以左转兮，指西海以为期。 屯余车其千乘兮，齐玉轪而并驰。 驾八龙之婉婉兮，载云旗之委蛇。 抑志而弭节兮，神高驰之邈邈。 奏《九歌》而舞《韶》兮，聊假日以媮乐。 陟升皇之赫戏兮，忽临睨夫旧乡。 仆夫悲余马怀兮，蜷局顾而不行。 乱曰：已矣哉！ 国无人莫我知兮，又何怀乎故都！ 既莫足与为美政兮，吾将从彭咸之所居！', '楚辞');
INSERT INTO `poem` VALUES (1440, '九歌', '先秦', '屈原', '东皇太一 吉日兮辰良，穆将愉兮上皇； 抚长剑兮玉珥，璆锵鸣兮琳琅； 瑶席兮玉瑱，盍将把兮琼芳； 蕙肴蒸兮兰藉，奠桂酒兮椒浆； 扬枹兮拊鼓，疏缓节兮安歌； 陈竽瑟兮浩倡； 灵偃蹇兮姣服，芳菲菲兮满堂； 五音纷兮繁会，君欣欣兮乐康。 云中君 浴兰汤兮沐芳，华采衣兮若英； 灵连蜷兮既留，烂昭昭兮未央； 謇将憺兮寿宫，与日月兮齐光； 龙驾兮帝服，聊翱游兮周章； 灵皇皇兮既降，猋远举兮云中； 览冀洲兮有余，横四海兮焉穷； 思夫君兮太息，极劳心兮忡忡； 湘君 君不行兮夷犹，蹇谁留兮中洲； 美要眇兮宜修，沛吾乘兮桂舟； 令沅湘兮无波，使江水兮安流； 望夫君兮未来，吹参差兮谁思； 驾飞龙兮北征，邅吾道兮洞庭； 薜荔柏兮蕙绸，荪桡兮兰旌； 望涔阳兮极浦，横大江兮扬灵； 扬灵兮未极，女婵媛兮为余太息； 横流涕兮潺湲，隐思君兮陫侧； 桂棹兮兰枻，斵冰兮积雪； 采薜荔兮水中，搴芙蓉兮木末； 心不同兮媒劳，恩不甚兮轻绝； 石濑兮浅浅，飞龙兮翩翩； 交不忠兮怨长，期不信兮告余以不闲； 朝骋骛兮江皋，夕弭节兮北渚； 鸟次兮屋上，水周兮堂下； 捐余玦兮江中，遗余佩兮醴浦； 采芳洲兮杜若，将以遗兮下女； 时不可兮再得，聊逍遥兮容与。 湘夫人 帝子降兮北渚，目眇眇兮愁予； 袅袅兮秋风，洞庭波兮木叶下； 登白薠兮骋望，与佳期兮夕张； 鸟何萃兮苹中，罾何为兮木上？(苹 通：蘋) 沅有茝兮醴有兰，思公子兮未敢言； 荒忽兮远望，观流水兮潺湲； 麋何食兮庭中，蛟何为兮水裔； 朝驰余马兮江皋，夕济兮西澨； 闻佳人兮召余，将腾驾兮偕逝； 筑室兮水中，葺之兮荷盖； 荪壁兮紫坛，播芳椒兮成堂； 桂栋兮兰橑，辛夷楣兮药房； 罔薜荔兮为帷，擗蕙櫋兮既张； 白玉兮为镇，疏石兰兮为芳； 芷葺兮荷屋，缭之兮杜衡； 合百草兮实庭，建芳馨兮庑门； 九嶷缤兮并迎，灵之来兮如云； 捐余袂兮江中，遗余褋兮醴浦； 搴汀洲兮杜若，将以遗兮远者； 时不可兮骤得，聊逍遥兮容与！ 大司命 广开兮天门，纷吾乘兮玄云； 令飘风兮先驱，使涷雨兮洒尘； 君回翔兮以下，逾空桑兮从女； 纷总总兮九州，何寿夭兮在予； 高飞兮安翔，乘清气兮御阴阳； 吾与君兮齐速，导帝之兮九坑； 灵衣兮被被，玉佩兮陆离； 一阴兮一阳，众莫知兮余所为； 折疏麻兮瑶华，将以遗兮离居； 老冉冉兮既极，不寖近兮愈疏； 乘龙兮辚辚，高驰兮冲天； 结桂枝兮延伫，羌愈思兮愁人； 愁人兮奈何，愿若今兮无亏； 固人命兮有当，孰离合兮何为？ 少司命 秋兰兮麋芜，罗生兮堂下； 绿叶兮素华，芳菲菲兮袭予； 夫人兮自有美子，荪何以兮愁苦； 秋兰兮青青，绿叶兮紫茎； 满堂兮美人，忽独与余兮目成； 入不言兮出不辞，乘回风兮载云旗； 悲莫悲兮生别离，乐莫乐兮新相知； 荷衣兮蕙带，儵而来兮忽而逝； 夕宿兮帝郊，君谁须兮云之际； 与女沐兮咸池，曦女发兮阳之阿； 望美人兮未来，临风怳兮浩歌； 孔盖兮翠旌，登九天兮抚彗星； 竦长剑兮拥幼艾，荪独宜兮为民正。 东君 暾将出兮东方，照吾槛兮扶桑； 抚余马兮安驱，夜皎皎兮既明； 驾龙輈兮乘雷，载云旗兮委蛇； 长太息兮将上，心低徊兮顾怀； 羌声色兮娱人，观者儋兮忘归； 縆瑟兮交鼓，萧钟兮瑶簴； 鸣篪兮吹竽，思灵保兮贤姱； 翾飞兮翠曾，展诗兮会舞； 应律兮合节，灵之来兮敝日； 青云衣兮白霓裳，举长矢兮射天狼； 操余弧兮反沦降，援北斗兮酌桂浆； 撰余辔兮高驰翔，杳冥冥兮以东行。 河伯 与女游兮九河，冲风起兮水扬波； 乘水车兮荷盖，驾两龙兮骖螭； 登昆仑兮四望，心飞扬兮浩荡； 日将暮兮怅忘归，惟极浦兮寤怀； 鱼鳞屋兮龙堂，紫贝阙兮珠宫； 灵何惟兮水中； 乘白鼋兮逐文鱼，与女游兮河之渚； 流澌纷兮将来下； 子交手兮东行，送美人兮南浦； 波滔滔兮来迎，鱼鳞鳞兮媵予。 山鬼 若有人兮山之阿，被薜荔兮带女萝； 既含睇兮又宜笑，子慕予兮善窈窕； 乘赤豹兮从文狸，辛夷车兮结桂旗； 被石兰兮带杜衡，折芳馨兮遗所思； 余处幽篁兮终不见天，路险难兮独后来； 表独立兮山之上，云容容兮而在下； 杳冥冥兮羌昼晦，东风飘兮神灵雨； 留灵修兮憺忘归，岁既晏兮孰华予； 采三秀兮于山间，石磊磊兮葛蔓蔓； 怨公子兮怅忘归，君思我兮不得闲； 山中人兮芳杜若，饮石泉兮荫松柏； 君思我兮然疑作； 雷填填兮雨冥冥，猿啾啾兮狖夜鸣； 风飒飒兮木萧萧，思公子兮徒离忧。 国殇 操吴戈兮被犀甲，车错毂兮短兵接； 旌蔽日兮敌若云，矢交坠兮士争先； 凌余阵兮躐余行，左骖殪兮右刃伤； 霾两轮兮絷四马，援玉枹兮击鸣鼓； 天时怼兮威灵怒，严杀尽兮弃原野； 出不入兮往不反，平原忽兮路超远； 带长剑兮挟秦弓，首身离兮心不惩； 诚既勇兮又以武，终刚强兮不可凌； 身既死兮神以灵，魂魄毅兮为鬼雄。 礼魂 成礼兮会鼓，传芭兮代舞； 姱女倡兮容与； 春兰兮秋菊，长无绝兮终古。', '楚辞');
INSERT INTO `poem` VALUES (1441, '天问', '先秦', '屈原', '曰：遂古之初，谁传道之？ 上下未形，何由考之？ 冥昭瞢暗，谁能极之？ 冯翼惟象，何以识之？ 明明暗暗，惟时何为？ 阴阳三合，何本何化？ 圜则九重，孰营度之？ 惟兹何功，孰初作之？ 斡维焉系，天极焉加？ 八柱何当，东南何亏？ 九天之际，安放安属？ 隅隈多有，谁知其数？ 天何所沓？十二焉分？ 日月安属？列星安陈？ 出自汤谷，次于蒙汜。 自明及晦，所行几里？ 夜光何德，死则又育？ 厥利维何，而顾菟在腹？ 女岐无合，夫焉取九子？ 伯强何处？惠气安在？ 何阖而晦？何开而明？ 角宿未旦，曜灵安藏？ 不任汩鸿，师何以尚之？ 佥曰“何忧，何不课而行之？” 鸱龟曳衔，鲧何听焉？ 顺欲成功，帝何刑焉？ 永遏在羽山，夫何三年不施？ 伯禹愎鲧，夫何以变化？ 纂就前绪，遂成考功。 何续初继业，而厥谋不同？ 洪泉极深，何以窴之？ 地方九则，何以坟之？ 河海应龙？何尽何历？ 鲧何所营？禹何所成？ 康回冯怒，墬何故以东南倾？ 九州安错？川谷何洿？ 东流不溢，孰知其故？ 东西南北，其修孰多？ 南北顺椭，其衍几何？ 昆仑悬圃，其尻安在？ 增城九重，其高几里？ 四方之门，其谁从焉？ 西北辟启，何气通焉？ 日安不到？烛龙何照？ 羲和之未扬，若华何光？ 何所冬暖？何所夏寒？ 焉有石林？何兽能言？ 焉有虬龙，负熊以游？ 雄虺九首，鯈忽焉在？ 何所不死？长人何守？ 靡蓱九衢，枲华安居？ 灵蛇吞象，厥大何如？ 黑水玄趾，三危安在？ 延年不死，寿何所止？ 鲮鱼何所？鬿堆焉处？ 羿焉彃日？乌焉解羽？ 禹之力献功，降省下土四方。 焉得彼嵞山女，而通之於台桑？ 闵妃匹合，厥身是继。 胡维嗜不同味，而快鼌饱？ 启代益作后，卒然离蠥。 何启惟忧，而能拘是达？ 皆归射鞫，而无害厥躬。 何后益作革，而禹播降？ 启棘宾商，《九辨》《九歌》。 何勤子屠母，而死分竟地？ 帝降夷羿，革孽夏民。 胡射夫河伯，而妻彼雒嫔？ 冯珧利决，封豨是射。 何献蒸肉之膏，而后帝不若？ 浞娶纯狐，眩妻爰谋。 何羿之射革，而交吞揆之？ 阻穷西征，岩何越焉？ 化而为黄熊，巫何活焉？ 咸播秬黍，莆雚是营。 何由并投，而鲧疾修盈？ 白蜺婴茀，胡为此堂？ 安得夫良药，不能固臧？ 天式从横，阳离爰死。 大鸟何鸣，夫焉丧厥体？ 蓱号起雨，何以兴之？ 撰体协胁，鹿何膺之？ 鳌戴山抃，何以安之？ 释舟陵行，何之迁之？ 惟浇在户，何求于嫂？ 何少康逐犬，而颠陨厥首？ 女歧缝裳，而馆同爰止。 何颠易厥首，而亲以逢殆？ 汤谋易旅，何以厚之？ 覆舟斟寻，何道取之？ 桀伐蒙山，何所得焉？ 妺嬉何肆，汤何殛焉？ 舜闵在家，父何以鳏？ 尧不姚告，二女何亲？ 厥萌在初，何所亿焉？ 璜台十成，谁所极焉？ 登立为帝，孰道尚之？ 女娲有体，孰制匠之？ 舜服厥弟，终然为害。 何肆犬豕，而厥身不危败？ 吴获迄古，南岳是止。 孰期去斯，得两男子？ 缘鹄饰玉，后帝是飨。 何承谋夏桀，终以灭丧？ 帝乃降观，下逢伊挚。 何条放致罚，而黎服大说？ 简狄在台，喾何宜？ 玄鸟致贻，女何喜？ 该秉季德，厥父是臧。 胡终弊于有扈，牧夫牛羊？ 干协时舞，何以怀之？ 平胁曼肤，何以肥之？ 有扈牧竖，云何而逢？ 击床先出，其命何从？ 恒秉季德，焉得夫朴牛？ 何往营班禄，不但还来？ 昏微循迹，有狄不宁。 何繁鸟萃棘，负子肆情？ 眩弟并淫，危害厥兄。 何变化以作诈，而后嗣逢长？ 成汤东巡，有莘爰极。 何乞彼小臣，而吉妃是得？ 水滨之木，得彼小子。 夫何恶之，媵有莘之妇？ 汤出重泉，夫何辠尤？ 不胜心伐帝，夫谁使挑之？ 会朝争盟，何践吾期？ 苍鸟群飞，孰使萃之？ 列击纣躬，叔旦不嘉。 何亲揆发足，周之命以咨嗟？ 授殷天下，其位安施？ 反成乃亡，其罪伊何？ 争遣伐器，何以行之？ 并驱击翼，何以将之？ 昭后成游，南土爰底。 厥利惟何，逢彼白雉？ 穆王巧梅，夫何为周流？ 环理天下，夫何索求？ 妖夫曳炫，何号于市？ 周幽谁诛？焉得夫褒姒？ 天命反侧，何罚何佑？ 齐桓九会，卒然身杀。 彼王纣之躬，孰使乱惑？ 何恶辅弼，谗谄是服？ 比干何逆，而抑沈之？ 雷开阿顺，而赐封之？ 何圣人之一德，卒其异方？ 梅伯受醢，箕子详狂？ 稷维元子，帝何竺之？ 投之于冰上，鸟何燠之？ 何冯弓挟矢，殊能将之？ 既惊帝切激，何逢长之？ 伯昌号衰，秉鞭作牧。 何令彻彼岐社，命有殷国？ 迁藏就岐，何能依？ 殷有惑妇，何所讥？ 受赐兹醢，西伯上告。 何亲就上帝罚，殷之命以不救？ 师望在肆，昌何识？ 鼓刀扬声，后何喜？ 武发杀殷，何所悒？ 载尸集战，何所急？ 伯林雉经，维其何故？ 何感天抑墬，夫谁畏惧？ 皇天集命，惟何戒之？ 受礼天下，又使至代之？ 初汤臣挚，后兹承辅。 何卒官汤，尊食宗绪？ 勋阖梦生，少离散亡。 何壮武历，能流厥严？ 彭铿斟雉，帝何飨？ 受寿永多，夫何久长？ 中央共牧，后何怒？ 蜂蛾微命，力何固？ 惊女采薇，鹿何佑？ 北至回水，萃何喜？ 兄有噬犬，弟何欲？ 易之以百两，卒无禄？ 薄暮雷电，归何忧？ 厥严不奉，帝何求？ 伏匿穴处，爰何云？ 荆勋作师，夫何长？ 悟过改更，我又何言？ 吴光争国，久余是胜。 何环穿自闾社丘陵，爰出子文？ 吾告堵敖以不长。 何试上自予，忠名弥彰？', '楚辞');
INSERT INTO `poem` VALUES (1442, '九章', '先秦', '屈原', '惜诵 　　惜诵以致愍兮，发愤以抒情。 　　所作忠而言之兮，指苍天以为正。 　　令五帝使折中兮，戒六神与向服。 　　俾山川以备御兮，命咎繇使听直。 　　竭忠诚而事君兮，反离群而赘肬。 　　忘儇媚以背众兮，待明君其知之。 　　言与行其可迹兮，情与貌其不变。 　　故相臣莫若君兮，所以证之不远。 　　吾谊先君而后身兮，羌众人之所仇也。 　　专惟君而无他兮，又众兆之所雠也。 　　壹心而不豫兮，羌无可保也。 　　疾亲君而无他兮，有招祸之道也。 　　思君其莫我忠兮，忽忘身之贱贫。 　　事君而不贰兮，迷不知宠之门。 　　患何罪以遇罚兮，亦非余之所志也。 　　行不群以巅越兮，又众兆之所咍也。 　　纷逢尤以离谤兮，謇不可释也。 　　情沉抑而不达兮，又蔽而莫之白也。 　　心郁邑余侘傺兮，又莫察余之中情。 　　固烦言不可结而诒兮，愿陈志而无路。 　　退静默而莫余知兮，进号呼又莫吾闻。 　　申侘傺之烦惑兮，中闷瞀之忳忳。 　　昔余梦登天兮，魂中道而无杭。 　　吾使厉神占之兮，曰有志极而无旁。 　　终危独以离异兮，曰君可思而不可恃。 　　故众口其铄金兮，初若是而逢殆。 　　惩于羹者而吹齑兮，何不变此志也？ 　　欲释阶而登天兮，犹有曩之态也。 　　众骇遽以离心兮，又何以为此伴也？ 　　同极而异路兮，又何以为此援也？ 　　晋申生之孝子兮，父信谗而不好。 　　行婞直而不豫兮，鲧功用而不就。 　　吾闻作忠以造怨兮，忽谓之过言。 　　九折臂而成医兮，吾至今而知其信然。 　　矰弋机而在上兮，罻罗张而在下。 　　设张辟以娱君兮，愿侧身而无所。 　　欲儃徊以干傺兮，恐重患而离尤。 　　欲高飞而远集兮，君罔谓汝何之？ 　　欲横奔而失路兮，盖志坚而不忍。 　　背膺牉以交痛兮，心郁结而纡轸。 　　擣木兰以矫蕙兮，糳申椒以为粮。 　　播江离与滋菊兮，愿春日以为糗芳。 　　恐情质之不信兮，故重著以自明。 　　矫兹媚以私处兮，愿曾思而远身。 涉江 　　余幼好此奇服兮，年既老而不衰。 　　带长铗之陆离兮，冠切云之崔嵬， 　　被明月兮佩宝璐。 　　世混浊而莫余知兮，吾方高驰而不顾。 　　驾青虬兮骖白螭，吾与重华游兮瑶之圃。 　　登昆仑兮食玉英，与天地兮同寿， 　　与日月兮同光。 　　哀南夷之莫吾知兮，旦余济乎江湘。 　　乘鄂渚而反顾兮，欸秋冬之绪风。 　　步余马兮山皋，邸余车兮方林。 　　乘舲船余上沅兮，齐吴榜以击汰。 　　船容与而不进兮，淹回水而疑滞。 　　朝发枉渚兮，夕宿辰阳。 　　苟余心其端直兮，虽僻远之何伤。 　　入溆浦余儃徊兮，迷不知吾所如。 　　深林杳以冥冥兮，乃猿狖之所居。 　　山峻高以蔽日兮，下幽晦以多雨。 　　霰雪纷其无垠兮，云霏霏而承宇。 　　哀吾生之无乐兮，幽独处乎山中。 　　吾不能变心而从俗兮，固将愁苦而终穷。 　　接舆髡首兮，桑扈臝行。 　　忠不必用兮，贤不必以。 　　伍子逢殃兮，比干菹醢。 　　与前世而皆然兮，吾又何怨乎今之人！ 　　余将董道而不豫兮，固将重昏而终身！ 　　乱曰：鸾鸟凤皇，日以远兮。 　　燕雀乌鹊，巢堂坛兮。 　　露申辛夷，死林薄兮。 　　腥臊并御，芳不得薄兮。 　　阴阳易位，时不当兮。 　　怀信佗傺，忽乎吾将行兮！ 哀郢 　　皇天之不纯命兮，何百姓之震愆？ 　　民离散而相失兮，方仲春而东迁。 　　去故乡而就远兮，遵江夏以流亡。 　　出国门而轸怀兮，甲之鼂吾以行。 　　发郢都而去闾兮，怊荒忽其焉极？ 　　楫齐扬以容与兮，哀见君而不再得。 　　望长楸而太息兮，涕淫淫其若霰。 　　过夏首而西浮兮，顾龙门而不见。 　　心婵媛而伤怀兮，眇不知其所蹠。 　　顺风波以从流兮，焉洋洋而为客。 　　凌阳侯之汜滥兮，忽翱翔之焉薄。 　　心絓结而不解兮，思蹇产而不释。 　　将运舟而下浮兮，上洞庭而下江。 　　去终古之所居兮，今逍遥而来东。 　　羌灵魂之欲归兮，何须臾而忘反。 　　背夏浦而西思兮，哀故都之日远。 　　登大坟以远望兮，聊以舒吾忧心。 　　哀州土之平乐兮，悲江介之遗风。 　　当陵阳之焉至兮，淼南渡之焉如？ 　　曾不知夏之为丘兮，孰两东门之可芜？ 　　心不怡之长久兮，忧与愁其相接。 　　惟郢路之辽远兮，江与夏之不可涉。 　　忽若不信兮，至今九年而不复。 　　惨郁郁而不通兮，蹇侘傺而含慼。 　　外承欢之汋约兮，谌荏弱而难持。 　　忠湛湛而愿进兮，妒被离而鄣之。 　　尧舜之抗行兮，瞭杳杳而薄天。 　　众谗人之嫉妒兮，被以不慈之伪名。 　　憎愠惀之修美兮，好夫人之慷慨。 　　众踥蹀而日进兮，美超远而逾迈。 　　乱曰： 　　曼余目以流观兮，冀一反之何时？ 　　鸟飞反故乡兮，狐死必首丘。 　　信非吾罪而弃逐兮，何日夜而忘之？ 抽思 　　心郁郁之忧思兮，独永叹乎增伤。 　　思蹇产之不释兮，曼遭夜之方长。 　　悲秋风之动容兮，何回极之浮浮。 　　数惟荪之多怒兮，伤余心之忧忧。 　　愿摇起而横奔兮，览民尤以自镇。 　　结微情以陈词兮，矫以遗夫美人。 　　昔君与我诚言兮，曰黄昏以为期。 　　羌中道而回畔兮，反既有此他志。 　　憍吾以其美好兮，览余以其修姱。 　　与余言而不信兮，盖为余而造怒。 　　愿承閒而自察兮，心震悼而不敢。 　　悲夷犹而冀进兮，心怛伤之憺憺。 　　兹历情以陈辞兮，荪详聋而不闻。 　　固切人之不媚兮，众果以我为患。 　　初吾所陈之耿著兮，岂至今其庸亡？ 　　何独乐斯之謇謇兮？愿荪美之可光。 　　望三王以为像兮，指彭咸以为仪。 　　夫何极而不至兮，故远闻而难亏。 　　善不由外来兮，名不可以虚作。 　　孰无施而有报兮，孰不实而有获？ 　　少歌曰： 　　与美人抽思兮，并日夜而无正。 　　憍吾以其美好兮，敖朕辞而不听。 　　倡曰：有鸟自南兮，来集汉北。 　　好姱佳丽兮，牉独处此异域。 　　惸茕独而不群兮，又无良媒在其侧。 　　道卓远而日忘兮，愿自申而不得。 　　望北山而流涕兮，临流水而太息。 　　望孟夏之短夜兮，何晦明之若岁？ 　　惟郢路之辽远兮，魂一夕而九逝。 　　曾不知路之曲直兮，南指月与列星。 　　愿径逝而未得兮，魂识路之营营。 　　何灵魂之信直兮，人之心不与吾心同！ 　　理弱而媒不通兮，尚不知余之从容。 　　乱曰： 　　长濑湍流，溯江潭兮。 　　狂顾南行，聊以娱心兮。 　　轸石崴嵬，蹇吾愿兮。 　　超回志度，行隐进兮。 　　低徊夷犹，宿北姑兮。 　　烦冤瞀容，实沛徂兮。 　　愁叹苦神，灵遥思兮。 　　路远处幽，又无行媒兮。 　　道思作颂，聊以自救兮。 　　忧心不遂，斯言谁告兮。 怀沙 　　滔滔孟夏兮，草木莽莽。 　　伤怀永哀兮，汩徂南土。 　　眴兮杳杳，孔静幽默。 　　郁结纡轸兮，离慜而长鞠。 　　抚情效志兮，冤屈而自抑。 　　刓方以为圜兮，常度未替。 　　易初本迪兮，君子所鄙。 　　章画志墨兮，前图未改。 　　内厚质正兮，大人所盛。 　　巧倕不斲兮，孰察其拨正。 　　玄文处幽兮，矇瞍谓之不章； 　　离娄微睇兮，瞽以为无明。 　　变白以为黑兮，倒上以为下。 　　凤皇在笯兮，鸡鹜翔舞。 　　同糅玉石兮，一概而相量。 　　夫惟党人之鄙固兮，羌不知余之所臧。 　　任重载盛兮，陷滞而不济。 　　怀瑾握瑜兮，穷不知所示。 　　邑犬之群吠兮，吠所怪也。 　　非俊疑杰兮，固庸态也。 　　文质疏内兮，众不知余之异采。 　　材朴委积兮，莫知余之所有。 　　重仁袭义兮，谨厚以为丰。 　　重华不可遌兮，孰知余之从容！ 　　古固有不并兮，岂知其何故也？ 　　汤禹久远兮，邈而不可慕也？ 　　惩违改忿兮，抑心而自强。 　　离慜而不迁兮，愿志之有像。 　　进路北次兮，日昧昧其将暮。 　　舒忧娱哀兮，限之以大故。 　　乱曰： 　　浩浩沅湘，分流汩兮。 　　修路幽蔽，道远忽兮。 　　怀质抱情，独无匹兮。 　　伯乐既没，骥焉程兮。 　　民生禀命，各有所错兮。 　　定心广志，余何所畏惧兮？ 　　曾伤爰哀，永叹喟兮。 　　世浑浊莫吾知，人心不可谓兮。 　　知死不可让，愿勿爱兮。 　　明告君子，吾将以为类兮。 思美人 　　思美人兮，揽涕而竚眙。 　　媒绝路阻兮，言不可结而诒。 　　蹇蹇之烦冤兮，陷滞而不发。 　　申旦以舒中情兮，志沉菀而莫达。 　　愿寄言于浮云兮，遇丰隆而不将。 　　因归鸟而致辞兮，羌迅高而难当。 　　高辛之灵盛兮，遭玄鸟而致诒。 　　欲变节以从俗兮，媿易初而屈志。 　　独历年而离愍兮，羌凭心犹未化。 　　宁隐闵而寿考兮，何变易之可为！ 　　知前辙之不遂兮，未改此度。 　　车既覆而马颠兮，蹇独怀此异路。 　　勒骐骥而更驾兮，造父为我操之， 　　迁逡次而勿驱兮，聊假日以须是时。 　　指嶓冢之西隈兮，与纁黄以为期。 　　开春发岁兮，白日出之悠悠。 　　吾将荡志而愉乐兮，遵江夏以娱忧。 　　揽大薄之芳茝兮，搴长洲之宿莽。 　　惜吾不及古人兮，吾谁与玩此芳草？ 　　解萹薄与杂菜兮，备以为交佩。 　　佩缤纷以缭转兮，遂萎绝而离异。 　　吾且儃徊以娱忧兮，观南人之变态。 　　窃快在中心兮，扬厥凭而不竢。 　　芳与泽其杂糅兮，羌芳华自中出。 　　纷郁郁其远蒸兮，满内而外扬。 　　情与质信可保兮，羌居蔽而闻章。 　　令薜荔以为理兮，惮举趾而缘木。 　　因芙蓉而为媒兮，惮褰裳而濡足。 　　登高吾不说兮，入下吾不能。 　　固朕形之不服兮，然容与而狐疑。 　　广遂前画兮，未改此度也。 　　命则处幽吾将罢兮，愿及白日之未暮也。 　　独茕茕而南行兮，思彭咸之故也。 惜往日 　　惜往日之曾信兮，受命诏以昭时。 　　奉先功以照下兮，明法度之嫌疑。 　　国富强而法立兮，属贞臣而日竢。 　　秘密事之载心兮，虽过失犹弗治。 　　心纯庞而不泄兮，遭谗人而嫉之。 　　君含怒而待臣兮，不清澈其然否。 　　蔽晦君之聪明兮，虚惑误又以欺。 　　弗参验以考实兮，远迁臣而弗思。 　　信谗谀之浑浊兮，盛气志而过之。 　　何贞臣之无罪兮，被离谤而见尤。 　　惭光景之诚信兮，身幽隐而备之。 　　临沅湘之玄渊兮，遂自忍而沉流。 　　卒没身而绝名兮，惜壅君之不昭。 　　君无度而弗察兮，使芳草为薮幽。 　　焉舒情而抽信兮，恬死亡而不聊。 　　独障壅而弊隐兮，使贞臣为无由。 　　闻百里之为虏兮，伊尹烹于庖厨。 　　吕望屠于朝歌兮，宁戚歌而饭牛。 　　不逢汤武与桓缪兮，世孰云而知之。 　　吴信谗而弗味兮，子胥死而后忧。 　　介子忠而立枯兮，文君寤而追求。 　　封介山而为之禁兮，报大德之优游。 　　思久故之亲身兮，因缟素而哭之。 　　或忠信而死节兮，或訑谩而不疑。 　　弗省察而按实兮，听谗人之虚辞。 　　芳与泽其杂糅兮，孰申旦而别之？ 　　何芳草之早殀兮，微霜降而下戒。 　　谅聪不明而蔽壅兮，使谗谀而日得。 　　自前世之嫉贤兮，谓蕙若其不可佩。 　　妒佳冶之芬芳兮，嫫母姣而自好。 　　虽有西施之美容兮，谗妒入以自代。 　　愿陈情以白行兮，得罪过之不意。 　　情冤见之日明兮，如列宿之错置。 　　乘骐骥而驰骋兮，无辔衔而自载； 　　乘泛泭以下流兮，无舟楫而自备。 　　背法度而心治兮，辟与此其无异。 　　宁溘死而流亡兮，恐祸殃之有再。 　　不毕辞而赴渊兮，惜壅君之不识。 橘颂 　　后皇嘉树，橘徕服兮。 　　受命不迁，生南国兮。 　　深固难徙，更壹志兮。 　　绿叶素荣，纷其可喜兮。 　　曾枝剡棘，圆果抟兮。 　　青黄杂糅，文章烂兮。 　　精色内白，类任道兮。 　　纷緼宜修，姱而不丑兮。 　　嗟尔幼志，有以异兮。 　　独立不迁，岂不可喜兮？ 　　深固难徙，廓其无求兮。 　　苏世独立，横而不流兮。 　　闭心自慎，终不失过兮。 　　秉德无私，参天地兮。 　　愿岁并谢，与长友兮。 　　淑离不淫，梗其有理兮。 　　年岁虽少，可师长兮。 　　行比伯夷，置以为像兮。 悲回风 　　悲回风之摇蕙兮，心冤结而内伤。 　　物有微而陨性兮，声有隐而先倡。 　　夫何彭咸之造思兮，暨志介而不忘！ 　　万变其情岂可盖兮，孰虚伪之可长？ 　　鸟兽鸣以号群兮，草苴比而不芳。 　　鱼葺鳞以自别兮，蛟龙隐其文章。 　　故荼荠不同亩兮，兰茝幽而独芳。 　　惟佳人之永都兮，更统世以自贶。 　　眇远志之所及兮，怜浮云之相羊。 　　介眇志之所惑兮，窃赋诗之所明。 　　惟佳人之独怀兮，折若椒以自处。 　　曾歔欷之嗟嗟兮，独隐伏而思虑。 　　涕泣交而凄凄兮，思不眠以至曙。 　　终长夜之曼曼兮，掩此哀而不去。 　　寤从容以周流兮，聊逍遥以自恃。 　　伤太息之愍怜兮，气于邑而不可止。 　　糺思心以为纕兮，编愁苦以为膺。 　　折若木以弊光兮，随飘风之所仍。 　　存彷佛而不见兮，心踊跃其若汤。 　　抚珮衽以案志兮，超惘惘而遂行。 　　岁曶曶其若颓兮，时亦冉冉而将至。 　　薠蘅槁而节离兮，芳以歇而不比。 　　怜思心之不可惩兮，证此言之不可聊。 　　宁溘死而流亡兮，不忍此心之常愁。 　　孤子吟而抆泪兮，放子出而不还。 　　孰能思而不隐兮，照彭咸之所闻。 　　登石峦以远望兮，路眇眇之默默。 　　入景响之无应兮，闻省想而不可得。 　　愁郁郁之无快兮，居戚戚而不可解。 　　心鞿羁而不开兮，气缭转而自缔。 　　穆眇眇之无垠兮，莽芒芒之无仪。 　　声有隐而相感兮，物有纯而不可为。 　　邈漫漫之不可量兮，缥绵绵之不可纡。 　　愁悄悄之常悲兮，翩冥冥之不可娱。 　　凌大波而流风兮，讬彭咸之所居。 　　上高岩之峭岸兮，处雌蜺之标颠。 　　据青冥而摅虹兮，遂儵忽而扪天。 　　吸湛露之浮源兮，漱凝霜之雰雰。 　　依风穴以自息兮，忽倾寤以婵媛。 　　冯昆仑以澂雾兮，隐渂山以清江。 　　惮涌湍之礚礚兮，听波声之汹汹。 　　纷容容之无经兮，罔芒芒之无纪。 　　轧洋洋之无从兮，驰委移之焉止？ 　　漂翻翻其上下兮，翼遥遥其左右。 　　氾潏潏其前后兮，伴张驰之信期。 　　观炎气之相仍兮，窥烟液之所积。 　　悲霜雪之俱下兮，听潮水之相击。 　　借光景以往来兮，施黄棘之枉策。 　　求介子之所存兮，见伯夷之放迹。 　　心调度而弗去兮，刻著志之无适。 　　曰吾怨往昔之所冀兮，悼来者之悐悐。 　　浮江淮而入海兮，从子胥而自适。 　　望大河之洲渚兮，悲申徒之抗迹。 　　骤谏君而不听兮，重任石之何益？ 　　心絓结而不解兮，思蹇产而不释。', '楚辞');
INSERT INTO `poem` VALUES (1443, '远游', '先秦', '屈原', '悲时俗之迫阨兮，愿轻举而远游。 质菲薄而无因兮，焉讬乘而上浮？ 遭沈浊而污秽兮，独郁结其谁语！ 夜耿耿而不寐兮，魂营营而至曙。 惟天地之无穷兮，哀人生之长勤。 往者余弗及兮，来者吾不闻。 步徙倚而遥思兮，怊惝怳而乖怀。 意荒忽而流荡兮，心愁悽而增悲。 神倏忽而不反兮，形枯槁而独留。 内惟省以操端兮，求正气之所由。 漠虚静以恬愉兮，澹无为而自得。 闻赤松之清尘兮，愿承风乎遗则。 贵真人之休德兮，美往世之登仙； 与化去而不见兮，名声著而日延。 奇傅说之讬辰星兮，羡韩众之得一。 形穆穆以浸远兮，离人群而遁逸。 因气变而遂曾举兮，忽神奔而鬼怪。 时仿佛以遥见兮，精晈晈以往来。 超氛埃而淑邮兮，终不反其故都。 免众患而不惧兮，世莫知其所如。 恐天时之代序兮，耀灵晔而西征。 微霜降而下沦兮，悼芳草之先蘦。 聊仿佯而逍遥兮，永历年而无成。 谁可与玩斯遗芳兮？长向风而舒情。 高阳邈以远兮，余将焉所程？ 重曰： 春秋忽其不淹兮，奚久留此故居。 轩辕不可攀援兮，吾将从王乔而娱戏。 餐六气而饮沆瀣兮，漱正阳而含朝霞。 保神明之清澄兮，精气入而麤秽除。 顺凯风以从游兮，至南巢而壹息。 见王子而宿之兮，审壹气之和德。 曰“道可受兮，不可传； 其小无内兮，其大无垠。 毋滑而魂兮，彼将自然； 壹气孔神兮，于中夜存。 虚以待之存，无为之先； 庶类以成兮，此德之门。” 闻至贵而遂徂兮，忽乎吾将行。 仍羽人于丹丘，留不死之旧乡。 朝濯发于汤谷兮，夕晞余身兮九阳。 吸飞泉之微液兮，怀琬琰之华英。 玉色頩以脕颜兮，精醇粹而始壮。 质销铄以汋约兮，神要眇以淫放。 嘉南州之炎德兮，丽桂树之冬荣； 山萧条而无兽兮，野寂漠其无人。 载营魄而登霞兮，掩浮云而上征。 命天阍其开关兮，排阊阖而望予。 召丰隆使先导兮，问太微之所居。 集重阳入帝宫兮，造旬始而观清都。 朝发轫于太仪兮，夕始临乎于微闾。 屯余车之万乘兮，纷容与而并驰。 驾八龙之婉婉兮，载云旗之逶蛇。 建雄虹之采旄兮，五色杂而炫耀。 服偃蹇以低昂兮，骖连蜷以骄骜。 骑胶葛以杂乱兮，斑漫衍而方行。 撰余辔而正策兮，吾将过乎句芒。 历太皓以右转兮，前飞廉以启路。 阳杲杲其未光兮，凌天地以径度。 风伯为余先驱兮，氛埃辟而清凉。 凤凰翼其承旂兮，遇蓐收乎西皇。 揽慧星以为旍兮，举斗柄以为麾。 叛陆离其上下兮，游惊雾之流波。 时暧曃其曭莽兮，召玄武而奔属。 后文昌使掌行兮，选署众神以并轂。 路漫漫其修远兮，徐弭节而高厉。 左雨师使径侍兮，右雷公以为卫。 欲度世以忘归兮，意姿睢以抯挢。 内欣欣而自美兮，聊媮娱以淫乐。 涉青云以汎滥游兮，忽临睨夫旧乡。 仆夫怀余心悲兮，边马顾而不行。 思旧故以想象兮，长太息而掩涕。 汜容与而遐举兮，聊抑志而自弭。 指炎神而直驰兮，吾将往乎南疑。 览方外之荒忽兮，沛罔瀁而自浮。 祝融戒而跸御兮，腾告鸾鸟迎宓妃。 张咸池奏承云兮，二女御九韶歌。 使湘灵鼓瑟兮，令海若舞冯夷。 玄螭虫象并出进兮，形蟉虯而逶蛇。 雌蜺便娟以增挠兮，鸾鸟轩翥而翔飞。 音乐博衍无终极兮，焉乃逝以徘徊。 舒并节以驰骛兮，逴绝垠乎寒门。 轶迅风于清源兮，从颛顼乎增冰。 历玄冥以邪径兮，乘间维以反顾。 召黔赢而见之兮，为余先乎平路。 经营四方兮，周流六漠。 上至列缺兮，降望大壑。 下峥嵘而无地兮，上寥廓而无天。 视倏忽而无见兮，听惝恍而无闻。 超无为以至清兮，与泰初而为邻。', '楚辞');
INSERT INTO `poem` VALUES (1444, '卜居', '先秦', '屈原', '　　屈原既放，三年不得复见。竭知尽忠而蔽障于谗。心烦虑乱，不知所从。乃往见太卜郑詹尹曰：“余有所疑，愿因先生决之。”詹尹乃端策拂龟，曰：“君将何以教之？” 　　屈原曰：“吾宁悃悃款款，朴以忠乎，将送往劳来，斯无穷乎？ 　　“宁诛锄草茅以力耕乎，将游大人以成名乎？宁正言不讳以危身乎，将从俗富贵以偷生乎？宁超然高举以保真乎，将哫訾栗斯，喔咿儒儿，以事妇人乎？宁廉洁正直以自清乎，将突梯滑稽，如脂如韦，以洁楹乎？ 　　“宁昂昂若千里之驹乎，将泛泛若水中之凫，与波上下，偷以全吾躯乎？宁与骐骥亢轭乎，将随驽马之迹乎？宁与黄鹄比翼乎，将与鸡鹜争食乎？ 　　“此孰吉孰凶？何去何从？ 　　“世溷浊而不清：蝉翼为重，千钧为轻；黄钟毁弃，瓦釜雷鸣；谗人高张，贤士无名。吁嗟默默兮，谁知吾之廉贞！” 　　詹尹乃释策而谢曰：“夫尺有所短，寸有所长；物有所不足，智有所不明；数有所不逮，神有所不通。用君之心，行君之意。龟策诚不能知此事。”', '楚辞');
INSERT INTO `poem` VALUES (1445, '渔父', '先秦', '佚名', '　　屈原既放，游于江潭，行吟泽畔，颜色憔悴，形容枯槁。渔父见而问之曰：“子非三闾大夫与？何故至于斯？”屈原曰：“举世皆浊我独清，众人皆醉我独醒，是以见放。” 　　渔父曰：“圣人不凝滞于物，而能与世推移。世人皆浊，何不淈其泥而扬其波？众人皆醉，何不餔其糟而歠其醨？何故深思高举，自令放为？” 　　屈原曰：“吾闻之，新沐者必弹冠，新浴者必振衣；安能以身之察察，受物之汶汶者乎？宁赴湘流，葬于江鱼之腹中。安能以皓皓之白，而蒙世俗之尘埃乎?” 　　渔父莞尔而笑，鼓枻而去，乃歌曰：“沧浪之水清兮，可以濯吾缨；沧浪之水浊兮，可以濯吾足。”遂去，不复与言。', '楚辞');
INSERT INTO `poem` VALUES (1446, '九辩', '先秦', '宋玉', '悲哉，秋之为气也！ 萧瑟兮草木摇落而变衰。 憭栗兮若在远行，登山临水兮送将归。 泬漻兮天高而气清，寂寥兮收潦而水清。 憯悽增欷兮，薄寒之中人， 怆怳懭悢兮，去故而就新。 坎廪兮贫士失职而志不平， 廓落兮羁旅而无友生， 惆怅兮而私自怜！ 燕翩翩其辞归兮，蝉寂漠而无声。 雁廱廱而南游兮，鹍鸡啁哳而悲鸣。 独申旦而不寐兮，哀蟋蟀之宵征。 时亹亹而过中兮，蹇淹留而无成。 悲忧穷戚兮独处廓，有美一人兮心不绎。 去乡离家兮来远客，超逍遥兮今焉薄！ 专思君兮不可化，君不知兮可奈何！ 蓄怨兮积思，心烦憺兮忘食事。 原一见兮道余意，君之心兮与余异。 车既驾兮朅而归，不得见兮心伤悲。 倚结軨兮长太息，涕潺湲兮下霑轼。 忼慨绝兮不得，中瞀乱兮迷惑。 私自怜兮何极？心怦怦兮谅直。 皇天平分四时兮，窃独悲此凛秋。 白露既下百草兮，奄离披此梧楸。 去白日之昭昭兮，袭长夜之悠悠。 离芳蔼之方壮兮，余萎约而悲愁。 秋既先戒以白露兮，冬又申之以严霜。 收恢台之孟夏兮，然欿傺而沉藏。 叶菸邑而无色兮，枝烦挐而交横。 颜淫溢而将罢兮，柯仿佛而萎黄。 萷櫹椮之可哀兮，形销铄而瘀伤。 惟其纷糅而将落兮，恨其失时而无当。 揽騑辔而下节兮，聊逍遥以相佯。 岁忽忽而遒尽兮，恐余寿之弗将。 悼余生之不时兮，逢此世之俇攘。 澹容与而独倚兮，蟋蟀鸣此西堂。 心怵惕而震荡兮，何所忧之多方。 卬明月而太息兮，步列星而极明。 窃悲夫蕙华之曾敷兮，纷旖旎乎都房。 何曾华之无实兮，从风雨而飞飏！ 以为君独服此蕙兮，羌无以异于众芳。 闵奇思之不通兮，将去君而高翔。 心闵怜之惨悽兮，愿一见而有明。 重无怨而生离兮，中结轸而增伤。 岂不郁陶而思君兮？君之门以九重！ 猛犬狺狺而迎吠兮，关梁闭而不通。 皇天淫溢而秋霖兮，后土何时而得漧？ 塊独守此无泽兮，仰浮云而永叹！ 何时俗之工巧兮？背绳墨而改错！ 郤骐骥而不乘兮，策驽骀而取路。 当世岂无骐骥兮，诚莫之能善御。 见执辔者非其人兮，故駶跳而远去。 凫雁皆唼夫梁藻兮，凤愈飘翔而高举。 圜凿而方枘兮，吾固知其鉏铻而难入。 众鸟皆有所登棲兮，凤独遑遑而无所集。 原衔枚而无言兮，尝被君之渥洽。 太公九十乃显荣兮，诚未遇其匹合。 谓骐骥兮安归？谓凤皇兮安棲？ 变古易俗兮世衰，今之相者兮举肥。 骐骥伏匿而不见兮，凤皇高飞而不下。 鸟兽犹知怀德兮，何云贤士之不处？ 骥不骤进而求服兮，凤亦不贪餧而妄食。 君弃远而不察兮，虽原忠其焉得？ 欲寂漠而绝端兮，窃不敢忘初之厚德。 独悲愁其伤人兮，冯郁郁其何极？ 霜露惨悽而交下兮，心尚幸其弗济。 霰雪雰糅其增加兮，乃知遭命之将至。 原徼幸而有待兮，泊莽莽与野草同死。 原自往而径游兮，路壅绝而不通。 欲循道而平驱兮，又未知其所从。 然中路而迷惑兮，自压桉而学诵。 性愚陋以褊浅兮，信未达乎从容。 窃美申包胥之气盛兮，恐时世之不固。 何时俗之工巧兮？灭规矩而改凿！ 独耿介而不随兮，原慕先圣之遗教。 处浊世而显荣兮，非余心之所乐。 与其无义而有名兮，宁穷处而守高。 食不媮而为饱兮，衣不苟而为温。 窃慕诗人之遗风兮，原讬志乎素餐。 蹇充倔而无端兮，泊莽莽而无垠。 无衣裘以御冬兮，恐溘死不得见乎阳春。 靓杪秋之遥夜兮，心缭悷而有哀。 春秋逴逴而日高兮，然惆怅而自悲。 四时递来而卒岁兮，阴阳不可与俪偕。 白日晼晚其将入兮，明月销铄而减毁。 岁忽忽而遒尽兮，老冉冉而愈弛。 心摇悦而日幸兮，然怊怅而无冀。 中憯恻之悽怆兮，长太息而增欷。 年洋洋以日往兮，老嵺廓而无处。 事亹亹而觊进兮，蹇淹留而踌躇。 何氾滥之浮云兮？猋壅蔽此明月。 忠昭昭而原见兮，然霠曀而莫达。 原皓日之显行兮，云蒙蒙而蔽之。 窃不自聊而原忠兮，或黕点而汙之。 尧舜之抗行兮，瞭冥冥而薄天。 何险巇之嫉妒兮？被以不慈之伪名。 彼日月之照明兮，尚黯黮而有瑕。 何况一国之事兮，亦多端而胶加。 被荷裯之晏晏兮，然潢洋而不可带。 既骄美而伐武兮，负左右之耿介。 憎愠惀之修美兮，好夫人之慷慨。 众踥蹀而日进兮，美超远而逾迈。 农夫辍耕而容与兮，恐田野之芜秽。 事緜緜而多私兮，窃悼後之危败。 世雷同而炫曜兮，何毁誉之昧昧！ 今修饰而窥镜兮，後尚可以竄藏。 愿寄言夫流星兮，羌倏忽而难当。 卒壅蔽此浮云，下暗漠而无光。 尧舜皆有所举任兮，故高枕而自适。 谅无怨于天下兮，心焉取此怵惕？ 乘骐骥之浏浏兮，驭安用夫强策？ 谅城郭之不足恃兮，虽重介之何益？ 邅翼翼而无终兮，忳惛惛而愁约。 生天地之若过兮，功不成而无嶜。 原沉滞而不见兮，尚欲布名乎天下。 然潢洋而不遇兮，直怐愗而自苦。 莽洋洋而无极兮，忽翱翔之焉薄？ 国有骥而不知乘兮，焉皇皇而更索？ 宁戚讴于车下兮，桓公闻而知之。 无伯乐之相善兮，今谁使乎誉之？ 罔流涕以聊虑兮，惟著意而得之。 纷纯纯之愿忠兮，妒被离而鄣之。 原赐不肖之躯而别离兮，放游志乎云中。 乘精气之抟抟兮，骛诸神之湛湛。 骖白霓之習習兮，历群灵之丰丰。 左硃雀之茇茇兮，右苍龙之躣躣。 属雷师之阗阗兮，通飞廉之衙衙。 前轻辌之锵锵兮，后辎乘之从从。 载云旗之委蛇兮，扈屯骑之容容。 计专专之不可化兮，原遂推而为臧。 赖皇天之厚德兮，还及君之无恙！', '楚辞');
INSERT INTO `poem` VALUES (1447, '招魂', '先秦', '屈原', '朕幼清以廉洁兮，身服义而未沫。 主此盛德兮，牵于俗而芜秽。 上无所考此盛德兮，长离殃而愁苦。 帝告巫阳曰：“有人在下，我欲辅之。 魂魄离散，汝筮予之。” 巫阳对曰：“掌梦！ 上帝其难从；若必筮予之， 恐后之谢，不能复用。” 巫阳焉乃下招曰： 魂兮归来！去君之恒干， 何为四方些？舍君之乐处， 而离彼不祥些！ 魂兮归来！东方不可以讬些。 长人千仞，惟魂是索些。 十日代出，流金铄石些。 彼皆习之，魂往必释些。 归来兮！不可以讬些。 魂兮归来！南方不可以止些。 雕题黑齿，得人肉以祀，以其骨为醢些。 蝮蛇蓁蓁，封狐千里些。 雄虺九首，往来倏忽，吞人以益其心些。 归来兮！不可久淫些。 魂兮归来！西方之害，流沙千里些。 旋入雷渊，爢散而不可止些。 幸而得脱，其外旷宇些。 赤蚁若象，玄蜂若壶些。 五谷不生，丛菅是食些。 其土烂人，求水无所得些。 彷徉无所倚，广大无所极些。 归来兮！恐自遗贼些。 魂兮归来！北方不可以止些。 增冰峨峨，飞雪千里些。 归来兮！不可以久些。 魂兮归来！君无上天些。 虎豹九关，啄害下人些。 一夫九首，拔木九千些。 豺狼从目，往来侁侁些。 悬人以嬉，投之深渊些。 致命于帝，然后得瞑些。 归来！往恐危身些。 魂兮归来！君无下此幽都些。 土伯九约，其角觺觺些。 敦脄血拇，逐人伂駓駓些。 参目虎首，其身若牛些。 此皆甘人，归来！恐自遗灾些。 魂兮归来！入修门些。 工祝招君，背行先些。 秦篝齐缕，郑绵络些。 招具该备，永啸呼些。 魂兮归来！反故居些。 天地四方，多贼奸些。 像设君室，静闲安些。 高堂邃宇，槛层轩些。 层台累榭，临高山些。 网户朱缀，刻方连些。 冬有穾厦，夏室寒些。 川谷径复，流潺湲些。 光风转蕙，氾崇兰些。 经堂入奥，朱尘筵些。 砥室翠翘，挂曲琼些。 翡翠珠被，烂齐光些。 蒻阿拂壁，罗帱张些。 纂组绮缟，结琦璜些。 室中之观，多珍怪些。 兰膏明烛，华容备些。 二八侍宿，射递代些。 九侯淑女，多迅众些。 盛鬋不同制，实满宫些。 容态好比，顺弥代些。 弱颜固植，謇其有意些。 姱容修态，絚洞房些。 蛾眉曼睩，目腾光些。 靡颜腻理，遗视矊些。 离榭修幕，侍君之闲些。 悲帷翠帐，饰高堂些。 红壁沙版，玄玉梁些。 仰观刻桷，画龙蛇些。 坐堂伏槛，临曲池些。 芙蓉始发，杂芰荷些。 紫茎屏风，文缘波些。 文异豹饰，侍陂陁些。 轩辌既低，步骑罗些。 兰薄户树，琼木篱些。 魂兮归来！何远为些？ 室家遂宗，食多方些。 稻粢穱麦，挐黄梁些。 大苦醎酸，辛甘行些。 肥牛之腱，臑若芳些。 和酸若苦，陈吴羹些。 胹鳖炮羔，有柘浆些。 鹄酸臇凫，煎鸿鸧些。 露鸡臛蠵，厉而不爽些。 粔籹蜜饵，有餦餭些。 瑶浆蜜勺，实羽觞些。 挫糟冻饮，酎清凉些。 华酌既陈，有琼浆些。 归来反故室，敬而无妨些。 肴羞未通，女乐罗些。 敶钟按鼓，造新歌些。 《涉江》《采菱》，发《扬荷》些。 美人既醉，朱颜酡些。 嬉光眇视，目曾波些。 被文服纤，丽而不奇些。 长发曼鬋，艳陆离些。 二八齐容，起郑舞些。 衽若交竿，抚案下些。 竽瑟狂会，搷鸣鼓些。 宫庭震惊，发<激楚>些。 吴歈蔡讴，奏大吕些。 士女杂坐，乱而不分些。 放敶组缨，班其相纷些。 郑卫妖玩，来杂陈些。 《激楚》之结，独秀先些。 菎蔽象棋，有六簙些。 分曹并进，遒相迫些。 成枭而牟，呼五白些。 晋制犀比，费白日些。 铿钟摇簴，揳梓瑟些。 娱酒不废，沈日夜些。 兰膏明烛，华灯错些。 结撰至思，兰芳假些。 人有所极，同心赋些。 酎饮尽欢，乐先故些。 魂兮归来！反故居些。 乱曰： 献岁发春兮，汨吾南征。 菉蘋齐叶兮，白芷生。 路贯庐江兮，左长薄。 倚沼畦瀛兮，遥望博。 青骊结驷兮，齐千乘。 悬火延起兮，玄颜烝。 步及骤处兮，诱骋先。 抑骛若通兮，引车右还。 与王趋梦兮，课后先。 君王亲发兮，惮青兕。 朱明承夜兮，时不可以淹。 皋兰被径兮，斯路渐。 湛湛江水兮，上有枫。 目极千里兮，伤春心。 魂兮归来，哀江南！', '楚辞');
INSERT INTO `poem` VALUES (1448, '大招', '先秦', '屈原', '青春受谢，白日昭只。 春气奋发，万物遽只。 冥凌浃行，魂无逃只。 魂魄归来！无远遥只。 魂乎归来！无东无西，无南无北只。 东有大海，溺水浟浟只。 螭龙并流，上下悠悠只。 雾雨淫淫，白皓胶只。 魂乎无东！汤谷寂寥只。 魂乎无南！南有炎火千里，蝮蛇蜒只。 山林险隘，虎豹蜿只。 鰅鳙短狐，王虺骞只。 魂乎无南！蜮伤躬只； 魂乎无西！西方流沙，漭洋洋只。 豕首纵目，被发鬤只。 长爪踞牙，诶笑狂只。 魂乎无西！多害伤只。 魂乎无北！北有寒山，趠龙赩只。 代水不可涉，深不可测只。 天白颢颢，寒凝凝只。 魂乎无往！盈北极只。 魂魄归来！闲以静只。 自恣荆楚，安以定只。 逞志究欲，心意安只。 穷身永乐，年寿延只。 魂乎归来！乐不可言只。 五谷六仞，设菰梁只。 鼎臑盈望，和致芳只。 内鸧鸽鹄，味豺羹只。 魂乎归来！恣所尝只。 鲜蠵甘鸡，和楚酪只。 醢豚苦狗，脍苴蒪只。 吴酸蒿蒌，不沾薄只。 魂兮归来！恣所择只。 炙鸹烝凫，煔鹑敶只。 煎鰿膗雀，遽爽存只。 魂乎归来！丽以先只。 四酎并孰，不涩嗌只。 清馨冻饮，不歠役只。 吴醴白蘖，和楚沥只。 魂乎归来！不遽惕只。 代秦郑卫，鸣竽张只。 伏戏《驾辩》，楚《劳商》只。 讴和《扬阿》，赵萧倡只。 魂乎归来！定空桑只。 二八接舞，投诗赋只。 叩钟调磬，娱人乱只。 四上竞气，极声变只。 魂乎归来！听歌譔只。 朱唇皓齿，嫭以姱只。 比德好闲，习以都只。 丰肉微骨，调以娱只。 魂乎归来！安以舒只。 嫮目宜笑，娥眉曼只。 容则秀雅，稚朱颜只。 魂乎归来！静以安只。 姱修滂浩，丽以佳只。 曾颊倚耳，曲眉规只。 滂心绰态，姣丽施只。 小腰秀颈，若鲜卑只。 魂乎归来！思怨移只。 易中利心，以动作只。 粉白黛黑，施芳泽只。 长袂拂面，善留客只。 魂乎归来！以娱昔只。 青色直眉，美目媔只。 靥辅奇牙，宜笑嘕只。 丰肉微骨，体便娟只。 魂乎归来！恣所便只。 夏屋广大，沙堂秀只。 南房小坛，观绝霤只。 曲屋步壛，宜扰畜只。 腾驾步游，猎春囿只。 琼轂错衡，英华假只。 茝兰桂树，郁弥路只。 魂乎归来！恣志虑只。 孔雀盈园，畜鸾皇只！ 鵾鸿群晨，杂鶖鸧只。 鸿鹄代游，曼骕驦只。 魂乎归来！凤凰翔只。 曼泽怡面，血气盛只。 永宜厥身，保寿命只。 室家盈廷，爵禄盛只。 魂乎归来！居室定只。 接径千里，出若云只。 三圭重侯，听类神只。 察笃夭隐，孤寡存只。 魂兮归来！正始昆只。 田邑千畛，人阜昌只。 美冒众流，德泽章只。 先威后文，善美明只。 魂乎归来！赏罚当只。 名声若日，照四海只。 德誉配天，万民理只。 北至幽陵，南交阯只。 西薄羊肠，东穷海只。 魂乎归来！尚贤士只。 发政献行，禁苛暴只。 举杰压陛，诛讥罢只。 直赢在位，近禹麾只。 豪杰执政，流泽施只。 魂乎来归！国家为只。 雄雄赫赫，天德明只。 三公穆穆，登降堂只。 诸侯毕极，立九卿只。 昭质既设，大侯张只。 执弓挟矢，揖辞让只。 魂乎来归！尚三王只。', '楚辞');
INSERT INTO `poem` VALUES (1449, '惜誓', '两汉', '贾谊', '惜余年老而日衰兮，岁忽忽而不反。 登苍天而高举兮，历众山而日远。 观江河之纡曲兮，离四海之霑濡。 攀北极而一息兮，吸沆瀣以充虚。 飞朱鸟使先驱兮，驾太一之象舆。 苍龙蚴虯于左骖兮，白虎骋而为右騑。 建日月以为盖兮，载玉女于後车。 驰骛于杳冥之中兮，休息虖昆仑之墟。 乐穷极而不厌兮，愿从容虖神明。 涉丹水而驰骋兮，右大夏之遗风。 黄鹄之一举兮，知山川之纡曲。 再举兮，睹天地之圜方。 临中国之众人兮，讬回飙乎尚羊。 乃至少原之野兮，赤松、王乔皆在旁。 二子拥瑟而调均兮，余因称乎清商。 澹然而自乐兮，吸众气而翱翔。 念我长生而久仙兮，不如反余之故乡。 黄鹄後时而寄处兮，鸱枭群而制之。 神龙失水而陆居兮，为蝼蚁之所裁。 夫黄鹄神龙犹如此兮，况贤者之逢乱世哉。 寿冉冉而日衰兮，固儃回而不息。 俗流从而不止兮，众枉聚而矫直。 或偷合而苟进兮，或隐居而深藏。 苦称量之不审兮，同权概而就衡。 或推迻而苟容兮，或直言之谔謣。 伤诚是之不察兮，并纫茅丝以为索。 方世俗之幽昏兮，眩白黑之美恶。 放山渊之龟玉兮，相与贵夫砾石。 梅伯数谏而至醢兮，来革顺志而用国。 悲仁人之尽节兮，反为小人之所贼。 比干忠谏而剖心兮，箕子被发而佯狂。 水背流而源竭兮，木去根而不长。 非重躯以虑难兮，惜伤身之无功。 已矣哉！ 独不见夫鸾凤之高翔兮，乃集大皇之野。 循四极而回周兮，见盛德而後下。 彼圣人之神德兮，远浊世而自藏。 使麒麟可得羁而係兮，又何以异虖犬羊？', '楚辞');
INSERT INTO `poem` VALUES (1450, '招隐士', '两汉', '淮南小山', '桂树丛生兮山之幽，偃蹇连蜷兮枝相缭。 山气巄嵷兮石嵯峨，溪谷崭岩兮水曾波。 猿狖群啸兮虎豹嗥，攀援桂枝兮聊淹留。 王孙游兮不归，春草生兮萋萋。 岁暮兮不自聊，蟪蛄鸣兮啾啾。 坱兮轧，山曲岪，心淹留兮恫慌忽。 罔兮沕，憭兮栗，虎豹穴。 丛薄深林兮，人上栗。 嵚岑碕礒兮，碅磳磈硊； 树轮相纠兮，林木茷骫。 青莎杂树兮，薠草靃靡； 白鹿麏麚兮，或腾或倚。 状貌崟崟兮峨峨，凄凄兮漇漇。 猕猴兮熊罴，慕类兮以悲； 攀援桂枝兮聊淹留。 虎豹斗兮熊罴咆，禽兽骇兮亡其曹。 王孙兮归来，山中兮不可以久留。', '楚辞');
INSERT INTO `poem` VALUES (1451, '七谏', '两汉', '东方朔', '初放 　　平生于国兮，长于原野。 　　言语讷譅兮，又无彊辅。 　　浅智褊能兮，闻见又寡。 　　数言便事兮，见怨门下。 　　王不察其长利兮，卒见弃乎原野。 　　伏念思过兮，无可改者。 　　群众成朋兮，上浸以惑。 　　巧佞在前兮，贤者灭息。 　　尧、舜圣已没兮，孰为忠直？ 　　高山崔巍兮，水流汤汤。 　　死日将至兮，与麋鹿同坑。 　　塊兮鞠，当道宿， 　　举世皆然兮，余将谁告？ 　　斥逐鸿鹄兮，近习鸱枭， 　　斩伐橘柚兮，列树苦桃。 　　便娟之修竹兮，寄生乎江潭。 　　上葳蕤而防露兮，下泠泠而来风。 　　孰知其不合兮，若竹柏之异心。 　　往者不可及兮，来者不可待。 　　悠悠苍天兮，莫我振理。 　　窃怨君之不寤兮，吾独死而後已。 沉江 　　惟往古之得失兮，览私微之所伤。 　　尧舜圣而慈仁兮，後世称而弗忘。 　　齐桓失于专任兮，夷吾忠而名彰。 　　晋献惑于孋姬兮，申生孝而被殃。 　　偃王行其仁义兮，荆文寤而徐亡。 　　纣暴虐以失位兮，周得佐乎吕望。 　　修往古以行恩兮，封比干之丘垄。 　　贤俊慕而自附兮，日浸淫而合同。 　　明法令而修理兮，兰芷幽而有芳。 　　苦众人之妒予兮，箕子寤而佯狂。 　　不顾地以贪名兮，心怫郁而内伤。 　　联蕙芷以为佩兮，过鲍肆而失香。 　　正臣端其操行兮，反离谤而见攘。 　　世俗更而变化兮，伯夷饿于首阳。 　　独廉洁而不容兮，叔齐久而逾明。 　　浮云陈而蔽晦兮，使日月乎无光。 　　忠臣贞而欲谏兮，谗谀毁而在旁。 　　秋草荣其将实兮，微霜下而夜降。 　　商风肃而害生兮，百草育而不长。 　　众并谐以妒贤兮，孤圣特而易伤。 　　怀计谋而不见用兮，岩穴处而隐藏。 　　成功隳而不卒兮，子胥死而不葬。 　　世从俗而变化兮，随风靡而成行。 　　信直退而毁败兮，虚伪进而得当。 　　追悔过之无及兮，岂尽忠而有功。 　　废制度而不用兮，务行私而去公。 　　终不变而死节兮，惜年齿之未央。 　　将方舟而下流兮，冀幸君之发矇。 　　痛忠言之逆耳兮，恨申子之沉江。 　　愿悉心之所闻兮，遭值君之不聪。 　　不开寤而难道兮，不别横之与纵。 　　听奸臣之浮说兮，绝国家之久长。 　　灭规矩而不用兮，背绳墨之正方。 　　离忧患而乃寤兮，若纵火于秋蓬。 　　业失之而不救兮，尚何论乎祸凶。 　　彼离畔而朋党兮，独行之士其何望？ 　　日渐染而不自知兮，秋毫微哉而变容。 　　众轻积而折轴兮，原咎杂而累重。 　　赴湘沅之流澌兮，恐逐波而复东。 　　怀沙砾而自沉兮，不忍见君之蔽壅。 怨世 　　世沉淖而难论兮，俗岒峨而嵾嵯。 　　清泠泠而歼灭兮，溷湛湛而日多。 　　枭鸮既以成群兮，玄鹤弭翼而屏移。 　　蓬艾亲入御于床笫兮，马兰踸踔而日加。 　　弃捐药芷与杜衡兮，余柰世之不知芳何？ 　　何周道之平易兮，然芜秽而险戏。 　　高阳无故而委尘兮，唐虞点灼而毁议。 　　谁使正其真是兮，虽有八师而不可为。 　　皇天保其高兮，后土持其久。 　　服清白以逍遥兮，偏与乎玄英异色。 　　西施媞媞而不得见兮，嫫母勃屑而日侍。 　　桂蠹不知所淹留兮，蓼虫不知徙乎葵菜。 　　处湣湣之浊世兮，今安所达乎吾志。 　　意有所载而远逝兮，固非众人之所识。 　　骥踌躇于弊輂兮，遇孙阳而得代。 　　吕望穷困而不聊生兮，遭周文而舒志。 　　宁戚饭牛而商歌兮，桓公闻而弗置。 　　路室女之方桑兮，孔子过之以自侍。 　　吾独乖剌而无当兮，心悼怵而耄思。 　　思比干之恲恲兮，哀子胥之慎事。 　　悲楚人之和氏兮，献宝玉以为石。 　　遇厉武之不察兮，羌两足以毕斮。 　　小人之居势兮，视忠正之何若？ 　　改前圣之法度兮，喜嗫嚅而妄作。 　　亲谗谀而疏贤圣兮，讼谓闾娵为丑恶。 　　愉近习而蔽远兮，孰知察其黑白？ 　　卒不得效其心容兮，安眇眇而无所归薄。 　　专精爽以自明兮，晦冥冥而壅蔽。 　　年既已过太半兮，然埳轲而留滞。 　　欲高飞而远集兮，恐离罔而灭败。 　　独冤抑而无极兮，伤精神而寿夭。 　　皇天既不纯命兮，余生终无所依。 　　愿自沉于江流兮，绝横流而径逝。 　　宁为江海之泥涂兮，安能久见此浊世？ 怨思 　　贤士穷而隐处兮，廉方正而不容。 　　子胥谏而靡躯兮，比干忠而剖心。 　　子推自割而飤君兮，德日忘而怨深。 　　行明白而曰黑兮，荆棘聚而成林。 　　江离弃于穷巷兮，蒺藜蔓乎东厢。 　　贤者蔽而不见兮，谗谀进而相朋。 　　枭鸮并进而俱鸣兮，凤皇飞而高翔。 　　原壹往而径逝兮，道壅绝而不通。 自悲 　　居愁懃其谁告兮，独永思而忧悲。 　　内自省而不惭兮，操愈坚而不衰。 　　隐三年而无决兮，岁忽忽其若颓。 　　怜余身不足以卒意兮，冀一见而复归。 　　哀人事之不幸兮，属天命而委之咸池。 　　身被疾而不闲兮，心沸热其若汤。 　　冰炭不可以相并兮，吾固知乎命之不长。 　　哀独苦死之无乐兮，惜予年之未央。 　　悲不反余之所居兮，恨离予之故乡。 　　鸟兽惊而失群兮，犹高飞而哀鸣。 　　狐死必首丘兮，夫人孰能不反其真情？ 　　故人疏而日忘兮，新人近而俞好。 　　莫能行于杳冥兮，孰能施于无报？ 　　苦众人之皆然兮，乘回风而远游。 　　凌恆山其若陋兮，聊愉娱以忘忧。 　　悲虚言之无实兮，苦众口之铄金。 　　过故乡而一顾兮，泣歔欷而霑衿。 　　厌白玉以为面兮，怀琬琰以为心。 　　邪气入而感内兮，施玉色而外淫。 　　何青云之流澜兮，微霜降之蒙蒙。 　　徐风至而徘徊兮，疾风过之汤汤。 　　闻南籓乐而欲往兮，至会稽而且止。 　　见韩众而宿之兮，问天道之所在？ 　　借浮云以送予兮，载雌霓而为旌。 　　驾青龙以驰骛兮，班衍衍之冥冥。 　　忽容容其安之兮，超慌忽其焉如？ 　　苦众人之难信兮，愿离群而远举。 　　登峦山而远望兮，好桂树之冬荣。 　　观天火之炎炀兮，听大壑之波声。 　　引八维以自道兮，含沆瀣以长生。 　　居不乐以时思兮，食草木之秋实。 　　饮菌若之朝露兮，构桂木而为室。 　　杂橘柚以为囿兮，列新夷与椒桢。 　　鹍鹤孤而夜号兮，哀居者之诚贞。 哀命 　　哀时命之不合兮，伤楚国之多忧。 　　内怀情之洁白兮，遭乱世而离尤。 　　恶耿介之直行兮，世溷浊而不知。 　　何君臣之相失兮，上沅湘而分离。 　　测汨罗之湘水兮，知时固而不反。 　　伤离散之交乱兮，遂侧身而既远。 　　处玄舍之幽门兮，穴岩石而窟伏。 　　从水蛟而为徙兮，与神龙乎休息。 　　何山石之崭岩兮，灵魂屈而偃蹇。 　　含素水而蒙深兮，日眇眇而既远。 　　哀形体之离解兮，神罔两而无舍。 　　惟椒兰之不反兮，魂迷惑而不知路。 　　愿无过之设行兮，虽灭没之自乐。 　　痛楚国之流亡兮，哀灵修之过到。 　　固时俗之溷浊兮，志瞀迷而不知路。 　　念私门之正匠兮，遥涉江而远去。 　　念女嬃之婵媛兮，涕泣流乎于悒。 　　我决死而不生兮，虽重追吾何及。 　　戏疾濑之素水兮，望高山之蹇产。 　　哀高丘之赤岸兮，遂没身而不反。 谬谏 　　怨灵修之浩荡兮，夫何执操之不固？ 　　悲太山之为隍兮，孰江河之可涸？ 　　愿承闲而效志兮，恐犯忌而干讳。 　　卒抚情以寂寞兮，然怊怅而自悲。 　　玉与石其同匮兮，贯鱼眼与珠玑。 　　驽骏杂而不分兮，服罢牛而骖骥。 　　年滔滔而自远兮，寿冉冉而愈衰。 　　心悇憛而烦冤兮，蹇超摇而无冀。 　　固时俗之工巧兮，灭规矩而改错。 　　郤骐骥而不乘兮，策驽骀而取路。 　　当世岂无骐骥兮，诚无王良之善驭。 　　见执辔者非其人兮，故驹跳而远去。 　　不量凿而正枘兮，恐矩矱之不同。 　　不论世而高举兮，恐操行之不调。 　　弧弓弛而不张兮，孰云知其所至？ 　　无倾危之患难兮，焉知贤士之所死？ 　　俗推佞而进富兮，节行张而不著。 　　贤良蔽而不群兮，朋曹比而党誉。 　　邪说饰而多曲兮，正法弧而不公。 　　直士隐而避匿兮，谗谀登乎明堂。 　　弃彭咸之娱乐兮，灭巧倕之绳墨。 　　菎蕗杂于黀蒸兮，机蓬矢以射革。 　　驾蹇驴而无策兮，又何路之能极？ 　　以直鍼而为钓兮，又何鱼之能得？ 　　伯牙之绝弦兮，无锺子期而听之。 　　和抱璞而泣血兮，安得良工而剖之？ 　　同音者相和兮，同类者相似。 　　飞鸟号其群兮，鹿鸣求其友。 　　故叩宫而宫应兮，弹角而角动。 　　虎啸而谷风至兮，龙举而景云往。 　　音声之相和兮，言物类之相感也。 　　夫方圜之异形兮，势不可以相错。 　　列子隐身而穷处兮，世莫可以寄讬。 　　众鸟皆有行列兮，凤独翔翔而无所薄。 　　经浊世而不得志兮，愿侧身岩穴而自讬。 　　欲阖口而无言兮，尝被君之厚德。 　　独便悁而怀毒兮，愁郁郁之焉极？ 　　念三年之积思兮，愿壹见而陈辞。 　　不及君而骋说兮，世孰可为明之？ 　　身寝疾而日愁兮，情沉抑而不扬。 　　众人莫可与论道兮，悲精神之不通。 　　乱曰： 　　鸾皇孔凤日以远兮，畜凫驾鹅。 　　鸡鹜满堂坛兮，鼉黽游乎华池。 　　要褭奔亡兮，腾驾橐驼。 　　铅刀进御兮，遥弃太阿。 　　拔搴玄芝兮，列树芋荷。 　　橘柚萎枯兮，苦李旖旎。 　　甂瓯登于明堂兮，周鼎潜潜乎深渊。 　　自古而固然兮，吾又何怨乎今之人。', '楚辞');
INSERT INTO `poem` VALUES (1452, '哀时命', '先秦', '庄忌', '哀时命之不及古人兮，夫何予生之不遘时！ 往者不可扳援兮，徠者不可与期。 志憾恨而不逞兮，杼中情而属诗。 夜炯炯而不寐兮，怀隐忧而历兹。 心郁郁而无告兮，众孰可与深谋！ 欿愁悴而委惰兮，老冉冉而逮之。 居处愁以隐约兮，志沉抑而不扬。 道壅塞而不通兮，江河广而无梁。 愿至昆仑之悬圃兮，采锺山之玉英。 揽瑶木之橝枝兮，望阆风之板桐。 弱水汩其为难兮，路中断而不通。 势不能凌波以径度兮，又无羽翼而高翔。 然隐悯而不达兮，独徙倚而彷徉。 怅惝罔以永思兮，心纡轸而增伤。 倚踌躇以淹留兮，日饥馑而绝粮。 廓抱景而独倚兮，超永思乎故乡。 廓落寂而无友兮，谁可与玩此遗芳？ 白日晼晼其將入兮，哀余寿之弗将。 车既弊而马罢兮，蹇邅徊而不能行。 身既不容于浊世兮，不知进退之宜当。 冠崔嵬而切云兮，剑淋离而从横。 衣摄叶以储与兮，左袪挂于榑桑； 右衽拂于不周兮，六合不足以肆行。 上同凿枘于伏戏兮，下合矩矱于虞唐。 原尊节而式高兮，志犹卑夫禹汤。 虽知困其不改操兮，终不以邪枉害方。 世并举而好朋兮，壹斗斛而相量。 众比周以肩迫兮，贤者远而隐藏。 为凤皇作鹑笼兮，虽翕翅其不容。 灵皇其不寤知兮，焉陈词而效忠。 俗嫉妒而蔽贤兮，孰知余之从容？ 愿舒志而抽冯兮，庸讵知其吉凶？ 璋珪杂于甑窐兮，陇廉与孟娵同宫。 举世以为恆俗兮，固将愁苦而终穷。 幽独转而不寐兮，惟烦懑而盈匈。 魂眇眇而驰骋兮，心烦冤之忡忡。 志欿憾而不憺兮，路幽昧而甚难。 塊独守此曲隅兮，然欿切而永叹。 愁修夜而宛转兮，气涫沸其若波。 握剞劂而不用兮，操规矩而无所施。 骋骐骥于中庭兮，焉能极夫远道？ 置援狖于棂槛兮，夫何以责其捷巧？ 驷跛鳖而上山兮，吾固知其不能陞。 释管晏而任臧获兮，何权衡之能称？ 箟簬杂于黀蒸兮，机蓬矢以射革。 负檐荷以丈尺兮，欲伸要而不可得。 外迫胁于机臂兮，上牵联于矰隿。 肩倾侧而不容兮，固陿腹而不得息。 务光自投于深渊兮，不获世之尘垢。 孰魁摧之可久兮，愿退身而穷处。 凿山楹而为室兮，下被衣于水渚。 雾露濛濛其晨降兮，云依斐而承宇。 虹霓纷其朝霞兮，夕淫淫而淋雨。 怊茫茫而无归兮，怅远望此旷野。 下垂钓于溪谷兮，上要求于仙者。 与赤松而结友兮，比王侨而为耦。 使枭杨先导兮，白虎为之前後。 浮云雾而入冥兮，骑白鹿而容与。 魂眐眐以寄独兮，汨徂往而不归。 处卓卓而日远兮，志浩荡而伤怀。 鸾凤翔于苍云兮，故矰缴而不能加。 蛟龙潜于旋渊兮，身不挂于罔罗。 知贪饵而近死兮，不如下游乎清波。 宁幽隐以远祸兮，孰侵辱之可为。 子胥死而成义兮，屈原沉于汨罗。 虽体解其不变兮，岂忠信之可化。 志怦怦而内直兮，履绳墨而不颇。 执权衡而无私兮，称轻重而不差。 摡尘垢之枉攘兮，除秽累而反真。 形体白而质素兮，中皎洁而淑清。 时猒饫而不用兮，且隐伏而远身。 聊窜端而匿迹兮，嗼寂默而无声。 独便悁而烦毒兮，焉发愤而筊抒。 时暧暧其将罢兮，遂闷叹而无名。 伯夷死于首阳兮，卒夭隐而不荣。 太公不遇文王兮，身至死而不得逞。 怀瑶象而佩琼兮，愿陈列而无正。 生天坠之若过兮，忽烂漫而无成。 邪气袭余之形体兮，疾憯怛而萌生。 原壹见阳春之白日兮，恐不终乎永年。', '楚辞');
INSERT INTO `poem` VALUES (1453, '九怀', '两汉', '王褒', '匡机 　　极运兮不中，来将屈兮困穷。 　　余深愍兮惨怛，愿一列兮无从。 　　乘日月兮上征，顾游心兮鄗酆。 　　弥览兮九隅，彷徨兮兰宫。 　　芷闾兮药房，奋摇兮众芳。 　　菌阁兮蕙楼，观道兮从横。 　　宝金兮委积，美玉兮盈堂。 　　桂水兮潺湲，扬流兮洋洋。 　　蓍蔡兮踊跃，孔鹤兮回翔。 　　抚槛兮远望，念君兮不忘。 　　怫郁兮莫陈，永怀兮内伤。 通路 　　天门兮墬户，孰由兮贤者？ 　　无正兮溷厕，怀德兮何睹？ 　　假寐兮愍斯，谁可与兮寤语？ 　　痛凤兮远逝，畜鴳兮近处。 　　鲸鱏兮幽潜，从虾兮游陼。 　　乘虬兮登阳，载象兮上行。 　　朝发兮葱岭，夕至兮明光。 　　北饮兮飞泉，南采兮芝英。 　　宣游兮列宿，顺极兮彷徉。 　　红采兮骍衣，翠缥兮为裳。 　　舒佩兮綝纚，竦余剑兮干将。 　　腾蛇兮后从，飞駏兮步旁。 　　微观兮玄圃，览察兮瑶光。 　　启匮兮探筴，悲命兮相当。 　　纫蕙兮永辞，将离兮所思。 　　浮云兮容与，道余兮何之？ 　　远望兮仟眠，闻雷兮阗阗。 　　阴忧兮感余，惆怅兮自怜。 危俊 　　林不容兮鸣蜩，余何留兮中州？ 　　陶嘉月兮总驾，搴玉英兮自修。 　　结荣茝兮逶逝，将去烝兮远游。 　　径岱土兮魏阙，历九曲兮牵牛。 　　聊假日兮相佯，遗光燿兮周流。 　　望太一兮淹息，纡余辔兮自休。 　　晞白日兮皎皎，弥远路兮悠悠。 　　顾列孛兮缥缥，观幽云兮陈浮。 　　钜宝迁兮砏磤，雉咸雊兮相求。 　　泱莽莽兮究志，惧吾心兮懤懤。 　　步余马兮飞柱，览可与兮匹俦。 　　卒莫有兮纤介，永余思兮怞怞。 昭世 　　世溷兮冥昏，违君兮归真。 　　乘龙兮偃蹇，高回翔兮上臻。 　　袭英衣兮缇[糹習]，披华裳兮芳芬。 　　登羊角兮扶舆，浮云漠兮自娱。 　　握神精兮雍容，与神人兮相胥。 　　流星坠兮成雨，进瞵盼兮上丘墟。 　　览旧邦兮滃郁，余安能兮久居。 　　志怀逝兮心懰栗，纡余辔兮踌躇。 　　闻素女兮微歌，听王后兮吹竽。 　　魂悽怆兮感哀，肠回回兮盘纡。 　　抚余佩兮缤纷，高太息兮自怜。 　　使祝融兮先行，令昭明兮开门。 　　驰六蛟兮上征，竦余驾兮入冥。 　　历九州兮索合，谁可与兮终生。 　　忽反顾兮西囿，睹轸丘兮崎倾。 　　横垂涕兮泫流，悲余后兮失灵。 尊嘉 　　季春兮阳阳，列草兮成行。 　　余悲兮兰生，委积兮从横。 　　江离兮遗捐，辛夷兮挤臧。 　　伊思兮往古，亦多兮遭殃。 　　伍胥兮浮江，屈子兮沉湘。 　　运余兮念兹，心内兮怀伤。 　　望淮兮沛沛，滨流兮则逝。 　　榜舫兮下流，东注兮磕磕。 　　蛟龙兮导引，文鱼兮上濑。 　　抽蒲兮陈坐，援芙蕖兮为盖。 　　水跃兮余旌，继以兮微蔡。 　　云旗兮电骛，倏忽兮容裔。 　　河伯兮开门，迎余兮欢欣。 　　顾念兮旧都，怀恨兮艰难。 　　窃哀兮浮萍，汎淫兮无根。 蓄英 　　秋风兮萧萧，舒芳兮振条。 　　微霜兮眇眇，病殀兮鸣蜩。 　　玄鸟兮辞归，飞翔兮灵丘。 　　望谿谷兮滃郁，熊罴兮呴嗥。 　　唐虞兮不存，何故兮久留？ 　　临渊兮汪洋，顾林兮忽荒。 　　修余兮袿衣，骑霓兮南上。 　　乘云兮回回，亹亹兮自强。 　　将息兮兰皋，失志兮悠悠。 　　蒶蕴兮霉黧，思君兮无聊。 　　身去兮意存，怆恨兮怀愁。 思忠 　　登九灵兮游神，静女歌兮微晨。 　　悲皇丘兮积葛，众体错兮交纷。 　　贞枝抑兮枯槁，枉车登兮庆云。 　　感余志兮惨栗，心怆怆兮自怜。 　　驾玄螭兮北征，曏吾路兮葱岭。 　　连五宿兮建旄，扬氛气兮为旌。 　　历广漠兮驰骛，览中国兮冥冥。 　　玄武步兮水母，与吾期兮南荣。 　　登华盖兮乘阳，聊逍遥兮播光。 　　抽库娄兮酌醴，援瓟瓜兮接粮。 　　毕休息兮远逝，发玉軔兮西行。 　　惟时俗兮疾正，弗可久兮此方。 　　寤辟摽兮永思，心怫郁兮内伤。 陶壅 　　览杳杳兮世惟，余惆怅兮何归。 　　伤时俗兮溷乱，将奋翼兮高飞。 　　驾八龙兮连蜷，建虹旌兮威夷。 　　观中宇兮浩浩，纷翼翼兮上跻。 　　浮溺水兮舒光，淹低佪兮京沶。 　　屯余车兮索友，睹皇公兮问师。 　　道莫贵兮归真，羡余术兮可夷。 　　吾乃逝兮南娭，道幽路兮九疑。 　　越炎火兮万里，过万首兮嶷嶷。 　　济江海兮蝉蜕，绝北梁兮永辞。 　　浮云郁兮昼昏，霾土忽兮塺々。 　　息阳城兮广夏，衰色罔兮中怠。 　　意晓阳兮燎寤，乃自诊兮在兹。 　　思尧舜兮袭兴，幸咎繇兮获谋。 　　悲九州兮靡君，抚轼叹兮作诗。 株昭 　　悲哉于嗟兮，心内切磋。 　　款冬而生兮，凋彼叶柯。 　　瓦砾进宝兮，捐弃随和。 　　铅刀厉御兮，顿弃太阿。 　　骥垂两耳兮，中坂蹉跎。 　　蹇驴服驾兮，无用日多。 　　修洁处幽兮，贵宠沙劘。 　　凤皇不翔兮，鹑鴳飞扬。 　　乘虹骖蜺兮，载云变化。 　　焦明开路兮，后属青蛇。 　　步骤桂林兮，超骧卷阿。 　　丘陵翔儛兮，谿谷悲歌。 　　神章灵篇兮，赴曲相和。 　　余私娱兹兮，孰哉复加。 　　还顾世俗兮，坏败罔罗。 　　卷佩将逝兮，涕流滂沲。 　　乱曰： 　　皇门开兮照下土，株秽除兮兰芷睹。 　　四佞放兮後得禹，圣舜摄兮昭尧绪， 　　孰能若兮原为辅。', '楚辞');
INSERT INTO `poem` VALUES (1454, '九叹', '两汉', '刘向', '逢纷 　　伊伯庸之末胄兮，谅皇直之屈原。 　　云余肇祖于高阳兮，惟楚怀之婵连。 　　原生受命于贞节兮，鸿永路有嘉名。 　　齐名字于天地兮，并光明于列星。 　　吸精粹而吐氛浊兮，横邪世而不取容。 　　行叩诚而不阿兮，遂见排而逢谗。 　　后听虚而黜实兮，不吾理而顺情。 　　肠愤悁而含怒兮，志迁蹇而左倾。 　　心戃慌其不我与兮，躬速速其不吾亲。 　　辞灵修而陨志兮，吟泽畔之江滨。 　　椒桂罗以颠覆兮，有竭信而归诚。 　　谗夫蔼蔼而漫著兮，曷其不舒予情？ 　　始结言于庙堂兮，信中涂而叛之。 　　怀兰蕙与衡芷兮，行中野而散之。 　　声哀哀而怀高丘兮，心愁愁而思旧邦。 　　愿承闲而自恃兮，径淫曀而道壅。 　　颜霉黧以沮败兮，精越裂而衰耄。 　　裳襜襜而含风兮，衣纳纳而掩露。 　　赴江湘之湍流兮，顺波凑而下降。 　　徐徘徊于山阿兮，飘风来之洶洶。 　　驰余车兮玄石，步余马兮洞庭。 　　平明发兮苍梧，夕投宿兮石城。 　　芙蓉盖而菱华车兮，紫贝阙而玉堂。 　　薜荔饰而陆离荐兮，鱼鳞衣而白蜺裳。 　　登逢龙而下陨兮，违故都之漫漫。 　　思南郢之旧俗兮，肠一夕而九运。 　　扬流波之潢潢兮，体溶溶而东回。 　　心怊怅以永思兮，意晻晻而日颓。 　　白露纷以涂涂兮，秋风浏以萧萧。 　　身永流而不还兮，魂长逝而常愁。 　　叹曰： 　　譬彼流水纷扬磕兮，波逢汹涌濆壅滂兮。 　　揄扬涤荡飘流陨往触崟石兮， 　　龙卬脟圈缭戾宛转阻相薄兮， 　　遭纷逢凶蹇离尤兮，垂文扬采遗将来兮。 离世 　　灵怀其不吾知兮，灵怀其不吾闻。 　　就灵怀之皇祖兮，愬灵怀之鬼神。 　　灵怀曾不吾与兮，即听夫人之谀辞。 　　余辞上参于天坠兮，旁引之于四时。 　　指日月使延照兮，抚招摇以质正。 　　立师旷俾端辞兮，命咎繇使并听。 　　兆出名曰正则兮，卦发字曰灵均。 　　余幼既有此鸿节兮，长愈固而弥纯。 　　不从俗而诐行兮，直躬指而信志。 　　不枉绳以追曲兮，屈情素以从事。 　　端余行其如玉兮，述皇舆之踵迹。 　　群阿容以晦光兮，皇舆覆以幽辟。 　　舆中涂以回畔兮，驷马惊而横奔。 　　执组者不能制兮，必折轭而摧辕。 　　断镳衔以驰骛兮，暮去次而敢止。 　　路荡荡其无人兮，遂不禦乎千里。 　　身衡陷而下沉兮，不可获而复登。 　　不顾身之卑贱兮，惜皇舆之不兴。 　　出国门而端指兮，冀壹寤而锡还。 　　哀仆夫之坎毒兮，屡离忧而逢患。 　　九年之中不吾反兮，思彭咸之水游。 　　惜师延之浮渚兮，赴汨罗之长流。 　　遵江曲之逶移兮，触石碕而衡游。 　　波澧澧而扬浇兮，顺长濑之浊流。 　　凌黄沱而下低兮，思还流而复反。 　　玄舆驰而并集兮，身容与而日远。 　　棹舟杭以横濿兮，济湘流而南极。 　　立江界而长吟兮，愁哀哀而累息。 　　情慌忽以忘归兮，神浮游以高历。 　　心蛩蛩而怀顾兮，魂眷眷而独逝。 　　叹曰： 　　余思旧邦心依违兮， 　　日暮黄昏羌幽悲兮， 　　去郢东迁余谁慕兮， 　　谗夫党旅其以兹故兮， 　　河水淫淫情所愿兮， 　　顾瞻郢路终不返兮。 怨思 　　惟郁郁之忧毒兮，志坎壈而不违。 　　身憔悴而考旦兮，日黄昏而长悲。 　　闵空宇之孤子兮，哀枯杨之冤雏。 　　孤雌吟于高墉兮，鸣鸠栖于桑榆。 　　玄蝯失于潜林兮，独偏弃而远放。 　　征夫劳于周行兮，处妇愤而长望。 　　申诚信而罔违兮，情素洁于纽帛。 　　光明齐于日月兮，文采耀燿于玉石。 　　伤压次而不发兮，思沉抑而不扬。 　　芳懿懿而终败兮，名靡散而不彰。 　　背玉门以奔骛兮，蹇离尤而干诟。 　　若龙逢之沉首兮，王子比干之逢醢。 　　念社稷之几危兮，反为雠而见怨。 　　思国家之离沮兮，躬获愆而结难。 　　若青蝇之伪质兮，晋骊姬之反情。 　　恐登阶之逢殆兮，故退伏于末庭。 　　孽臣之号咷兮，本朝芜而不治。 　　犯颜色而触谏兮，反蒙辜而被疑。 　　菀蘼芜与菌若兮，渐藁本于洿渎。 　　淹芳芷于腐井兮，弃鸡骇于筐簏。 　　执棠谿以刜蓬兮，秉干将以割肉。 　　筐泽泻以豹鞟兮，破荆和以继筑。 　　时溷浊犹未清兮，世殽乱犹未察。 　　欲容与以俟时兮，惧年岁之既晏。 　　顾屈节以从流兮，心巩巩而不夷。 　　宁浮沅而驰骋兮，下江湘以邅回。 　　叹曰： 　　山中槛槛余伤怀兮，征夫皇皇其孰依兮， 　　经营原野杳冥冥兮，乘骐骋骥舒吾情兮， 　　归骸旧邦莫谁语兮，长辞远逝乘湘去兮。 远逝 　　志隐隐而郁怫兮，愁独哀而冤结。 　　肠纷纭以缭转兮，涕渐渐其若屑。 　　情慨慨而长怀兮，信上皇而质正。 　　合五岳与八灵兮，讯九鬿与六神。 　　指列宿以白情兮，诉五帝以置辞。 　　北斗为我折中兮，太一为余听之。 　　云服阴阳之正道兮，御后土之中和。 　　佩苍龙之蚴虬兮，带隐虹之逶蛇。 　　曳彗星之皓旰兮，抚朱爵与鵔鸃。 　　游清灵之飒戾兮，服云衣之披披。 　　杖玉策与朱旗兮，垂明月之玄珠。 　　举霓旌之墆翳兮，建黄纁之总旄。 　　躬纯粹而罔愆兮，承皇考之妙仪。 　　惜往事之不合兮，横汨罗而下沥。 　　乘隆波而南渡兮，逐江湘之顺流。 　　赴阳侯之潢洋兮，下石濑而登洲。 　　陆魁堆以蔽视兮，云冥冥而闇前。 　　山峻高以无垠兮，遂曾闳而迫身。 　　雪雰雰而薄木兮，云霏霏而陨集。 　　阜隘狭而幽险兮，石嵾嵯以翳日。 　　悲故乡而发忿兮，去余邦之弥久。 　　背龙门而入河兮，登大坟而望夏首。 　　横舟航而济湘兮，耳聊啾而戃慌。 　　波淫淫而周流兮，鸿溶溢而滔荡。 　　路曼曼其无端兮，周容容而无识。 　　引日月以指极兮，少须臾而释思。 　　水波远以冥冥兮，眇不睹其东西。 　　顺风波以南北兮，雾宵晦以纷纷。 　　日杳杳以西颓兮，路长远而窘迫。 　　欲酌醴以娱忧兮，蹇骚骚而不释。 　　叹曰： 　　飘风蓬龙埃坲々兮，草木摇落时槁悴兮， 　　遭倾遇祸不可救兮，长吟永欷涕究究兮， 　　舒情陈诗冀以自免兮，颓流下陨身日远兮。 惜贤 　　览屈氏之离骚兮，心哀哀而怫郁。 　　声嗷嗷以寂寥兮，顾仆夫之憔悴。 　　拨谄谀而匡邪兮，切淟涊之流俗。 　　荡渨涹之奸咎兮，夷蠢蠢之溷浊。 　　怀芬香而挟蕙兮，佩江蓠之婓婓。 　　握申椒与杜若兮，冠浮云之峨峨。 　　登长陵而四望兮，览芷圃之蠡蠡。 　　游兰皋与蕙林兮，睨玉石之嵾嵯。 　　扬精华以炫燿兮，芳郁渥而纯美。 　　结桂树之旖旎兮，纫荃蕙与辛夷。 　　芳若兹而不御兮，捐林薄而菀死。 　　驱子侨之奔走兮，申徒狄之赴渊。 　　若由夷之纯美兮，介子推之隐山。 　　晋申生之离殃兮，荆和氏之泣血。 　　吴申胥之抉眼兮，王子比干之横废。 　　欲卑身而下体兮，心隐恻而不置。 　　方圜殊而不合兮，钩绳用而异态。 　　欲俟时于须臾兮，日阴曀其将暮。 　　时迟迟其日进兮，年忽忽而日度。 　　妄周容而入世兮，内距闭而不开。 　　俟时风之清激兮，愈氛雾其如塺。 　　进雄鸠之耿耿兮，谗介介而蔽之。 　　默顺风以偃仰兮，尚由由而进之。 　　心懭悢以冤结兮，情舛错以曼忧。 　　搴薜荔于山野兮，采撚支于中洲。 　　望高丘而叹涕兮，悲吸吸而长怀。 　　孰契契而委栋兮，日晻晻而下颓。 　　叹曰： 　　江湘油油长流汩兮，挑揄扬汰荡迅疾兮。 　　忧心展转愁怫郁兮，冤结未舒长隐忿兮， 　　丁时逢殃可奈何兮，劳心悁悁涕滂沱兮。 忧苦 　　悲余心之悁悁兮，哀故邦之逢殃。 　　辞九年而不复兮，独茕茕而南行。 　　思余俗之流风兮，心纷错而不受。 　　遵野莽以呼风兮，步从容于山廋。 　　巡陆夷之曲衍兮，幽空虚以寂寞。 　　倚石岩以流涕兮，忧憔悴而无乐。 　　登巑岏以长企兮，望南郢而闚之。 　　山修远其辽辽兮，涂漫漫其无时。 　　听玄鹤之晨鸣兮，于高冈之峨峨。 　　独愤积而哀娱兮，翔江洲而安歌。 　　三鸟飞以自南兮，览其志而欲北。 　　原寄言于三鸟兮，去飘疾而不可得。 　　欲迁志而改操兮，心纷结其未离。 　　外彷徨而游览兮，内恻隐而含哀。 　　聊须臾以时忘兮，心渐渐其烦错。 　　原假簧以舒忧兮，志纡郁其难释。 　　叹《离骚》以扬意兮，犹未殫于《九章》。 　　长嘘吸以于悒兮，涕横集而成行。 　　伤明珠之赴泥兮，鱼眼玑之坚藏。 　　同驽骡与乘駔兮，杂斑駮与阘茸。 　　葛藟虆于桂树兮，鸱鸮集于木兰。 　　偓促谈于廊庙兮，律魁放乎山间。 　　恶虞氏之箫《韶》兮，好遗风之《激楚》。 　　潜周鼎于江淮兮，爨土鬵于中宇。 　　且人心之持旧兮，而不可保长。 　　邅彼南道兮，征夫宵行。 　　思念郢路兮，还顾睠睠。 　　涕流交集兮，泣下涟涟。 　　叹曰： 　　登山长望中心悲兮，菀彼青青泣如颓兮， 　　留思北顾涕渐渐兮，折锐摧矜凝氾滥兮， 　　念我茕茕魂谁求兮，仆夫慌悴散若流兮。 愍命 　　昔皇考之嘉志兮，喜登能而亮贤。 　　情纯洁而罔薉兮，姿盛质而无愆。 　　放佞人与谄谀兮，斥谗夫与便嬖。 　　亲忠正之悃诚兮，招贞良与明智。 　　心溶溶其不可量兮，情澹澹其若渊。 　　回邪辟而不能入兮，诚原藏而不可迁。 　　逐下袟于後堂兮，迎虙妃于伊雒。 　　刜谗贼于中廇兮，选吕管于榛薄。 　　丛林之下无怨士兮，江河之畔无隐夫。 　　三苗之徒以放逐兮，伊皋之伦以充庐。 　　今反表以为里兮，颠裳以为衣。 　　戚宋万于两楹兮，废周邵于遐夷。 　　却骐骥以转运兮，腾驴骡以驰逐。 　　蔡女黜而出帷兮，戎妇入而綵绣服。 　　庆忌囚于阱室兮，陈不占战而赴围。 　　破伯牙之号钟兮，挟人筝而弹纬。 　　藏瑉石于金匮兮，捐赤瑾于中庭。 　　韩信蒙于介胄兮，行夫将而攻城。 　　莞芎弃于泽洲兮，瓟瓥蠹于筐簏。 　　麒麟奔于九皋兮，熊罴群而逸囿。 　　折芳枝与琼华兮，树枳棘与薪柴。 　　掘荃蕙与射干兮，耘藜藿与蘘荷。 　　惜今世其何殊兮，远近思而不同。 　　或沉沦其无所达兮，或清激其无所通。 　　哀余生之不当兮，独蒙毒而逢尤。 　　虽謇謇以申志兮，君乖差而屏之。 　　诚惜芳之菲菲兮，反以兹为腐也。 　　怀椒聊之蔎蔎兮，乃逢纷以罹诟也。 　　叹曰： 　　嘉皇既殁终不返兮，山中幽险郢路远兮。 　　谗人諓諓孰可愬兮，征夫罔极谁可语兮。 　　行吟累欷声喟喟兮，怀忧含戚何侘傺兮。 思古 　　冥冥深林兮，树木郁郁。 　　山参差以崭岩兮，阜杳杳以蔽日。 　　悲余心之悁悁兮，目眇眇而遗泣。 　　风骚屑以摇木兮，云吸吸以湫戾。 　　悲余生之无欢兮，愁倥傯于山陆。 　　旦徘徊于长阪兮，夕彷徨而独宿。 　　发披披以鬤鬤兮，躬劬劳而瘏悴。 　　魂俇俇而南行兮，泣霑襟而濡袂。 　　心婵媛而无告兮，口噤闭而不言。 　　违郢都之旧闾兮，回湘、沅而远迁。 　　念余邦之横陷兮，宗鬼神之无次。 　　闵先嗣之中绝兮，心惶惑而自悲。 　　聊浮游于山陿兮，步周流于江畔。 　　临深水而长啸兮，且倘佯而氾观。 　　兴离骚之微文兮，冀灵修之壹悟。 　　还余车于南郢兮，复往轨于初古。 　　道修远其难迁兮，伤余心之不能已。 　　背三五之典刑兮，绝洪范之辟纪。 　　播规矩以背度兮，错权衡而任意。 　　操绳墨而放弃兮，倾容幸而侍侧。 　　甘棠枯于丰草兮，藜棘树于中庭。 　　西施斥于北宫兮，仳倠倚于弥楹。 　　乌获戚而骖乘兮，燕公操于马圉。 　　蒯聩登于清府兮，咎繇弃而在野。 　　盖见兹以永叹兮，欲登阶而狐疑。 　　乘白水而高骛兮，因徙弛而长词。 　　叹曰： 　　倘佯垆阪沼水深兮，容与汉渚涕淫淫兮， 　　锺牙已死谁为声兮？纤阿不御焉舒情兮， 　　曾哀悽欷心离离兮，还顾高丘泣如洒兮。 远游 　　悲余性之不可改兮，屡惩艾而不迻。 　　服觉皓以殊俗兮，貌揭揭以巍巍。 　　譬若王侨之乘云兮，载赤霄而凌太清。 　　欲与天地参寿兮，与日月而比荣。 　　登昆仑而北首兮，悉灵圉而来谒。 　　选鬼神于太阴兮，登阊阖于玄阙。 　　回朕车俾西引兮，褰虹旗于玉门。 　　驰六龙于三危兮，朝西灵于九滨。 　　结余轸于西山兮，横飞谷以南征。 　　绝都广以直指兮，历祝融于硃冥。 　　枉玉衡于炎火兮，委两馆于咸唐。 　　贯澒濛以东朅兮，维六龙于扶桑。 　　周流览于四海兮，志升降以高驰。 　　徵九神于回极兮，建虹采以招指。 　　驾鸾凤以上游兮，从玄鹤与鹪明。 　　孔鸟飞而送迎兮，腾群鹤于瑶光。 　　排帝宫与罗囿兮，升县圃以眩灭。 　　结琼枝以杂佩兮，立长庚以继日。 　　凌惊雷以轶骇电兮，缀鬼谷于北辰。 　　鞭风伯使先驱兮，囚灵玄于虞渊。 　　遡高风以低佪兮，览周流于朔方。 　　就颛顼而敶辞兮，考玄冥于空桑。 　　旋车逝于崇山兮，奏虞舜于苍梧。 　　济杨舟于会稽兮，就申胥于五湖。 　　见南郢之流风兮，殒余躬于沅湘。 　　望旧邦之黯黮兮，时溷浊其犹未央。 　　怀兰茝之芬芳兮，妒被离而折之。 　　张绛帷以襜襜兮，风邑邑而蔽之。 　　日暾暾其西舍兮，阳焱焱而复顾。 　　聊假日以须臾兮，何骚骚而自故。 　　叹曰： 　　譬彼蛟龙乘云浮兮， 　　汎淫澒溶纷若雾兮。 　　潺湲轇轕雷动电发馺高举兮。 　　升虚凌冥沛浊浮清入帝宫兮， 　　摇翘奋羽驰风骋雨游无穷兮。', '楚辞');
INSERT INTO `poem` VALUES (1455, '九思', '两汉', '王逸', '逢尤 　　悲兮愁，哀兮忧！ 　　天生我兮当闇时，被诼谮兮虚获尤。 　　心烦憒兮意无聊，严载驾兮出戏游。 　　周八极兮历九州，求轩辕兮索重华。 　　世既卓兮远眇眇，握佩玖兮中路躇。 　　羡咎繇兮建典谟，懿风后兮受瑞图。 　　愍余命兮遭六极，委玉质兮於泥涂。 　　遽傽遑兮驱林泽，步屏营兮行丘阿。 　　车軏折兮马虺颓，惷怅立兮涕滂沱。 　　思丁文兮圣明哲，哀平差兮迷谬愚。 　　吕傅举兮殷周兴，忌嚭专兮郢吴虚。 　　仰长叹兮气噎结，悒殟绝兮咶复苏。 　　虎兕争兮於廷中，豺狼斗兮我之隅。 　　云雾会兮日冥晦，飘风起兮扬尘埃。 　　走鬯罔兮乍东西，欲窜伏兮其焉如？ 　　念灵闺兮隩重深，原竭节兮隔无由。 　　望旧邦兮路逶随，忧心悄兮志勤劬。 　　魂茕茕兮不遑寐，目眽眽兮寤终朝。 怨上 　　令尹兮謷謷，群司兮譨譨。 　　哀哉兮淈淈，上下兮同流。 　　菽藟兮蔓衍，芳虈兮挫枯。 　　硃紫兮杂乱，曾莫兮别诸。 　　倚此兮岩穴，永思兮窈悠。 　　嗟怀兮眩惑，用志兮不昭。 　　将丧兮玉斗，遗失兮钮枢。 　　我心兮煎熬，惟是兮用忧。 　　进恶兮九旬，复顾兮彭务。 　　拟斯兮二踪，未知兮所投。 　　谣吟兮中野，上察兮璇玑。 　　大火兮西睨，摄提兮运低。 　　雷霆兮硠磕，雹霰兮霏霏。 　　奔电兮光晃，凉风兮怆悽。 　　鸟兽兮惊骇，相从兮宿栖。 　　鸳鸯兮噰噰，狐狸兮徾徾。 　　哀吾兮介特，独处兮罔依。 　　蝼蛄兮鸣东，蟊蠽兮号西。 　　蛓缘兮我裳，蠋入兮我怀。 　　虫豸兮夹余，惆怅兮自悲。 　　伫立兮忉怛，心结縎兮折摧。 疾世 　　周徘徊兮汉渚，求水神兮灵女。 　　嗟此国兮无良，媒女诎兮謰謱。 　　鴳雀列兮譁讙，鸲鹆鸣兮聒余。 　　抱昭华兮宝璋，欲衒鬻兮莫取。 　　言旋迈兮北徂，叫我友兮配耦。 　　日阴曀兮未光，阒睄窕兮靡睹。 　　纷载驱兮高驰，将谘询兮皇羲。 　　遵河皋兮周流，路变易兮时乖。 　　濿沧海兮东游，沐盥浴兮天池。 　　访太昊兮道要，云靡贵兮仁义。 　　志欣乐兮反征，就周文兮邠歧。 　　秉玉英兮结誓，日欲暮兮心悲。 　　惟天禄兮不再，背我信兮自违。 　　逾陇堆兮渡漠，过桂车兮合黎。 　　赴昆山兮馽騄，从邛遨兮栖迟。 　　吮玉液兮止渴，齧芝华兮疗饥。 　　居嵺廓兮尠畴，远梁昌兮几迷。 　　望江汉兮濩渃，心紧絭兮伤怀。 　　时昢昢兮且旦，尘莫莫兮未晞。 　　忧不暇兮寝食，吒增叹兮如雷。 悯上 　　哀世兮睩睩，諓諓兮嗌喔。 　　众多兮阿媚，骪靡兮成俗。 　　贪枉兮党比，贞良兮茕独。 　　鹄窜兮枳棘，鹈集兮帷幄。 　　罽蕠兮青葱，槁本兮萎落。 　　睹斯兮伪惑，心为兮隔错。 　　逡巡兮圃薮，率彼兮畛陌。 　　川谷兮渊渊，山阜兮峉峉。 　　丛林兮崟崟，株榛兮岳岳。 　　霜雪兮漼溰，冰冻兮洛泽。 　　东西兮南北，罔所兮归薄。 　　庇廕兮枯树，匍匐兮岩石。 　　蜷跼兮寒局数，独处兮志不申。 　　年齿尽兮命迫促，魁垒挤摧兮常困辱。 　　含忧强老兮愁无乐，须发苎悴兮顠鬓白。 　　思灵泽兮一膏沐，怀兰英兮把琼若， 　　待天明兮立踯躅。 　　云蒙蒙兮电鯈烁，孤雌惊兮鸣呴呴。 　　思怫郁兮肝切剥，忿悁悒兮孰诉告。 遭厄 　　悼屈子兮遭厄，沉王躬兮湘汨。 　　何楚国兮难化，迄于今兮不易。 　　士莫志兮羔裘，竞佞谀兮谗阋。 　　指正义兮为曲，訿玉璧兮为石。 　　殦雕游兮华屋，鵔鸃栖兮柴蔟。 　　起奋迅兮奔走，违群小兮謑訽。 　　载青云兮上昇，適昭明兮所处。 　　蹑天衢兮长驱，踵九阳兮戏荡。 　　越云汉兮南济，秣余马兮河鼓。 　　云霓纷兮晻翳，参辰回兮颠倒。 　　逢流星兮问路，顾我指兮从左。 　　俓娵觜兮直驰，御者迷兮失轨。 　　遂踢达兮邪造，与日月兮殊道。 　　志阏绝兮安如，哀所求兮不耦。 　　攀天阶兮下视，见鄢郢兮旧宇。 　　意逍遥兮欲归，众秽盛兮沓沓。 　　思哽饐兮诘诎，涕流澜兮如雨。 悼乱 　　嗟嗟兮悲夫，殽乱兮纷挐。 　　茅丝兮同综，冠屦兮共絇。 　　督万兮侍宴，周邵兮负刍。 　　白龙兮见射，灵龟兮执拘。 　　仲尼兮困厄，邹衍兮幽囚。 　　伊余兮念兹，奔遁兮隐居。 　　将升兮高山，上有兮猴猿。 　　欲入兮深谷，下有兮虺蛇。 　　左见兮鸣鵙，右睹兮呼枭。 　　惶悸兮失气，踊跃兮距跳。 　　便旋兮中原，仰天兮增叹。 　　菅蒯兮楙莽，雚苇兮仟眠。 　　鹿蹊兮躖躖，貒貉兮蟫蟫。 　　鹯鹞兮轩轩，鹑鹌兮甄甄。 　　哀我兮寡独，靡有兮齐伦。 　　意欲兮沉吟，迫日兮黄昏。 　　玄鹤兮高飞，曾逝兮青冥。 　　鶬鶊兮喈喈，山鹊兮嚶嚶。 　　鸿鸬兮振翅，归雁兮于征。 　　吾志兮觉悟，怀我兮圣京。 　　垂屣兮将起，跓俟兮硕明。 伤时 　　惟昊天兮昭灵，阳气发兮清明。 　　风習習兮和暖，百草萌兮华荣。 　　堇荼茂兮扶疏，蘅芷彫兮莹嫇。 　　愍贞良兮遇害，将夭折兮碎糜。 　　时混混兮浇饡，哀当世兮莫知。 　　览往昔兮俊彦，亦诎辱兮系纍。 　　管束缚兮桎梏，百贸易兮传卖。 　　遭桓缪兮识举，才德用兮列施。 　　且从容兮自慰，玩琴书兮游戏。 　　迫中国兮迮陿，吾欲之兮九夷。 　　超五岭兮嵯峨，观浮石兮崔嵬。 　　陟丹山兮炎野，屯余车兮黄支。 　　就祝融兮稽疑，嘉己行兮无为。 　　乃回朅兮北逝，遇神孈兮宴娭。 　　欲静居兮自娱，心愁慼兮不能。 　　放余辔兮策驷，忽飙腾兮浮云。 　　蹠飞杭兮越海，从安期兮蓬莱。 　　缘天梯兮北上，登太一兮玉台。 　　使素女兮鼓簧，乘戈和兮讴谣。 　　声噭誂兮清和，音晏衍兮要婬。 　　咸欣欣兮酣乐，余眷眷兮独悲。 　　顾章华兮太息，志恋恋兮依依。 哀岁 　　旻天兮清凉，玄气兮高朗。 　　北风兮潦洌，草木兮苍唐。 　　蛜蚗兮噍噍，蝍蛆兮穰穰。 　　岁忽忽兮惟暮，余感时兮悽怆。 　　伤俗兮泥浊，矇蔽兮不章。 　　宝彼兮沙砾，捐此兮夜光。 　　椒瑛兮湟汙，葈耳兮充房。 　　摄衣兮缓带，操我兮墨阳。 　　昇车兮命仆，将驰兮四荒。 　　下堂兮见虿，出门兮触螽。 　　巷有兮蚰蜓，邑多兮螳螂。 　　睹斯兮嫉贼，心为兮切伤。 　　俛念兮子胥，仰怜兮比干。 　　投剑兮脱冕，龙屈兮蜿蟤。 　　潜藏兮山泽，匍匐兮丛攒。 　　窥见兮溪涧，流水兮沄沄。 　　鼋鼍兮欣欣，鱣鲇兮延延。 　　群行兮上下，骈罗兮列陈。 　　自恨兮无友，特处兮茕茕。 　　冬夜兮陶陶，雨雪兮冥冥。 　　神光兮颎颎，鬼火兮荧荧。 　　修德兮困控，愁不聊兮遑生。 　　忧纡兮郁郁，恶所兮写情。 守志 　　陟玉峦兮逍遥，览高冈兮峣峣。 　　桂树列兮纷敷，吐紫华兮布条。 　　实孔鸾兮所居，今其集兮惟鸮。 　　乌鹊惊兮哑哑，余顾盼兮怊怊。 　　彼日月兮闇昧，障覆天兮祲氛。 　　伊我后兮不聪，焉陈诚兮效忠。 　　摅羽翮兮超俗，游陶遨兮养神。 　　乘六蛟兮蜿蝉，遂驰骋兮升云。 　　扬彗光兮为旗，秉电策兮为鞭。 　　朝晨发兮鄢郢，食时至兮增泉。 　　绕曲阿兮北次，造我车兮南端。 　　谒玄黄兮纳贽，崇忠贞兮弥坚。 　　历九宫兮遍观，睹秘藏兮宝珍。 　　就传说兮骑龙，与织女兮合婚。 　　举天罼兮掩邪，彀天弧兮射奸。 　　随真人兮翱翔，食元气兮长存。 　　望太微兮穆穆，睨三阶兮炳分。 　　相辅政兮成化，建烈业兮垂勋。 　　目瞥瞥兮西没，道遐回兮阻叹。 　　志稸积兮未通，怅敞罔兮自怜。 　　乱曰： 　　天庭明兮云霓藏，三光朗兮镜万方。 　　斥蜥蜴兮进龟龙，策谋从兮翼机衡。 　　配稷契兮恢唐功，嗟英俊兮未为双。', '楚辞');
INSERT INTO `poem` VALUES (1456, '天地', '两汉', '刘彻', '天地并况，惟予有慕，爰熙紫坛，思求厥路。 恭承禋祀，缊豫为纷，黼绣周张，承神至尊。 千童罗舞成八溢，合好效欢虞泰一。 九歌毕奏斐然殊，鸣琴竽瑟会轩朱。 璆磬金鼓，灵其有喜，百官济济，各敬厥事。 盛牲实俎进闻膏，神奄留，临须摇。 长丽前掞光耀明，寒暑不忒况皇章。 展诗应律鋗玉鸣，函宫吐角激徵清。 发梁扬羽申以商，造兹新音永久长。 声气远条凤鸟翔，神夕奄虞盖孔享。', '乐府');
INSERT INTO `poem` VALUES (1457, '日出入', '两汉', '佚名', '日出入安穷？时世不与人同。 故春非我春，夏非我夏， 秋非我秋，冬非我冬。 泊如四海之池，遍观是邪谓何？ 吾知所乐，独乐六龙， 六龙之调，使我心若。 訾黄其何不徕下。', '乐府');
INSERT INTO `poem` VALUES (1458, '天马二首·其一', '两汉', '刘彻', '太一况，天马下，沾赤汗，沫流赭。 志俶傥，精权奇，籋浮云，晻上驰。 体容与，迣万里，今安匹，龙为友。', '乐府');
INSERT INTO `poem` VALUES (1459, '天马二首·其二', '两汉', '刘彻', '天马徕，从西极，涉流沙，九夷服。 天马徕，出泉水，虎脊两，化若鬼。 天马徕，历无草，径千里，循东道。 天马徕，执徐时，将摇举，谁与期？ 天马徕，开远门，竦予身，逝昆仑。 天马徕，龙之媒，游阊阖，观玉台。', '乐府');
INSERT INTO `poem` VALUES (1460, '天门', '两汉', '佚名', '天门开，詄荡荡，穆并骋，以临飨。 光夜烛，德信著，灵浸鸿，长生豫。 太朱涂广，夷石为堂，饰玉梢以舞歌，体招摇若永望。 星留俞，塞陨光，照紫幄，珠烦黄。 幡比翅回集，贰双飞常羊。 月穆穆以金波，日华耀以宣明。 假清风轧忽，激长至重觞。 神裴回若留放，殣冀亲以肆章。 函蒙祉福常若期，寂谬上天知厥时。 泛泛滇滇从高斿，殷勤此路胪所求。 佻正嘉古弘以昌，休嘉砰隐溢四方。 专精厉意逝九阂，纷云六幕浮大海。', '乐府');
INSERT INTO `poem` VALUES (1461, '青阳', '两汉', '佚名', '青阳开动，根荄以遂，膏润并爱，跂行毕逮。 霆声发荣，壧处顷听，枯槁复产，乃成厥命。 众庶熙熙，施及夭胎，群生啿噬，惟春之祺。', '乐府');
INSERT INTO `poem` VALUES (1462, '景星', '两汉', '佚名', '景星显见，信星彪列，象载昭庭，日亲以察。 参侔开阖，爰推本纪，汾脽出鼎，皇佑元始。 五音六律，依韦飨昭，杂变并会，雅声远姚。 空桑琴瑟结信成，四兴递代八风生。 殷殷钟石羽龠鸣。河龙供鲤醇牺牲。 百末旨酒布兰生。泰尊柘浆析朝酲。 微感心攸通修名，周流常羊思所并。 穰穰复正直往宁，冯蠵切和疏写平。 上天布施后土成，穰穰丰年四时荣。', '乐府');
INSERT INTO `poem` VALUES (1463, '华晔晔', '两汉', '刘彻', '华晔晔，固灵根。 神之斿，过天门，车千乘，敦昆仑。 神之出，排玉房，周流杂，拔兰堂。 神之行，旌容容，骑沓沓，般纵纵。 神之徕，泛翊翊，甘露降，庆云集。 神之揄，临坛宇，九疑宾，夔龙舞。 神安坐，翔吉时，共翊翊，合所思。 神嘉虞，申贰觞，福滂洋，迈延长。 沛施佑，汾之阿，扬金光，横泰河，莽若云，增阳波。 遍胪欢，腾天歌。', '乐府');
INSERT INTO `poem` VALUES (1464, '朱鹭', '两汉', '佚名', '朱鹭，鱼以乌。路訾邪鹭何食？食茄下。不之食，不以吐，将以问诛者。', '乐府');
INSERT INTO `poem` VALUES (1465, '战城南', '两汉', '佚名', '战城南，死郭北，野死不葬乌可食。 为我谓乌：且为客豪！ 野死谅不葬，腐肉安能去子逃？ 水深激激，蒲苇冥冥； 枭骑战斗死，驽马徘徊鸣。 梁筑室，何以南？何以北？ 禾黍不获君何食？愿为忠臣安可得？ 思子良臣，良臣诚可思： 朝行出攻，暮不夜归！', '乐府');
INSERT INTO `poem` VALUES (1466, '巫山高', '两汉', '佚名', '巫山高，高以大；淮水深，难以逝。 我欲东归，害梁不为？ 我集无高曳，水何梁汤汤回回。 临水远望，泣下沾衣。 远道之人心思归，谓之何！', '乐府');
INSERT INTO `poem` VALUES (1467, '上陵', '两汉', '佚名', '上陵何美美，下津风以寒。 问客从何来，言从水中央。 桂树为君船，青丝为君笮，木兰为君棹，黄金错其间。 沧海之雀赤翅鸿，白雁随。 山林乍开乍合，曾不知日月明。 醴泉之水，光泽何蔚蔚。 芝为车，龙为马，览遨游，四海外。 甘露初二年，芝生铜池中，仙人下来饮，延寿千万岁。', '乐府');
INSERT INTO `poem` VALUES (1468, '有所思', '两汉', '佚名', '有所思，乃在大海南。 何用问遗君，双珠玳瑁簪。 用玉绍缭之。 闻君有他心，拉杂摧烧之。 摧烧之，当风扬其灰！ 从今以往，勿复相思，相思与君绝！ 鸡鸣狗吠，兄嫂当知之。 妃呼狶！ 秋风肃肃晨风飔，东方须臾高知之！', '乐府');
INSERT INTO `poem` VALUES (1469, '雉子班', '两汉', '佚名', '雉子，班如此。之于雉梁。无以吾翁孺，雉子。知得雉子高蜚止，黄鹄蜚，之以千里，王可思。雄来蜚从雌，视子趋一雉。雉子，车大驾马滕，被王送行所中。尧羊蜚从王孙行。', '乐府');
INSERT INTO `poem` VALUES (1470, '上邪', '两汉', '佚名', '上邪！我欲与君相知，长命无绝衰。 山无陵，江水为竭，冬雷震震，夏雨雪，天地合，乃敢与君绝。', '乐府');
INSERT INTO `poem` VALUES (1471, '临高台', '两汉', '佚名', '临高台以轩，下有清水清且寒。 江有香草目以兰，黄鹄高飞离哉翻。 关弓射鹄，令我主寿万年。', '乐府');
INSERT INTO `poem` VALUES (1472, '上之回', '唐代', '李白', '三十六离宫，楼台与天通。 阁道步行月，美人愁烟空。 恩疏宠不及，桃李伤春风。 淫乐意何极，金舆向回中。 万乘出黄道，千旗扬彩虹。 前军细柳北，后骑甘泉东。 岂问渭川老，宁邀襄野童。 但慕瑶池宴，归来乐未穷。', '乐府');
INSERT INTO `poem` VALUES (1473, '战城南', '南北朝', '吴均', '躞蹀青骊马，往战城南畿。 五历鱼丽阵，三入九重围。 名慑武安将，血污秦王衣。 为君意气重，无功终不归。', '乐府');
INSERT INTO `poem` VALUES (1474, '战城南', '唐代', '卢照邻', '将军出紫塞，冒顿在乌贪。 笳喧雁门北，阵翼龙城南。 雕弓夜宛转，铁骑晓参驔。 应须驻白日，为待战方酣。', '乐府');
INSERT INTO `poem` VALUES (1475, '战城南', '唐代', '李白', '去年战，桑干源，今年战，葱河道。 洗兵条支海上波，放马天山雪中草。 万里长征战，三军尽衰老。 匈奴以杀戮为耕作，古来唯见白骨黄沙田。 秦家筑城避胡处，汉家还有烽火燃。 烽火燃不息，征战无已时。 野战格斗死，败马号鸣向天悲。 乌鸢啄人肠，衔飞上挂枯树枝。 士卒涂草莽，将军空尔为。 乃知兵者是凶器，圣人不得已而用之。', '乐府');
INSERT INTO `poem` VALUES (1476, '将进酒', '唐代', '李白', '君不见，黄河之水天上来，奔流到海不复回。 君不见，高堂明镜悲白发，朝如青丝暮成雪。 人生得意须尽欢，莫使金樽空对月。 天生我材必有用，千金散尽还复来。 烹羊宰牛且为乐，会须一饮三百杯。 岑夫子，丹丘生，将进酒，杯莫停。 与君歌一曲，请君为我倾耳听。(倾耳听 一作：侧耳听) 钟鼓馔玉不足贵，但愿长醉不复醒。(不足贵 一作：何足贵；不复醒 一作：不愿醒/不用醒) 古来圣贤皆寂寞，惟有饮者留其名。(古来 一作：自古；惟 通：唯) 陈王昔时宴平乐，斗酒十千恣欢谑。 主人何为言少钱，径须沽取对君酌。 五花马，千金裘，呼儿将出换美酒，与尔同销万古愁。', '乐府');
INSERT INTO `poem` VALUES (1477, '将进酒', '唐代', '李贺', '琉璃钟，琥珀浓，小槽酒滴真珠红。 烹龙炮凤玉脂泣，罗帏绣幕围香风。 吹龙笛，击鼍鼓；皓齿歌，细腰舞。 况是青春日将暮，桃花乱落如红雨。 劝君终日酩酊醉，酒不到刘伶坟上土。', '乐府');
INSERT INTO `poem` VALUES (1478, '君马黄', '唐代', '李白', '君马黄，我马白。 马色虽不同，人心本无隔。 共作游冶盘，双行洛阳陌。 长剑既照曜，高冠何赩赫。 各有千金裘，俱为五侯客。 猛虎落陷阱，壮夫时屈厄。 相知在急难，独好亦何益。', '乐府');
INSERT INTO `poem` VALUES (1479, '芳树', '南北朝', '李爽', '芳树千株发，摇荡三阳时。 气软来风易，枝繁度鸟迟。 春至花如锦，夏近叶成帷。 欲寄边城客，路远谁能持。', '乐府');
INSERT INTO `poem` VALUES (1480, '芳树', '南北朝', '萧衍', '绿树始摇芳，芳生非一叶。 一叶度春风，芳芳自相接。 色杂乱参差，众花纷重叠。 重叠不可思，思此谁能惬。', '乐府');
INSERT INTO `poem` VALUES (1481, '木兰诗 / 木兰辞', '南北朝', '佚名', '　　唧唧复唧唧，木兰当户织。不闻机杼声，惟闻女叹息。(惟闻 通：唯)问女何所思，问女何所忆。女亦无所思，女亦无所忆。昨夜见军帖，可汗大点兵，军书十二卷，卷卷有爷名。阿爷无大儿，木兰无长兄，愿为市鞍马，从此替爷征。 　　东市买骏马，西市买鞍鞯，南市买辔头，北市买长鞭。旦辞爷娘去，暮宿黄河边，不闻爷娘唤女声，但闻黄河流水鸣溅溅。旦辞黄河去，暮至黑山头，不闻爷娘唤女声，但闻燕山胡骑鸣啾啾。 　　万里赴戎机，关山度若飞。朔气传金柝，寒光照铁衣。将军百战死，壮士十年归。 　　归来见天子，天子坐明堂。策勋十二转，赏赐百千强。可汗问所欲，木兰不用尚书郎，愿驰千里足，送儿还故乡。(一作：愿借明驼千里足) 　　爷娘闻女来，出郭相扶将；阿姊闻妹来，当户理红妆；小弟闻姊来，磨刀霍霍向猪羊。开我东阁门，坐我西阁床，脱我战时袍，著我旧时裳。当窗理云鬓，对镜帖花黄。出门看火伴，火伴皆惊忙：同行十二年，不知木兰是女郎。（帖 通：贴；惊忙 一作：惶；惶 火伴 通：伙） 　　雄兔脚扑朔，雌兔眼迷离；双兔傍地走，安能辨我是雄雌？', '乐府');
INSERT INTO `poem` VALUES (1482, '折杨柳', '唐代', '李白', '垂杨拂绿水，摇艳东风年。 花明玉关雪，叶暖金窗烟。 美人结长想，对此心凄然。 攀条折春色，远寄龙庭前。', '乐府');
INSERT INTO `poem` VALUES (1483, '紫骝马', '唐代', '李白', '紫骝行且嘶，双翻碧玉蹄。 临流不肯渡，似惜锦障泥。 白雪关山远，黄云海戍迷。 挥鞭万里去，安得念春闺。', '乐府');
INSERT INTO `poem` VALUES (1484, '梅花落', '南北朝', '鲍照', '中庭多杂树，偏为梅咨嗟。问君何独然？念其霜中能作花，露中能作实。摇荡春风媚春日，念尔零落逐寒风，徒有霜华无霜质。(寒风 一作：风飚)', '乐府');
INSERT INTO `poem` VALUES (1485, '公无渡河', '唐代', '李白', '黄河西来决昆仑，咆哮万里触龙门。 波滔天，尧咨嗟。 大禹理百川，儿啼不窥家。 杀湍湮洪水，九州始蚕麻。 其害乃去，茫然风沙。 被发之叟狂而痴，清晨临流欲奚为。 旁人不惜妻止之，公无渡河苦渡之。 虎可搏，河难凭，公果溺死流海湄。 有长鲸白齿若雪山，公乎公乎挂罥于其间。 箜篌所悲竟不还。', '乐府');
INSERT INTO `poem` VALUES (1486, '江南', '两汉', '佚名', '江南可采莲，莲叶何田田，鱼戏莲叶间。 鱼戏莲叶东，鱼戏莲叶西，鱼戏莲叶南，鱼戏莲叶北。', '乐府');
INSERT INTO `poem` VALUES (1487, '登高丘而望远', '唐代', '李白', '登高丘，望远海。 六鳌骨已霜，三山流安在？ 扶桑半摧折，白日沈光彩。 银台金阙如梦中，秦皇汉武空相待。 精卫费木石，鼋鼍无所凭。 君不见骊山茂陵尽灰灭，牧羊之子来攀登。 盗贼劫宝玉，精灵竟何能？ 穷兵黩武今如此，鼎湖飞龙安可乘？', '乐府');
INSERT INTO `poem` VALUES (1488, '东光', '两汉', '佚名', '东光乎，苍梧何不乎。 苍梧多腐粟，无益诸军粮。 诸军游荡子，早行多悲伤。', '乐府');
INSERT INTO `poem` VALUES (1489, '饮马长城窟行', '两汉', '佚名', '青青河畔草，绵绵思远道。 远道不可思，宿昔梦见之。(宿昔 一作：夙昔) 梦见在我傍，忽觉在他乡。 他乡各异县，辗转不相见。 枯桑知天风，海水知天寒。 入门各自媚，谁肯相为言。 客从远方来，遗我双鲤鱼。 呼儿烹鲤鱼，中有尺素书。 长跪读素书，书中竟何如。 上言加餐食，下言长相忆。', '乐府');
INSERT INTO `poem` VALUES (1490, '薤露', '两汉', '佚名', '薤上露，何易晞。 露晞明朝更复落，人死一去何时归。', '乐府');
INSERT INTO `poem` VALUES (1491, '陌上桑', '唐代', '李白', '美女渭桥东，春还事蚕作。 五马如飞龙，青丝结金络。 不知谁家子，调笑来相谑。 妾本秦罗敷，玉颜艳名都。 绿条映素手，采桑向城隅。 使君且不顾，况复论秋胡。 寒螀爱碧草，鸣凤栖青梧。 托心自有处，但怪傍人愚。 徒令白日暮，高驾空踟蹰。', '乐府');
INSERT INTO `poem` VALUES (1492, '长歌行', '魏晋', '陆机', '逝矣经天日，悲哉带地川。 寸阴无停晷，尺波岂徒旋。 年往迅劲矢，时来亮急弦。 远期鲜克及，盈数固希全。 容华夙夜零，体泽坐自捐。 兹物苟难停，吾寿安得延。 俛仰逝将过，倏忽几何间。 慷慨亦焉诉，天道良自然。 但恨功名薄，竹帛无所宣。 迨及岁未暮，长歌乘我闲。', '乐府');
INSERT INTO `poem` VALUES (1493, '长歌行', '唐代', '李白', '桃李待日开，荣华照当年。 东风动百物，草木尽欲言。 枯枝无丑叶，涸水吐清泉。 大力运天地，羲和无停鞭。 功名不早著，竹帛将何宣。 桃李务青春，谁能贳白日。 富贵与神仙，蹉跎成两失。 金石犹销铄，风霜无久质。 畏落日月后，强欢歌与酒。 秋霜不惜人，倏忽侵蒲柳。', '乐府');
INSERT INTO `poem` VALUES (1494, '薤露', '两汉', '曹操', '惟汉廿二世，所任诚不良。 沐猴而冠带，知小而谋强。 犹豫不敢断，因狩执君王。 白虹为贯日，己亦先受殃。 贼臣持国柄，杀主灭宇京。 荡覆帝基业，宗庙以燔丧。 播越西迁移，号泣而且行。 瞻彼洛城郭，微子为哀伤。', '乐府');
INSERT INTO `poem` VALUES (1495, '薤露', '两汉', '曹植', '天地无穷极，阴阳转相因。 人居一世间，忽若风吹尘。 愿得展功勤，输力于明君。 怀此王佐才，慷慨独不群。 鳞介尊神龙，走兽宗麒麟。 虫兽犹知德，何况于士人。 孔氏删诗书，王业粲已分。 骋我径寸翰，流藻垂华芬。', '乐府');
INSERT INTO `poem` VALUES (1496, '蒿里', '两汉', '佚名', '蒿里谁家地，聚敛魂魄无贤愚。 鬼伯一何相催促，人命不得稍踟蹰。', '乐府');
INSERT INTO `poem` VALUES (1497, '蒿里行', '两汉', '曹操', '关东有义士，兴兵讨群凶。 初期会盟津，乃心在咸阳。 军合力不齐，踌躇而雁行。 势利使人争，嗣还自相戕。 淮南弟称号，刻玺于北方。 铠甲生虮虱，万姓以死亡。 白骨露於野，千里无鸡鸣。 生民百遗一，念之断人肠。', '乐府');
INSERT INTO `poem` VALUES (1498, '短歌行', '两汉', '曹操', '对酒当歌，人生几何！譬如朝露，去日苦多。 慨当以慷，忧思难忘。何以解忧？唯有杜康。 青青子衿，悠悠我心。但为君故，沉吟至今。 呦呦鹿鸣，食野之苹。我有嘉宾，鼓瑟吹笙。 明明如月，何时可掇？忧从中来，不可断绝。 越陌度阡，枉用相存。契阔谈讌，心念旧恩。(谈讌 一作：谈宴) 月明星稀，乌鹊南飞。绕树三匝，何枝可依？ 山不厌高，海不厌深。周公吐哺，天下归心。(海 一作：水)', '乐府');
INSERT INTO `poem` VALUES (1499, '短歌行', '魏晋', '陆机', '置酒高堂，悲歌临觞。 人寿几何，逝如朝霜。 时无重至，华不再阳。 苹以春晖，兰以秋芳。 来日苦短，去日苦长。 今我不乐，蟋蟀在房。 乐以会兴，悲以别章。 岂曰无感，忧为子忘。 我酒既旨，我肴既臧。 短歌可咏，长夜无荒。', '乐府');
INSERT INTO `poem` VALUES (1500, '短歌行', '唐代', '李白', '白日何短短，百年苦易满。 苍穹浩茫茫，万劫太极长。 麻姑垂两鬓，一半已成霜。 天公见玉女，大笑亿千场。 吾欲揽六龙，回车挂扶桑。 北斗酌美酒，劝龙各一觞。 富贵非所愿，与人驻颜光。', '乐府');
INSERT INTO `poem` VALUES (1501, '猛虎行', '魏晋', '陆机', '渴不饮盗泉水，热不息恶木阴。 恶木岂无枝？志士多苦心。 整驾肃时命，杖策将远寻。 饥食猛虎窟，寒栖野雀林。 日归功未建，时往岁载阴。 崇云临岸骇，鸣条随风吟。 静言幽谷底，长啸高山岑。 急弦无懦响，亮节难为音。 人生诚未易，曷云开此衿？ 眷我耿介怀，俯仰愧古今。', '乐府');
INSERT INTO `poem` VALUES (1502, '猛虎行', '唐代', '李白', '朝作猛虎行，暮作猛虎吟。 肠断非关陇头水，泪下不为雍门琴。 旌旗缤纷两河道，战鼓惊山欲倾倒。 秦人半作燕地囚，胡马翻衔洛阳草。 一输一失关下兵，朝降夕叛幽蓟城。 巨鳌未斩海水动，鱼龙奔走安得宁。 颇似楚汉时，翻覆无定止。 朝过博浪沙，暮入淮阴市。 张良未遇韩信贫，刘项存亡在两臣。 暂到下邳受兵略，来投漂母作主人。 贤哲栖栖古如此，今时亦弃青云士。 有策不敢犯龙鳞，窜身南国避胡尘。 宝书长剑挂高阁，金鞍骏马散故人。 昨日方为宣城客，掣铃交通二千石。 有时六博快壮心，绕床三匝呼一掷。 楚人每道张旭奇，心藏风云世莫知。 三吴邦伯多顾盼，四海雄侠皆相推。 萧曹曾作沛中吏，攀龙附凤当有时。 溧阳酒楼三月春，杨花漠漠愁杀人。 胡人绿眼吹玉笛，吴歌白纻飞梁尘。 丈夫相见且为乐，槌牛挝鼓会众宾。 我从此去钓东海，得鱼笑寄情相亲。', '乐府');
INSERT INTO `poem` VALUES (1503, '对酒行', '唐代', '李白', '松子栖金华，安期入蓬海。 此人古之仙，羽化竟何在。 浮生速流电，倏忽变光彩。 天地无凋换，容颜有迁改。 对酒不肯饮，含情欲谁待。', '乐府');
INSERT INTO `poem` VALUES (1504, '燕歌行二首·其一', '魏晋', '曹丕', '秋风萧瑟天气凉，草木摇落露为霜，群燕辞归鹄南翔。(鹄南翔 一作 雁南翔） 念君客游思断肠，慊慊思归恋故乡，君何淹留寄他方？ 贱妾茕茕守空房，忧来思君不敢忘，不觉泪下沾衣裳。 援琴鸣弦发清商，短歌微吟不能长。 明月皎皎照我床，星汉西流夜未央。 牵牛织女遥相望，尔独何辜限河梁。', '乐府');
INSERT INTO `poem` VALUES (1505, '燕歌行二首·其二', '魏晋', '曹丕', '别日何易会日难，山川悠远路漫漫。 郁陶思君未敢言，寄声浮云往不还。 涕零雨面毁形颜，谁能怀忧独不叹。 展诗清歌仰自宽，乐往哀来摧肺肝。 耿耿伏枕不能眠，披衣出户步东西。 仰看星月观云间，飞鸽晨鸣声可怜，留连顾怀不能存。', '乐府');
INSERT INTO `poem` VALUES (1506, '燕歌行', '唐代', '高适', '开元二十六年，客有从御史大夫张公出塞而还者；作《燕歌行》以示适，感征戍之事，因而和焉。 汉家烟尘在东北，汉将辞家破残贼。 男儿本自重横行，天子非常赐颜色。 摐金伐鼓下榆关，旌旆逶迤碣石间。 校尉羽书飞瀚海，单于猎火照狼山。 山川萧条极边土，胡骑凭陵杂风雨。 战士军前半死生，美人帐下犹歌舞。 大漠穷秋塞草腓，孤城落日斗兵稀。 身当恩遇常轻敌，力尽关山未解围。(常轻敌 一作：恒轻敌) 铁衣远戍辛勤久，玉箸应啼别离后。 少妇城南欲断肠，征人蓟北空回首。 边庭飘飖那可度，绝域苍茫更何有。(飘飖 一作：飘飘) 杀气三时作阵云，寒声一夜传刁斗。 相看白刃血纷纷，死节从来岂顾勋。 君不见沙场征战苦，至今犹忆李将军。', '乐府');
INSERT INTO `poem` VALUES (1507, '古从军行', '唐代', '李颀', '白日登山望烽火，黄昏饮马傍交河。 行人刁斗风沙暗，公主琵琶幽怨多。 野云万里无城郭，雨雪纷纷连大漠。 胡雁哀鸣夜夜飞，胡儿眼泪双双落。 闻道玉门犹被遮，应将性命逐轻车。 年年战骨埋荒外，空见蒲桃入汉家。', '乐府');
INSERT INTO `poem` VALUES (1508, '从军行·其二', '唐代', '李白', '百战沙场碎铁衣，城南已合数重围。 突营射杀呼延将，独领残兵千骑归。', '乐府');
INSERT INTO `poem` VALUES (1509, '鞠歌行', '唐代', '李白', '玉不自言如桃李，鱼目笑之卞和耻。 楚国青蝇何太多，连城白璧遭谗毁。 荆山长号泣血人，忠臣死为刖足鬼。 听曲知甯戚，夷吾因小妻。 秦穆五羊皮，买死百里奚。 洗拂青云上，当时贱如泥。 朝歌鼓刀叟，虎变磻溪中。 一举钓六合，遂荒营丘东。 平生渭水曲，谁识此老翁。 奈何今之人，双目送飞鸿。', '乐府');
INSERT INTO `poem` VALUES (1510, '吁嗟篇', '两汉', '曹植', '吁嗟此转蓬，居世何独然。 长去本根逝，夙夜无休闲。 东西经七陌，南北越九阡。 卒遇回风起，吹我入云间。 自谓终天路，忽然下沉渊。 惊飙接我出，故归彼中田。 当南而更北，谓东而反西。 宕宕当何依，忽亡而复存。 飘飘周八泽，连翩历五山。 流转无恒处，谁知吾苦艰。 愿为中林草，秋随野火燔。 糜灭岂不痛，愿与株荄连。', '乐府');
INSERT INTO `poem` VALUES (1511, '豫章行苦相篇', '魏晋', '傅玄', '苦相身为女，卑陋难再陈。 男儿当门户，堕地自生神。 雄心志四海，万里望风尘。 女育无欣爱，不为家所珍。 长大逃深室，藏头羞见人。 垂泪适他乡，忽如雨绝云。 低头和颜色，素齿结朱唇。 跪拜无复数，婢妾如严宾。 情合同云汉，葵藿仰阳春。 心乖甚水火，百恶集其身。 玉颜随年变，丈夫多好新。 昔为形与影，今为胡与秦。 胡秦时相见，一绝逾参辰。', '乐府');
INSERT INTO `poem` VALUES (1512, '豫章行', '唐代', '李白', '胡风吹代马，北拥鲁阳关。 吴兵照海雪，西讨何时还。 半渡上辽津，黄云惨无颜。 老母与子别，呼天野草间。 白马绕旌旗，悲鸣相追攀。 白杨秋月苦，早落豫章山。 本为休明人，斩虏素不闲。 岂惜战斗死，为君扫凶顽。 精感石没羽，岂云惮险艰。 楼船若鲸飞，波荡落星湾。 此曲不可奏，三军鬓成斑。', '乐府');
INSERT INTO `poem` VALUES (1513, '相逢行', '两汉', '佚名', '相逢狭路间，道隘不容车。 不知何年少？夹毂问君家。 君家诚易知，易知复难忘； 黄金为君门，白玉为君堂。 堂上置樽酒，作使邯郸倡。 中庭生桂树，华灯何煌煌。 兄弟两三人，中子为侍郎； 五日一来归，道上自生光； 黄金络马头，观者盈道傍。 入门时左顾，但见双鸳鸯； 鸳鸯七十二，罗列自成行。 音声何噰噰，鹤鸣东西厢。 大妇织绮罗，中妇织流黄； 小妇无所为，挟瑟上高堂： “丈人且安坐，调丝方未央。”', '乐府');
INSERT INTO `poem` VALUES (1514, '相逢行二首', '唐代', '李白', '朝骑五花马，谒帝出银台。 秀色谁家子，云车珠箔开。 金鞭遥指点，玉勒近迟回。 夹毂相借问，疑从天上来。 蹙入青绮门，当歌共衔杯。 衔杯映歌扇，似月云中见。 相见不得亲，不如不相见。 相见情已深，未语可知心。 胡为守空闺，孤眠愁锦衾。 锦衾与罗帏，缠绵会有时。 春风正澹荡，暮雨来何迟。 愿因三青鸟，更报长相思。 光景不待人，须臾发成丝。 当年失行乐，老去徒伤悲。 持此道密意，毋令旷佳期。 相逢红尘内，高揖黄金鞭。 万户垂杨里，君家阿那边。', '乐府');
INSERT INTO `poem` VALUES (1515, '塘上行', '两汉', '甄宓', '蒲生我池中，其叶何离离。 傍能行仁义，莫若妾自知。 众口铄黄金，使君生别离。 念君去我时，独愁常苦悲。 想见君颜色，感结伤心脾。 念君常苦悲，夜夜不能寐。 莫以豪贤故，弃捐素所爱。 莫以鱼肉贱，弃捐葱与薤。 莫以麻枲贱，弃捐菅与蒯。 出亦复苦愁，入亦复苦愁。 边地多悲风，树木何修修。 从君致独乐，延年寿千秋。', '乐府');
INSERT INTO `poem` VALUES (1516, '上留田行', '唐代', '李白', '行至上留田，孤坟何峥嵘。 积此万古恨，春草不复生。 悲风四边来，肠断白杨声。 借问谁家地，埋没蒿里茔。 古老向余言，言是上留田，蓬科马鬣今已平。 昔之弟死兄不葬，他人于此举铭旌。 一鸟死，百鸟鸣。一兽走，百兽惊。 桓山之禽别离苦，欲去回翔不能征。 田氏仓卒骨肉分，青天白日摧紫荆。 交柯之木本同形，东枝憔悴西枝荣。 无心之物尚如此，参商胡乃寻天兵。 孤竹延陵，让国扬名。 高风缅邈，颓波激清。 尺布之谣，塞耳不能听。', '乐府');
INSERT INTO `poem` VALUES (1517, '妇病行', '两汉', '佚名', '妇病连年累岁，传呼丈人前一言。 当言未及得言，不知泪下一何翩翩。 “属累君两三孤子，莫我儿饥且寒， 有过慎莫笪笞，行当折摇，思复念之!” 乱曰：抱时无衣，襦复无里。 闭门塞牖，舍孤儿到市。 道逢亲交，泣坐不能起。 从乞求与孤儿买饵，对交啼泣，泪不可止：“我欲不伤悲不能已。” 探怀中钱持授交。 入门见孤儿，啼索其母抱。 徘徊空舍中，“行复尔耳，弃置勿复道！”', '乐府');
INSERT INTO `poem` VALUES (1518, '野田黄雀行', '唐代', '李白', '游莫逐炎洲翠，栖莫近吴宫燕。 吴宫火起焚巢窠，炎洲逐翠遭网罗。 萧条两翅蓬蒿下，纵有鹰鹯奈若何。', '乐府');
INSERT INTO `poem` VALUES (1519, '野田黄雀行', '两汉', '曹植', '高树多悲风，海水扬其波。 利剑不在掌，结友何须多？ 不见篱间雀，见鹞自投罗。 罗家得雀喜，少年见雀悲。 拔剑捎罗网，黄雀得飞飞。 飞飞摩苍天，来下谢少年。', '乐府');
INSERT INTO `poem` VALUES (1520, '门有万里客行', '两汉', '曹植', '门有万里客，问君何乡人。 褰裳起从之，果得心所亲。 挽裳对我泣，太息前自陈。 本是朔方士，今为吴越民。 行行将复行，去去适西秦。', '乐府');
INSERT INTO `poem` VALUES (1521, '门有车马客行', '魏晋', '陆机', '门有车马客，驾言发故乡。 念君久不归，濡迹涉江湘。 投袂赴门涂，揽衣不及裳。 拊膺携客泣，掩泪叙温凉。 借问邦族间，恻怆论存亡。 亲友多零落，旧齿皆凋丧。 市朝互迁易，城阙或丘荒。 坟垄日月多，松柏郁茫茫。 天道信崇替，人生安得长。 慷慨惟平生，俯仰独悲伤。', '乐府');
INSERT INTO `poem` VALUES (1522, '门有车马客行', '南北朝', '鲍照', '门有车马客，问君何乡士。 捷步往相讯，果得旧邻里。 凄凄声中情，慊慊增下俚。 语昔有故悲，论今无新喜。 清晨相访慰，日暮不能已。 欢戚竞寻叙，谈调何终止。 辞端竟未究，忽唱分途始。 前悲尚未弭，后戚方复起。 嘶声盈我口，谈言在我耳。 “手迹可传心，愿尔笃行李。”', '乐府');
INSERT INTO `poem` VALUES (1523, '门有车马客行', '唐代', '李白', '门有车马宾，金鞍曜朱轮。 谓从丹霄落，乃是故乡亲。 呼儿扫中堂，坐客论悲辛。 对酒两不饮，停觞泪盈巾。 叹我万里游，飘飘三十春。 空谈帝王略，紫绶不挂身。 雄剑藏玉匣，阴符生素尘。 廓落无所合，流离湘水滨。 借问宗党间，多为泉下人。 生苦百战役，死托万鬼邻。 北风扬胡沙，埋翳周与秦。 大运且如此，苍穹宁匪仁。 恻怆竟何道，存亡任大钧。', '乐府');
INSERT INTO `poem` VALUES (1524, '蜀道难·其二', '南北朝', '萧纲', '巫山七百里，巴水三回曲。 笛声下复高，猿啼断还续。', '乐府');
INSERT INTO `poem` VALUES (1525, '蜀道难', '唐代', '李白', '噫吁嚱，危乎高哉！蜀道之难，难于上青天！ 蚕丛及鱼凫，开国何茫然！ 尔来四万八千岁，不与秦塞通人烟。 西当太白有鸟道，可以横绝峨眉巅。 地崩山摧壮士死，然后天梯石栈相钩连。 上有六龙回日之高标，下有冲波逆折之回川。 黄鹤之飞尚不得过，猿猱欲度愁攀援。(攀援 一作：攀缘) 青泥何盘盘，百步九折萦岩峦。 扪参历井仰胁息，以手抚膺坐长叹。 问君西游何时还？畏途巉岩不可攀。 但见悲鸟号古木，雄飞雌从绕林间。 又闻子规啼夜月，愁空山。 蜀道之难,难于上青天，使人听此凋朱颜！ 连峰去天不盈尺，枯松倒挂倚绝壁。 飞湍瀑流争喧豗，砯崖转石万壑雷。 其险也如此，嗟尔远道之人胡为乎来哉！(也如此 一作：也若此) 剑阁峥嵘而崔嵬，一夫当关，万夫莫开。 所守或匪亲，化为狼与豺。 朝避猛虎，夕避长蛇；磨牙吮血，杀人如麻。 锦城虽云乐，不如早还家。 蜀道之难,难于上青天，侧身西望长咨嗟！', '乐府');
INSERT INTO `poem` VALUES (1526, '白头吟', '两汉', '卓文君', '皑如山上雪，皎若云间月。 闻君有两意，故来相决绝。 今日斗酒会，明旦沟水头。 躞蹀御沟上，沟水东西流。 凄凄复凄凄，嫁娶不须啼。 愿得一心人，白头不相离。 竹竿何袅袅，鱼尾何簁簁！ 男儿重意气，何用钱刀为！', '乐府');
INSERT INTO `poem` VALUES (1527, '代白头吟', '南北朝', '鲍照', '直如朱丝绳，清如玉壶冰。何惭宿昔意，猜恨坐相仍。 人情贱恩旧，世义逐衰兴。毫发一为瑕，丘山不可胜。 食苗实硕鼠，点白信苍蝇。凫鹄远成美，薪刍前见凌。 申黜褒女进，班去赵姬升。周王日沦惑，汉帝益嗟称。 心赏犹难恃，貌恭岂易凭。古来共如此，非君独抚膺。', '乐府');
INSERT INTO `poem` VALUES (1528, '代悲白头翁', '唐代', '刘希夷', '洛阳城东桃李花，飞来飞去落谁家？ 洛阳女儿惜颜色，坐见落花长叹息。 今年花落颜色改，明年花开复谁在？ 已见松柏摧为薪，更闻桑田变成海。 古人无复洛城东，今人还对落花风。 年年岁岁花相似，岁岁年年人不同。 寄言全盛红颜子，应怜半死白头翁。 此翁白头真可怜，伊昔红颜美少年。 公子王孙芳树下，清歌妙舞落花前。 光禄池台文锦绣，将军楼阁画神仙。 一朝卧病无相识，三春行乐在谁边？ 宛转蛾眉能几时？须臾鹤发乱如丝。 但看古来歌舞地，唯有黄昏鸟雀悲。', '乐府');
INSERT INTO `poem` VALUES (1529, '白头吟', '唐代', '李白', '锦水东北流，波荡双鸳鸯。 雄巢汉宫树，雌弄秦草芳。 宁同万死碎绮翼，不忍云间两分张。 此时阿娇正娇妒，独坐长门愁日暮。 但愿君恩顾妾深，岂惜黄金买词赋。 相如作赋得黄金，丈夫好新多异心。 一朝将聘茂陵女，文君因赠白头吟。 东流不作西归水，落花辞条归故林。 兔丝固无情，随风任倾倒。 谁使女萝枝，而来强萦抱。 两草犹一心，人心不如草。 莫卷龙须席，从他生网丝。 且留琥珀枕，或有梦来时。 覆水再收岂满杯，弃妾已去难重回。 古来得意不相负，只今惟见青陵台。', '乐府');
INSERT INTO `poem` VALUES (1530, '梁甫吟', '魏晋', '陆机', '玉衡固已骖，羲和若飞凌。 四运循环转，寒暑自相承。 冉冉年时暮，迢迢天路征。 招摇东北指，大火西南升。 悲风无绝响，玄云互相仍。 丰水凭川结，零露弥天凝。 年命特相逝，庆云鲜克乘。 履信多愆期，思顺焉足凭。 慷慨临川响，非此孰为兴。 哀吟梁甫颠，慷慨独抚膺。', '乐府');
INSERT INTO `poem` VALUES (1531, '东武吟', '唐代', '李白', '好古笑流俗，素闻贤达风。 方希佐明主，长揖辞成功。 白日在高天，回光烛微躬。 恭承凤凰诏，欻起云萝中。 清切紫霄迥，优游丹禁通。 君王赐颜色，声价凌烟虹。 乘舆拥翠盖，扈从金城东。 宝马丽绝景，锦衣入新丰。 依岩望松雪，对酒鸣丝桐。 因学扬子云，献赋甘泉宫。 天书美片善，清芬播无穷。 归来入咸阳，谈笑皆王公。 一朝去金马，飘落成飞蓬。 宾客日疏散，玉樽亦已空。 才力犹可倚，不惭世上雄。 闲作东武吟，曲尽情未终。 书此谢知己，吾寻黄绮翁。', '乐府');
INSERT INTO `poem` VALUES (1532, '王昭君二首', '唐代', '李白', '汉家秦地月，流影照明妃。 一上玉关道，天涯去不归。 汉月还从东海出，明妃西嫁无来日。 燕支长寒雪作花，蛾眉憔悴没胡沙。 生乏黄金枉图画，死留青冢使人嗟。 昭君拂玉鞍，上马啼红颊。 今日汉宫人，明朝胡地妾。', '乐府');
INSERT INTO `poem` VALUES (1533, '王明君', '魏晋', '石崇', '我本汉家子，将适单于庭。 辞决未及终，前驱已抗旌。 仆御涕流离，辕马悲且鸣。 哀郁伤五内，泣泪沾朱缨。 行行日已远，遂造匈奴城。 延我于穹庐，加我阏氏名。 殊类非所安，虽贵非所荣。 父子见陵辱，对之惭且惊。 杀身良不易，默默以苟生。 苟生亦何聊，积思常愤盈。 愿假飞鸿翼，弃之以遐征。 飞鸿不我顾，伫立以屏营。 昔为匣中玉，今为粪上英。 朝华不足欢，甘与秋草并。 传语后世人，远嫁难为情。', '乐府');
INSERT INTO `poem` VALUES (1534, '艳歌何尝行', '未知', '佚名', '　　飞来双白鹄，乃从西北来。十十五五，罗列成行。妻卒被病，行不能相随。五里一返顾，六里一徘徊。“吾欲衔汝去，口噤不能开；吾欲负汝去，毛羽何摧颓。乐哉新相知，忧来生别离。躇蹰顾群侣，泪下不自知。”“念与君别离，气结不能言。各各重自爱，道远归还难。”“妾当守空房，闭门下重关。若生当相见，亡者会黄泉。”今日乐相乐，延年万岁期。', '乐府');
INSERT INTO `poem` VALUES (1535, '怨诗行', '未知', '佚名', '天德悠且长，人命一何促。 百年未几时，奄若风吹烛。 嘉宾难再遇，人命不可赎。 齐度游四方，各系太山录。 人间乐未央，忽然归东岳。 当须荡中情，游心恣所欲。', '乐府');
INSERT INTO `poem` VALUES (1536, '怨歌行', '两汉', '曹植', '为君既不易，为臣良独难。 忠信事不显，乃有见疑患。 周公佐成王，金縢功不刊。 推心辅王室，二叔反流言。 待罪居东国，泣涕常流连。 皇灵大动变，震雷风且寒。 拔树偃秋稼，天威不可干。 素服开金縢，感悟求其端。 公旦事既显，成王乃哀叹。 吾欲竟此曲，此曲悲且长。 今日乐相乐，别后莫相忘。', '乐府');
INSERT INTO `poem` VALUES (1537, '怨歌行', '唐代', '李白', '十五入汉宫，花颜笑春红。 君王选玉色，侍寝金屏中。 荐枕娇夕月，卷衣恋春风。 宁知赵飞燕，夺宠恨无穷。 沉忧能伤人，绿鬓成霜蓬。 一朝不得意，世事徒为空。 鹔鹴换美酒，舞衣罢雕龙。 寒苦不忍言，为君奏丝桐。 肠断弦亦绝，悲心夜忡忡。', '乐府');
INSERT INTO `poem` VALUES (1538, '大子夜歌二首·其二', '唐代', '陆龟蒙', '丝竹发歌响，假器扬清音。 不知歌谣妙，声势出口心。', '乐府');
INSERT INTO `poem` VALUES (1539, '子夜吴歌·春歌', '唐代', '李白', '秦地罗敷女，采桑绿水边。 素手青条上，红妆白日鲜。 蚕饥妾欲去，五马莫留连。', '乐府');
INSERT INTO `poem` VALUES (1540, '子夜吴歌·夏歌', '唐代', '李白', '镜湖三百里，菡萏发荷花。 五月西施采，人看隘若耶。 回舟不待月，归去越王家。', '乐府');
INSERT INTO `poem` VALUES (1541, '子夜吴歌·秋歌', '唐代', '李白', '长安一片月，万户捣衣声。 秋风吹不尽，总是玉关情。 何日平胡虏，良人罢远征。', '乐府');
INSERT INTO `poem` VALUES (1542, '子夜吴歌·冬歌', '唐代', '李白', '明朝驿使发，一夜絮征袍。 素手抽针冷，那堪把剪刀。 裁缝寄远道，几日到临洮。', '乐府');
INSERT INTO `poem` VALUES (1543, '丁督护歌', '唐代', '李白', '云阳上征去，两岸饶商贾。 吴牛喘月时，拖船一何苦。 水浊不可饮，壶浆半成土。 一唱都护歌，心摧泪如雨。 万人凿盘石，无由达江浒。 君看石芒砀，掩泪悲千古。', '乐府');
INSERT INTO `poem` VALUES (1544, '春江花月夜', '唐代', '张若虚', '春江潮水连海平，海上明月共潮生。 滟滟随波千万里，何处春江无月明！ 江流宛转绕芳甸，月照花林皆似霰。 空里流霜不觉飞，汀上白沙看不见。 江天一色无纤尘，皎皎空中孤月轮。 江畔何人初见月？江月何年初照人？ 人生代代无穷已，江月年年只相似。(只相似 一作：望相似) 不知江月待何人，但见长江送流水。 白云一片去悠悠，青枫浦上不胜愁。 谁家今夜扁舟子？何处相思明月楼？ 可怜楼上月徘徊，应照离人妆镜台。 玉户帘中卷不去，捣衣砧上拂还来。 此时相望不相闻，愿逐月华流照君。 鸿雁长飞光不度，鱼龙潜跃水成文。 昨夜闲潭梦落花，可怜春半不还家。 江水流春去欲尽，江潭落月复西斜。 斜月沉沉藏海雾，碣石潇湘无限路。 不知乘月几人归，落月摇情满江树。(落月 一作：落花)', '乐府');
INSERT INTO `poem` VALUES (1545, '阳春歌', '唐代', '李白', '长安白日照春空，绿杨结烟垂袅风。 披香殿前花始红，流芳发色绣户中。 绣户中，相经过。 飞燕皇后轻身舞，紫宫夫人绝世歌。 圣君三万六千日，岁岁年年奈乐何。', '乐府');
INSERT INTO `poem` VALUES (1546, '采莲曲', '唐代', '李白', '若耶溪傍采莲女，笑隔荷花共人语。 日照新妆水底明，风飘香袂空中举。 岸上谁家游冶郎，三三五五映垂杨。 紫骝嘶入落花去，见此踟蹰空断肠。', '乐府');
INSERT INTO `poem` VALUES (1547, '杨叛儿', '唐代', '李白', '君歌杨叛儿，妾劝新丰酒。 何许最关人，乌啼白门柳。 乌啼隐杨花，君醉留妾家。 博山炉中沉香火，双烟一气凌紫霞。', '乐府');
INSERT INTO `poem` VALUES (1548, '玉树后庭花', '南北朝', '陈叔宝', '丽宇芳林对高阁，新装艳质本倾城。 映户凝娇乍不进，出帷含态笑相迎。 妖姬脸似花含露，玉树流光照后庭。 花开花落不长久，落红满地归寂中。', '乐府');
INSERT INTO `poem` VALUES (1549, '大堤曲', '唐代', '李白', '汉水临襄阳，花开大堤暖。 佳期大堤下，泪向南云满。 春风无复情，吹我梦魂散。 不见眼中人，天长音信断。', '乐府');
INSERT INTO `poem` VALUES (1550, '拔蒲二首', '未知', '佚名', '青蒲衔紫茸，长叶复从风。 与君同舟去，拔蒲五湖中。 朝发桂兰渚，昼息桑榆下。 与君同拔蒲，竟日不成把。', '乐府');
INSERT INTO `poem` VALUES (1551, '七日夜女歌·其一', '未知', '佚名', '三春怨离泣，九秋欣期歌。 驾鸾行日时，月明济长河。', '乐府');
INSERT INTO `poem` VALUES (1552, '七日夜女歌·其二', '未知', '佚名', '长河起秋云，汉渚风凉发。 含欣出霄路，可笑向明月。', '乐府');
INSERT INTO `poem` VALUES (1553, '东海有勇妇', '唐代', '李白', '梁山感杞妻，恸哭为之倾。 金石忽暂开，都由激深情。 东海有勇妇，何惭苏子卿。 学剑越处子，超然若流星。 损躯报夫仇，万死不顾生。 白刃耀素雪，苍天感精诚。 十步两躩跃，三呼一交兵。 斩首掉国门，蹴踏五藏行。 豁此伉俪愤，粲然大义明。 北海李使君，飞章奏天庭。 舍罪警风俗，流芳播沧瀛。 名在列女籍，竹帛已光荣。 淳于免诏狱，汉主为缇萦。 津妾一棹歌，脱父于严刑。 十子若不肖，不如一女英。 豫让斩空衣，有心竟无成。 要离杀庆忌，壮夫所素轻。 妻子亦何辜，焚之买虚声。 岂如东海妇，事立独扬名。', '乐府');
INSERT INTO `poem` VALUES (1554, '白纻辞三首', '唐代', '李白', '扬清歌，发皓齿，北方佳人东邻子。 且吟白纻停绿水，长袖拂面为君起。 寒云夜卷霜海空，胡风吹天飘塞鸿。 玉颜满堂乐未终，馆娃日落歌吹濛。 月寒江清夜沉沉，美人一笑千黄金。 垂罗舞縠扬哀音，郢中白雪且莫吟，子夜吴歌动君心。 动君心，冀君赏。 愿作天池双鸳鸯，一朝飞去青云上。 吴刀剪彩缝舞衣，明妆丽服夺春晖。 扬眉转袖若雪飞，倾城独立世所稀。 激楚结风醉忘归，高堂月落烛已微，玉钗挂缨君莫违。', '乐府');
INSERT INTO `poem` VALUES (1555, '观沧海', '两汉', '曹操', '东临碣石，以观沧海。 水何澹澹，山岛竦峙。 树木丛生，百草丰茂。 秋风萧瑟，洪波涌起。 日月之行，若出其中； 星汉灿烂，若出其里。 幸甚至哉，歌以咏志。', '乐府');
INSERT INTO `poem` VALUES (1556, '渌水曲', '唐代', '李白', '渌水明秋月，南湖采白蘋。 荷花娇欲语，愁杀荡舟人。', '乐府');
INSERT INTO `poem` VALUES (1557, '秋思', '唐代', '李白', '春阳如昨日，碧树鸣黄鹂。 芜然蕙草暮，飒尔凉风吹。 天秋木叶下，月冷莎鸡悲。 坐愁群芳歇，白露凋华滋。', '乐府');
INSERT INTO `poem` VALUES (1558, '飞龙引二首·其一', '唐代', '李白', '黄帝铸鼎于荆山，炼丹砂。 丹砂成黄金，骑龙飞上太清家，云愁海思令人嗟。 宫中彩女颜如花，飘然挥手凌紫霞，从风纵体登鸾车。 登鸾车，侍轩辕，遨游青天中，其乐不可言。', '乐府');
INSERT INTO `poem` VALUES (1559, '飞龙引二首·其二', '唐代', '李白', '鼎湖流水清且闲，轩辕去时有弓剑，古人传道留其间。 后宫婵娟多花颜，乘鸾飞烟亦不还，骑龙攀天造天关。 造天关，闻天语，屯云河车载玉女。 载玉女，过紫皇，紫皇乃赐白兔所捣之药方。 后天而老凋三光，下视瑶池见王母，蛾眉萧飒如秋霜。', '乐府');
INSERT INTO `poem` VALUES (1560, '山人劝酒', '唐代', '李白', '苍苍云松，落落绮皓。 春风尔来为阿谁，蝴蝶忽然满芳草。 秀眉霜雪颜桃花，骨青髓绿长美好。 称是秦时避世人，劝酒相欢不知老。 各守麋鹿志，耻随龙虎争。 欻起佐太子，汉王乃复惊。 顾谓戚夫人，彼翁羽翼成。 归来商山下，泛若云无情。 举觞酹巢由，洗耳何独清。 浩歌望嵩岳，意气还相倾。', '乐府');
INSERT INTO `poem` VALUES (1561, '幽涧泉', '唐代', '李白', '拂彼白石，弹吾素琴。 幽涧愀兮流泉深，善手明徽高张清。 心寂历似千古，松飕飗兮万寻。 中见愁猿吊影而危处兮，叫秋木而长吟。 客有哀时失职而听者，泪淋浪以沾襟。 乃缉商缀羽，潺湲成音。 吾但写声发情于妙指，殊不知此曲之古今。 幽涧泉，鸣深林。', '乐府');
INSERT INTO `poem` VALUES (1562, '雉朝飞', '唐代', '李白', '麦陇青青三月时，白雉朝飞挟两雌。 锦衣绣翼何离褷，犊牧采薪感之悲。 春天和，白日暖。 啄食饮泉勇气满，争雄斗死绣颈断。 雉子班奏急管弦，倾心酒美尽玉碗。 枯杨枯杨尔生稊，我独七十而孤栖。 弹弦写恨意不尽，瞑目归黄泥。', '乐府');
INSERT INTO `poem` VALUES (1563, '代出自蓟北门行', '南北朝', '鲍照', '羽檄起边亭，烽火入咸阳。 征师屯广武，分兵救朔方。(征师 一作：征骑) 严秋筋竿劲，虏阵精且强。 天子按剑怒，使者遥相望。 雁行缘石径，鱼贯度飞梁。 箫鼓流汉思，旌甲被胡霜。 疾风冲塞起，沙砾自飘扬。 马毛缩如蝟，角弓不可张。 时危见臣节，世乱识忠良。 投躯报明主，身死为国殇。', '乐府');
INSERT INTO `poem` VALUES (1564, '出自蓟北门行', '唐代', '李白', '虏阵横北荒，胡星曜精芒。 羽书速惊电，烽火昼连光。 虎竹救边急，戎车森已行。 明主不安席，按剑心飞扬。 推毂出猛将，连旗登战场。 兵威冲绝幕，杀气凌穹苍。 列卒赤山下，开营紫塞傍。 孟冬沙风紧，旌旗飒凋伤。 画角悲海月，征衣卷天霜。 挥刃斩楼兰，弯弓射贤王。 单于一平荡，种落自奔亡。 收功报天子，行歌归咸阳。', '乐府');
INSERT INTO `poem` VALUES (1565, '君子有所思行', '唐代', '李白', '紫阁连终南，青冥天倪色。 凭崖望咸阳，宫阙罗北极。 万井惊画出，九衢如弦直。 渭水银河清，横天流不息。 朝野盛文物，衣冠何翕赩。 厩马散连山，军容威绝域。 伊皋运元化，卫霍输筋力。 歌钟乐未休，荣去老还逼。 圆光过满缺，太阳移中昃。 不散东海金，何争西飞匿。 无作牛山悲，恻怆泪沾臆。', '乐府');
INSERT INTO `poem` VALUES (1566, '空城雀', '唐代', '李白', '嗷嗷空城雀，身计何戚促。 本与鹪鹩群，不随凤凰族。 提携四黄口，饮乳未尝足。 食君糠秕馀，尝恐乌鸢逐。 耻涉太行险，羞营覆车粟。 天命有定端，守分绝所欲。', '乐府');
INSERT INTO `poem` VALUES (1567, '妾薄命', '唐代', '李白', '汉帝重阿娇，贮之黄金屋。 咳唾落九天，随风生珠玉。 宠极爱还歇，妒深情却疏。 长门一步地，不肯暂回车。 雨落不上天，水覆难再收。 君情与妾意，各自东西流。 昔日芙蓉花，今成断根草。 以色事他人，能得几时好。', '乐府');
INSERT INTO `poem` VALUES (1568, '名都篇', '两汉', '曹植', '名都多妖女，京洛出少年。 宝剑值千金，被服丽且鲜。 斗鸡东郊道，走马长楸间。 驰骋未能半，双兔过我前。 揽弓捷鸣镝，长驱上南山。 左挽因右发，一纵两禽连。 余巧未及展，仰手接飞鸢。 观者咸称善，众工归我妍。 归来宴平乐，美酒斗十千。 脍鲤臇胎鰕，炮鳖炙熊蹯。 鸣俦啸匹侣，列坐竟长筵。 连翩击鞠壤，巧捷惟万端。 白日西南驰，光景不可攀。 云散还城邑，清晨复来还。', '乐府');
INSERT INTO `poem` VALUES (1569, '美女篇', '两汉', '曹植', '美女妖且闲，采桑歧路间。 柔条纷冉冉，落叶何翩翩。 攘袖见素手，皓腕约金环。 头上金爵钗，腰佩翠琅玕。 明珠交玉体，珊瑚间木难。 罗衣何飘飘，轻裾随风还。 顾盼遗光彩，长啸气若兰。 行徒用息驾，休者以忘餐。 借问女安居，乃在城南端。 青楼临大路，高门结重关。 容华耀朝日，谁不希令颜？ 媒氏何所营？玉帛不时安。 佳人慕高义，求贤良独难。 众人徒嗷嗷，安知彼所观？ 盛年处房室，中夜起长叹。', '乐府');
INSERT INTO `poem` VALUES (1570, '白马篇', '两汉', '曹植', '白马饰金羁，连翩西北驰。借问谁家子，幽并游侠儿。 少小去乡邑，扬声沙漠垂。宿昔秉良弓，楛矢何参差。 控弦破左的，右发摧月支。仰手接飞猱，俯身散马蹄。 狡捷过猴猿，勇剽若豹螭。边城多警急，虏骑数迁移。 羽檄从北来，厉马登高堤。长驱蹈匈奴，左顾凌鲜卑。 弃身锋刃端，性命安可怀？父母且不顾，何言子与妻！ 名编壮士籍，不得中顾私。捐躯赴国难，视死忽如归！(名编 一作：名在)', '乐府');
INSERT INTO `poem` VALUES (1571, '发白马', '唐代', '李白', '将军发白马，旌节度黄河。 箫鼓聒川岳，沧溟涌涛波。 武安有振瓦，易水无寒歌。 铁骑若雪山，饮流涸滹沱。 扬兵猎月窟，转战略朝那。 倚剑登燕然，边烽列嵯峨。 萧条万里外，耕作五原多。 一扫清大漠，包虎戢金戈。', '乐府');
INSERT INTO `poem` VALUES (1572, '千里思', '唐代', '李白', '李陵没胡沙，苏武还汉家。 迢迢五原关，朔雪乱边花。 一去隔绝国，思归但长嗟。 鸿雁向西北，因书报天涯。', '乐府');
INSERT INTO `poem` VALUES (1573, '伤歌行', '未知', '佚名', '昭昭素明月，辉光烛我床。 忧人不能寐，耿耿夜何长。 微风吹闺闼，罗帷自飘扬。 揽衣曳长带，屣履下高堂。 东西安所之，徘徊以彷徨。 春鸟翻南飞，翩翩独翱翔。 悲声命俦匹，哀鸣伤我肠。 感物怀所思，泣涕忽沾裳。 伫立吐高吟，舒愤诉穹苍。', '乐府');
INSERT INTO `poem` VALUES (1574, '长相思·其二', '唐代', '李白', '日色欲尽花含烟，月明欲素愁不眠。(欲素 一作：如素) 赵瑟初停凤凰柱，蜀琴欲奏鸳鸯弦。 此曲有意无人传，愿随春风寄燕然。 忆君迢迢隔青天，昔日横波目，今作流泪泉。 不信妾断肠，归来看取明镜前。(断肠 一作：肠断)', '乐府');
INSERT INTO `poem` VALUES (1575, '仙人篇', '两汉', '曹植', '仙人揽六箸，对博太山隅。 湘娥拊琴瑟，秦女吹笙竽。 玉樽盈桂酒，河伯献神鱼。 四海一何局，九州安所如。 韩终与王乔，要我于天衢。 万里不足步，轻举凌太虚。 飞腾逾景云，高风吹我躯。 回驾观紫微，与帝合灵符。 阊阖正嵯峨，双阙万丈余。 玉树扶道生，白虎夹门枢。 驱风游四海，东过王母庐。 俯观五岳间，人生如寄居。 潜光养羽翼，进趣且徐徐。 不见昔轩辕，升龙出鼎湖。 徘徊九天下，与尔长相须。', '乐府');
INSERT INTO `poem` VALUES (1576, '春日行', '南北朝', '鲍照', '献岁发，吾将行。 春山茂，春日明。 园中鸟，多嘉声。 梅始发，柳始青。 泛舟舻，齐棹惊。 奏《采菱》，歌《鹿鸣》。 风微起，波微生。 弦亦发，酒亦倾。 入莲池，折桂枝。 芳袖动，芬叶披。 两相思，两不知。', '乐府');
INSERT INTO `poem` VALUES (1577, '春日行', '唐代', '李白', '深宫高楼入紫清，金作蛟龙盘绣楹。 佳人当窗弄白日，弦将手语弹鸣筝。 春风吹落君王耳，此曲乃是升天行。 因出天池泛蓬瀛，楼船蹙沓波浪惊。 三千双蛾献歌笑，挝钟考鼓宫殿倾，万姓聚舞歌太平。 我无为，人自宁。 三十六帝欲相迎，仙人飘翩下云輧。 帝不去，留镐京。 安能为轩辕，独往入窅冥。 小臣拜献南山寿，陛下万古垂鸿名。', '乐府');
INSERT INTO `poem` VALUES (1578, '古朗月行', '唐代', '李白', '小时不识月，呼作白玉盘。 又疑瑶台镜，飞在青云端。(青云 一作：白云) 仙人垂两足，桂树何团团。 白兔捣药成，问言与谁餐？ 蟾蜍蚀圆影，大明夜已残。 羿昔落九乌，天人清且安。 阴精此沦惑，去去不足观。 忧来其如何？凄怆摧心肝。', '乐府');
INSERT INTO `poem` VALUES (1579, '少年行四首', '唐代', '王维', '新丰美酒斗十千，咸阳游侠多少年。 相逢意气为君饮，系马高楼垂柳边。 出身仕汉羽林郎，初随骠骑战渔阳。 孰知不向边庭苦，纵死犹闻侠骨香。 一身能擘两雕弧，虏骑千重只似无。 偏坐金鞍调白羽，纷纷射杀五单于。 汉家君臣欢宴终，高议云台论战功。 天子临轩赐侯印，将军佩出明光宫。', '乐府');
INSERT INTO `poem` VALUES (1580, '游侠篇', '魏晋', '张华', '翩翩四公子，浊世称贤明。 龙虎方交争，七国并抗衡。 食客三千余，门下多豪英。 游说朝夕至，辩士自纵横。 孟尝东出关，济身由鸡鸣。 信陵西反魏，秦人不窥兵。 赵胜南诅楚，乃与毛遂行。 黄歇北适秦，太子还入荆。 美哉游侠士，何以尚四卿。 我则异于是，好古师老、彭。', '乐府');
INSERT INTO `poem` VALUES (1581, '同声歌', '两汉', '张衡', '邂逅承际会，得充君后房。 情好新交接，恐栗若探汤。 不才勉自竭，贱妾职所当。 绸缪主中馈，奉礼助蒸尝。 思为苑蒻席，在下蔽匡床。 愿为罗衾帱，在上卫风霜。 洒扫清枕席，鞮芬以狄香。 重户结金扃，高下华灯光。 衣解巾粉御，列图陈枕张。 素女为我师，仪态盈万方。 众夫希所见，天老教轩皇。 乐莫斯夜乐，没齿焉可忘。', '乐府');
INSERT INTO `poem` VALUES (1582, '侠客行', '唐代', '李白', '赵客缦胡缨，吴钩霜雪明。 银鞍照白马，飒沓如流星。 十步杀一人，千里不留行。 事了拂衣去，深藏身与名。 闲过信陵饮，脱剑膝前横。 将炙啖朱亥，持觞劝侯嬴。 三杯吐然诺，五岳倒为轻。 眼花耳热后，意气素霓生。 救赵挥金槌，邯郸先震惊。 千秋二壮士，烜赫大梁城。 纵死侠骨香，不惭世上英。 谁能书阁下，白首太玄经。', '乐府');
INSERT INTO `poem` VALUES (1583, '游子吟', '唐代', '孟郊', '慈母手中线，游子身上衣。 临行密密缝，意恐迟迟归。 谁言寸草心，报得三春晖。', '乐府');
INSERT INTO `poem` VALUES (1584, '秦女休行', '唐代', '李白', '西门秦氏女，秀色如琼花。 手挥白杨刀，清昼杀雠家。 罗袖洒赤血，英声凌紫霞。 直上西山去，关吏相邀遮。 婿为燕国王，身被诏狱加。 犯刑若履虎，不畏落爪牙。 素颈未及断，摧眉伏泥沙。 金鸡忽放赦，大辟得宽赊。 何惭聂政姊，万古共惊嗟。', '乐府');
INSERT INTO `poem` VALUES (1585, '壮士篇', '魏晋', '张华', '天地相震荡，回薄不知穷。 人物禀常格，有始必有终。 年时俯仰过，功名宜速崇。 壮士怀愤激，安能守虚冲？ 乘我大宛马，抚我繁弱弓。 长剑横九野，高冠拂玄穹。 慷慨成素霓，啸咤起清风。 震响骇八荒，奋威曜四戎。 濯鳞沧海畔，驰骋大漠中。 独步圣明世，四海称英雄。', '乐府');
INSERT INTO `poem` VALUES (1586, '秦女卷衣', '唐代', '李白', '天子居未央，妾侍卷衣裳。 顾无紫宫宠，敢拂黄金床。 水至亦不去，熊来尚可当。 微身奉日月，飘若萤之光。 愿君采葑菲，无以下体妨。', '乐府');
INSERT INTO `poem` VALUES (1587, '丽人行', '唐代', '杜甫', '三月三日天气新，长安水边多丽人。 态浓意远淑且真，肌理细腻骨肉匀。 绣罗衣裳照暮春，蹙金孔雀银麒麟。 头上何所有？翠微盍叶垂鬓唇。 背后何所见？珠压腰衱稳称身。 就中云幕椒房亲，赐名大国虢与秦。 紫驼之峰出翠釜，水精之盘行素鳞。 犀箸厌饫久未下，鸾刀缕切空纷纶。 黄门飞鞚不动尘，御厨络绎送八珍。 箫鼓哀吟感鬼神，宾从杂遝实要津。 后来鞍马何逡巡，当轩下马入锦茵。 杨花雪落覆白苹，青鸟飞去衔红巾。 炙手可热势绝伦，慎莫近前丞相嗔！', '乐府');
INSERT INTO `poem` VALUES (1588, '东飞伯劳歌', '南北朝', '萧衍', '东飞伯劳西飞燕，黄姑织女时相见。 谁家女儿对门居，开颜发艳照里闾。 南窗北牖挂明光，罗帷绮箔脂粉香。 女儿年几十五六，窈窕无双颜如玉。 三春已暮花从风，空留可怜与谁同。', '乐府');
INSERT INTO `poem` VALUES (1589, '树中草', '唐代', '李白', '鸟衔野田草，误入枯桑里。 客土植危根，逢春犹不死。 草木虽无情，因依尚可生。 如何同枝叶，各自有枯荣。', '乐府');
INSERT INTO `poem` VALUES (1590, '长相思·其一', '唐代', '李白', '长相思，在长安。 络纬秋啼金井阑，微霜凄凄簟色寒。 孤灯不明思欲绝，卷帷望月空长叹。 美人如花隔云端！ 上有青冥之长天，下有渌水之波澜。 天长路远魂飞苦，梦魂不到关山难。 长相思，摧心肝！', '乐府');
INSERT INTO `poem` VALUES (1591, '荆州歌', '唐代', '李白', '白帝城边足风波，瞿塘五月谁敢过。 荆州麦熟茧成蛾，缲丝忆君头绪多。 拨谷飞鸣奈妾何。', '乐府');
INSERT INTO `poem` VALUES (1592, '拟行路难·其六', '南北朝', '鲍照', '对案不能食，拔剑击柱长叹息。 丈夫生世会几时？安能蹀躞垂羽翼！ 弃置罢官去，还家自休息。 朝出与亲辞，暮还在亲侧。 弄儿床前戏，看妇机中织。 自古圣贤尽贫贱，何况我辈孤且直！', '乐府');
INSERT INTO `poem` VALUES (1593, '行路难三首', '唐代', '李白', '金樽清酒斗十千，玉盘珍羞直万钱。 停杯投箸不能食，拔剑四顾心茫然。 欲渡黄河冰塞川，将登太行雪满山。 闲来垂钓碧溪上，忽复乘舟梦日边。 行路难！行路难！多歧路，今安在？ 长风破浪会有时，直挂云帆济沧海。 大道如青天，我独不得出。 羞逐长安社中儿，赤鸡白雉赌梨栗。 弹剑作歌奏苦声，曳裾王门不称情。 淮阴市井笑韩信，汉朝公卿忌贾生。 君不见昔时燕家重郭隗，拥篲折节无嫌猜。 剧辛乐毅感恩分，输肝剖胆效英才。 昭王白骨萦蔓草，谁人更扫黄金台？ 行路难，归去来！ 有耳莫洗颍川水，有口莫食首阳蕨。 含光混世贵无名，何用孤高比云月？ 吾观自古贤达人，功成不退皆殒身。 子胥既弃吴江上，屈原终投湘水滨。 陆机雄才岂自保？李斯税驾苦不早。 华亭鹤唳讵可闻？上蔡苍鹰何足道？ 君不见吴中张翰称达生，秋风忽忆江东行。 且乐生前一杯酒，何须身后千载名？', '乐府');
INSERT INTO `poem` VALUES (1594, '远别离', '唐代', '李白', '远别离，古有皇英之二女，乃在洞庭之南，潇湘之浦。 海水直下万里深，谁人不言此离苦？ 日惨惨兮云冥冥，猩猩啼烟兮鬼啸雨。 我纵言之将何补？ 皇穹窃恐不照余之忠诚，雷凭凭兮欲吼怒。 尧舜当之亦禅禹。 君失臣兮龙为鱼，权归臣兮鼠变虎。 或云：尧幽囚，舜野死。 九疑联绵皆相似，重瞳孤坟竟何是？ 帝子泣兮绿云间，随风波兮去无还。 恸哭兮远望，见苍梧之深山。 苍梧山崩湘水绝，竹上之泪乃可灭。', '乐府');
INSERT INTO `poem` VALUES (1595, '久别离', '唐代', '李白', '别来几春未还家，玉窗五见樱桃花。 况有锦字书，开缄使人嗟。 至此肠断彼心绝。 云鬟绿鬓罢梳结，愁如回飙乱白雪。 去年寄书报阳台，今年寄书重相催。 东风兮东风，为我吹行云使西来。 待来竟不来，落花寂寂委青苔。', '乐府');
INSERT INTO `poem` VALUES (1596, '西洲曲', '南北朝', '佚名', '忆梅下西洲，折梅寄江北。 单衫杏子红，双鬓鸦雏色。 西洲在何处？两桨桥头渡。 日暮伯劳飞，风吹乌臼树。 树下即门前，门中露翠钿。 开门郎不至，出门采红莲。 采莲南塘秋，莲花过人头。 低头弄莲子，莲子清如水。 置莲怀袖中，莲心彻底红。 忆郎郎不至，仰首望飞鸿。 鸿飞满西洲，望郎上青楼。 楼高望不见，尽日栏杆头。 栏杆十二曲，垂手明如玉。 卷帘天自高，海水摇空绿。 海水梦悠悠，君愁我亦愁。 南风知我意，吹梦到西洲。', '乐府');
INSERT INTO `poem` VALUES (1597, '长干行二首', '唐代', '李白', '妾发初覆额，折花门前剧。 郎骑竹马来，绕床弄青梅。 同居长干里，两小无嫌猜， 十四为君妇，羞颜未尝开。 低头向暗壁，千唤不一回。 十五始展眉，愿同尘与灰。 常存抱柱信，岂上望夫台。 十六君远行，瞿塘滟滪堆。 五月不可触，猿声天上哀。 门前迟行迹，一一生绿苔。 苔深不能扫，落叶秋风早。 八月胡蝶来，双飞西园草。 感此伤妾心，坐愁红颜老。 早晚下三巴，预将书报家。 相迎不道远，直至长风沙。 忆妾深闺里，烟尘不曾识。 嫁与长干人，沙头候风色。 五月南风兴，思君下巴陵。 八月西风起，想君发扬子。 去来悲如何，见少离别多。 湘潭几日到，妾梦越风波。 昨夜狂风度，吹折江头树。 淼淼暗无边，行人在何处。 好乘浮云骢，佳期兰渚东。 鸳鸯绿蒲上，翡翠锦屏中。 自怜十五余，颜色桃花红。 那作商人妇，愁水复愁风。', '乐府');
INSERT INTO `poem` VALUES (1598, '悲歌', '两汉', '佚名', '悲歌可以当泣，远望可以当归。 思念故乡，郁郁累累。 欲归家无人，欲渡河无船。 心思不能言，肠中车轮转。', '乐府');
INSERT INTO `poem` VALUES (1599, '结客少年场行', '唐代', '李白', '紫燕黄金瞳，啾啾摇绿騣。 平明相驰逐，结客洛门东。 少年学剑术，凌轹白猿公。 珠袍曳锦带，匕首插吴鸿。 由来万夫勇，挟此生雄风。 托交从剧孟，买醉入新丰。 笑尽一杯酒，杀人都市中。 羞道易水寒，从令日贯虹。 燕丹事不立，虚没秦帝宫。 舞阳死灰人，安可与成功。', '乐府');
INSERT INTO `poem` VALUES (1600, '董娇饶', '两汉', '宋子侯', '洛阳城东路，桃李生路旁。 花花自相对，叶叶自相当。 春风东北起，花叶正低昂。 不知谁家子，提笼行采桑。 纤手折其枝，花落何飘扬。 请谢彼姝子，何为见损伤。 高秋八九月，白露变为霜。 终年会飘堕，安得久馨香。 秋时自零落，春月复芬芳。 何如盛年去，欢爱永相忘。 吾欲竟此曲，此曲愁人肠。 归来酌美酒，挟瑟上高堂。', '乐府');
INSERT INTO `poem` VALUES (1601, '羽林郎', '两汉', '辛延年', '昔有霍家奴，姓冯名子都。 依倚将军势，调笑酒家胡。 胡姬年十五，春日独当垆。 长裾连理带，广袖合欢襦。 头上蓝田玉，耳后大秦珠。 两鬟何窈窕，一世良所无。 一鬟五百万，两鬟千万余。 不意金吾子，娉婷过我庐。 银鞍何煜耀，翠盖空踟蹰。 就我求清酒，丝绳提玉壶。 就我求珍肴，金盘脍鲤鱼。 贻我青铜镜，结我红罗裾。 不惜红罗裂，何论轻贱躯。 男儿爱后妇，女子重前夫。 人生有新旧，贵贱不相逾。 多谢金吾子，私爱徒区区。', '乐府');
INSERT INTO `poem` VALUES (1602, '孔雀东南飞 / 古诗为焦仲卿妻作', '两汉', '佚名', '　　汉末建安中，庐江府小吏焦仲卿妻刘氏，为仲卿母所遣，自誓不嫁。其家逼之，乃投水而死。仲卿闻之，亦自缢于庭树。时人伤之，为诗云尔。 　　孔雀东南飞，五里一徘徊。 　　“十三能织素，十四学裁衣。十五弹箜篌，十六诵诗书。十七为君妇，心中常苦悲。君既为府吏，守节情不移。贱妾留空房，相见常日稀。鸡鸣入机织，夜夜不得息。三日断五匹，大人故嫌迟。非为织作迟，君家妇难为！妾不堪驱使，徒留无所施。便可白公姥，及时相遣归。” 　　府吏得闻之，堂上启阿母：“儿已薄禄相，幸复得此妇。结发同枕席，黄泉共为友。共事二三年，始尔未为久。女行无偏斜，何意致不厚。” 　　阿母谓府吏：“何乃太区区！此妇无礼节，举动自专由。吾意久怀忿，汝岂得自由！东家有贤女，自名秦罗敷。可怜体无比，阿母为汝求。便可速遣之，遣去慎莫留！” 　　府吏长跪告：“伏惟启阿母。今若遣此妇，终老不复取！” 　　阿母得闻之，槌床便大怒：“小子无所畏，何敢助妇语！吾已失恩义，会不相从许！” 　　府吏默无声，再拜还入户。举言谓新妇，哽咽不能语：“我自不驱卿，逼迫有阿母。卿但暂还家，吾今且报府。不久当归还，还必相迎取。以此下心意，慎勿违吾语。” 　　新妇谓府吏：“勿复重纷纭。往昔初阳岁，谢家来贵门。奉事循公姥，进止敢自专？昼夜勤作息，伶俜萦苦辛。谓言无罪过，供养卒大恩；仍更被驱遣，何言复来还！妾有绣腰襦，葳蕤自生光；红罗复斗帐，四角垂香囊；箱帘六七十，绿碧青丝绳，物物各自异，种种在其中。人贱物亦鄙，不足迎后人，留待作遗施，于今无会因。时时为安慰，久久莫相忘！” 　　鸡鸣外欲曙，新妇起严妆。著我绣夹裙，事事四五通。足下蹑丝履，头上玳瑁光。腰若流纨素，耳著明月珰。指如削葱根，口如含朱丹。纤纤作细步，精妙世无双。 　　上堂拜阿母，阿母怒不止。“昔作女儿时，生小出野里。本自无教训，兼愧贵家子。受母钱帛多，不堪母驱使。今日还家去，念母劳家里。”却与小姑别，泪落连珠子。“新妇初来时，小姑始扶床；今日被驱遣，小姑如我长。勤心养公姥，好自相扶将。初七及下九，嬉戏莫相忘。”出门登车去，涕落百余行。 　　府吏马在前，新妇车在后。隐隐何甸甸，俱会大道口。下马入车中，低头共耳语：“誓不相隔卿，且暂还家去。吾今且赴府，不久当还归。誓天不相负！” 　　新妇谓府吏：“感君区区怀！君既若见录，不久望君来。君当作磐石，妾当作蒲苇。蒲苇纫如丝，磐石无转移。我有亲父兄，性行暴如雷，恐不任我意，逆以煎我怀。”举手长劳劳，二情同依依 。 　　入门上家堂，进退无颜仪。阿母大拊掌，不图子自归：“十三教汝织，十四能裁衣，十五弹箜篌，十六知礼仪，十七遣汝嫁，谓言无誓违。汝今何罪过，不迎而自归？”兰芝惭阿母：“儿实无罪过。”阿母大悲摧。 　　还家十余日，县令遣媒来。云有第三郎，窈窕世无双。年始十八九，便言多令才。 　　阿母谓阿女：“汝可去应之。” 　　阿女含泪答：“兰芝初还时，府吏见丁宁，结誓不别离。今日违情义，恐此事非奇。自可断来信，徐徐更谓之。” 　　阿母白媒人：“贫贱有此女，始适还家门。不堪吏人妇，岂合令郎君？幸可广问讯，不得便相许。” 　　媒人去数日，寻遣丞请还，说有兰家女，承籍有宦官。云有第五郎，娇逸未有婚。遣丞为媒人，主簿通语言。直说太守家，有此令郎君，既欲结大义，故遣来贵门。 　　阿母谢媒人：“女子先有誓，老姥岂敢言！” 　　阿兄得闻之，怅然心中烦。举言谓阿妹：“作计何不量！先嫁得府吏，后嫁得郎君。否泰如天地，足以荣汝身。不嫁义郎体，其往欲何云？” 　　兰芝仰头答：“理实如兄言。谢家事夫婿，中道还兄门。处分适兄意，那得自任专！虽与府吏要，渠会永无缘。登即相许和，便可作婚姻。” 　　媒人下床去。诺诺复尔尔。还部白府君：“下官奉使命，言谈大有缘。”府君得闻之，心中大欢喜。视历复开书，便利此月内，六合正相应。良吉三十日，今已二十七，卿可去成婚。交语速装束，络绎如浮云。青雀白鹄舫，四角龙子幡。婀娜随风转，金车玉作轮。踯躅青骢马，流苏金镂鞍。赍钱三百万，皆用青丝穿。杂彩三百匹，交广市鲑珍。从人四五百，郁郁登郡门。 　　阿母谓阿女：“适得府君书，明日来迎汝。何不作衣裳？莫令事不举！” 　　阿女默无声，手巾掩口啼，泪落便如泻。移我琉璃榻，出置前窗下。左手持刀尺，右手执绫罗。朝成绣夹裙，晚成单罗衫。晻晻日欲暝，愁思出门啼。 　　府吏闻此变，因求假暂归。未至二三里，摧藏马悲哀。新妇识马声，蹑履相逢迎。怅然遥相望，知是故人来。举手拍马鞍，嗟叹使心伤：“自君别我后，人事不可量。果不如先愿，又非君所详。我有亲父母，逼迫兼弟兄。以我应他人，君还何所望！” 　　府吏谓新妇：“贺卿得高迁！磐石方且厚，可以卒千年；蒲苇一时纫，便作旦夕间。卿当日胜贵，吾独向黄泉！” 　　新妇谓府吏：“何意出此言！同是被逼迫，君尔妾亦然。黄泉下相见，勿违今日言！”执手分道去，各各还家门。生人作死别，恨恨那可论？念与世间辞，千万不复全！ 　　府吏还家去，上堂拜阿母：“今日大风寒，寒风摧树木，严霜结庭兰。儿今日冥冥，令母在后单。故作不良计，勿复怨鬼神！命如南山石，四体康且直！” 　　阿母得闻之，零泪应声落：“汝是大家子，仕宦于台阁。慎勿为妇死，贵贱情何薄！东家有贤女，窈窕艳城郭，阿母为汝求，便复在旦夕。” 　　府吏再拜还，长叹空房中，作计乃尔立。转头向户里，渐见愁煎迫。 　　其日牛马嘶，新妇入青庐。奄奄黄昏后，寂寂人定初。我命绝今日，魂去尸长留！揽裙脱丝履，举身赴清池。 　　府吏闻此事，心知长别离。徘徊庭树下，自挂东南枝。 　　两家求合葬，合葬华山傍。东西植松柏，左右种梧桐。枝枝相覆盖，叶叶相交通。中有双飞鸟，自名为鸳鸯。仰头相向鸣，夜夜达五更。行人驻足听，寡妇起彷徨。多谢后世人，戒之慎勿忘。', '乐府');
INSERT INTO `poem` VALUES (1603, '于阗采花', '唐代', '李白', '于阗采花人，自言花相似。 明妃一朝西入胡，胡中美女多羞死。 乃知汉地多名姝，胡中无花可方比。 丹青能令丑者妍，无盐翻在深宫里。 自古妒蛾眉，胡沙埋皓齿。', '乐府');
INSERT INTO `poem` VALUES (1604, '夜坐吟', '唐代', '李白', '冬夜夜寒觉夜长，沉吟久坐坐北堂。 冰合井泉月入闺，金缸青凝照悲啼。 金缸灭，啼转多。掩妾泪，听君歌。 歌有声，妾有情。情声合，两无违。 一语不入意，从君万曲梁尘飞。', '乐府');
INSERT INTO `poem` VALUES (1605, '古离别', '南北朝', '江淹', '远与君别者，乃至雁门关。 黄云蔽千里，游子何时还。 送君如昨日，檐前露已团。 不惜蕙草晚，所悲道里寒。 君在天一涯，妾身长别离。 愿一见颜色，不异琼树枝。 菟丝及水萍，所寄终不移。', '乐府');
INSERT INTO `poem` VALUES (1606, '独不见', '唐代', '李白', '白马谁家子，黄龙边塞儿。 天山三丈雪，岂是远行时。 春蕙忽秋草，莎鸡鸣西池。 风摧寒棕响，月入霜闺悲。 忆与君别年，种桃齐蛾眉。 桃今百馀尺，花落成枯枝。 终然独不见，流泪空自知。', '乐府');
INSERT INTO `poem` VALUES (1607, '定情诗', '魏晋', '繁钦', '我出东门游，邂逅承清尘。 思君即幽房，侍寝执衣巾。 时无桑中契，迫此路侧人。 我既媚君姿，君亦悦我颜。 何以致拳拳？绾臂双金环。 何以道殷勤？约指一双银。 何以致区区？耳中双明珠。 何以致叩叩？香囊系肘后。 何以致契阔？绕腕双跳脱。 何以结恩情？美玉缀罗缨。 何以结中心？素缕连双针。 何以结相于？金薄画搔头。 何以慰别离？耳后玳瑁钗。 何以答欢忻？纨素三条裙。 何以结愁悲？白绢双中衣。 与我期何所？乃期东山隅。 日旰兮不来，谷风吹我襦。 远望无所见，涕泣起踟蹰。 与我期何所？乃期山南阳。 日中兮不来，飘风吹我裳。 逍遥莫谁睹，望君愁我肠。 与我期何所？乃期西山侧。 日夕兮不来，踯躅长叹息。 远望凉风至，俯仰正衣服。 与我期何所？乃期山北岑。 日暮兮不来，凄风吹我襟。 望君不能坐，悲苦愁我心。 爱身以何为，惜我华色时。 中情既款款，然后克密期。 褰衣蹑茂草，谓君不我欺。 厕此丑陋质，徙倚无所之。 自伤失所欲，泪下如连丝。', '乐府');
INSERT INTO `poem` VALUES (1608, '纪辽东二首', '隋代', '杨广', '辽东海北翦长鲸，风云万里清。 方当销锋散马牛，旋师宴镐京。 前歌后舞振军威，饮至解戎衣。 判不徒行万里去，空道五原归。 秉旄仗节定辽东，俘馘变夷风。 清歌凯捷九都水，归宴洛阳宫。 策功行赏不淹留，全军藉智谋。 讵似南宫复道上，先封雍齿侯。', '乐府');
INSERT INTO `poem` VALUES (1609, '辽东行', '唐代', '王建', '辽东万里辽水曲，古戍无城复无屋。 黄云盖地雪作山，不惜黄金买衣服。 战回各自收弓箭，正西回面家乡远。 年年郡县送征人，将与辽东作丘坂。 宁为草木乡中生，有身不向辽东行。', '乐府');
INSERT INTO `poem` VALUES (1610, '渡辽水', '唐代', '王建', '渡辽水，此去咸阳五千里。 来时父母知隔生，重著衣裳如送死。 亦有白骨归咸阳，营家各与题本乡。 身在应无回渡日，驻马相看辽水傍。', '乐府');
INSERT INTO `poem` VALUES (1611, '昔昔盐', '南北朝', '薛道衡', '垂柳覆金堤，蘼芜叶复齐。 水溢芙蓉沼，花飞桃李蹊。 采桑秦氏女，织锦窦家妻。 关山别荡子，风月守空闺。 恒敛千金笑，长垂双玉啼。 盘龙随镜隐，彩凤逐帷低。 飞魂同夜鹊，倦寝忆晨鸡。 暗牖悬蛛网，空梁落燕泥。 前年过代北，今岁往辽西。 一去无消息，那能惜马蹄。', '乐府');
INSERT INTO `poem` VALUES (1612, '清平调·其一', '唐代', '李白', '云想衣裳花想容，春风拂槛露华浓。 若非群玉山头见，会向瑶台月下逢。', '乐府');
INSERT INTO `poem` VALUES (1613, '清平调·其二', '唐代', '李白', '一枝红艳露凝香，云雨巫山枉断肠。 借问汉宫谁得似，可怜飞燕倚新妆。', '乐府');
INSERT INTO `poem` VALUES (1614, '清平调·其三', '唐代', '李白', '名花倾国两相欢，长得君王带笑看。 解释春风无限恨，沉香亭北倚阑干。', '乐府');
INSERT INTO `poem` VALUES (1615, '送元二使安西 / 渭城曲', '唐代', '王维', '渭城朝雨浥轻尘，客舍青青柳色新。(一作：客舍依依杨柳春) 劝君更尽一杯酒，西出阳关无故人。', '乐府');
INSERT INTO `poem` VALUES (1616, '杨柳枝词', '唐代', '白居易', '一树春风千万枝，嫩于金色软于丝。 永丰西角荒园里，尽日无人属阿谁？', '乐府');
INSERT INTO `poem` VALUES (1617, '诏取永丰柳植禁苑感赋', '唐代', '白居易', '一树衰残委泥土，双枝荣耀植天庭。 定知玄象今春后，柳宿光中添两星。', '乐府');
INSERT INTO `poem` VALUES (1618, '忆江南词三首', '唐代', '白居易', '江南好，风景旧曾谙。日出江花红胜火，春来江水绿如蓝。能不忆江南？ 江南忆，最忆是杭州。山寺月中寻桂子，郡亭枕上看潮头。何日更重游！ 江南忆，其次忆吴宫。吴酒一杯春竹叶，吴娃双舞醉芙蓉。早晚复相逢！', '乐府');
INSERT INTO `poem` VALUES (1619, '竹枝词二首·其一', '唐代', '刘禹锡', '杨柳青青江水平，闻郎江上踏歌声。(踏歌声 一作：唱歌声) 东边日出西边雨，道是无晴却有晴。(却有晴 一作：还有晴)', '乐府');
INSERT INTO `poem` VALUES (1620, '宫中调笑·团扇', '唐代', '王建', '团扇，团扇，美人病来遮面。 玉颜憔悴三年，谁复商量管弦。 弦管，弦管，春草昭阳路断。', '乐府');
INSERT INTO `poem` VALUES (1621, '鸡鸣歌', '未知', '佚名', '东方欲明星烂烂，汝南晨鸡登坛唤。 曲终漏尽严具陈，月没星稀天下旦。 千门万户递鱼钥，宫中城上飞乌鹊。', '乐府');
INSERT INTO `poem` VALUES (1622, '舂歌', '两汉', '戚夫人', '子为王，母为虏。 终日舂薄暮，常与死为伍。 相离三千里，当谁使告汝。', '乐府');
INSERT INTO `poem` VALUES (1623, '秋风辞', '两汉', '刘彻', '秋风起兮白云飞，草木黄落兮雁南归。 兰有秀兮菊有芳，怀佳人兮不能忘。 泛楼船兮济汾河，横中流兮扬素波。 箫鼓鸣兮发棹歌，欢乐极兮哀情多。 少壮几时兮奈老何！', '乐府');
INSERT INTO `poem` VALUES (1624, '李延年歌', '两汉', '李延年', '北方有佳人，绝世而独立。 一顾倾人城，再顾倾人国。 宁不知倾城与倾国？佳人难再得。', '乐府');
INSERT INTO `poem` VALUES (1625, '中山孺子妾歌', '唐代', '李白', '中山孺子妾，特以色见珍。 虽然不如延年妹，亦是当时绝世人。 桃李出深井，花艳惊上春。 一贵复一贱，关天岂由身。 芙蓉老秋霜，团扇羞网尘。 戚姬髡发入舂市，万古共悲辛。', '乐府');
INSERT INTO `poem` VALUES (1626, '匈奴歌', '两汉', '佚名', '失我焉支山，令我妇女无颜色。 失我祁连山，使我六畜不蕃息。', '乐府');
INSERT INTO `poem` VALUES (1627, '司马将军歌', '唐代', '李白', '狂风吹古月，窃弄章华台。 北落明星动光彩，南征猛将如云雷。 手中电击倚天剑，直斩长鲸海水开。 我见楼船壮心目，颇似龙骧下三蜀。 扬兵习战张虎旗，江中白浪如银屋。 身居玉帐临河魁，紫髯若戟冠崔嵬， 细柳开营揖天子，始知灞上为婴孩。 羌笛横吹阿亸回，向月楼中吹落梅。 将军自起舞长剑，壮士呼声动九垓。 功成献凯见明主，丹青画像麒麟台。', '乐府');
INSERT INTO `poem` VALUES (1628, '上云乐', '唐代', '李白', '金天之西，白日所没。 康老胡雏，生彼月窟。 巉岩容仪，戍削风骨。 碧玉炅炅双目瞳，黄金拳拳两鬓红。 华盖垂下睫，嵩岳临上唇。 不睹诡谲貌，岂知造化神。 大道是文康之严父，元气乃文康之老亲。 抚顶弄盘古，推车转天轮。 云见日月初生时，铸冶火精与水银。 阳乌未出谷，顾兔半藏身。 女娲戏黄土，团作愚下人。 散在六合间，濛濛若沙尘。 生死了不尽，谁明此胡是仙真。 西海栽若木，东溟植扶桑。 别来几多时，枝叶万里长。 中国有七圣，半路颓洪荒。 陛下应运起，龙飞入咸阳。 赤眉立盆子，白水兴汉光。 叱咤四海动，洪涛为簸扬。 举足蹋紫微，天关自开张。 老胡感至德，东来进仙倡。 五色师子，九苞凤凰。 是老胡鸡犬，鸣舞飞帝乡。 淋漓飒沓，进退成行。 能胡歌，献汉酒。 跪双膝，立两肘。 散花指天举素手。 拜龙颜，献圣寿。 北斗戾，南山摧。 天子九九八十一万岁，长倾万岁杯。', '乐府');
INSERT INTO `poem` VALUES (1629, '敕勒歌', '南北朝', '乐府诗集', '敕勒川，阴山下。天似穹庐，笼盖四野。 天苍苍，野茫茫。风吹草低见牛羊。', '乐府');
INSERT INTO `poem` VALUES (1630, '箜篌谣', '唐代', '李白', '攀天莫登龙，走山莫骑虎。 贵贱结交心不移，唯有严陵及光武。 周公称大圣，管蔡宁相容。 汉谣一斗粟，不与淮南舂。 兄弟尚路人，吾心安所从。 他人方寸间，山海几千重。 轻言托朋友，对面九疑峰。 开花必早落，桃李不如松。 管鲍久已死，何人继其踪。', '乐府');
INSERT INTO `poem` VALUES (1631, '老将行', '唐代', '王维', '少年十五二十时，步行夺得胡马骑。 射杀山中白额虎，肯数邺下黄须儿！ 一身转战三千里，一剑曾当百万师。 汉兵奋迅如霹雳，虏骑崩腾畏蒺藜。 卫青不败由天幸，李广无功缘数奇。 自从弃置便衰朽，世事蹉跎成白首。 昔时飞箭无全目，今日垂杨生左肘。 路旁时卖故侯瓜，门前学种先生柳。 苍茫古木连穷巷，寥落寒山对虚牖。 誓令疏勒出飞泉，不似颍川空使酒。 贺兰山下阵如云，羽檄交驰日夕闻。 节使三河募年少，诏书五道出将军。 试拂铁衣如雪色，聊持宝剑动星文。 愿得燕弓射大将，耻令越甲鸣吾君。 莫嫌旧日云中守，犹堪一战取功勋。', '乐府');
INSERT INTO `poem` VALUES (1632, '洛阳女儿行', '唐代', '王维', '洛阳女儿对门居，才可颜容十五余。 良人玉勒乘骢马，侍女金盘脍鲤鱼。 画阁朱楼尽相望，红桃绿柳垂檐向。 罗帷送上七香车，宝扇迎归九华帐。 狂夫富贵在青春，意气骄奢剧季伦。 自怜碧玉亲教舞，不惜珊瑚持与人。 春窗曙灭九微火，九微片片飞花琐。 戏罢曾无理曲时，妆成祗是熏香坐。 城中相识尽繁华，日夜经过赵李家。 谁怜越女颜如玉，贫贱江头自浣纱。', '乐府');
INSERT INTO `poem` VALUES (1633, '悲陈陶', '唐代', '杜甫', '孟冬十郡良家子，血作陈陶泽中水。 野旷天清无战声，四万义军同日死。 群胡归来血洗箭，仍唱胡歌饮都市。 都人回面向北啼，日夜更望官军至。', '乐府');
INSERT INTO `poem` VALUES (1634, '悲青坂', '唐代', '杜甫', '我军青坂在东门，天寒饮马太白窟。 黄头奚儿日向西，数骑弯弓敢驰突。 山雪河冰野萧瑟，青是烽烟白人骨。 焉得附书与我军，忍待明年莫仓卒。', '乐府');
INSERT INTO `poem` VALUES (1635, '哀江头', '唐代', '杜甫', '少陵野老吞声哭，春日潜行曲江曲。 江头宫殿锁千门，细柳新蒲为谁绿？ 忆昔霓旌下南苑，苑中万物生颜色。 昭阳殿里第一人，同辇随君侍君侧。 辇前才人带弓箭，白马嚼啮黄金勒。 翻身向天仰射云，一笑正坠双飞翼。 明眸皓齿今何在？血污游魂归不得。 清渭东流剑阁深，去住彼此无消息。 人生有情泪沾臆，江水江花岂终极！ 黄昏胡骑尘满城，欲往城南望城北。', '乐府');
INSERT INTO `poem` VALUES (1636, '哀王孙', '唐代', '杜甫', '长安城头头白乌，夜飞延秋门上呼。 又向人家啄大屋，屋底达官走避胡。 金鞭断折九马死，骨肉不得同驰驱。 腰下宝玦青珊瑚，可怜王孙泣路隅。 问之不肯道姓名，但道困苦乞为奴。 已经百日窜荆棘，身上无有完肌肤。 高帝子孙尽隆准，龙种自与常人殊。 豺狼在邑龙在野，王孙善保千金躯。 不敢长语临交衢，且为王孙立斯须。 昨夜东风吹血腥，东来橐驼满旧都。 朔方健儿好身手，昔何勇锐今何愚。 窃闻天子已传位，圣德北服南单于。 花门剺面请雪耻，慎勿出口他人狙。 哀哉王孙慎勿疏，五陵佳气无时无。', '乐府');
INSERT INTO `poem` VALUES (1637, '卖炭翁', '唐代', '白居易', '卖炭翁，伐薪烧炭南山中。 满面尘灰烟火色，两鬓苍苍十指黑。 卖炭得钱何所营？身上衣裳口中食。 可怜身上衣正单，心忧炭贱愿天寒。 夜来城外一尺雪，晓驾炭车辗冰辙。 牛困人饥日已高，市南门外泥中歇。 翩翩两骑来是谁？黄衣使者白衫儿。 手把文书口称敕，回车叱牛牵向北。 一车炭，千余斤，宫使驱将惜不得。 半匹红绡一丈绫，系向牛头充炭直。', '乐府');
INSERT INTO `poem` VALUES (1638, '桃源行', '唐代', '王维', '渔舟逐水爱山春，两岸桃花夹古津。 坐看红树不知远，行尽青溪不见人。 山口潜行始隈隩，山开旷望旋平陆。 遥看一处攒云树，近入千家散花竹。 樵客初传汉姓名，居人未改秦衣服。 居人共住武陵源，还从物外起田园。 月明松下房栊静，日出云中鸡犬喧。 惊闻俗客争来集，竞引还家问都邑。 平明闾巷扫花开，薄暮渔樵乘水入。 初因避地去人间，及至成仙遂不还。 峡里谁知有人事，世中遥望空云山。 不疑灵境难闻见，尘心未尽思乡县。 出洞无论隔山水，辞家终拟长游衍。 自谓经过旧不迷，安知峰壑今来变。 当时只记入山深，青溪几度到云林。 春来遍是桃花水，不辨仙源何处寻。', '乐府');
INSERT INTO `poem` VALUES (1639, '横江词六首', '唐代', '李白', '人道横江好，侬道横江恶。 一风三日吹倒山，白浪高于瓦官阁。 海潮南去过浔阳，牛渚由来险马当。 横江欲渡风波恶，一水牵愁万里长。 横江西望阻西秦，汉水东连扬子津。 白浪如山那可渡，狂风愁杀峭帆人。 海神来过恶风回，浪打天门石壁开。 浙江八月何如此？涛似连山喷雪来！ 横江馆前津吏迎，向余东指海云生。 郎今欲渡缘何事？如此风波不可行！ 月晕天风雾不开，海鲸东蹙百川回。 惊波一起三山动，公无渡河归去来。', '乐府');
INSERT INTO `poem` VALUES (1640, '静夜思', '唐代', '李白', '床前明月光，疑是地上霜。 举头望明月，低头思故乡。', '乐府');
INSERT INTO `poem` VALUES (1641, '兵车行', '唐代', '杜甫', '车辚辚，马萧萧，行人弓箭各在腰。 耶娘妻子走相送，尘埃不见咸阳桥。(耶娘 一作：“爷”) 牵衣顿足拦道哭，哭声直上干云霄。 道旁过者问行人，行人但云点行频。 或从十五北防河，便至四十西营田。 去时里正与裹头，归来头白还戍边。 边庭流血成海水，武皇开边意未已。 君不闻汉家山东二百州，千村万落生荆杞。 纵有健妇把锄犁，禾生陇亩无东西。 况复秦兵耐苦战，被驱不异犬与鸡。 长者虽有问，役夫敢申恨？ 且如今年冬，未休关西卒。 县官急索租，租税从何出？ 信知生男恶，反是生女好。 生女犹得嫁比邻，生男埋没随百草。 君不见，青海头，古来白骨无人收。 新鬼烦冤旧鬼哭，天阴雨湿声啾啾！', '乐府');
INSERT INTO `poem` VALUES (1642, '塞上曲·其一', '唐代', '王昌龄', '蝉鸣空桑林，八月萧关道。 出塞入塞寒，处处黄芦草。 从来幽并客，皆共尘沙老。 莫学游侠儿，矜夸紫骝好。', '乐府');
INSERT INTO `poem` VALUES (1643, '塞下曲六首', '唐代', '李白', '五月天山雪，无花只有寒。 笛中闻折柳，春色未曾看。 晓战随金鼓，宵眠抱玉鞍。 愿将腰下剑，直为斩楼兰。 天兵下北荒，胡马欲南饮。 横戈从百战，直为衔恩甚。 握雪海上餐，拂沙陇头寝。 何当破月氏，然后方高枕。 骏马似风飙，鸣鞭出渭桥。 弯弓辞汉月，插羽破天骄。 阵解星芒尽，营空海雾消。 功成画麟阁，独有霍嫖姚。 白马黄金塞，云砂绕梦思。 那堪愁苦节，远忆边城儿。 萤飞秋窗满，月度霜闺迟。 摧残梧桐叶，萧飒沙棠枝。 无时独不见，流泪空自知。 塞虏乘秋下，天兵出汉家。 将军分虎竹，战士卧龙沙。 边月随弓影，胡霜拂剑花。 玉关殊未入，少妇莫长嗟。 烽火动沙漠，连照甘泉云。 汉皇按剑起，还召李将军。 兵气天上合，鼓声陇底闻。 横行负勇气，一战净妖氛。', '乐府');
INSERT INTO `poem` VALUES (1644, '塞下曲', '唐代', '王昌龄', '饮马渡秋水，水寒风似刀。 平沙日未没，黯黯见临洮。 昔日长城战，咸言意气高。 黄尘足今古，白骨乱蓬蒿。', '乐府');
INSERT INTO `poem` VALUES (1645, '塞下曲·秋风夜渡河', '唐代', '王昌龄', '秋风夜渡河，吹却雁门桑。 遥见胡地猎，鞴马宿严霜。 五道分兵去，孤军百战场。 功多翻下狱，士卒但心伤。', '乐府');
INSERT INTO `poem` VALUES (1646, '节妇吟·寄东平李司空师道', '唐代', '张籍', '君知妾有夫，赠妾双明珠。 感君缠绵意，系在红罗襦。 妾家高楼连苑起，良人执戟明光里。 知君用心如日月，事夫誓拟同生死。 还君明珠双泪垂，恨不相逢未嫁时。', '乐府');
INSERT INTO `poem` VALUES (1647, '征妇怨', '唐代', '张籍', '九月匈奴杀边将，汉军全没辽水上。 万里无人收白骨，家家城下招魂葬。 妇人依倚子与夫，同居贫贱心亦舒。 夫死战场子在腹，妾身虽存如昼烛。', '乐府');
INSERT INTO `poem` VALUES (1648, '青青水中蒲二首', '唐代', '韩愈', '青青水中蒲，下有一双鱼。 君今上陇去，我在与谁居？ 青青水中蒲，长在水中居。 寄语浮萍草，相随我不如。', '乐府');
INSERT INTO `poem` VALUES (1649, '堤上行二首', '唐代', '刘禹锡', '酒旗相望大堤头，堤下连樯堤上楼。 日暮行人争渡急，桨声幽轧满中流。 江南江北望烟波，入夜行人相应歌。 《桃叶》传情《竹枝》怨，水流无限月明多。', '乐府');
INSERT INTO `poem` VALUES (1650, '更衣曲', '唐代', '刘禹锡', '博山炯炯吐香雾，红烛引至更衣处。 夜如何其夜漫漫，邻鸡未鸣寒雁度。 庭前雪压松桂丛，廊下点点悬纱笼。 满堂醉客争笑语，嘈囋琵琶青幕中。', '乐府');
INSERT INTO `poem` VALUES (1651, '上阳白发人', '唐代', '白居易', '上阳人，上阳人，红颜暗老白发新。 绿衣监使守宫门，一闭上阳多少春。 玄宗末岁初选入，入时十六今六十。 同时采择百余人，零落年深残此身。 忆昔吞悲别亲族，扶入车中不教哭。 皆云入内便承恩，脸似芙蓉胸似玉。 未容君王得见面，已被杨妃遥侧目。 妒令潜配上阳宫，一生遂向空房宿。 宿空房，秋夜长，夜长无寐天不明。 耿耿残灯背壁影，萧萧暗雨打窗声。 春日迟，日迟独坐天难暮。 宫莺百啭愁厌闻，梁燕双栖老休妒。 莺归燕去长悄然，春往秋来不记年。 唯向深宫望明月，东西四五百回圆。 今日宫中年最老，大家遥赐尚书号。 小头鞵履窄衣裳，青黛点眉眉细长。 外人不见见应笑，天宝末年时世妆。 上阳人，苦最多。 　 少亦苦，老亦苦，少苦老苦两如何！ 君不见昔时吕向美人赋， 又不见今日上阳白发歌！', '乐府');
INSERT INTO `poem` VALUES (1652, '杜陵叟', '唐代', '白居易', '杜陵叟，杜陵居，岁种薄田一顷余。 三月无雨旱风起，麦苗不秀多黄死。 九月降霜秋早寒，禾穗未熟皆青乾。 长吏明知不申破，急敛暴征求考课。 典桑卖地纳官租，明年衣食将何如？ 剥我身上帛，夺我口中粟。 虐人害物即豺狼，何必钩爪锯牙食人肉？ 不知何人奏皇帝，帝心恻隐知人弊。 白麻纸上书德音，京畿尽放今年税。 昨日里胥方到门，手持敕牒榜乡村。 十家租税九家毕，虚受吾君蠲免恩。', '乐府');
INSERT INTO `poem` VALUES (1653, '农臣怨', '唐代', '元结', '农臣何所怨，乃欲干人主。 不识天地心，徒然怨风雨。 将论草木患，欲说昆虫苦。 巡回宫阙傍，其意无由吐。 一朝哭都市，泪尽归田亩。 谣颂若采之，此言当可取。', '乐府');
INSERT INTO `poem` VALUES (1654, '田家行', '唐代', '王建', '男声欣欣女颜悦，人家不怨言语别。 五月虽热麦风清，檐头索索缲车鸣。 野蚕作茧人不取，叶间扑扑秋蛾生。 麦收上场绢在轴，的知输得官家足。 不望入口复上身，且免向城卖黄犊。 回家衣食无厚薄，不见县门身即乐。', '乐府');
INSERT INTO `poem` VALUES (1655, '织妇词', '唐代', '元稹', '织妇何太忙，蚕经三卧行欲老。 蚕神女圣早成丝，今年丝税抽征早。 早征非是官人恶，去岁官家事戎索。 征人战苦束刀疮，主将勋高换罗幕。 缫丝织帛犹努力，变缉撩机苦难织。 东家头白双女儿，为解挑纹嫁不得。 檐前袅袅游丝上，上有蜘蛛巧来往。 羡他虫豸解缘天，能向虚空织罗网。', '乐府');
INSERT INTO `poem` VALUES (1656, '青楼曲二首', '唐代', '王昌龄', '白马金鞍从武皇，旌旗十万宿长杨。 楼头小妇鸣筝坐，遥见飞尘入建章。 驰道杨花满御沟，红妆缦绾上青楼。 金章紫绶千馀骑，夫婿朝回初拜侯。', '乐府');
INSERT INTO `poem` VALUES (1657, '田家词 / 田家行', '唐代', '元稹', '牛靿咤咤，田确确，旱块敲牛蹄趵趵。 种得官仓珠颗谷，六十年来兵簇簇，日月食粮车辘辘。 一日官军收海服，驱牛驾车食牛肉，归来攸得牛两角。 重铸锄犁作斤，姑舂妇担去轮官，输官不足归卖屋。 愿官早胜仇早覆，农死有儿牛有犊，不遣官军粮不足。', '乐府');
INSERT INTO `poem` VALUES (1658, '红线毯', '唐代', '白居易', '红线毯，择茧缲丝清水煮，拣丝练线红蓝染。 染为红线红于蓝，织作披香殿上毯。 披香殿广十丈馀，红线织成可殿铺。 彩丝茸茸香拂拂，线软花虚不胜物。 美人蹋上歌舞来，罗袜绣鞋随步没。 太原毯涩毳缕硬，蜀都褥薄锦花冷， 不如此毯温且柔，年年十月来宣州。 宣城太守加样织，自谓为臣能竭力。 百夫同担进宫中，线厚丝多卷不得。 宣城太守知不知，一丈毯，千两丝。 地不知寒人要暖，少夺人衣作地衣。', '乐府');
